INSERT INTO crossref_explanations (source_book_id, source_chapter, source_verse, target_book_id, target_chapter, target_verse_start, target_verse_end, anchor_phrase, title, content, connection_type, weight, confidence, prompt_version, model)
VALUES
(44, 8, 14, 52, 3, 2, 2, 'sent unto them', 'Sending Ministers for Support', 'This connection highlights the theme of sending individuals to support and establish faith in communities. Both passages emphasize the role of apostles and ministers in nurturing believers.', 'theme', 0.34199999999999997, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 14, 44, 8, 1, 1, 'received the word of God', 'Persecution and Spread of the Church', 'Both passages emphasize the scattering of believers due to persecution, which leads to the spread of the word of God in Samaria. The apostles'' response to this spread demonstrates their commitment to supporting the new believers.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 14, 48, 2, 9, 9, 'the apostles which were at Jerusalem', 'Apostolic Authority and Fellowship', 'This connection highlights the role of apostles in spreading the Gospel. Both passages emphasize the collaboration of Peter and John in Samaria and the fellowship extended to Paul and Barnabas by key apostles.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 14, 40, 13, 23, 23, 'received the word of God', 'Receiving the Word of God', 'This connection highlights the theme of receiving the word of God and its fruitful impact. In Acts, the apostles respond to Samaria receiving the word, while in Matthew, the emphasis is on those who hear and understand the word, leading to fruitful outcomes.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 14, 42, 22, 8, 8, 'Peter and John', 'Connection of Peter and John', 'This connection highlights the recurring partnership of Peter and John in significant missions. Both passages emphasize their roles in responding to the needs of the community, whether preparing for the Passover or spreading the word of God.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 14, 43, 12, 48, 48, 'received the word of God', 'Connection of Word Reception', 'This connection highlights the importance of receiving the word of God. In Acts, the apostles respond to the reception of God''s word in Samaria, while in John, the rejection of Jesus'' words leads to judgment.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 15, 44, 2, 38, 38, 'receive the Holy Ghost', 'Connection of Holy Spirit Reception', 'Both passages emphasize the reception of the Holy Ghost. Acts 2:38 mentions receiving the gift of the Holy Ghost after repentance and baptism, while Acts 8:15 describes prayer for the Holy Ghost to be received.', 'theme', 0.356, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 15, 43, 16, 23, 24, 'prayed for them, that they might receive', 'Connection of Prayer and Receiving', 'This connection highlights the act of prayer as a means to receive the Holy Spirit and the promise of receiving from the Father. Both passages emphasize the importance of asking in faith to receive spiritual blessings.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 15, 40, 18, 19, 19, 'prayed for them', 'Prayer and Agreement in Faith', 'This connection highlights the power of prayer and agreement in seeking divine intervention. Both passages emphasize the importance of collective prayer in receiving blessings from God.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 15, 43, 14, 13, 14, 'prayed for them, that they might receive', 'Connection of Prayer and Holy Spirit', 'This connection highlights the act of prayer in both passages. In Acts, prayer is made for the Holy Ghost, while in John, asking in Jesus'' name is emphasized as a means to receive what is requested.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 15, 50, 1, 19, 19, 'prayed for them, that they might receive the Holy Ghost', 'Connection of Prayer and Spirit', 'Both passages emphasize the role of prayer in receiving the Holy Spirit. Acts 8:15 speaks of prayer for the Holy Ghost, while Philippians 1:19 mentions prayer and the supply of the Spirit.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 16, 44, 2, 38, 38, 'baptized in the name of the Lord Jesus', 'Connection of Baptism and Holy Spirit', 'This connection highlights the relationship between baptism and the reception of the Holy Spirit. Both passages emphasize the act of being baptized in the name of Jesus, linking it to the subsequent experience of receiving the Holy Ghost.', 'theme', 0.37, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 16, 44, 19, 2, 2, 'only they were baptized in the name of the Lord Jesus', 'Connection of Holy Spirit Reception', 'Both passages emphasize the reception of the Holy Spirit following baptism. In Acts 8:16, the believers were baptized but had not yet received the Holy Spirit, similar to the believers in Acts 19:2 who were unaware of the Holy Spirit''s existence.', 'theme', 0.356, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 16, 40, 28, 19, 19, 'baptized in the name of the Lord Jesus', 'Baptism in the Name', 'Both passages emphasize the act of baptism, with Acts 8:16 noting that individuals were baptized in the name of the Lord Jesus, while Matthew 28:19 instructs to baptize in the name of the Father, Son, and Holy Ghost. This connection highlights the significance of baptism in the early Christian community.', 'theme', 0.349, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 16, 46, 1, 13, 15, 'baptized in the name of the Lord Jesus', 'Baptism in the Name of Jesus', 'Both passages emphasize the importance of baptism in the name of Jesus. Acts 8:16 notes that believers were baptized in the name of the Lord Jesus, while 1 Corinthians 1:13-15 questions the validity of being baptized in the name of Paul, highlighting the centrality of Christ in baptism.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 16, 48, 3, 27, 27, 'baptized in the name of the Lord Jesus', 'Baptism and Identity in Christ', 'This connection highlights the significance of baptism as a means of entering into a relationship with Christ. Both passages emphasize the act of being baptized in the name of Jesus and its transformative effect on believers.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 17, 44, 19, 6, 6, 'laid they their hands on them', 'Connection of Holy Spirit Reception', 'Both passages emphasize the act of laying hands as a means for receiving the Holy Spirit. This connection highlights the continuity of the practice within the early church.', 'theme', 0.37, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 17, 44, 6, 6, 6, 'laid they their hands on them', 'Connection of Laying on Hands', 'Both passages emphasize the act of laying hands on individuals. In Acts 6:6, this act precedes the apostles'' prayer, while in Acts 8:17, it leads to the reception of the Holy Ghost.', 'theme', 0.356, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 17, 55, 1, 6, 6, NULL, 'Connection of Spiritual Gifts', 'Both passages emphasize the act of laying on of hands as a means to receive spiritual gifts, specifically the Holy Spirit and the gift of God. This connection highlights the importance of this practice in the early church.', 'theme', 0.34199999999999997, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 17, 4, 8, 10, 10, 'laid they their hands on them', 'Connection of Hand-Laying Rituals', 'Both passages emphasize the act of laying hands as a means of imparting a spiritual blessing or gift. In Acts, the laying on of hands results in receiving the Holy Ghost, while in Numbers, it signifies the consecration of the Levites.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 17, 4, 27, 18, 18, NULL, 'Connection of Spirit and Laying Hands', 'Both passages emphasize the act of laying hands to impart the Spirit. In Acts, the apostles lay hands on believers to receive the Holy Ghost, while in Numbers, Moses lays hands on Joshua to signify the Spirit''s presence in him.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 17, 58, 6, 2, 2, NULL, 'Connection of Laying on of Hands', 'This connection highlights the practice of laying on of hands as a significant act in both passages. Acts 8:17 describes the apostles laying hands on believers to receive the Holy Spirit, while Hebrews 6:2 mentions the doctrine of laying on of hands as a foundational teaching.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 17, 45, 1, 11, 11, NULL, 'Imparting Spiritual Gifts', 'This connection highlights the act of imparting spiritual gifts through the laying on of hands. Both passages emphasize the transmission of the Holy Spirit and spiritual gifts to establish believers.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 18, 54, 6, 5, 5, NULL, 'Corruption and Misuse of Faith', 'This connection highlights the theme of corrupt intentions in faith practices. Both passages emphasize the danger of equating spiritual gifts or godliness with financial gain.', 'theme', 0.34199999999999997, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 18, 40, 10, 8, 8, NULL, 'Connection of Free Giving', 'This connection highlights the theme of freely giving spiritual gifts. In Acts, Simon''s attempt to buy the Holy Spirit contrasts with the command in Matthew to give freely what has been received.', 'theme', 0.33499999999999996, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 18, 12, 8, 9, 9, 'offered them money', 'Connection of Gifts and Requests', 'This connection highlights the theme of offering gifts in exchange for spiritual or physical benefits. In both passages, individuals present valuable items—money in Acts and camels'' burden in 2 Kings—seeking favor or healing.', 'theme', 0.32799999999999996, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 18, 12, 5, 15, 16, 'offered them money', 'Offerings and Refusal', 'Both passages emphasize the refusal to accept money or gifts in exchange for spiritual blessings. In Acts, Simon offers money for the Holy Ghost, while in 2 Kings, Elisha refuses a blessing from Naaman. This connection highlights the integrity of God''s servants in matters of faith.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 18, 26, 13, 19, 19, 'offered them money', 'Corruption for Gain', 'This connection highlights the theme of using spiritual authority for personal gain. Both passages emphasize the exploitation of faith for material benefit, with Simon offering money and the false prophets profiting from their lies.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 19, 42, 14, 7, 11, NULL, 'Humility and Exaltation Theme', 'This connection highlights the theme of humility in both passages. In Acts, the desire for power reflects a lack of humility, while Luke emphasizes that those who exalt themselves will be abased.', 'theme', 0.33499999999999996, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 8, 19, 40, 18, 1, 3, 'this power', 'Power and Humility in Faith', 'This connection highlights the contrast between seeking power, as seen in Acts 8:19, and the humility required to enter the kingdom of heaven, emphasized in Matthew 18:1-3. Both passages emphasize the importance of a childlike faith over a desire for authority.', 'theme', 0.32799999999999996, 'medium', 'v1.2', 'gpt-4o-mini')
