INSERT INTO crossref_explanations (source_book_id, source_chapter, source_verse, target_book_id, target_chapter, target_verse_start, target_verse_end, anchor_phrase, title, content, connection_type, weight, confidence, prompt_version, model)
VALUES
(44, 28, 31, 50, 1, 14, 14, 'with all confidence', 'Confidence in Boldness of Preaching', 'This connection highlights the theme of confidence in preaching the word of God. Both passages emphasize the boldness that arises from confidence, whether from Paul''s own situation or the encouragement it provides to others.', 'theme', 0.349, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 28, 31, 55, 4, 17, 17, 'preaching the kingdom of God', 'Preaching and Strength in Ministry', 'Both passages emphasize the act of preaching, with Acts highlighting ''preaching the kingdom of God'' and 2 Timothy noting the importance of ''the preaching might be fully known.'' This connection highlights the role of divine support in the ministry, as seen in both verses.', 'theme', 0.349, 'high', 'v1.2', 'gpt-4o-mini'),
(44, 28, 31, 42, 8, 1, 1, 'preaching the kingdom of God', 'Preaching the Kingdom of God', 'Both passages emphasize the act of preaching the kingdom of God. In Acts, it is noted that Paul preached with confidence, while in Luke, Jesus is depicted as going throughout cities preaching the glad tidings of the kingdom.', 'theme', 0.32799999999999996, 'high', 'v1.2', 'gpt-4o-mini')
