INSERT INTO crossref_explanations (source_book_id, source_chapter, source_verse, target_book_id, target_chapter, target_verse_start, target_verse_end, anchor_phrase, title, content, connection_type, weight, confidence, prompt_version, model)
VALUES
(40, 2, 2, 66, 22, 16, 16, 'King of the Jews', 'Connection of Kingship and Star', 'This connection highlights the theme of Jesus'' royal identity as both the ''King of the Jews'' and the ''bright and morning star.'' Both passages emphasize the significance of a star in relation to Jesus'' birth and his lineage from David.', 'theme', 0.44699999999999995, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 2, 38, 9, 9, 9, 'King of the Jews', 'Connection of Kingship and Worship', 'This connection highlights the theme of kingship, as both passages refer to a King. Matthew 2:2 speaks of the King of the Jews, while Zechariah 9:9 announces the coming of a King who brings salvation.', 'theme', 0.384, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 2, 23, 9, 6, 7, 'King of the Jews', 'Connection of Kingship and Birth', 'This connection highlights the theme of a king being born, as both passages refer to a significant birth that holds royal implications. Matthew 2:2 speaks of the ''King of the Jews,'' while Isaiah 9:6-7 describes a child who will govern and be called ''The Prince of Peace.''', 'theme', 0.377, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 2, 23, 60, 3, 3, NULL, 'Kings and Gentiles Seeking Light', 'This connection highlights the theme of kings coming to worship and seek the light. Both passages emphasize the arrival of significant figures, such as kings and Gentiles, in response to divine revelation.', 'theme', 0.356, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 2, 43, 18, 37, 37, 'King of the Jews', 'Kingship of Jesus', 'This connection highlights the theme of Jesus'' kingship. In Matthew, the Magi seek the ''King of the Jews,'' while in John, Jesus affirms his identity as a king.', 'theme', 0.349, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 3, 44, 17, 6, 7, 'troubled, and all Jerusalem with him', 'Response to Kingship and Authority', 'This connection highlights the theme of political upheaval associated with the claim of another king. In both passages, the presence of a new king, Jesus, causes disturbance and trouble among rulers and the populace.', 'theme', 0.349, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 3, 43, 11, 47, 48, 'troubled', 'Fear of Losing Power', 'This connection highlights the theme of fear and concern for power in both passages. In Matthew, Herod''s troubled reaction reflects a fear of losing his kingship, while in John, the chief priests and Pharisees express anxiety about the Romans taking away their place and nation.', 'theme', 0.34199999999999997, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 3, 40, 23, 37, 37, 'all Jerusalem with him', 'Jerusalem''s Troubled Response', 'This connection highlights Jerusalem''s troubled state in both passages. In Matthew 2:3, Jerusalem is troubled by Herod''s actions, while in Matthew 23:37, Jesus laments Jerusalem''s rejection of the prophets.', 'theme', 0.33499999999999996, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 3, 40, 8, 29, 29, 'troubled', 'Fear and Disturbance', 'This connection highlights the theme of fear and disturbance present in both passages. In Matthew 2:3, Herod and Jerusalem are troubled by the news of Jesus'' birth, while in Matthew 8:29, the demons express fear of Jesus'' authority and impending judgment.', 'theme', 0.32799999999999996, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 3, 44, 4, 2, 2, 'troubled', 'Emotional Responses to Leadership', 'This connection highlights the emotional turmoil caused by leadership. In both passages, the reaction of being ''troubled'' in Matthew parallels the ''grieved'' response in Acts, showing how powerful figures can evoke distress in their communities.', 'theme', 0.32799999999999996, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 3, 11, 18, 17, 18, 'troubled', 'Connection of Troubling Figures', 'This connection highlights the theme of troubling figures in leadership. In both passages, the presence of a king leads to turmoil, with Herod troubling Jerusalem and Ahab accusing Elijah of troubling Israel.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 4, 40, 7, 29, 29, 'scribes', 'Authority of Christ vs. Scribes', 'This connection highlights the contrast between Christ''s authority and the role of the scribes. In Matthew 2:4, the scribes provide information about Christ, while Matthew 7:29 emphasizes that Christ teaches with authority, distinguishing Him from the scribes.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 4, 40, 21, 23, 23, 'chief priests', 'Authority of Chief Priests', 'This connection highlights the role of the chief priests in both passages. In Matthew 2:4, they are consulted about Christ''s birth, while in Matthew 21:23, they question Jesus'' authority, indicating their ongoing significance in relation to Christ.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 4, 44, 6, 12, 12, 'scribes', 'Connection of Religious Authority', 'Both passages emphasize the role of religious leaders, specifically the ''scribes.'' In Matthew, they provide information about Christ''s birth, while in Acts, they are involved in the arrest of Stephen.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 4, 44, 23, 9, 9, 'scribes', 'Role of Scribes in Scripture', 'Both passages emphasize the involvement of scribes in significant events. In Matthew, they provide information about Christ''s birth, while in Acts, they defend Paul against accusations, showcasing their influential role in religious discourse.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 4, 24, 8, 8, 8, 'scribes of the people', 'Wisdom of Scribes and Prophecy', 'This connection highlights the role of scribes in interpreting the law and their knowledge of prophecy. In Matthew, the scribes provide information about Christ''s birthplace, while Jeremiah critiques the wisdom of the scribes, suggesting their understanding may be flawed.', 'theme', 0.314, 'medium', 'v1.2', 'gpt-4o-mini'),
(40, 2, 4, 42, 20, 19, 19, 'chief priests and scribes', 'Role of Chief Priests and Scribes', 'This connection highlights the involvement of the chief priests and scribes in both passages. In Matthew, they are consulted about Christ''s birth, while in Luke, they seek to arrest Jesus, indicating their ongoing significance in the narrative.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 4, 42, 23, 10, 10, 'chief priests and scribes', 'Role of Chief Priests and Scribes', 'This connection highlights the involvement of the chief priests and scribes in both passages. In Matthew, they provide information about Christ''s birth, while in Luke, they actively accuse Him.', 'theme', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 5, 43, 7, 42, 42, 'In Bethlehem of Judea', 'Connection of Bethlehem Prophecy', 'Both passages emphasize Bethlehem as the birthplace of Christ, fulfilling the prophecy regarding the Messiah''s lineage from David. This connection highlights the significance of Bethlehem in the context of Jesus'' identity.', 'prophecy', 0.363, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 5, 8, 1, 1, 1, 'Bethlehem of Judea', 'Connection Between Bethlehem References', 'Both passages emphasize Bethlehem as a significant location. Matthew identifies Bethlehem as the birthplace of Jesus, while Ruth introduces a man from Bethlehem, linking the two narratives through this shared geographical reference.', 'keyword', 0.33499999999999996, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 5, 9, 16, 1, 1, 'In Bethlehem of Judea', 'Connection Between Kingship and Bethlehem', 'Both passages emphasize Bethlehem''s significance in God''s plan for leadership. Matthew references the prophecy about a ruler from Bethlehem, while 1 Samuel identifies Jesse, a Bethlehemite, as the father of a future king.', 'prophecy', 0.32799999999999996, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 5, 8, 2, 4, 4, 'Bethlehem of Judea', 'Connection Between Bethlehem References', 'This connection highlights the significance of Bethlehem as a central location in both passages. In Matthew, Bethlehem is identified as the birthplace of Jesus, while in Ruth, it serves as the setting for Boaz, linking the two narratives through this shared geographical significance.', 'theme', 0.321, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 5, 6, 19, 15, 15, 'Bethlehem of Judea', 'Connection of Bethlehem in Scripture', 'This connection highlights the mention of Bethlehem in both passages. Matthew 2:5 refers to Bethlehem as prophesied, while Joshua 19:15 lists it as a city, establishing its significance in biblical geography.', 'keyword', 0.314, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 5, 1, 35, 19, 19, 'In Bethlehem of Judea', 'Connection Between Bethlehem References', 'Both passages emphasize Bethlehem as a significant location. Matthew cites Bethlehem in relation to prophecy, while Genesis mentions Rachel''s burial near Ephrath, which is Bethlehem.', 'theme', 0.3, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 6, 26, 34, 23, 25, 'Governor, that shall rule my people Israel', 'Connection of Shepherd and Governor', 'Both passages emphasize a leader who will guide and protect the people of Israel. Matthew refers to a ''Governor'' from Bethlehem, while Ezekiel speaks of a ''shepherd'' who will care for them, suggesting a fulfillment of leadership roles.', 'theme', 1.0, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 6, 33, 5, 2, 2, 'out of thee shall come a Governor', 'Connection of Bethlehem Prophecy', 'Both passages emphasize Bethlehem as the birthplace of a ruler for Israel. They highlight the significance of this location in God''s plan for leadership.', 'prophecy', 1.0, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 6, 23, 9, 6, 7, 'out of thee shall come a Governor', 'Connection of Prophetic Leadership', 'Both passages emphasize the theme of a ruler emerging from Bethlehem. Matthew cites Bethlehem as the birthplace of a Governor, while Isaiah describes a child who will bear the government on his shoulders.', 'prophecy', 0.482, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 6, 43, 7, 42, 42, 'out of thee shall come a Governor', 'Connection of Bethlehem and David', 'Both passages emphasize Bethlehem as the birthplace of significant leaders. Matthew identifies Bethlehem as the origin of a Governor, while John connects it to Christ''s lineage from David.', 'theme', 0.475, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 6, 1, 49, 10, 10, 'Governor, that shall rule my people Israel', 'Connection of Leadership in Judah', 'This connection highlights the theme of leadership emerging from Judah. Both passages reference a ruler or governor who will arise from this lineage, emphasizing the significance of Judah in God''s plan for Israel.', 'theme', 0.44699999999999995, 'high', 'v1.2', 'gpt-4o-mini'),
(40, 2, 6, 66, 11, 15, 15, 'Governor, that shall rule my people Israel', 'Connection of Reign and Governance', 'This connection highlights the theme of governance, as both passages refer to a ruler. Matthew 2:6 speaks of a Governor who will come from Bethlehem, while Revelation 11:15 proclaims that Christ will reign forever.', 'theme', 0.426, 'high', 'v1.2', 'gpt-4o-mini')
