INSERT INTO crossref_explanations (source_book_id, source_chapter, source_verse, target_book_id, target_chapter, target_verse_start, target_verse_end, anchor_phrase, title, content, connection_type, weight, prompt_version, model) VALUES (43, 1, 51, 42, 22, 43, 43, 'angels of God ascending and descending', 'Angels and Divine Support', 'Both passages highlight the role of angels in relation to Jesus, illustrating a deeper connection between heaven and earth. In John, angels are depicted as ministering to the Son of Man, while in Luke, an angel specifically strengthens Him during His time of distress. This connection underscores the ongoing divine support Jesus receives as He fulfills His mission.', 'theme', 0.391, 'v1.0', 'gpt-4o-mini'), (43, 1, 51, 40, 16, 27, 28, 'angels of God ascending and descending', 'Heavenly Realms and the Son of Man', 'Both passages emphasize the role of the Son of Man in relation to heavenly authority and divine revelation. John describes angels ascending and descending upon Jesus, indicating his connection to God''s presence, while Matthew speaks of Jesus coming in glory with angels, highlighting his authority to judge and reward. This connection reveals that Jesus embodies the intersection between heaven and earth, fulfilling the role of mediator and king.', 'theme', 0.384, 'v1.0', 'gpt-4o-mini'), (43, 1, 51, 42, 2, 13, 13, 'angels of God ascending and descending', 'Angelic Announcement and Revelation', 'Both passages highlight the role of angels in revealing God''s presence and purpose. In John, the angels ascend and descend upon Jesus, indicating His divine connection to heaven, while in Luke, the heavenly host announces the birth of Christ, celebrating His significance. This connection emphasizes Jesus as the bridge between heaven and earth, fulfilling God''s plan of salvation.', 'theme', 0.377, 'v1.0', 'gpt-4o-mini'), (43, 1, 51, 44, 10, 11, 11, 'heaven opened', 'Heaven Opened and Divine Revelation', 'Both passages depict the opening of heaven, symbolizing divine revelation and the connection between God and humanity. In John, this event foreshadows the role of Jesus as the mediator between heaven and earth, while in Acts, it illustrates Godâ€™s inclusive vision for all people through the sheet representing Gentiles. Together, they highlight the ongoing unfolding of God''s plan through Christ and the early church.', 'theme', 0.377, 'v1.0', 'gpt-4o-mini') ON CONFLICT (source_book_id, source_chapter, source_verse, target_book_id, target_chapter, target_verse_start, target_verse_end, connection_type) DO UPDATE SET title = EXCLUDED.title, content = EXCLUDED.content, anchor_phrase = EXCLUDED.anchor_phrase, weight = EXCLUDED.weight, updated_at = now();