INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(40, 23, 26, 26, 'kjv', 'cleanse first that which is within the cup and platter', 21, 75, 'connection', 'Inner Purity Emphasis', 'This concept parallels 1 Samuel 16:7, where God emphasizes that He looks at the heart rather than the outward appearance. Jesus'' instruction to cleanse the inside first reflects the same principle that true righteousness must originate from within. It highlights the importance of inner transformation over mere external conformity.', 'link', '[{"type": "crossReference", "reference": "1 Samuel 16:7", "description": "God sees the heart, not the outward appearance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 26, 26, 'kjv', 'cleanse first that which is within the cup and platter', 21, 75, 'greek', 'Key Greek Terms', 'κἀθαρὸς (katharos, G2513) - clean, pure. This term indicates a state of moral purity. In this context, Jesus stresses that true cleanliness is a matter of the heart, which must be prioritized over outward rituals.', 'textformat.abc', '[{"type": "strongs", "reference": "G2513", "description": "Strong''s number for ''clean''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 26, 26, 'kjv', 'cleanse first that which is within the cup and platter', 21, 75, 'theology', 'Doctrine of Inner Transformation', 'This verse suggests that true holiness comes from an inner transformation of the heart rather than mere outward observance of the law. Many scholars believe this underscores the necessity of the Holy Spirit''s work in the believer, aligning with Pauline theology in 2 Corinthians 5:17 about becoming a new creation.', 'person.2.fill', '[{"type": "commentary", "reference": "2 Corinthians 5:17", "description": "Paul speaks of the transformation in Christ."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 26, 26, 'kjv', 'cleanse first that which is within the cup and platter', 21, 75, 'question', 'Reflect on Inner Cleanliness', 'What practices can help you focus more on inner purity rather than just outward appearances? How does understanding this verse change your approach to personal holiness?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 27, 27, 'kjv', 'whited sepulchres', 70, 87, 'connection', 'Symbol of Death', 'The term ''whited sepulchres'' evokes imagery found in Numbers 19:16, which discusses the defilement caused by contact with a dead body. This connection highlights the spiritual death associated with hypocrisy, showing that outward appearances can mask inner corruption. Just as a sepulchre is meant to contain death, so too do the hypocritical actions of the Pharisees conceal spiritual decay.', 'link', '[{"type": "crossReference", "reference": "Numbers 19:16", "description": "Discusses contact with the dead and its defilement."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 27, 27, 'kjv', 'full of dead men''s bones', 143, 167, 'theology', 'Inner Corruption', 'This vivid imagery illustrates the doctrine of inner purity versus outward appearances. Many scholars believe that this passage emphasizes the necessity of inner transformation through faith, as seen in 1 Samuel 16:7, which states that God looks at the heart. This suggests that true righteousness is a matter of the heart, not mere external compliance to the law.', 'sparkles', '[{"type": "crossReference", "reference": "1 Samuel 16:7", "description": "God looks at the heart, emphasizing inner purity."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 27, 27, 'kjv', 'appear beautiful outward', 102, 126, 'question', 'Outward vs. Inward', 'What does it mean to appear beautiful outwardly in your life, and how does that contrast with your inward reality? Reflecting on this can lead to a deeper understanding of authenticity in your faith and actions.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 28, 28, 'kjv', 'outwardly appear righteous unto men', 16, 51, 'connection', 'Righteousness and Appearance', 'This phrase connects to 1 Samuel 16:7, where God emphasizes that He looks at the heart rather than outward appearances. This connection highlights the theme of inner versus outer righteousness, suggesting that true righteousness is a matter of the heart, not merely external behavior.', 'link', '[{"type": "crossReference", "reference": "1 Samuel 16:7", "description": "God sees the heart, not appearances."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 28, 28, 'kjv', 'full of hypocrisy and iniquity', 71, 101, 'greek', 'Understanding Hypocrisy', 'ὑπόκρισις (hypokrisis, G5272) - acting, pretense. This term is deeply connected to the idea of being two-faced or insincere. By using this word, Jesus underscores the danger of living a life of pretense, where one''s true moral state is hidden behind a facade of righteousness.', 'textformat.abc', '[{"type": "strongs", "reference": "G5272", "description": "Greek term for hypocrisy."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 28, 28, 'kjv', 'Even so ye also outwardly appear righteous unto men', 0, 51, 'theology', 'Outward vs. Inner Righteousness', 'This passage suggests that many believers struggle with the dichotomy of appearing righteous to others while grappling with sin internally. Many scholars believe this highlights the importance of authenticity in faith, emphasizing that God desires genuine repentance and transformation rather than mere external compliance.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses inner transformation."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 28, 28, 'kjv', 'full of hypocrisy and iniquity', 71, 101, 'question', 'Reflecting on Personal Integrity', 'What practices can help ensure that your outward appearance of righteousness aligns with your inner moral state, as this verse cautions against hypocrisy?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 29, 29, 'kjv', 'Woe unto you, scribes and Pharisees, hypocrites!', 0, 48, 'theology', 'Hypocrisy and Judgment', 'This passage highlights Jesus'' condemnation of hypocrisy among religious leaders. Many scholars believe that this reflects a broader theme in Scripture where God seeks genuine faith rather than mere outward appearances. The repeated ''woe'' serves as a grave warning against the dangers of spiritual blindness and self-righteousness, emphasizing that true faith must be accompanied by authenticity.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "This commentary discusses the implications of hypocrisy in religious practice."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 29, 29, 'kjv', 'ye build the tombs of the prophets,', 57, 92, 'connection', 'Building Tombs of Prophets', 'This phrase can be connected to Luke 11:47-48, where Jesus similarly rebukes the Pharisees for honoring the prophets while rejecting their messages. This connection underscores the irony of their actions, as they venerate the dead prophets but fail to heed the living truth they proclaimed. The act of building tombs symbolizes a disconnect between honoring past righteousness and living righteously in the present.', 'link', '[{"type": "crossReference", "reference": "Luke 11:47-48", "description": "Similar condemnation of honoring prophets while failing to accept their messages."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 29, 29, 'kjv', 'garnish the sepulchres of the righteous,', 97, 137, 'greek', 'Garnish - κοσμέω', 'κοσμέω (kosmeō, G2885) - to adorn, decorate, or arrange. This term implies an emphasis on outward beautification as opposed to inward righteousness. The use of ''garnish'' indicates a focus on external appearances rather than true moral or spiritual integrity, which Jesus critiques throughout His ministry.', 'textformat.abc', '[{"type": "strongs", "reference": "G2885", "description": "Strong''s reference for the Greek word meaning to adorn or beautify."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 29, 29, 'kjv', 'because ye build the tombs of the prophets,', 49, 92, 'question', 'True Honor vs. Hypocrisy', 'What does it mean to honor the prophets while failing to act on their messages? In what ways might we be guilty of external displays of faith without genuine commitment to the truth they spoke? Reflecting on this can lead to deeper personal accountability in our faith practices.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 30, 30, 'kjv', 'If we had been in the days of our fathers, we would not have been partakers with them in the blood of the prophets.', 9, 124, 'connection', 'Historical Accountability', 'This statement echoes sentiments found in Luke 11:47-48, where Jesus rebukes the Pharisees for building tombs for the prophets while still sharing in the guilt of their ancestors. This connection highlights the danger of historical amnesia regarding one''s own complicity in injustice, emphasizing the need for self-examination in faith communities.', 'link', '[{"type": "crossReference", "reference": "Luke 11:47-48", "description": "Jesus'' rebuke of the Pharisees for honoring the prophets while rejecting their message."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 30, 30, 'kjv', 'If we had been in the days of our fathers, we would not have been partakers with them in the blood of the prophets.', 9, 124, 'theology', 'Hypocrisy and Guilt', 'This passage suggests a broader theological theme of hypocrisy within religious communities. Many scholars believe it reveals how self-righteousness can blind individuals to their participation in systemic sins, thus calling for a reflective and humble approach to faith, lest one repeat the mistakes of the past.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 30, 30, 'kjv', 'blood of the prophets.', 102, 124, 'greek', 'Significant Terms', 'προφήτης (prophetes, G4396) - prophet, one who speaks for God. This term emphasizes the role of prophets as divine messengers and underscores the seriousness of rejecting their messages, which is tantamount to spilling their blood.', 'textformat.abc', '[{"type": "strongs", "reference": "G4396", "description": "Strong''s Concordance entry for ''prophet''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 30, 30, 'kjv', 'If we had been in the days of our fathers, we would not have been partakers with them in the blood of the prophets.', 9, 124, 'question', 'Reflecting on Legacy', 'What does this verse reveal about our own potential complicity in the injustices of our spiritual heritage? How can we ensure that we honor the messages of God''s prophets today rather than falling into the same patterns of denial?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 31, 31, 'kjv', 'ye be witnesses unto yourselves', 10, 41, 'connection', 'Witnesses in Scripture', 'This phrase parallels Acts 1:8, where believers are called to be witnesses of Christ. Both passages emphasize the importance of testimony and the accountability that comes with it. Here, Jesus challenges the Pharisees to acknowledge their role in the lineage of those who persecuted the prophets, highlighting their hypocrisy in claiming righteousness while denying their actions.', 'link', '[{"type": "crossReference", "reference": "Acts 1:8", "description": "Calls believers to be witnesses of Christ."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 31, 31, 'kjv', 'children of them which killed the prophets', 59, 101, 'theology', 'Generational Accountability', 'This passage suggests that spiritual heritage carries both privilege and responsibility. Many scholars believe that Jesus is confronting the Pharisees with their failure to learn from the past, illustrating a broader theological point about the continuity of sin across generations. This invites reflection on how we may perpetuate past failures if we do not heed the lessons of history.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 31, 31, 'kjv', 'ye are the children of them which killed the prophets', 48, 101, 'question', 'Reflecting on Spiritual Heritage', 'What does it mean to be identified as ''children'' of those who oppose God''s messengers? How can this understanding inform our own actions and attitudes towards God''s word today? Consider how we might unintentionally align with past wrongs if we do not actively seek truth and justice.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 32, 32, 'kjv', 'Fill ye up then the measure of your fathers.', 0, 44, 'connection', 'Historical Continuity', 'This phrase connects with Luke 11:51, where Jesus speaks of the blood of the prophets being required from this generation. Both verses highlight the continuity of sin and judgment across generations, emphasizing the accountability of the current generation for the actions of their forebears. This connection urges readers to reflect on how history influences present choices.', 'link', '[{"type": "crossReference", "reference": "Luke 11:51", "description": "Jesus speaks of the blood of the prophets from Abel to Zechariah."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 32, 32, 'kjv', 'Fill ye up then the measure of your fathers.', 0, 44, 'greek', 'Measure of Guilt', 'με measure (metron, G3358) - a measure, standard. This term implies not only quantity but also quality, suggesting that the sins of the fathers have reached a full measure. The use of ''measure'' indicates that there is a divine standard for judgment, which adds weight to the call for accountability.', 'textformat.abc', '[{"type": "strongs", "reference": "G3358", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 32, 32, 'kjv', 'Fill ye up then the measure of your fathers.', 0, 44, 'theology', 'Judgment and Accountability', 'This passage suggests that God''s justice requires the fulfillment of the ''measure'' of sin, reflecting a principle of divine retribution. Many scholars believe it illustrates the seriousness of rejecting God''s messengers, reinforcing the doctrine that God holds individuals accountable for their choices and actions, especially in light of historical precedents.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of generational sin."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 32, 32, 'kjv', 'Fill ye up then the measure of your fathers.', 0, 44, 'question', 'Personal Responsibility', 'How do we interpret ''filling up the measure'' in our own lives? In what ways might we be perpetuating sins of our forebears, and how can we actively choose to break that cycle? This reflection challenges us to consider our individual and communal responsibilities in light of our spiritual heritage.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 33, 33, 'kjv', 'Ye serpents, ye generation of vipers,', 0, 37, 'connection', 'Symbolism of Serpents', 'The phrase ''Ye serpents, ye generation of vipers'' connects to Genesis 3:15, where the serpent represents deceit and evil. Jesus uses this metaphor to highlight the moral corruption of the Pharisees and their role in opposing God''s messengers. The imagery emphasizes the danger posed by their hypocrisy and the spiritual poison they spread among the people.', 'link', '[{"type": "crossReference", "reference": "Genesis 3:15", "description": "The first mention of the serpent as a symbol of evil."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 33, 33, 'kjv', 'how can ye escape the damnation of hell?', 38, 78, 'theology', 'Understanding Damnation', 'This passage raises important theological questions about judgment and accountability. Many scholars believe that Jesus is addressing the gravity of rejecting God''s truth and the consequences that follow. The term ''damnation'' implies a finality in separation from God, reflecting the seriousness of unrepentant sin and the rejection of divine revelation.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Insight on the meaning of damnation in the context of the Gospel."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 33, 33, 'kjv', 'Ye serpents, ye generation of vipers,', 0, 37, 'greek', 'Greek Terms of Reproach', 'ἔχιδνα (echidna, G2191) - viper, snake. This term is used to denote not just a physical snake but also carries connotations of malice and deceit. The use of this word by Jesus underscores the severe nature of the Pharisees'' actions and their spiritual state, suggesting that they are not only harmful but also intentionally deceptive.', 'textformat.abc', '[{"type": "strongs", "reference": "G2191", "description": "Greek word for viper, emphasizing the metaphor of danger."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 33, 33, 'kjv', 'how can ye escape the damnation of hell?', 38, 78, 'question', 'Reflection on Escape', 'What does it mean to ''escape the damnation of hell'' in light of our choices and actions? In what ways are we, like the Pharisees, resistant to the truth God reveals to us? This verse challenges us to examine our lives and consider the seriousness of our spiritual condition and the urgency of repentance.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 34, 34, 'kjv', 'Wherefore, behold, I send unto you prophets,', 0, 44, 'connection', 'Prophets Sent by God', 'This phrase connects to Luke 11:49, where Jesus states, ''Therefore also said the wisdom of God, I will send them prophets and apostles...'' This highlights the continuity of God''s communication through prophets across both testaments and emphasizes the rejection and persecution they often faced.', 'link', '[{"type": "crossReference", "reference": "Luke 11:49", "description": "Jesus speaks of God''s wisdom sending prophets."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 34, 34, 'kjv', 'some of them ye shall kill and crucify;', 76, 115, 'theology', 'Persecution of Righteous', 'This passage hints at the broader theme of suffering and persecution faced by the righteous throughout biblical history. Many scholars believe this foreshadows Christ''s own suffering and highlights the cost of discipleship and the inevitable conflict between divine truth and human rejection.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 34, 34, 'kjv', 'scourge in your synagogues,', 142, 169, 'greek', 'Scourge Meaning', 'μάστιξ (mastix, G3148) - whip, scourge. This term indicates not only physical punishment but also conveys a sense of severe discipline and rejection faced by those who proclaim God''s message, reinforcing the violent opposition to divine truth.', 'textformat.abc', '[{"type": "strongs", "reference": "G3148", "description": "Strong''s number for ''scourge''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 34, 34, 'kjv', 'persecute them from city to city:', 174, 207, 'question', 'Endurance in Persecution', 'What does it reveal about human nature that persecution often follows the truth bearers? How can we apply the call to endurance in the face of opposition in our own lives today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 35, 35, 'kjv', 'all the righteous blood shed upon the earth', 23, 66, 'connection', 'Righteous Blood Theme', 'This phrase connects to Genesis 4:10, where God speaks of Abel''s blood crying out from the ground. The continuity of righteous blood throughout Scripture highlights the serious consequences of sin and the weight of injustice. It underscores the biblical theme of God''s justice and the call for accountability, which resonates deeply in the prophetic tradition.', 'link', '[{"type": "crossReference", "reference": "Genesis 4:10", "description": "God''s response to Cain after Abel''s murder."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 35, 35, 'kjv', 'the blood of Zacharias son of Barachias', 106, 145, 'theology', 'Prophetic Martyrdom', 'This reference to Zacharias, a martyr for speaking God''s truth, illustrates the rejection of prophetic voices throughout Israel''s history. Many scholars believe this passage emphasizes the continuity of God''s message and the tragic fate of those who bear witness to it. It reflects the broader theme of persecution faced by God''s messengers, culminating in the ultimate sacrifice of Jesus.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Insights on the rejection of prophets."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 35, 35, 'kjv', 'whom ye slew between the temple and the altar', 147, 192, 'question', 'Sacred Spaces and Actions', 'What does it reveal about the nature of sin that it occurs in such a sacred space? Consider how this juxtaposition of violence and holiness in the temple challenges our understanding of worship and righteousness. How can we ensure that our places of worship reflect God''s heart rather than becoming sites of rejection of His truth?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 35, 35, 'kjv', 'That upon you may come all the righteous blood', 0, 46, 'greek', 'Meaning of ''righteous''', 'δικαίος (dikaios, G1342) - righteous, just, innocent. This term signifies not just moral uprightness but also being in right relationship with God. The use of ''righteous'' blood here emphasizes the gravity of the injustices done against those who were faithful to God''s calling.', 'textformat.abc', '[{"type": "strongs", "reference": "G1342", "description": "Strong''s number for ''righteous''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 36, 36, 'kjv', 'All these things shall come upon this generation.', 23, 72, 'connection', 'Judgment on a Generation', 'This statement echoes the prophetic warnings found in Jeremiah 7:20, where God announces judgment over His people due to their persistent disobedience. It also parallels Luke 21:32, where Jesus speaks of the generation witnessing certain events. The connection is significant as it emphasizes the seriousness of rejecting God''s messengers and the consequences that follow.', 'link', '[{"type": "crossReference", "reference": "Jeremiah 7:20", "description": "God''s judgment as a response to Israel''s sin."}, {"type": "crossReference", "reference": "Luke 21:32", "description": "Jesus'' reference to the current generation''s fate."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 36, 36, 'kjv', 'Verily I say unto you,', 0, 22, 'greek', 'Introduction of Authority', 'ἀμήν (amēn, G281) - truly, certainly, so be it. This term is used by Jesus to affirm the truth of His statements, underscoring the weight of His proclamation. It serves to draw attention to the gravity of the message that follows, marking it as authoritative and deserving of serious consideration.', 'textformat.abc', '[{"type": "strongs", "reference": "G281", "description": "Strong''s number for ''\u1f00\u03bc\u03ae\u03bd''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 36, 36, 'kjv', 'All these things shall come upon this generation.', 23, 72, 'theology', 'Consequences of Rejection', 'Many scholars believe this verse reflects the broader theological theme of judgment for unrepentance. It suggests that the rejection of God''s messengers leads to inevitable consequences. This reinforces the idea that the Gospel calls for a response, and ignoring it can result in dire outcomes, as illustrated throughout Scripture.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of rejecting God''s truth."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 36, 36, 'kjv', 'Verily I say unto you,', 0, 22, 'question', 'Understanding Divine Authority', 'What does it mean for Jesus to assert, ''Verily I say unto you''? How does recognizing His authority impact your understanding of the warnings He gives? Reflect on the implications of His words for your own life and the urgency of responding faithfully to His message.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 37, 37, 'kjv', 'O Jerusalem, Jerusalem, thou that killest the prophets, and stonest them which are sent unto thee,', 0, 98, 'connection', 'Prophets and Rejection', 'This phrase reflects a recurring theme in Scripture of God''s messengers being rejected. See Luke 13:34, where Jesus laments over Jerusalem''s history of killing prophets. This connection highlights the deep sorrow of God over His people''s unfaithfulness and the consequences of such rejection.', 'link', '[{"type": "crossReference", "reference": "Luke 13:34", "description": "Jesus expresses His sorrow over Jerusalem''s history of rejecting prophets."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 37, 37, 'kjv', 'how often would I have gathered thy children together, even as a hen gathereth her chickens under her wings,', 99, 207, 'theology', 'Divine Desire for Protection', 'This imagery of a hen gathering her chicks illustrates God''s nurturing desire to protect and unite His people. Many scholars believe this reflects God''s covenantal love and the longing for reconciliation with Israel, emphasizing the relational aspect of God''s nature.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 37, 37, 'kjv', 'and ye would not!', 208, 225, 'question', 'Resistance to God''s Call', 'What does it reveal about human nature that despite God''s desire to gather and protect, the people chose rejection? How does this understanding impact your view of God''s patience and mercy in your own life?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 37, 37, 'kjv', 'O Jerusalem, Jerusalem, thou that killest the prophets,', 0, 55, 'greek', 'Repeated Address', 'Ἰερουσαλήμ (Ierousalēm, G2419) - Jerusalem, the holy city of God, signifies not just a geographical location but a spiritual reality. The repetition of ''Jerusalem'' underscores the weight of its actions and the depth of Jesus'' lament.', 'textformat.abc', '[{"type": "strongs", "reference": "G2419", "description": "Greek term for Jerusalem, emphasizing its spiritual significance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 38, 38, 'kjv', 'your house is left unto you desolate.', 8, 45, 'connection', 'Desolation in Scripture', 'This phrase resonates with Jeremiah 22:5, where God declares that a house left desolate signifies abandonment due to disobedience. The connection is crucial as it highlights the seriousness of Jerusalem''s rejection of God''s messengers, leading to divine judgment. Similarly, in Luke 13:35, Jesus laments over Jerusalem, reinforcing the theme of desolation as a consequence of unrepentance.', 'link', '[{"type": "crossReference", "reference": "Jeremiah 22:5", "description": "God''s warning about judgment and desolation."}, {"type": "crossReference", "reference": "Luke 13:35", "description": "Jesus'' lament over Jerusalem''s fate."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 38, 38, 'kjv', 'your house is left unto you desolate.', 8, 45, 'theology', 'The Concept of Desolation', 'Many scholars believe that ''desolation'' in this context signifies a profound spiritual abandonment. It reflects the consequences of rejecting God''s covenant and the presence of His grace. The idea of a ''house'' left desolate can be seen as a metaphor for the spiritual state of the people, indicating that without repentance and acceptance of Christ, they would experience separation from God.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explores the implications of desolation in spiritual terms."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 23, 38, 38, 'kjv', 'your house is left unto you desolate.', 8, 45, 'greek', 'Desolate in Greek', 'ἐρημόω (erēmóō, G2048) - to make desolate, to lay waste. This term conveys not just physical emptiness but also spiritual barrenness. Jesus uses this term to emphasize the severe consequences of Jerusalem''s actions, using a word that evokes a sense of abandonment and loss of divine favor.', 'textformat.abc', '[{"type": "strongs", "reference": "G2048", "description": "Strong''s number for the Greek term indicating desolation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 23, 38, 38, 'kjv', 'your house is left unto you desolate.', 8, 45, 'question', 'Reflection on Spiritual State', 'What does it mean for a community or a person to have their ''house'' left desolate? In what ways might we recognize signs of spiritual desolation in our lives or communities today? How does this prompt us to seek restoration and reconciliation with God?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini');