INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(40, 25, 36, 36, 'kjv', 'Naked, and ye clothed me:', 0, 25, 'question', 'Reflecting Christ''s Care', 'What does it mean to you personally to ''clothe the naked'' in today''s context? How can your actions reflect Christ''s compassion for those who are vulnerable and in need?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 37, 37, 'kjv', 'when saw we thee an hungred, and fed thee?', 51, 93, 'connection', 'Feeding the Hungry', 'This phrase connects closely with John 21:17, where Jesus asks Peter three times if he loves Him, to which He responds, ''Feed my sheep.'' Both passages emphasize the importance of caring for those in need as an expression of love for Christ. The connection highlights that serving others is synonymous with serving Christ Himself.', 'link', '[{"type": "crossReference", "reference": "John 21:17", "description": "Jesus emphasizes the duty to care for others."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 37, 37, 'kjv', 'when saw we thee an hungred, and fed thee?', 51, 93, 'theology', 'Role of Works', 'This passage suggests that deeds of kindness are crucial to the Christian faith. Many scholars believe that this aligns with the doctrine of faith and works, indicating that genuine faith manifests through acts of love and service. This reflects the belief that our actions toward others are a reflection of our relationship with Christ.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discussion on faith and works."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 37, 37, 'kjv', 'when saw we thee an hungred, and fed thee?', 51, 93, 'question', 'Recognizing Christ in Others', 'What does it mean to truly recognize Christ in those who are hungry or in need? How might this awareness change our interactions with the marginalized in our communities? Reflecting on this question can deepen our understanding of serving others as a form of serving Christ Himself.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 38, 38, 'kjv', 'When saw we thee a stranger, and took thee in?', 0, 46, 'connection', 'Welcoming the Stranger', 'This phrase echoes Hebrews 13:2, which states, ''Do not forget to entertain strangers, for by so doing some have unwittingly entertained angels.'' This connection underscores the importance of hospitality in the Christian faith, showing that welcoming others is equated with serving God Himself. The act of taking in a stranger is a profound expression of love and grace.', 'link', '[{"type": "crossReference", "reference": "Hebrews 13:2", "description": "Discusses the importance of hospitality"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 38, 38, 'kjv', 'or naked, and clothed thee?', 47, 74, 'greek', 'Clothing the Naked', 'γυμνός (gymnos, G1131) - naked, bare. This term conveys not just physical nakedness but also vulnerability and need. In the cultural context of the time, clothing was a symbol of dignity and protection. By addressing the act of clothing the naked, Jesus emphasizes the call to meet both physical and spiritual needs, highlighting the dignity of those in need.', 'textformat.abc', '[{"type": "strongs", "reference": "G1131", "description": "Strong''s number for the Greek word meaning naked"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 38, 38, 'kjv', 'When saw we thee a stranger, and took thee in?', 0, 46, 'theology', 'Theology of Service', 'This passage suggests that our treatment of others, especially the marginalized, is a direct reflection of our relationship with Christ. Many scholars believe that the ''least of these'' represents not only the poor but also those whom society overlooks. This reinforces the Christian doctrine of serving others as a manifestation of serving God.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the significance of serving the marginalized"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 38, 38, 'kjv', 'or naked, and clothed thee?', 47, 74, 'question', 'Reflecting on Needs', 'What does it mean for you to ''clothe the naked'' in your community? How might God be calling you to address the needs of those who are vulnerable and marginalized? This reflection encourages a deeper understanding of practical faith and the call to action within our local contexts.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 39, 39, 'kjv', 'sick, or in prison, and came unto thee?', 20, 59, 'connection', 'Caring for the Vulnerable', 'This phrase connects to James 1:27, which emphasizes pure religion as caring for orphans and widows in their distress. Both passages highlight the importance of serving those who are marginalized and suffering. This connection matters as it reinforces the Christian call to social justice and compassion, showing that our faith is demonstrated through acts of love toward those in need.', 'link', '[{"type": "crossReference", "reference": "James 1:27", "description": "Emphasizes caring for orphans and widows"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 39, 39, 'kjv', 'sick, or in prison, and came unto thee?', 20, 59, 'greek', 'Greek Term Insight', 'ἐν φυλακῇ (en phylakē, G5438) - in prison. This term signifies not just physical imprisonment but can also imply a state of being confined or restricted. Understanding this term enhances our comprehension of the depth of suffering that Christ identifies with, urging believers to reach out to those who are marginalized or oppressed.', 'textformat.abc', '[{"type": "strongs", "reference": "G5438", "description": "Greek word for prison"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 39, 39, 'kjv', 'sick, or in prison, and came unto thee?', 20, 59, 'theology', 'The Importance of Compassion', 'This passage suggests a foundational Christian doctrine of compassion and service to others. Many scholars believe that Christ''s identification with the suffering reflects a deeper theological truth: that how we treat the least among us is a direct reflection of our relationship with Him. This teaching challenges believers to manifest their faith through tangible acts of kindness and empathy.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 39, 39, 'kjv', 'sick, or in prison, and came unto thee?', 20, 59, 'question', 'Reflecting on Service', 'In what ways can you actively serve those who are sick or imprisoned in your community? Reflecting on this text, consider how your actions toward the vulnerable reflect your faith and commitment to Christ''s teachings.', 'heart.text.square', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 40, 40, 'kjv', 'Inasmuch as ye have done it unto one of the least of these my brethren, ye have done it unto me.', 68, 164, 'connection', 'Service to Others', 'This phrase resonates with Matthew 10:40-42, where Jesus highlights that receiving a prophet or a righteous person is akin to receiving Him. The connection underscores the theme of Christ''s identification with the marginalized. Acts of kindness toward others reflect our relationship with Christ, illustrating the profound truth that serving the ''least'' is serving Him directly.', 'link', '[{"type": "crossReference", "reference": "Matthew 10:40", "description": "Jesus emphasizes receiving His messengers as receiving Him."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 40, 40, 'kjv', 'Inasmuch as ye have done it unto one of the least of these my brethren, ye have done it unto me.', 68, 164, 'greek', 'Greek Terminology', 'ἀδελφός (adelphos, G80) - brother, fellow believer. This term signifies not just biological kinship but a deep spiritual connection within the community of faith. Understanding ''brethren'' in this context expands the scope of our responsibility towards all believers, emphasizing that acts of love towards them are recognized by Christ Himself.', 'textformat.abc', '[{"type": "strongs", "reference": "G80", "description": "Greek word for brother, indicating strong communal ties."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 40, 40, 'kjv', 'Inasmuch as ye have done it unto one of the least of these my brethren, ye have done it unto me.', 68, 164, 'theology', 'Christ''s Identification', 'This passage suggests a profound theological principle: Christ identifies intimately with the marginalized and suffering. Many scholars believe this reflects the doctrine of the imago Dei, wherein every individual bears God''s image. Thus, our treatment of the least is a direct reflection of our faith and love for Christ Himself, urging believers to act with compassion and justice.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 40, 40, 'kjv', 'Inasmuch as ye have done it unto one of the least of these my brethren, ye have done it unto me.', 68, 164, 'question', 'Reflecting on Service', 'How does recognizing Christ''s presence in the marginalized change the way you view service and charity? In what practical ways can you embody this teaching in your daily life, especially towards those society often overlooks?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 41, 41, 'kjv', 'Depart from me, ye cursed,', 51, 77, 'theology', 'Eternal Separation', 'This phrase highlights the serious consequence of rejecting Christ and failing to serve others. Many scholars believe that ''cursed'' refers to the ultimate separation from God''s presence, emphasizing the gravity of one''s choices in this life. This separation is not merely physical but spiritual, illustrating the final judgment as a reflection of one''s earthly actions.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of eternal separation from God."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 41, 41, 'kjv', 'into everlasting fire,', 78, 100, 'connection', 'Judgment Imagery', 'This phrase connects to Revelation 20:10, where the fate of the devil and his angels is described in similar terms. The imagery of ''everlasting fire'' serves as a stark warning about the consequences of sin and rebellion against God. Understanding this link underscores the seriousness of divine judgment and the eternal nature of the consequences for one''s choices.', 'link', '[{"type": "crossReference", "reference": "Revelation 20:10", "description": "Describes the eternal punishment of Satan, reinforcing the idea of everlasting fire."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 41, 41, 'kjv', 'prepared for the devil and his angels:', 101, 139, 'greek', 'Prepared (ἡτοιμασμένον)', 'ἡτοιμασμένον (hetoimasmenon, G2090) - prepared, made ready. This term implies intentionality in the creation of a place of judgment. It suggests that the everlasting fire was not arbitrarily assigned but specifically prepared for those who rebel against God. This highlights God''s justice and the seriousness of turning away from His ways.', 'textformat.abc', '[{"type": "strongs", "reference": "G2090", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 41, 41, 'kjv', 'Then shall he say also unto them on the left hand,', 0, 50, 'question', 'Who are the ''left hand''?', 'What does it mean to be placed on the ''left hand'' in this context? This positioning signifies judgment and separation from the righteous. How does this understanding of divine judgment influence your perspective on how you live out your faith and service to others?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 42, 42, 'kjv', 'I was an hungred, and ye gave me no meat:', 4, 45, 'connection', 'Feeding the Hungry', 'This phrase connects to Isaiah 58:7, which emphasizes the importance of feeding the hungry as an act of righteousness. It highlights that caring for those in need is not just a social obligation but a spiritual one, reflecting God''s heart for the marginalized.', 'link', '[{"type": "crossReference", "reference": "Isaiah 58:7", "description": "Calls believers to share food with the hungry."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 42, 42, 'kjv', 'I was thirsty, and ye gave me no drink:', 46, 85, 'theology', 'The Call to Serve', 'This passage suggests that acts of service towards the needy are intrinsically linked to one''s relationship with Christ. Many scholars believe that neglecting these acts is tantamount to neglecting Christ Himself, thus reinforcing the doctrine of the Body of Christ where each member is interconnected.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 42, 42, 'kjv', 'I was an hungred, and ye gave me no meat:', 4, 45, 'greek', 'Greek Term Analysis', 'ἰδού (idou, G2400) - behold, look. This term invites the reader to recognize the urgency and importance of the need presented. It serves to draw attention to the plight of those who suffer and calls for immediate action.', 'textformat.abc', '[{"type": "strongs", "reference": "G2400", "description": "The term used to draw attention in the context."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 42, 42, 'kjv', 'I was thirsty, and ye gave me no drink:', 46, 85, 'question', 'Reflect on Compassion', 'How does your understanding of Jesus'' identification with the thirsty shape your response to those in need around you? Consider what practical steps you can take to embody this compassion in your community.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 43, 43, 'kjv', 'I was a stranger, and ye took me not in:', 0, 40, 'connection', 'Welcoming the Stranger', 'This phrase echoes Leviticus 19:34, which commands Israel to love the stranger as themselves, reflecting God''s concern for the marginalized. By failing to welcome the stranger, the neglect of the needy is highlighted, emphasizing the significance of hospitality in the Christian faith.', 'link', '[{"type": "crossReference", "reference": "Leviticus 19:34", "description": "A commandment emphasizing love for the stranger."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 43, 43, 'kjv', 'naked, and ye clothed me not:', 41, 70, 'theology', 'Call to Compassion', 'Many scholars believe this passage underscores the Christian imperative to care for those in physical need. The act of clothing the naked symbolizes not only physical provision but also emotional and spiritual support, reflecting the holistic nature of Christian charity.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 43, 43, 'kjv', 'sick, and in prison, and ye visited me not.', 71, 114, 'greek', 'Visiting the Sick', 'ἐν φυλακῇ (en phylakē, G5438) - in prison, confinement. This term emphasizes not merely a physical location but also the social isolation experienced by prisoners. Visiting the imprisoned is a call to engage with those ostracized by society, reflecting Christ''s ministry to the outcast.', 'textformat.abc', '[{"type": "strongs", "reference": "G5438", "description": "Greek term for ''prison'' highlighting the context of isolation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 43, 43, 'kjv', 'I was a stranger, and ye took me not in:', 0, 40, 'question', 'Reflecting on Hospitality', 'What does it mean for us today to embrace those who are strangers or outsiders within our communities? How can our actions towards the marginalized reflect our faith in Christ?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 44, 44, 'kjv', 'when saw we thee an hungred, or athirst, or a stranger, or naked, or sick, or in prison', 47, 134, 'connection', 'Compassion in Action', 'This passage resonates with James 2:15-16, where it discusses the importance of faith being accompanied by action, particularly in helping those in need. The connection emphasizes that true faith manifests itself through practical acts of kindness and care for the vulnerable, illustrating that neglecting these acts is akin to neglecting Christ Himself.', 'link', '[{"type": "crossReference", "reference": "James 2:15-16", "description": "Highlights the necessity of action accompanying faith."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 44, 44, 'kjv', 'when saw we thee an hungred, or athirst, or a stranger, or naked, or sick, or in prison', 47, 134, 'theology', 'Christ''s Identification with the Needy', 'This passage suggests a profound theological insight into the nature of Christ''s solidarity with the marginalized. Many scholars believe that Christ identifies so closely with the least of society that their suffering is His suffering. This emphasizes the Christian call to social justice and compassion as a reflection of Christ''s character.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 44, 44, 'kjv', 'or in prison', 122, 134, 'question', 'Reflection on Imprisonment', 'What does it mean to recognize Christ in those who are imprisoned? How can our actions reflect His love and compassion toward those who are incarcerated or marginalized in society? This invites a deeper examination of our own attitudes and responses to those in dire circumstances.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 44, 44, 'kjv', 'Lord, when saw we thee', 41, 63, 'greek', 'The Title ''Lord''', 'κύριος (kyrios, G2962) - lord, master, owner. The use of ''Lord'' by the accusers signifies both recognition of authority and a plea for understanding, highlighting their misunderstanding of how they have related to Christ through their treatment of others.', 'textformat.abc', '[{"type": "strongs", "reference": "G2962", "description": "Strong''s number for the Greek term ''\u03ba\u03cd\u03c1\u03b9\u03bf\u03c2''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 45, 45, 'kjv', 'Inasmuch as ye did it not to one of the least of these', 58, 112, 'connection', 'Connections to Other Teachings', 'This phrase echoes the teaching found in Luke 10:25-37, where the Good Samaritan is used to illustrate love for one''s neighbor. Both passages emphasize the importance of caring for those in need as a reflection of one''s relationship with Christ. The connection highlights that neglecting the marginalized is akin to neglecting Christ Himself.', 'link', '[{"type": "crossReference", "reference": "Luke 10:25-37", "description": "Parable of the Good Samaritan teaches the importance of loving one''s neighbor."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 45, 45, 'kjv', 'Inasmuch as ye did it not to one of the least of these', 58, 112, 'theology', 'Christ''s Identification with the Marginalized', 'This passage suggests that Jesus identifies Himself with the marginalized and suffering. Many scholars believe this reflects a core Christian teaching that serving the needy is equivalent to serving Christ. It challenges believers to recognize the divine presence in those who are often overlooked in society.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the identification of Christ with the needy."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 45, 45, 'kjv', 'ye did it not to me', 114, 133, 'question', 'Reflection on Service', 'What does it mean to you that neglecting service to the least is neglecting Christ Himself? How does this understanding change your perspective on acts of kindness and service in your community?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 45, 45, 'kjv', 'Verily I say unto you', 35, 56, 'greek', 'Understanding ''Verily''', 'ἀμήν (amēn, G281) - truly, surely. This word emphasizes the certainty and authority of Jesus'' statement. It underscores the importance of what follows, showing that His teachings about service to the needy are not mere suggestions but divine truths.', 'textformat.abc', '[{"type": "strongs", "reference": "G281", "description": "Strong''s number for the Greek word meaning ''truly'' or ''surely''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 46, 46, 'kjv', 'everlasting punishment', 29, 51, 'greek', 'Greek Term Insight', 'αιώνιος (aionios, G166) - eternal, everlasting. This term emphasizes the unending nature of the punishment described. It often contrasts with the eternal life promised to the righteous, highlighting the stark differences in outcomes based on one''s actions and faithfulness.', 'textformat.abc', '[{"type": "strongs", "reference": "G166", "description": "Strong''s number for ''everlasting''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 46, 46, 'kjv', 'righteous into life eternal', 61, 88, 'theology', 'Eternal Life Doctrine', 'This passage suggests that the concept of eternal life is a central tenet of Christian faith. Many scholars believe it reflects Jesus'' teaching on the final judgment, where the separation of the righteous from the unrighteous is emphasized. This underscores the importance of living a life that aligns with God''s will as a pathway to salvation.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Offers insights on the final judgment and the nature of eternal life."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 25, 46, 46, 'kjv', 'everlasting punishment', 29, 51, 'connection', 'Cross-Reference for Punishment', 'Revelation 20:10 - ''And the devil who deceived them was cast into the lake of fire and brimstone...'' This connection matters as it further illustrates the concept of eternal punishment for those who oppose God, reinforcing the seriousness of the judgment Jesus describes.', 'link', '[{"type": "crossReference", "reference": "Revelation 20:10", "description": "Illustrates the finality and severity of divine judgment."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 25, 46, 46, 'kjv', 'these shall go away', 4, 23, 'question', 'Reflection on Judgment', 'What actions in your life reflect a commitment to serve the ''least of these''? This question encourages introspection on how one''s daily choices align with the teachings of Jesus regarding service and compassion.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 26, 1, 1, 'kjv', 'when Jesus had finished all these sayings,', 21, 63, 'connection', 'Connection to Teaching Completion', 'This phrase echoes the conclusion of Jesus'' teaching ministry, reminiscent of Matthew 7:28-29, where the crowds are astonished at His authority after He finishes the Sermon on the Mount. This connection highlights the importance of Jesus'' teachings, establishing His authority as a teacher whose words carry weight as He transitions into His impending passion.', 'link', '[{"type": "crossReference", "reference": "Matthew 7:28-29", "description": "The crowd''s reaction to Jesus'' teachings emphasizes His authority."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 26, 1, 1, 'kjv', 'he said unto his disciples,', 64, 91, 'theology', 'Discipleship and Authority', 'The phrase signifies the intimate relationship between Jesus and His disciples, underscoring a key theological principle: Jesus, as the authoritative teacher, imparts crucial truths to His followers. Many scholars believe this reflects the model of discipleship in the early Church, where teaching and learning were foundational to spiritual growth and community formation.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary on the Whole Bible", "description": "Explains the significance of Jesus'' relationship with His disciples."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 26, 1, 1, 'kjv', 'when Jesus had finished all these sayings,', 21, 63, 'greek', 'Significance of ''Finished''', 'τελέω (teleō, G5055) - to bring to an end, complete, fulfill. This term suggests that Jesus'' teachings were not only concluded but also fulfilled the purpose for which He came. This completion sets the stage for the events of His passion, emphasizing the transition from teaching to action.', 'textformat.abc', '[{"type": "strongs", "reference": "G5055", "description": "Strong''s number for the Greek term ''tele\u014d''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 26, 1, 1, 'kjv', 'he said unto his disciples,', 64, 91, 'question', 'Disciples'' Response', 'What does it mean for us today that Jesus speaks directly to His disciples, and by extension, to all believers? How does this personal communication shape our understanding of His teachings and our responsibility to live them out?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 26, 2, 2, 'kjv', 'the feast of the passover', 31, 56, 'connection', 'Passover Significance', 'The mention of the Passover connects to Exodus 12:13, where the blood of the lamb protects the Israelites from death. This connection underscores the sacrificial nature of Jesus'' impending death, as He is often referred to as the Lamb of God (John 1:29), linking the Old and New Testament themes of redemption.', 'link', '[{"type": "crossReference", "reference": "Exodus 12:13", "description": "The Passover lamb''s blood protecting the Israelites."}, {"type": "crossReference", "reference": "John 1:29", "description": "Jesus identified as the Lamb of God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 26, 2, 2, 'kjv', 'the Son of man', 62, 76, 'greek', 'Son of Man', 'υἱὸς ἀνθρώπου (huios anthrōpou, G5207) - son of man. This title emphasizes Jesus'' identification with humanity while also hinting at His divine authority, fulfilling the prophecy in Daniel 7:13-14, where the ''Son of Man'' is given dominion. It encapsulates the dual nature of Christ as fully divine and fully human.', 'textformat.abc', '[{"type": "strongs", "reference": "G5207", "description": "Greek term for ''son'' used in this context."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 26, 2, 2, 'kjv', 'is betrayed to be crucified', 77, 104, 'theology', 'Betrayal and Crucifixion', 'This passage suggests a profound theological truth about the nature of sin and redemption. The betrayal of Jesus reflects humanity''s rejection of God, yet it simultaneously sets the stage for the ultimate act of love—His crucifixion. Many scholars believe this indicates that God sovereignly uses human sin for a greater redemptive purpose.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Henry discusses the significance of betrayal in God''s plan."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(40, 26, 2, 2, 'kjv', 'after two days', 13, 27, 'question', 'Timing of Events', 'What does the specific mention of ''after two days'' reveal about God''s timing in the unfolding of His plan? How can we understand the significance of waiting and preparation in our own spiritual journeys, especially in light of Christ''s imminent sacrifice?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 26, 3, 3, 'kjv', 'Then assembled together the chief priests,', 0, 42, 'connection', 'Religious Leaders Uniting', 'This assembly of chief priests, scribes, and elders echoes similar gatherings found in John 11:47-53, where the Jewish leaders convene to plot against Jesus. This connection highlights the growing conspiracy against Him and their collaborative efforts to maintain control over the populace. It illustrates the institutional resistance Jesus faced, emphasizing the tension between divine authority and human governance.', 'link', '[{"type": "crossReference", "reference": "John 11:47-53", "description": "Jewish leaders plot to kill Jesus"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(40, 26, 3, 3, 'kjv', 'the palace of the high priest, who was called Caiaphas,', 95, 150, 'theology', 'Authority of Caiaphas', 'Caiaphas, as high priest, represents the pinnacle of religious authority during this period. Many scholars believe this highlights the conflict between the true High Priest, Jesus, and the corrupted system that Caiaphas leads. The setting in Caiaphas'' palace foreshadows the spiritual and physical trials Jesus will endure, emphasizing the contrast between human judgment and divine justice.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Insights on the role of Caiaphas"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini');