INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(41, 2, 9, 9, 'kjv', 'or to say, Arise, and take up thy bed, and walk?', 81, 129, 'greek', 'Understanding ''Arise''', 'ἐγείρω (egeirō, G1453) - to raise up, awaken. This term not only refers to physical healing but also has connotations of spiritual awakening. The choice of this word emphasizes that Jesus'' command is both a physical miracle and a call to new life, reflecting the holistic nature of His ministry.', 'textformat.abc', '[{"type": "strongs", "reference": "G1453", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 9, 9, 'kjv', 'Thy sins be forgiven thee;', 54, 80, 'connection', 'Forgiveness in Scripture', 'This phrase echoes the themes found in Psalm 103:3, where the psalmist states, ''Who forgiveth all thine iniquities; who healeth all thy diseases.'' Both passages highlight God''s authority and willingness to forgive sins, which is foundational to understanding Jesus'' divine nature and mission.', 'link', '[{"type": "crossReference", "reference": "Psalm 103:3", "description": "Points to God''s forgiving nature, paralleling Jesus'' authority."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 9, 9, 'kjv', 'or to say, Arise, and take up thy bed, and walk?', 81, 129, 'greek', 'Key Greek Terms', 'ἀνάστηθι (anastēthi, G450) - arise, stand up. This command not only signifies physical healing but also represents spiritual resurrection, illustrating Jesus'' power over both sin and physical ailments.', 'textformat.abc', '[{"type": "strongs", "reference": "G450", "description": "Indicates the power of Jesus'' command to restore."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 9, 9, 'kjv', 'Whether is it easier to say', 0, 27, 'theology', 'Authority of Christ', 'This passage suggests that Jesus is establishing His authority to forgive sins, a prerogative traditionally reserved for God alone. Many scholars believe this moment is pivotal in revealing Jesus as both divine and human, as He demonstrates power over sin and physical infirmity alike.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of Jesus'' authority in forgiving sins."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 9, 9, 'kjv', 'Whether is it easier to say', 0, 27, 'question', 'Understanding Jesus'' Authority', 'What does it mean for Jesus to equate forgiveness of sins with physical healing? How does this challenge your understanding of His authority in your life today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 10, 10, 'kjv', 'the Son of man hath power on earth to forgive sins', 26, 76, 'connection', 'Forgiveness in Scripture', 'This phrase connects to John 5:22, where it states that the Father judges no one but has given all judgment to the Son. This highlights the authority of Jesus not only to heal but also to forgive sins, emphasizing His divine power and role in salvation. Understanding this connection deepens our appreciation of Jesus'' dual role as healer and redeemer.', 'link', '[{"type": "crossReference", "reference": "John 5:22", "description": "The Father has granted all judgment to the Son, reinforcing His authority."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 10, 10, 'kjv', 'the Son of man', 26, 40, 'greek', 'Significance of ''Son of Man''', 'υἱὸς ἀνθρώπου (huios anthropou, G5207) - Son of man. This term signifies Jesus'' humanity and His representative role for mankind. It emphasizes His ability to empathize with human suffering while also being divinely authoritative. Notably, this term is often linked to Daniel 7:13-14, where the ''Son of Man'' receives authority and glory, pointing to Jesus'' messianic identity.', 'textformat.abc', '[{"type": "strongs", "reference": "G5207", "description": "Greek word for ''Son of man'' used frequently by Jesus."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 10, 10, 'kjv', 'power on earth to forgive sins', 46, 76, 'theology', 'Authority of Forgiveness', 'This passage suggests that Jesus possesses the unique authority to forgive sins, a divine prerogative. Many scholars believe that this power signifies the establishment of a new covenant, where forgiveness is not only a future hope but a present reality offered through Christ. This understanding redefines the relationship between humanity and God, emphasizing grace over judgment.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of Christ''s authority to forgive sins."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 10, 10, 'kjv', 'that ye may know', 4, 20, 'question', 'Understanding Knowledge', 'What does it mean for us to ''know'' the power of Christ to forgive? How does this knowledge impact our daily lives and relationships with others? Reflecting on this can lead to deeper faith and understanding of grace as we recognize our own need for forgiveness.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 11, 11, 'kjv', 'Arise, and take up thy bed, and go thy way into thine house.', 17, 77, 'connection', 'Healing and Restoration', 'This command echoes the healing narratives found in John 5:8, where Jesus tells the invalid at the pool of Bethesda to ''rise, take up thy bed, and walk.'' Both passages emphasize Jesus'' authority to heal and restore, illustrating His divine power. The act of taking up the bed symbolizes a departure from the old life of suffering into a new existence empowered by Christ.', 'link', '[{"type": "crossReference", "reference": "John 5:8", "description": "Similar healing narrative emphasizing Jesus'' authority."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 11, 11, 'kjv', 'I say unto thee, Arise, and take up thy bed, and go thy way into thine house.', 0, 77, 'greek', 'Authority of Christ', 'ἀνάστηθι (anastēthi, G450) - arise, stand up. This term is a command that not only signifies physical action but also implies spiritual awakening. It shows Christ''s authority to call individuals out of their physical and spiritual limitations, reinforcing the transformative power of His words.', 'textformat.abc', '[{"type": "strongs", "reference": "G450", "description": "Strong''s number for the Greek term meaning ''to arise.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 11, 11, 'kjv', 'Arise, and take up thy bed, and go thy way into thine house.', 17, 77, 'theology', 'Physical and Spiritual Healing', 'This passage suggests a holistic approach to healing, where physical restoration is intertwined with spiritual forgiveness. Many scholars believe that Jesus'' directive to take up the bed symbolizes leaving behind past burdens and embracing a new life in Him, which reflects the Christian understanding of redemption and transformation.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 11, 11, 'kjv', 'Arise, and take up thy bed, and go thy way into thine house.', 17, 77, 'question', 'What does this command mean?', 'What does it mean for you to ''arise'' and leave behind your burdens? In what areas of your life is Christ calling you to experience healing and restoration? Reflect on how obedience to His word might lead to a transformative change in your daily walk.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 12, 12, 'kjv', 'immediately he arose, took up the bed, and went forth before them all', 4, 73, 'connection', 'Healing and Response', 'This phrase reflects a theme found in John 5:8, where Jesus commands a sick man to rise and walk. Both instances showcase the authority of Christ over physical ailments and the immediate obedience required in response to His word. The promptness of the healing emphasizes the transformative power of faith in Jesus, a recurring motif in the Gospels.', 'link', '[{"type": "crossReference", "reference": "John 5:8", "description": "Another account of Jesus healing and commanding immediate action."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 12, 12, 'kjv', 'insomuch that they were all amazed, and glorified God', 75, 128, 'theology', 'Amazement and Worship', 'This passage illustrates the appropriate response to divine action: amazement leading to worship. Many scholars believe that the reaction of the crowd highlights the fundamental Christian teaching that encounters with God inspire awe and reverence, prompting believers to glorify Him. This reflects the purpose of miracles in the Gospel: to reveal God''s glory and draw people to faith.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the nature of responses to God''s miracles."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 12, 12, 'kjv', 'We never saw it on this fashion', 138, 169, 'question', 'Unique Encounters', 'What does it mean to witness something unprecedented in your spiritual journey? How does recognizing the uniqueness of Christ''s works impact your faith? This phrase invites reflection on the distinctiveness of Jesus'' ministry and challenges believers to remain open to new experiences of God''s power in their lives.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 12, 12, 'kjv', 'immediately he arose', 4, 24, 'greek', 'Greek Insight: ἄρτι', 'ἄρτι (arti, G737) - now, just now, at this moment. This term underlines the immediacy of the man''s response to Jesus'' command, suggesting that faith often requires prompt action. The use of ''immediately'' serves to accentuate the miraculous nature of the healing and the urgency of obedience in faith.', 'textformat.abc', '[{"type": "strongs", "reference": "G737", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 12, 12, 'kjv', 'immediately he arose', 4, 24, 'greek', 'Greek Term Insight', 'ἀμέσως (amēthōs, G2112) - immediately. This term emphasizes the urgency and decisiveness of the man''s response to Jesus'' command. It underscores the transformative power of Christ''s words, prompting instant action and highlighting the faith of the healed individual.', 'textformat.abc', '[{"type": "strongs", "reference": "G2112", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 12, 12, 'kjv', 'went forth before them all', 47, 73, 'connection', 'Public Witness', 'This phrase resonates with Matthew 5:16, which encourages believers to let their light shine before others. The healed man''s public demonstration of faith serves as a testimony to Jesus'' power and authority. Both passages emphasize the importance of visible faith as a witness to others.', 'arrow.triangle.branch', '[{"type": "crossReference", "reference": "Matthew 5:16", "description": "Referencing the call to shine one''s light before others."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 12, 12, 'kjv', 'they were all amazed', 89, 109, 'theology', 'Response to Divine Power', 'Many scholars believe that the amazement of the crowd reflects a common biblical theme where encounters with divine acts lead to awe and worship. This response highlights the necessity of recognizing God''s authority, encouraging believers to respond in faith and glorification in their own lives.', 'sparkles', '[{"type": "commentary", "reference": "Expositor''s Bible Commentary", "description": "Explores the themes of amazement and worship in response to miracles."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 12, 12, 'kjv', 'We never saw it on this fashion', 138, 169, 'question', 'Unique Miracles', 'What does this statement reveal about our expectations of God''s work in our lives? Are we open to experiencing God''s power in ways we have never seen before? This inquiry invites reflection on the transformative nature of Christ''s ministry and our receptiveness to His actions.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 13, 13, 'kjv', 'by the sea side', 24, 39, 'connection', 'Jesus and the Sea', 'The phrase ''by the sea side'' echoes the calling of His first disciples in Matthew 4:18-19, where Jesus also teaches by the Sea of Galilee. This connection emphasizes Jesus'' continual engagement with the natural world as a backdrop for His ministry and teaching, highlighting the accessibility of His message to the people. The sea symbolizes both chaos and calm, reflecting the dual nature of His ministry as one of authority over life''s tumultuous waters.', 'link', '[{"type": "crossReference", "reference": "Matthew 4:18-19", "description": "Jesus calls His first disciples by the sea, highlighting His ministry''s setting."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 13, 13, 'kjv', 'all the multitude resorted unto him', 45, 80, 'theology', 'The Draw of Christ', 'This phrase illustrates a key theological concept: the magnetic draw of Jesus to the crowds. Many scholars believe this reflects the universal appeal of Christ''s teachings and the hope He represents for the marginalized and suffering. His ability to attract a multitude signifies His role as the Savior who addresses both physical and spiritual needs, affirming His identity as the Messiah.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 13, 13, 'kjv', 'taught them', 89, 100, 'greek', 'Teaching Authority', 'διδάσκω (didaskō, G1321) - to teach, instruct. This Greek term emphasizes Jesus'' role as a teacher, one who not only imparts knowledge but also transforms lives through His teachings. The use of this word indicates a formal teaching setting and underscores the authority with which He spoke, setting Him apart from other teachers of the law.', 'textformat.abc', '[{"type": "strongs", "reference": "G1321", "description": "Strong''s number for the Greek term for teaching."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 13, 13, 'kjv', 'taught them', 89, 100, 'question', 'What is Christ''s Teaching?', 'What does it mean for us today that Jesus taught those who came to Him? How does His approach inform our understanding of learning and discipleship? Considering the context of His teachings, how might we apply His lessons in our own lives and communities?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 14, 14, 'kjv', 'Levi the son of Alpheus', 28, 51, 'connection', 'Levi''s Identity', 'Levi, also known as Matthew, is significant in the Gospel narrative as he transitions from a tax collector to a disciple. This transformation mirrors the calling of others like Peter (Mark 1:16-18) and Andrew, emphasizing Jesus'' ability to call and redeem those society often overlooks. Understanding Levi''s background enriches our appreciation of God''s grace towards sinners.', 'link', '[{"type": "crossReference", "reference": "Matthew 9:9", "description": "Matthew''s account of his own calling."}, {"type": "crossReference", "reference": "Mark 1:16-18", "description": "Jesus calls His first disciples, highlighting His authority to transform lives."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 14, 14, 'kjv', 'Follow me.', 105, 115, 'theology', 'The Call to Discipleship', 'The phrase ''Follow me'' encapsulates the essence of discipleship in Christianity. It suggests a radical commitment to leave one''s past and embrace a new life centered on Christ. Many scholars believe this reflects the broader biblical theme of calling, where God invites individuals into a transformative relationship (Matthew 4:19).', 'person.2.fill', '[{"type": "commentary", "reference": "Expositor''s Bible Commentary", "description": "Discussion on the implications of Jesus'' call to discipleship."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 14, 14, 'kjv', 'receipt of custom', 67, 84, 'greek', 'Tax Collector''s Role', 'ἀνθρώπων (anthrōpōn, G444) - people, humans. The term for ''receipt of custom'' suggests Levi''s role as a tax collector, often viewed negatively in Jewish society. This illustrates the radical nature of Jesus'' ministry, as He reaches out to those marginalized and despised, emphasizing His mission to the lost.', 'textformat.abc', '[{"type": "strongs", "reference": "G444", "description": "Strong''s number related to ''people''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 14, 14, 'kjv', 'he arose and followed him.', 120, 146, 'question', 'Response to the Call', 'What does Levi''s immediate response to ''Follow me'' teach us about obedience to God''s call? Consider the implications of leaving one''s job and past life behind. How can we apply this urgency in our own lives when we sense God''s direction?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 15, 15, 'kjv', 'sat at meat in his house', 36, 60, 'theology', 'Jesus'' Table Fellowship', 'This phrase highlights the radical inclusivity of Jesus'' ministry. By sharing a meal with publicans and sinners, Jesus demonstrates that his message of grace and redemption extends to all, regardless of their social standing or past actions. Many scholars believe this reflects the Kingdom of God''s values, where the marginalized are embraced rather than excluded. This act serves as a model for Christian fellowship today.', 'person.2.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 15, 15, 'kjv', 'publicans and sinners', 67, 88, 'connection', 'Inclusivity in Jesus'' Ministry', 'This phrase can be cross-referenced with Luke 15:1-2, where tax collectors and sinners gather around Jesus, prompting criticism from the Pharisees. This connection matters as it emphasizes Jesus'' mission to seek and save the lost, illustrating his radical approach to social boundaries and acceptance.', 'link', '[{"type": "crossReference", "reference": "Luke 15:1-2", "description": "Jesus welcomes sinners and critiques the Pharisees'' attitudes."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 15, 15, 'kjv', 'many publicans and sinners', 62, 88, 'greek', 'The Meaning of ''Publicans''', 'τελώνης (telōnēs, G5057) - tax collector. This term refers to individuals who collected taxes for the Roman Empire, often viewed as traitors by their fellow Jews. Understanding this word underscores the social stigma faced by these individuals and highlights the transformative nature of Jesus'' acceptance, which challenges societal norms.', 'textformat.abc', '[{"type": "strongs", "reference": "G5057", "description": "Strong''s number for the Greek term for tax collector."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 15, 15, 'kjv', 'many publicans and sinners', 62, 88, 'connection', 'Jesus and Outcasts', 'This phrase connects with Luke 19:10, where Jesus states, ''For the Son of Man came to seek and to save the lost.'' Both passages illustrate Jesus'' mission to include those marginalized by society. By dining with publicans and sinners, Jesus exemplifies God''s grace extended to all, regardless of social standing or sinfulness.', 'link', '[{"type": "crossReference", "reference": "Luke 19:10", "description": "Highlights Jesus'' mission to the marginalized"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 15, 15, 'kjv', 'sat at meat in his house', 36, 60, 'greek', 'The Significance of ''Sat at Meat''', 'ἔφαγον (ephagon, G2068) - to eat, feast. The choice of this term suggests a communal and celebratory meal, which in Jewish culture symbolized fellowship and acceptance. Jesus breaking bread with ''publicans and sinners'' indicates a radical inclusivity that challenges societal norms.', 'textformat.abc', '[{"type": "strongs", "reference": "G2068", "description": "Strong''s number for the Greek term used"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 15, 15, 'kjv', 'followed him', 167, 179, 'theology', 'Call to Discipleship', 'This phrase encapsulates the essence of discipleship in Christianity. Many scholars believe it signifies not just physical following but a deeper commitment to Jesus'' teachings and lifestyle. It reflects a transformative relationship where followers are called from their past to a new life, emphasizing grace and redemption.', 'sparkles', '[{"type": "commentary", "reference": "The New International Commentary on the New Testament", "description": "Discusses the transformative aspect of following Jesus"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 15, 15, 'kjv', 'sat also together with Jesus', 89, 117, 'question', 'What Does Fellowship Mean?', 'What does it mean for Jesus to sit and eat with those labeled as ''sinners''? Consider how this act of fellowship challenges contemporary understandings of community and acceptance. In what ways can we extend this model of inclusion to those who feel marginalized in our own contexts?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 16, 16, 'kjv', 'How is it that he eateth and drinketh with publicans and sinners?', 105, 170, 'question', 'Challenging Religious Norms', 'What does this question reveal about the attitudes of the scribes and Pharisees towards those they deemed unworthy? Reflecting on this can lead to deeper understanding of how societal or religious barriers might affect our relationships today. Are there modern parallels in how we engage with those considered ''sinners''?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 16, 16, 'kjv', 'scribes and Pharisees', 13, 34, 'theology', 'Critique of Legalism', 'Many scholars believe this confrontation illustrates Jesus'' critique of legalism and the rigid adherence to the law by the religious elite. The Pharisees represent a mindset that prioritizes ritual purity over compassion. This passage invites reflection on how religious practices can sometimes lead to exclusion rather than inclusion.', 'figure.mind.and.body', '[{"type": "commentary", "reference": "The Gospel According to Mark by R.T. France", "description": "Insight on Jesus'' challenge to religious norms"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 16, 16, 'kjv', 'publicans and sinners', 52, 73, 'connection', 'Jesus and Social Outcasts', 'This phrase resonates with Luke 15:1-2, where Jesus is criticized for welcoming sinners. This connection emphasizes Jesus'' mission to reach those marginalized by society, reinforcing the theme of inclusivity in His ministry. It showcases God''s grace that extends beyond social boundaries.', 'link', '[{"type": "crossReference", "reference": "Luke 15:1-2", "description": "Jesus'' acceptance of sinners is a central theme in His ministry."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 16, 16, 'kjv', 'eat with publicans and sinners', 43, 73, 'theology', 'Grace Over Law', 'This passage highlights the contrast between Jesus'' approach and the Pharisees'' legalism. Many scholars believe this reflects the broader theological theme that Jesus came to fulfill the law by embodying grace, suggesting that spiritual health is found not in strict adherence to rules but in relationship and repentance.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 16, 16, 'kjv', 'How is it that he eateth and drinketh with publicans and sinners?', 105, 170, 'question', 'Understanding Jesus'' Outreach', 'What does Jesus'' willingness to share meals with ''publicans and sinners'' reveal about His character and mission? How does this challenge our understanding of holiness and community? Reflect on how we can embody this inclusive love in our own lives.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 16, 16, 'kjv', 'scribes and Pharisees', 13, 34, 'greek', 'Religious Authorities', 'φαρισαῖος (pharisaios, G5330) - Pharisee, a member of a Jewish sect known for strict adherence to the law. This term underscores the conflict between Jesus and these religious authorities, who represent a rigid interpretation of righteousness, contrasting with Jesus'' message of grace.', 'textformat.abc', '[{"type": "strongs", "reference": "G5330", "description": "Strong''s number for Pharisee."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 17, 17, 'kjv', 'but sinners to repentance', 150, 175, 'greek', 'Repentance in Greek', 'μετανοία (metanoia, G3341) - repentance, a change of mind. This term indicates not just feeling sorry for sins, but a transformative shift in thinking and behavior. It underscores the holistic nature of repentance that Jesus calls for, which involves turning away from sin and towards God. Understanding this term deepens the comprehension of Jesus'' mission and the invitation to experience true change.', 'textformat.abc', '[{"type": "strongs", "reference": "G3341", "description": "Greek word for repentance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 17, 17, 'kjv', 'I came not to call the righteous, but sinners to repentance', 116, 175, 'question', 'Understanding Righteousness', 'What does it mean to be ''righteous'' in the context of this verse, and how might this perception affect our approach to evangelism? Consider how the distinction between the righteous and sinners challenges common views of worthiness and the need for grace in all lives.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 17, 17, 'kjv', 'They that are whole have no need of the physician, but they that are sick', 41, 114, 'connection', 'Healing and Wholeness', 'This phrase echoes the sentiment found in Luke 5:31, which states, ''Jesus answered them, “Those who are well have no need of a physician, but those who are sick.”'' The connection highlights Jesus'' mission to reach those who recognize their need for healing, both physically and spiritually. It emphasizes that the call to repentance is not for the self-righteous, but for those aware of their brokenness.', 'link', '[{"type": "crossReference", "reference": "Luke 5:31", "description": "Similar teaching on the need for spiritual healing."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 17, 17, 'kjv', 'I came not to call the righteous, but sinners to repentance.', 116, 176, 'theology', 'Mission of Christ', 'This passage suggests a core aspect of Christ''s mission: to seek and save the lost (Luke 19:10). Many scholars believe that this highlights the inclusive nature of Jesus'' ministry, emphasizing God''s grace towards those marginalized by society. It challenges believers to reflect on their own attitudes towards those considered ''sinners'' and to recognize the transformative power of repentance.', 'person.2.fill', '[{"type": "commentary", "reference": "Luke 19:10", "description": "Affirms Jesus'' mission to save the lost."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(41, 2, 17, 17, 'kjv', 'I came not to call the righteous, but sinners to repentance.', 116, 176, 'question', 'Understanding Repentance', 'What does it mean to truly repent, and how can I ensure that I am not self-righteous in my faith? Reflecting on this question helps to clarify the depth of Jesus'' call and our own need for humility before God.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 17, 17, 'kjv', 'but they that are sick', 92, 114, 'greek', 'Understanding ''sick''', 'ἀσθενής (astheneis, G772) - weak, sick, feeble. This term indicates not just physical ailment but also spiritual weakness. It illustrates the condition of those who are lost, emphasizing their need for the healing that Christ offers.', 'textformat.abc', '[{"type": "strongs", "reference": "G772", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 18, 18, 'kjv', 'used to fast', 47, 59, 'greek', 'Understanding ''Fast''', 'νηστεύω (nēsteuō, G3522) - to fast, abstain from food. This term indicates not merely a physical absence of food but a spiritual discipline aimed at seeking God. In the context of the Pharisees, it underscores their ritualistic approach to spirituality, contrasting with the new joy found in Jesus'' presence.', 'textformat.abc', '[{"type": "strongs", "reference": "G3522", "description": "Strong''s Greek number for ''to fast''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(41, 2, 18, 18, 'kjv', 'but thy disciples fast not?', 149, 176, 'theology', 'Joy in Christ''s Presence', 'Many scholars believe this question reveals a deeper theological truth about the nature of salvation and the joy that accompanies Christ''s ministry. Unlike the ascetic practices of the Pharisees, Jesus'' presence signifies a new era where rejoicing takes precedence over mourning, inviting believers to experience the fullness of life in Him.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of fasting in the context of Jesus'' ministry."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini');