INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(42, 11, 20, 20, 'kjv', 'But if I with the finger of God', 0, 31, 'greek', 'Finger of God', 'χείρ (cheir, G5495) - hand; often used metaphorically in Scripture to denote power and authority. In this context, the ''finger of God'' implies divine intervention and authority in casting out demons, highlighting that such actions are not merely human efforts but are empowered by God Himself.', 'textformat.abc', '[{"type": "strongs", "reference": "G5495", "description": "Strong''s number for ''hand''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 20, 20, 'kjv', 'cast out devils', 32, 47, 'connection', 'Exorcism in Scripture', 'This phrase connects to Mark 1:34, where Jesus heals many and casts out demons. Both passages illustrate Jesus'' authority over evil spirits, emphasizing His role as the Messiah who brings the kingdom of God through His powerful acts. This connection reinforces the understanding of Christ as the one who liberates humanity from spiritual bondage.', 'link', '[{"type": "crossReference", "reference": "Mark 1:34", "description": "Demonstrates Jesus'' authority in exorcism"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 20, 20, 'kjv', 'the kingdom of God is come upon you', 58, 93, 'theology', 'Inauguration of the Kingdom', 'This statement suggests that the kingdom of God is not only a future hope but a present reality initiated by Jesus'' ministry. Many scholars believe this reflects the ''already but not yet'' theology, where God''s reign is breaking into the world through Christ''s works. This understanding challenges believers to recognize and participate in the kingdom''s present manifestation.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 20, 20, 'kjv', 'the finger of God cast out devils', 14, 47, 'question', 'Divine Authority Reflection', 'What does it mean for you to recognize the ''finger of God'' at work in your life? How might acknowledging God''s authority in spiritual matters influence your daily decisions and interactions with others?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 21, 21, 'kjv', 'When a strong man armed keepeth his palace, his goods are in peace:', 0, 67, 'connection', 'Strong Man Concept', 'This idea of a strong man protecting his possessions resonates with Matthew 12:29, where Jesus speaks of binding the strong man before plundering his house. Both passages highlight the spiritual warfare context, illustrating the battle between good and evil. Understanding this connection emphasizes the necessity of divine intervention in overcoming spiritual strongholds.', 'link', '[{"type": "crossReference", "reference": "Matthew 12:29", "description": "Jesus speaks about overcoming the strong man."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 21, 21, 'kjv', 'When a strong man armed keepeth his palace, his goods are in peace:', 0, 67, 'theology', 'Spiritual Security', 'This verse suggests a theological principle regarding spiritual security. Many scholars believe that the ''strong man'' represents the devil or evil forces that maintain control over individuals or communities. The ''goods'' symbolize the souls or lives that are safeguarded until a stronger power intervenes. This teaches about the authority of Christ over spiritual realms and the importance of His power in securing peace.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 21, 21, 'kjv', 'When a strong man armed keepeth his palace, his goods are in peace:', 0, 67, 'greek', 'Key Term: Strong Man', 'ἰσχυρός (ischuros, G2478) - strong, mighty. This term indicates not just physical strength but also the power to defend and maintain control. In this context, it highlights the formidable presence of evil that can only be challenged by an even greater power, emphasizing the supremacy of Christ''s authority.', 'textformat.abc', '[{"type": "strongs", "reference": "G2478", "description": "Strong''s number for the Greek word used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 21, 21, 'kjv', 'When a strong man armed keepeth his palace, his goods are in peace:', 0, 67, 'question', 'Personal Application', 'What does it mean for you personally to have your ''goods'' in peace under the protection of Christ? In what areas of your life do you need to invite His stronger presence to ensure your spiritual security?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 22, 22, 'kjv', 'a stronger than he shall come upon him', 9, 47, 'connection', 'Spiritual Warfare Theme', 'This phrase reflects a broader biblical theme of spiritual warfare. For instance, Ephesians 6:12 speaks of wrestling against spiritual forces of evil, highlighting the ongoing battle between good and evil. Additionally, 1 John 4:4 reassures believers that ''he who is in you is greater than he who is in the world,'' emphasizing the strength of Christ over evil forces. These connections matter as they illustrate the assurance believers have in Christ''s ultimate victory.', 'link', '[{"type": "crossReference", "reference": "Ephesians 6:12", "description": "Highlights the nature of spiritual warfare."}, {"type": "crossReference", "reference": "1 John 4:4", "description": "Reassures believers of Christ''s power over evil."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 22, 22, 'kjv', 'he taketh from him all his armour wherein he trusted', 67, 119, 'greek', 'Armor Significance', 'ἀρμάτωμα (armáto̱ma, G2288) - armor, equipment. This term emphasizes the protective gear that one relies on for security. In the context of spiritual battles, it signifies the false securities that one may trust in, which can be stripped away by a stronger force. This insight challenges believers to evaluate what they truly rely on for protection and strength in their spiritual lives.', 'textformat.abc', '[{"type": "strongs", "reference": "G2288", "description": "Relates to the concept of spiritual armor."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 22, 22, 'kjv', 'divideth his spoils', 125, 144, 'theology', 'Victory and Spoils', 'The act of dividing spoils signifies the victory of a conqueror. Many scholars believe this reflects the theological concept of Christ''s victory over sin and death, which allows believers to partake in the blessings of salvation. This passage suggests that through Christ''s triumph, believers not only receive protection but also inherit the spoils of eternal life, grace, and righteousness.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of Christ''s victory."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 22, 22, 'kjv', 'when a stronger than he shall come upon him', 4, 47, 'question', 'Who is the Stronger One?', 'Reflecting on the phrase ''a stronger than he shall come upon him,'' who do you recognize as the ''stronger'' in your life? In what areas do you need to rely on Christ''s strength rather than your own? This contemplation invites you to consider how divine intervention can overcome the challenges you face.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 23, 23, 'kjv', 'He that is not with me is against me:', 0, 37, 'theology', 'Allegiance to Christ', 'This phrase emphasizes the necessity of active allegiance to Christ. Many scholars believe it underscores the dichotomy in spiritual allegiance; neutrality is not an option in the kingdom of God. This reflects a common theme in the New Testament where following Jesus requires a definitive choice, echoing Matthew 12:30, where Jesus states, ''Whoever is not with me is against me.''', 'person.2.fill', '[{"type": "crossReference", "reference": "Matthew 12:30", "description": "Indicates Jesus'' teaching on allegiance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 23, 23, 'kjv', 'and he that gathereth not with me scattereth.', 38, 83, 'connection', 'Gathering and Scattering', 'This phrase connects to John 10:16, where Jesus speaks of gathering the scattered sheep. The imagery of gathering suggests a unifying mission of Christ, contrasting with scattering, which denotes division and chaos. Understanding this duality highlights the importance of participating in Christ''s mission to bring people together rather than allowing division.', 'link', '[{"type": "crossReference", "reference": "John 10:16", "description": "Jesus'' role as the shepherd gathering His flock."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 23, 23, 'kjv', 'He that is not with me is against me:', 0, 37, 'greek', 'Key Greek Terms', 'Against (κατὰ, kata, G2596) - down against, in opposition. This term reinforces the idea that one cannot simply be passive in their relationship with Christ. The usage of ''against'' indicates an active resistance rather than mere absence of support, emphasizing the seriousness of one''s spiritual position.', 'textformat.abc', '[{"type": "strongs", "reference": "G2596", "description": "Strong''s number for ''against''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 23, 23, 'kjv', 'and he that gathereth not with me scattereth.', 38, 83, 'question', 'Reflection on Gathering', 'What does it mean to actively gather with Christ in your daily life? How can you contribute to the mission of bringing others into His fold, rather than allowing division and separation? Reflecting on this can help clarify how you engage with others in your faith community.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 24, 24, 'kjv', 'When the unclean spirit is gone out of a man', 0, 44, 'connection', 'Spiritual Warfare', 'This phrase echoes Matthew 12:43, where Jesus describes a similar scenario of an unclean spirit leaving a person. The connection highlights the reality of spiritual warfare and the necessity for vigilance after liberation from evil. Both passages emphasize that mere removal of an unclean spirit is insufficient without a change in the individual''s life to prevent a return.', 'link', '[{"type": "crossReference", "reference": "Matthew 12:43", "description": "Jesus illustrates the return of the unclean spirit."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 24, 24, 'kjv', 'he walketh through dry places, seeking rest', 46, 89, 'theology', 'Desolation of Evil', 'This passage suggests that evil spirits experience a state of desolation and unrest when expelled. Many scholars believe this reflects the inherent nature of evil, which thrives on chaos and seeks to re-enter the soul it once inhabited, illustrating the importance of spiritual filling and protection post-deliverance.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 24, 24, 'kjv', 'I will return unto my house whence I came out', 119, 164, 'greek', 'House (οἰκία)', 'οἰκία (oikia, G3624) - house, dwelling. This term signifies more than a physical structure; it symbolizes the spiritual condition of a person. The phrase implies that the spirit views the individual as its rightful domain, indicating a sense of ownership and the need for ongoing spiritual vigilance.', 'textformat.abc', '[{"type": "strongs", "reference": "G3624", "description": "Strong''s number for the Greek term related to house."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 24, 24, 'kjv', 'seeking rest', 77, 89, 'question', 'Rest and Fulfillment', 'What does it mean for the unclean spirit to seek rest, and how does this reflect our own search for true peace? In what ways do we ensure our lives are filled with the Spirit of God, so that we do not leave ourselves vulnerable to spiritual attacks?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 25, 25, 'kjv', 'swept and garnished', 34, 53, 'greek', 'Greek Terms Explained', 'The phrase ''swept and garnished'' translates the Greek words ''συνεσπασμένον'' (synespasmenon, G4868) meaning ''swept together'' and ''κοσμημένον'' (kosmēmenon, G2885) meaning ''adorned'' or ''decorated.'' This imagery suggests a surface-level cleanliness and order that may mask deeper issues, highlighting the importance of true inner transformation rather than mere outward appearance.', 'textformat.abc', '[{"type": "strongs", "reference": "G4868", "description": "Swept together"}, {"type": "strongs", "reference": "G2885", "description": "Adorned or decorated"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 25, 25, 'kjv', 'swept and garnished', 34, 53, 'connection', 'Connection to Inner Purity', 'This phrase evokes connections to Matthew 23:25-26, where Jesus calls out the Pharisees for being like ''whited sepulchres'' that appear beautiful outwardly but are full of dead men''s bones. This highlights a central theme in Jesus'' teaching: outer appearances can be deceiving, and true spiritual health requires inward purity.', 'link', '[{"type": "crossReference", "reference": "Matthew 23:25", "description": "Jesus critiques outward appearances"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 25, 25, 'kjv', 'findeth it swept and garnished', 23, 53, 'theology', 'Theology of Spiritual Warfare', 'This passage suggests the reality of spiritual warfare, where the removal of unclean spirits does not guarantee lasting change. Many scholars believe that personal transformation must accompany spiritual cleansing to prevent a return to a worse state. This underscores the necessity of ongoing vigilance and spiritual growth in the life of a believer.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discussion on spiritual vigilance"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 25, 25, 'kjv', 'swept and garnished', 34, 53, 'question', 'Reflection on Spiritual Condition', 'What does it mean to have a life that is ''swept and garnished'' yet devoid of true transformation? How can we ensure that our spiritual lives reflect genuine change rather than mere external appearances?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 26, 26, 'kjv', 'seven other spirits more wicked than himself', 33, 77, 'connection', 'Evil Spirits in Scripture', 'This phrase echoes the idea of demonic possession and spiritual warfare found in other passages such as Matthew 12:45, where a similar concept of a possessed person becoming worse after receiving an unclean spirit is discussed. Additionally, Revelation 12:4 refers to a third of the angels being cast down, indicating the presence of evil spirits in opposition to God. These connections highlight the seriousness of spiritual struggle and the complexity of demonic influences in the lives of individuals.', 'link', '[{"type": "crossReference", "reference": "Matthew 12:45", "description": "Discusses the return of an unclean spirit."}, {"type": "crossReference", "reference": "Revelation 12:4", "description": "Mentions the fall of angels."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 26, 26, 'kjv', 'last state of that man is worse than the first', 123, 169, 'theology', 'Spiritual Consequences', 'Many scholars believe this verse illustrates the doctrine of spiritual renewal and the consequences of failing to fill one''s life with righteousness after deliverance. This suggests that mere external cleansing without internal transformation can lead to a more severe condition. It emphasizes the importance of not only being freed from sin but also actively pursuing a life aligned with God''s will, highlighting the necessity of ongoing spiritual vigilance.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of spiritual renewal."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 26, 26, 'kjv', 'Then goeth he, and taketh to him', 0, 32, 'greek', 'Meaning of ''taketh''', 'λαμβάνει (lambanei, G2983) - to take, receive, or seize. This term is significant as it implies an active choice of the spirit to gather more wicked companions, suggesting a strategic and deliberate action against the individual. This choice reflects the intensifying nature of spiritual battles, emphasizing that evil does not remain idle but seeks to grow in strength and influence.', 'textformat.abc', '[{"type": "strongs", "reference": "G2983", "description": "Strong''s number for ''taketh''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 26, 26, 'kjv', 'worse than the first', 149, 169, 'question', 'Reflection on Spiritual State', 'What does it mean for a person''s spiritual condition to be ''worse than the first''? How can we ensure that, after experiencing transformation, we do not fall back into a state of greater despair or sin? Reflecting on this can help us understand the importance of spiritual growth and the need for continuous engagement with God''s word and community.', 'heart.text.square', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 27, 27, 'kjv', 'Blessed is the womb that bare thee, and the paps which thou hast sucked.', 118, 190, 'connection', 'Mary''s Blessing', 'This phrase echoes the sentiments expressed in Luke 1:42, where Elizabeth blesses Mary, recognizing her as the mother of the Lord. This connection underscores the significance of Mary''s role in the incarnation and highlights the theme of blessing in relation to Jesus'' birth. It invites readers to reflect on the honor bestowed upon Mary and the divine nature of motherhood in the context of Jesus'' life.', 'link', '[{"type": "crossReference", "reference": "Luke 1:42", "description": "Elizabeth''s blessing of Mary emphasizes the sacredness of her role."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 27, 27, 'kjv', 'Blessed is the womb that bare thee, and the paps which thou hast sucked.', 118, 190, 'theology', 'Theological Implications of Blessing', 'This passage prompts reflection on the nature of true blessedness. While the woman recognizes the physical motherhood of Jesus, He redirects the focus to spiritual obedience. Many scholars believe this suggests that the essence of being blessed lies not merely in physical lineage but in hearing and keeping God''s word, emphasizing the importance of spiritual fidelity over mere biological connection.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 27, 27, 'kjv', 'a certain woman of the company lifted up her voice', 47, 97, 'question', 'Understanding the Response', 'What does the woman''s proclamation reveal about societal views of motherhood and honor? How does her expression compare to the way Jesus later emphasizes spiritual kinship over biological ties? Reflecting on this can deepen our understanding of discipleship and the values of the Kingdom of God.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 27, 27, 'kjv', 'Blessed is the womb that bare thee, and the paps which thou hast sucked.', 118, 190, 'greek', 'Greek Terms of Blessing', 'μακάριος (makarios, G3107) - blessed, happy. This term often signifies a state of divine favor and joy. The use of ''makarios'' in this context highlights the deep spiritual significance of motherhood, linking it to the joy and blessing that accompany Jesus'' identity and mission.', 'textformat.abc', '[{"type": "strongs", "reference": "G3107", "description": "Strong''s number for the Greek term translated as ''blessed''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 28, 28, 'kjv', 'hear the word of God, and keep it.', 47, 81, 'connection', 'Hearing and Keeping', 'This phrase connects closely to James 1:22, where believers are encouraged to not only hear the word but also to put it into practice. This is significant as it emphasizes the active nature of faith; mere hearing is insufficient without obedient action. Similarly, in Matthew 7:24-27, Jesus illustrates the wise man who builds his house on the rock by hearing and doing His words, further reinforcing the importance of obedience in the life of a disciple.', 'link', '[{"type": "crossReference", "reference": "James 1:22", "description": "Encouragement to be doers of the word."}, {"type": "crossReference", "reference": "Matthew 7:24-27", "description": "The parable of the wise and foolish builders."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 28, 28, 'kjv', 'hear the word of God, and keep it.', 47, 81, 'greek', 'Key Greek Terms', 'The term ''word'' is translated from ῥῆμα (rhēma, G4487), which denotes a spoken word or utterance, emphasizing the dynamic nature of God''s communication. This contrasts with λόγος (logos), which refers to the overarching divine reason or plan. Understanding this distinction highlights that God''s specific spoken commands require attentive listening and faithful adherence.', 'textformat.abc', '[{"type": "strongs", "reference": "G4487", "description": "Strong''s number for ''\u1fe5\u1fc6\u03bc\u03b1''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 28, 28, 'kjv', 'Yea rather, blessed are they that hear the word of God, and keep it.', 13, 81, 'theology', 'Blessing in Obedience', 'This passage suggests that true blessedness comes not from biological or familial connections to Jesus, but from a relationship characterized by listening and obedience to God''s word. Many scholars believe this reflects the broader theological theme of discipleship in the Gospel, where the call to follow Christ is tied to active faith and commitment, echoing the idea found in John 14:15, ''If you love me, keep my commands.''', 'sparkles', '[{"type": "crossReference", "reference": "John 14:15", "description": "Connection between love for Christ and obedience."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 28, 28, 'kjv', 'Yea rather, blessed are they that hear the word of God, and keep it.', 13, 81, 'question', 'Application of Hearing', 'What does it mean for us today to ''hear'' the word of God? In what ways can we actively keep His teachings in our daily lives? Reflecting on this can lead to deeper engagement with Scripture and a more profound commitment to living out our faith.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 29, 29, 'kjv', 'This is an evil generation: they seek a sign;', 67, 112, 'connection', 'Seeking Signs', 'This phrase resonates with Matthew 16:4, where Jesus states that a wicked and adulterous generation seeks a sign. The parallel highlights the spiritual blindness of the people, emphasizing that true faith does not rely on miraculous signs but on the word of God. This connection deepens the understanding of Jesus'' frustration with the demand for signs as evidence of His authority.', 'link', '[{"type": "crossReference", "reference": "Matthew 16:4", "description": "Jesus expresses similar sentiments about seeking signs."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 29, 29, 'kjv', 'but the sign of Jonas the prophet.', 150, 184, 'theology', 'Sign of Jonah', 'Many scholars believe that the ''sign of Jonas'' refers to Jonah''s three days in the belly of the fish as a foreshadowing of Christ''s death and resurrection. This doctrine underscores the necessity of Jesus'' sacrifice and serves as a call for repentance, reflecting God''s mercy and the importance of faith over mere signs.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of Jonah''s sign."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 29, 29, 'kjv', 'This is an evil generation: they seek a sign;', 67, 112, 'question', 'Faith Without Signs', 'What does it reveal about our faith if we find ourselves seeking signs instead of trusting in God''s word? How might this challenge our understanding of what it means to believe in Christ without needing continual proof?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 29, 29, 'kjv', 'there shall no sign be given it,', 117, 149, 'greek', 'Understanding ''sign''', 'σημεῖον (sēmeion, G4592) - sign, miracle, token. This term often indicates a divine confirmation or miraculous event meant to convey a deeper spiritual truth. In this context, Jesus conveys that true belief comes from understanding the message behind His ministry rather than from witnessing signs alone.', 'textformat.abc', '[{"type": "strongs", "reference": "G4592", "description": "Greek word for ''sign'' used in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 30, 30, 'kjv', 'as Jonas was a sign unto the Ninevites', 4, 42, 'connection', 'Significance of Signs', 'The mention of Jonas as a sign connects to Matthew 12:39-41, where Jesus emphasizes the importance of His own ministry as a sign. This parallel highlights the prophetic role of Jonas and illustrates that just as the Ninevites responded to Jonas, so too should this generation respond to Christ''s message. The use of signs in Scripture often calls for a response of faith and repentance.', 'link', '[{"type": "crossReference", "reference": "Matthew 12:39-41", "description": "Jesus speaks about the sign of Jonah and its implications for His generation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 30, 30, 'kjv', 'the Son of man be to this generation', 58, 94, 'theology', 'Christ''s Role as a Sign', 'This passage suggests that the Son of Man serves as a profound sign of God''s presence and intention. Many scholars believe that Jesus is drawing a parallel between Himself and Old Testament figures like Jonas, indicating His role as the ultimate revelation of God''s truth and the necessity of repentance. This reflects the broader theological theme of the incarnation as God''s definitive sign to humanity.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses Christ as the ultimate sign and revelation of God''s grace."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 30, 30, 'kjv', 'a sign unto the Ninevites', 17, 42, 'greek', 'Understanding ''sign''', 'σημεῖον (sēmeion, G4592) - sign, miracle, token. This term denotes a miraculous event that serves as a pointer to a deeper truth. In this context, it emphasizes that Jonas''s presence was not merely an event but a divine sign meant to provoke thought and action among the Ninevites, paralleling the call to repentance that Jesus offers to His listeners.', 'textformat.abc', '[{"type": "strongs", "reference": "G4592", "description": "Strong''s entry for ''sign'' in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 30, 30, 'kjv', 'the Son of man be to this generation', 58, 94, 'question', 'Response to the Sign', 'What does it mean for our generation to recognize the Son of Man as a sign? In light of this verse, how should we respond to the message of Christ today? Are there areas in our lives where we seek signs or proofs instead of simply believing and acting upon His word?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 31, 31, 'kjv', 'The queen of the south shall rise up in the judgment with the men of this generation, and condemn them: for she came from the utmost parts of the earth to hear the wisdom of Solomon; and, behold, a greater than Solomon is here.', 0, 227, 'connection', 'Wisdom of Solomon', 'This reference to the queen of the south, likely the Queen of Sheba, can be cross-referenced with 1 Kings 10:1-2, where she travels to test Solomon''s wisdom. This connection highlights the value of seeking wisdom and contrasts the queen''s humble pursuit with the indifference of Jesus'' contemporaries. Additionally, Matthew 12:42 reinforces this theme by also referencing her in connection with the wisdom of Christ, emphasizing that Jesus is the fulfillment of that wisdom.', 'link', '[{"type": "crossReference", "reference": "1 Kings 10:1-2", "description": "The account of the Queen of Sheba visiting Solomon."}, {"type": "crossReference", "reference": "Matthew 12:42", "description": "Jesus refers to the Queen of Sheba in relation to His wisdom."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 31, 31, 'kjv', 'a greater than Solomon is here.', 196, 227, 'theology', 'Christ''s Supremacy', 'This declaration that Jesus is greater than Solomon underscores the doctrine of Christ''s supremacy in wisdom and authority. Many scholars believe it reflects the belief that Christ embodies divine wisdom, surpassing even the greatest figures of the Old Testament. This assertion is foundational for understanding the nature of Jesus'' mission and His role as the ultimate revelation of God to humanity, as seen in Colossians 2:3, which states that in Christ are hidden all the treasures of wisdom and knowledge.', 'sparkles', '[{"type": "crossReference", "reference": "Colossians 2:3", "description": "Christ as the embodiment of all wisdom and knowledge."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 31, 31, 'kjv', 'The queen of the south shall rise up in the judgment', 0, 52, 'question', 'Judgment Reflection', 'What does it mean for the queen of the south to ''rise up in judgment''? How does this image challenge our understanding of accountability before God? This prompts reflection on the seriousness of our response to divine revelation and the importance of seeking wisdom in our own lives, just as she did.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 32, 32, 'kjv', 'The men of Nineve shall rise up in the judgment with this generation, and shall condemn it: for they repented at the preaching of Jonas; and, behold, a greater than Jonas is here.', 0, 179, 'connection', 'Nineveh''s Repentance', 'This verse references the repentance of the Ninevites in Jonah 3:5, where they turned from their evil ways at Jonah''s preaching. This connection highlights the contrast between their genuine response to God''s message and the unbelief shown by Jesus'' contemporaries, emphasizing the importance of repentance before God. Additionally, Matthew 12:41 reiterates this comparison, illustrating the accountability of this generation in light of greater revelation.', 'link', '[{"type": "crossReference", "reference": "Jonah 3:5", "description": "Nineveh''s response to Jonah''s preaching"}, {"type": "crossReference", "reference": "Matthew 12:41", "description": "Jesus cites Nineveh''s repentance as a judgment against the current generation"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(42, 11, 32, 32, 'kjv', 'for they repented at the preaching of Jonas; and, behold, a greater than Jonas is here.', 92, 179, 'theology', 'Greater Revelation', 'This passage suggests that Jesus represents a greater authority and message than that of Jonah. Many scholars believe that the emphasis on Jesus being ''greater than Jonah'' highlights the escalation of God''s revelation and the call for repentance, underscoring the seriousness of rejecting Christ''s message. This aligns with the doctrine of progressive revelation, where God reveals Himself more fully over time.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(42, 11, 32, 32, 'kjv', 'behold, a greater than Jonas is here.', 142, 179, 'greek', 'The Greater One', 'Ἰωνᾶς (Iōnas, G2495) - Jonas, the prophet who preached to Nineveh. This reference not only identifies Jonah but also signifies the role of prophets in calling people to repentance. The use of ''greater'' here (μείζων, meizōn, G3187) indicates not just superiority but also the fulfillment of prophetic expectation in Jesus, emphasizing His divine authority and mission.', 'textformat.abc', '[{"type": "strongs", "reference": "G2495", "description": "Greek word for Jonah"}, {"type": "strongs", "reference": "G3187", "description": "Greek word for greater"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');