INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(43, 1, 42, 42, 'kjv', 'And he brought him to Jesus.', 0, 28, 'question', 'Importance of Introduction', 'What does it mean to bring others to Jesus in our daily lives, as Andrew did with Simon? Reflecting on this verse, how can we actively share our faith and introduce others to Christ, recognizing the transformative power of such encounters?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 43, 43, 'kjv', 'Jesus would go forth into Galilee', 18, 51, 'connection', 'Galilee''s Significance', 'Galilee is significant as it is often seen as a place of revelation and ministry for Jesus. Matthew 4:12-16 references Galilee as fulfilling Isaiah''s prophecy about light shining in darkness, highlighting its role in Jesus'' public ministry. This connection underscores how Jesus actively sought out places and people to reveal His mission.', 'link', '[{"type": "crossReference", "reference": "Matthew 4:12-16", "description": "Prophecy fulfillment regarding Galilee"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 43, 43, 'kjv', 'findeth Philip', 57, 71, 'greek', 'Finding in Greek', 'εὑρίσκω (heuriskō, G2147) - to find, discover. This term indicates a purposeful search, reflecting Jesus'' active role in calling His disciples. It emphasizes the intentionality of Jesus in engaging with individuals to follow Him, showcasing His desire for relationship.', 'textformat.abc', '[{"type": "strongs", "reference": "G2147", "description": "Greek term for ''find''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 43, 43, 'kjv', 'Follow me', 93, 102, 'theology', 'Call to Discipleship', 'This phrase, ''Follow me,'' encapsulates the core of Jesus'' call to discipleship, representing an invitation to a transformative relationship. Many scholars believe it signifies not just physical following but also a deeper commitment to live according to His teachings and example. It challenges believers to consider their own response to Jesus'' call.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Insights on the nature of discipleship"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 43, 43, 'kjv', 'The day following', 0, 17, 'question', 'Timing of Jesus'' Actions', 'What does the phrase ''the day following'' reveal about the immediacy of Jesus'' ministry? How does this prompt us to reflect on the urgency of responding to His call in our own lives? Consider the implications of timing in your own journey of faith.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 44, 44, 'kjv', 'Now Philip was of Bethsaida, the city of Andrew and Peter.', 0, 58, 'question', 'Reflecting on Community', 'How does knowing that Philip came from the same city as Andrew and Peter shape your understanding of their discipleship? In what ways might our own communities influence our faith journeys and the calling we respond to in Christ?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 44, 44, 'kjv', 'Now Philip was of Bethsaida, the city of Andrew and Peter.', 0, 58, 'greek', 'Understanding ''Bethsaida''', 'Βηθσαϊδά (Bethsaida, G966) - house of fishing. The name suggests a place associated with fishing, reflecting the occupation of many of its inhabitants, including Peter and Andrew. This background is significant as it contextualizes the calling of these fishermen to become ''fishers of men'' (Matthew 4:19).', 'textformat.abc', '[{"type": "strongs", "reference": "G966", "description": "Strong''s number for ''Bethsaida.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 44, 44, 'kjv', 'Bethsaida, the city of Andrew and Peter.', 18, 58, 'connection', 'Location Significance', 'Bethsaida is significant as it serves as a geographical reference point for the early ministry of Jesus and the calling of His disciples. This city is mentioned in other passages, such as Matthew 11:21, where Jesus rebukes it for its lack of repentance despite witnessing His miracles. The connection highlights the importance of location in the narrative of Jesus'' ministry.', 'link', '[{"type": "crossReference", "reference": "Matthew 11:21", "description": "Jesus'' rebuke of Bethsaida for its unbelief despite miracles."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 44, 44, 'kjv', 'Philip was of Bethsaida', 4, 27, 'theology', 'Role of Discipleship', 'This verse demonstrates the calling of ordinary individuals to follow Jesus, emphasizing that discipleship can arise from common backgrounds. Many scholars believe this reflects the broader theological theme of God choosing the humble and unassuming to accomplish His purposes, as seen throughout Scripture.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 44, 44, 'kjv', 'of Andrew and Peter.', 38, 58, 'greek', 'Andrew and Peter''s Names', 'Ἀνδρέας (Andreas, G406) - Andrew, meaning ''manly'' or ''brave''. Πέτρος (Petros, G4074) - Peter, meaning ''rock''. Their names indicate characteristics that are significant in the formation of the early church, where Peter''s leadership and Andrew''s role as a bringer of others to Christ are highlighted.', 'textformat.abc', '[{"type": "strongs", "reference": "G406", "description": "Strong''s number for Andrew."}, {"type": "strongs", "reference": "G4074", "description": "Strong''s number for Peter."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 44, 44, 'kjv', 'Philip was of Bethsaida', 4, 27, 'question', 'Identity in Christ', 'How does knowing where Philip comes from shape your understanding of who God calls to follow Him? What does this suggest about the diversity of backgrounds among Jesus'' disciples?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 45, 45, 'kjv', 'Jesus of Nazareth, the son of Joseph.', 120, 157, 'theology', 'Incarnation and Identity', 'This phrase reveals the dual nature of Christ as fully human and fully divine. The reference to Jesus as ''the son of Joseph'' underscores His earthly lineage, affirming the incarnation. Many scholars believe this aspect of His identity is crucial for understanding the nature of God''s revelation through Jesus, as he relates to humanity while being the promised Messiah.', 'person.2.fill', '[{"type": "commentary", "reference": "Gospel of John, John 1:45-46 Commentary", "description": "Analysis of Jesus'' identity and significance."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 45, 45, 'kjv', 'Jesus of Nazareth, the son of Joseph.', 120, 157, 'greek', 'Jesus'' Identity', 'Ἰησοῦς (Iēsous, G2424) - Jesus, the Greek form of the Hebrew name Yeshua, meaning ''Yahweh saves''. This highlights His role as the Savior and connects His earthly lineage through Joseph, affirming His humanity while also being divine. This duality is central to Christian theology.', 'textformat.abc', '[{"type": "strongs", "reference": "G2424", "description": "Strong''s entry for Jesus."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 45, 45, 'kjv', 'We have found him', 46, 63, 'question', 'Seeking the Messiah', 'What does it mean to truly ''find'' Jesus in our lives today? How does our understanding of Him as the fulfillment of the Law and the Prophets shape our faith journey and daily decisions?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 46, 46, 'kjv', 'Can there any good thing come out of Nazareth?', 29, 75, 'connection', 'Nazareth''s Perception', 'Nathanael''s skepticism about Nazareth reflects a broader disdain for the region, similar to how the prophet Isaiah speaks of Nazareth''s spiritual state in Isaiah 9:1-2. This connection emphasizes that Jesus'' humble origins do not detract from His identity as the Messiah. The unexpected nature of the Messiah''s coming from such a place challenges preconceived notions of divinity and authority.', 'link', '[{"type": "crossReference", "reference": "Isaiah 9:1-2", "description": "Isaiah prophesies about a light coming from a place of darkness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 46, 46, 'kjv', 'Philip saith unto him, Come and see.', 76, 112, 'theology', 'Invitation to Experience', 'This phrase encapsulates the essence of evangelism in Christianity: inviting others to encounter Christ personally. Many scholars believe this reflects the call to discipleship, emphasizing that faith is not merely intellectual but experiential. The invitation ''Come and see'' suggests that true understanding of Jesus comes through personal engagement and relationship rather than mere hearsay.', 'sparkles', '[{"type": "commentary", "reference": "The Gospel of John by F. F. Bruce", "description": "Bruce discusses the relational aspect of faith in John''s Gospel."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 46, 46, 'kjv', 'Can there any good thing come out of Nazareth?', 29, 75, 'question', 'Challenging Assumptions', 'What does Nathanael''s question reveal about our own biases toward people or places? How can we learn to see beyond societal expectations to recognize the potential for good in unexpected places, particularly in our spiritual lives?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 46, 46, 'kjv', 'Philip saith unto him, Come and see.', 76, 112, 'greek', 'Invitation in Greek', 'δεῦτε καὶ ἴδετε (deute kai idete, G2064 & G1492) - ''Come and see''. This phrase illustrates the active nature of faith and discovery in Greek culture, emphasizing a physical movement towards understanding and belief.', 'textformat.abc', '[{"type": "strongs", "reference": "G2064", "description": "Strong''s number for ''come''."}, {"type": "strongs", "reference": "G1492", "description": "Strong''s number for ''see''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 47, 47, 'kjv', 'Behold an Israelite indeed, in whom is no guile!', 53, 101, 'connection', 'True Israelite Theme', 'This phrase echoes Psalm 32:2, which states, ''Blessed is the man to whom the Lord does not impute iniquity, and in whose spirit there is no deceit.'' The connection highlights the importance of integrity and sincerity in the covenant community. Jesus'' recognition of Nathanael as an ''Israelite indeed'' suggests that true identity in God''s eyes is marked by honesty and purity of heart.', 'link', '[{"type": "crossReference", "reference": "Psalm 32:2", "description": "Connection to the idea of integrity and honesty."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 47, 47, 'kjv', 'Behold an Israelite indeed, in whom is no guile!', 53, 101, 'greek', 'Meaning of ''guile''', 'δόλος (dolos, G1388) - deceit, guile. This term emphasizes the absence of deception in Nathanael''s character. In the context of Jesus'' declaration, it suggests that Nathanael embodies a true Israelite spirit, reflecting the call for honesty and authenticity among God''s people.', 'textformat.abc', '[{"type": "strongs", "reference": "G1388", "description": "Strong''s number for the Greek word meaning ''deceit'' or ''guile.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 47, 47, 'kjv', 'Behold an Israelite indeed, in whom is no guile!', 53, 101, 'theology', 'Identity in Christ', 'This passage suggests that true identity is found in Christ. Nathanael''s lack of guile reflects the transformative power of encountering Jesus. Many scholars believe that this moment marks the beginning of Nathanael''s journey as a disciple, illustrating how an honest heart is receptive to divine revelation and relationship.', 'sparkles', '[{"type": "commentary", "reference": "John 1:47-49", "description": "Commentaries on how Nathanael''s identity is shaped by his encounter with Jesus."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 47, 47, 'kjv', 'Behold an Israelite indeed, in whom is no guile!', 53, 101, 'question', 'Reflecting on Integrity', 'What does it mean to be called an ''Israelite indeed'' in today''s context? How can we embody the sincerity and integrity that Jesus recognizes in Nathanael? Consider how this challenges us to live authentically in our faith.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 48, 48, 'kjv', 'when thou wast under the fig tree', 116, 149, 'connection', 'Fig Tree Symbolism', 'The fig tree often symbolizes peace and security in Jewish tradition (see Micah 4:4). This connection highlights the personal nature of Jesus'' knowledge and the comfort it brings. Jesus sees Nathanael in a place of rest, indicating a deeper understanding of his heart.', 'link', '[{"type": "crossReference", "reference": "Micah 4:4", "description": "Describes a future time of peace under one''s vine and fig tree."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 48, 48, 'kjv', 'I saw thee.', 151, 162, 'theology', 'Christ''s Omniscience', 'This statement affirms the Christian belief in Christ''s divine omniscience. Many scholars believe that Jesus'' ability to know Nathanael reflects his divine nature, reinforcing the idea that Christ is intimately aware of our lives and circumstances.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 48, 48, 'kjv', 'Whence knowest thou me?', 26, 49, 'question', 'Understanding Christ''s Knowledge', 'What does Nathanael''s question reveal about our understanding of Jesus'' omniscience? How might this shape our view of personal encounters with Christ, knowing He sees us even before we approach Him?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 48, 48, 'kjv', 'I saw thee.', 151, 162, 'theology', 'Christ''s Divine Awareness', 'This statement suggests the theological concept of Christ''s omnipresence and omniscience. Many scholars believe that Jesus'' awareness of Nathanael under the fig tree illustrates His divine nature, affirming that He knows each person intimately and personally.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 48, 48, 'kjv', 'Before that Philip called thee', 84, 114, 'connection', 'Preemptive Knowledge of God', 'This phrase connects well with Psalm 139:1-4, which speaks of God''s knowledge of our thoughts and actions even before we speak. This connection emphasizes God''s proactive engagement in our lives, affirming that He knows us deeply before we even seek Him.', 'link', '[{"type": "crossReference", "reference": "Psalm 139:1-4", "description": "God''s intimate knowledge of humanity"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 48, 48, 'kjv', 'when thou wast under the fig tree', 116, 149, 'greek', 'Fig Tree Symbolism', 'συκῆ (sykē, G4800) - fig tree, a symbol of peace and prosperity in Jewish culture. The mention of the fig tree carries connotations of spiritual insight and personal reflection, suggesting a place of contemplation for Nathanael.', 'textformat.abc', '[{"type": "strongs", "reference": "G4800", "description": "Fig tree in Greek"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 49, 49, 'kjv', 'thou art the Son of God;', 46, 70, 'greek', 'Son of God in Greek', 'υἱὸς τοῦ θεοῦ (huios tou Theou, G5207) - son of God. This phrase signifies a unique relationship between Jesus and God, emphasizing His divine nature and authority. In the New Testament context, it often denotes the Messiahship of Jesus, which is central to Christian faith.', 'textformat.abc', '[{"type": "strongs", "reference": "G5207", "description": "Strong''s number for ''Son'' indicating a significant title."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 49, 49, 'kjv', 'thou art the King of Israel.', 71, 99, 'theology', 'Christ as King', 'This acknowledgment of Jesus as ''King of Israel'' reflects the fulfillment of Old Testament prophecies regarding the Messiah. Many scholars believe this title emphasizes Jesus'' role in establishing God''s kingdom on earth, aligning with prophetic texts like Isaiah 9:6-7. It reinforces the belief that Jesus'' kingship is both spiritual and eschatological.', 'sparkles', '[{"type": "crossReference", "reference": "Isaiah 9:6-7", "description": "Prophecy about the coming Messiah as a ruler."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 49, 49, 'kjv', 'Rabbi, thou art the Son of God;', 39, 70, 'connection', 'Recognition of Authority', 'Nathanael''s declaration connects to Matthew 16:16, where Peter declares Jesus as the Messiah. Both instances highlight the recognition of Jesus'' divine authority and identity. This is significant as it sets the stage for understanding the nature of Christ throughout the Gospels.', 'link', '[{"type": "crossReference", "reference": "Matthew 16:16", "description": "Peter''s confession of Christ''s identity"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 49, 49, 'kjv', 'thou art the King of Israel.', 71, 99, 'theology', 'Christ''s Kingship', 'This passage underscores the belief in Jesus as the prophesied King from the line of David, fulfilling Old Testament prophecy (e.g., Isaiah 9:6-7). Many scholars believe this recognition by Nathanael illustrates the early acknowledgment of Jesus'' messianic role, which is central to Christian theology.', 'sparkles', '[{"type": "commentary", "reference": "Isaiah 9:6-7", "description": "Prophecy of the coming King"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 49, 49, 'kjv', 'thou art the Son of God;', 46, 70, 'greek', 'Understanding ''Son of God''', 'υἱὸς τοῦ θεοῦ (huios tou theou, G5207) - son of God. This phrase emphasizes Jesus'' unique relationship with God the Father, depicting Him as divine and part of the Holy Trinity. This term carries significant theological implications about Jesus'' nature and mission.', 'textformat.abc', '[{"type": "strongs", "reference": "G5207", "description": "Strong''s number for ''son''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 49, 49, 'kjv', 'Rabbi, thou art the Son of God;', 39, 70, 'question', 'Personal Reflection on Jesus'' Identity', 'What does it mean for you personally to recognize Jesus as ''the Son of God''? How does this affirmation shape your understanding of His authority in your life?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 50, 50, 'kjv', 'thou shalt see greater things than these.', 107, 148, 'question', 'Expectations of Faith', 'What does it mean to expect ''greater things'' from your relationship with Christ? How can you position yourself to witness more of His work in your life and community?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 50, 50, 'kjv', 'I saw thee under the fig tree', 60, 89, 'greek', 'Seeing and Knowledge', 'είδω (eidō, G1492) - to see, perceive, know. This verb underscores the personal nature of Jesus'' knowledge of Nathanael. It reflects a deeper understanding beyond mere physical sight, emphasizing the intimate knowledge Jesus has of individuals.', 'textformat.abc', '[{"type": "strongs", "reference": "G1492", "description": "Strong''s number associated with the Greek word for seeing."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 50, 50, 'kjv', 'Because I said unto thee, I saw thee under the fig tree, believest thou?', 34, 106, 'connection', 'Fig Tree Significance', 'The reference to the fig tree may connect to Micah 4:4, where sitting under one''s vine and fig tree symbolizes peace and security. This connection highlights the deeper layers of faith and revelation in Jesus'' statement. Nathanael''s recognition of Jesus'' insight indicates a transformative moment, suggesting that personal encounters with Christ bring profound understanding.', 'link', '[{"type": "crossReference", "reference": "Micah 4:4", "description": "Symbolizes peace and security in ancient Israel."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 50, 50, 'kjv', 'thou shalt see greater things than these.', 107, 148, 'theology', 'Promise of Revelation', 'This phrase suggests that faith in Christ opens the door to greater spiritual truths and revelations. Many scholars believe this reflects the Christian teaching on the progressive nature of revelation, where initial belief leads to deeper understanding and experiences of God''s glory, particularly through Christ''s ministry and ultimately in the eschaton.', 'sparkles', '[{"type": "commentary", "reference": "The Gospel of John by F.F. Bruce", "description": "Discusses the theme of revelation in John''s Gospel."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 1, 50, 50, 'kjv', 'I saw thee under the fig tree', 60, 89, 'greek', 'Seeing and Knowledge', 'εἶδον (eidon, G1492) - I saw, perceived, understood. This term emphasizes not just physical sight but also deeper awareness and insight. In this context, it highlights Jesus'' divine knowledge of Nathanael, reinforcing His identity as the omniscient Son of God.', 'textformat.abc', '[{"type": "strongs", "reference": "G1492", "description": "Denotes perception and understanding, not just sight."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 50, 50, 'kjv', 'thou shalt see greater things than these.', 107, 148, 'question', 'Anticipating Greater Revelations', 'What does it mean for your faith to anticipate greater things from God? In light of this promise, how can you remain open to the unfolding of God''s work in your life beyond initial beliefs?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 51, 51, 'kjv', 'Verily, verily, I say unto you', 23, 53, 'theology', 'Importance of ''Verily, verily''', 'The phrase ''Verily, verily'' (or ''Truly, truly'') emphasizes the certainty and authority of Jesus'' words. This repetition signifies a profound truth and is used frequently in the Gospel of John to underscore pivotal teachings. Many scholars believe this highlights the divine authority of Christ''s message, indicating that what follows is of utmost importance for understanding His nature and mission.', 'person.2.fill', '[{"type": "commentary", "reference": "John 3:3", "description": "In this verse, Jesus uses the same phrase to introduce a fundamental truth about being born again."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 51, 51, 'kjv', 'heaven open', 78, 89, 'connection', 'Heaven Opened in Scripture', 'The concept of ''heaven open'' can be seen in other significant moments in Scripture, such as in Genesis 28:12, where Jacob dreams of a ladder with angels ascending and descending, symbolizing God''s connection to humanity. This connection stresses the divine revelation through Christ, as He fulfills the role of mediator between heaven and earth.', 'link', '[{"type": "crossReference", "reference": "Genesis 28:12", "description": "Jacob''s dream emphasizes the connection between God and humanity."}, {"type": "crossReference", "reference": "Matthew 3:16", "description": "At Jesus'' baptism, the heavens open, affirming His divine identity."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 51, 51, 'kjv', 'angels of God ascending and descending', 99, 137, 'greek', 'Angels in Greek', 'ἄγγελοι (angeloi, G32) - angels, messengers. This term refers to divine beings who serve as God''s messengers. The use of ''angels'' here reflects the active role of heavenly beings in God''s plan and their connection to Jesus as the ''Son of man,'' illustrating His divine authority and the heavenly realities He brings to earth.', 'textformat.abc', '[{"type": "strongs", "reference": "G32", "description": "Strong''s number for ''angels'' in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 1, 51, 51, 'kjv', 'upon the Son of man', 138, 157, 'question', 'Understanding the Son of Man', 'What does it mean for angels to ascend and descend ''upon the Son of man''? How does this relationship illustrate the dual nature of Christ as both divine and human? Reflecting on this can deepen our understanding of Jesus'' role as mediator and His connection to both heaven and humanity.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 2, 1, 1, 'kjv', 'And the third day there was a marriage in Cana of Galilee; and the mother of Jesus was there:', 0, 93, 'connection', 'Marriage as Covenant', 'The mention of a marriage in Cana echoes the significance of marriage throughout Scripture, notably in Genesis 2:24, which states, ''Therefore a man shall leave his father and mother and be joined to his wife, and they shall become one flesh.'' This connection highlights the importance of marriage as a divine institution and sets the stage for Jesus'' first miracle, which signifies His blessing and presence in such sacred commitments.', 'link', '[{"type": "crossReference", "reference": "Genesis 2:24", "description": "Foundation of marriage in the creation narrative."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 2, 1, 1, 'kjv', 'And the third day there was a marriage in Cana of Galilee; and the mother of Jesus was there:', 0, 93, 'greek', 'Cana (Κανά)', 'Κανά (Kaná, G2580) - a place name, specifically referring to the village in Galilee where Jesus performed His first miracle. Understanding the geographical context of Cana enriches the narrative, as it signifies the beginning of Jesus'' public ministry and His active involvement in the everyday lives of people, including social celebrations.', 'textformat.abc', '[{"type": "strongs", "reference": "G2580", "description": "Strong''s number for the location of the miracle."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 2, 1, 1, 'kjv', 'and the mother of Jesus was there:', 59, 93, 'theology', 'Role of Mary', 'Mary''s presence at the wedding serves as a theological anchor for understanding her role in the life of Jesus. Many scholars believe this highlights her as a crucial intercessor, as she identifies the problem of the lack of wine, leading to Jesus'' first miraculous act. This suggests that Mary exemplifies the awareness and responsiveness to human needs, which is a key aspect of Christian discipleship.', 'figure.mind.and.body', '[{"type": "commentary", "reference": "The Gospel of John by D.A. Carson", "description": "Insight into the role of Mary in Jesus'' ministry."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 2, 1, 1, 'kjv', 'And the third day there was a marriage in Cana of Galilee; and the mother of Jesus was there:', 0, 93, 'question', 'Understanding Jesus'' Timing', 'What significance does ''the third day'' hold in this context? Reflecting on the timing can lead to deeper insights about God''s timing in our lives and how Jesus often acts at pivotal moments. How can we be more attuned to His presence and action during our own significant events?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 2, 2, 2, 'kjv', 'And both Jesus was called, and his disciples, to the marriage.', 0, 62, 'connection', 'Invitation to Community', 'The phrase ''both Jesus was called, and his disciples'' highlights the communal aspect of Jesus'' ministry. This can be connected to Matthew 18:20, where Jesus states, ''For where two or three are gathered together in my name, there am I in the midst of them.'' Here, the presence of Jesus among His followers in social settings reinforces the importance of community in faith.', 'link', '[{"type": "crossReference", "reference": "Matthew 18:20", "description": "Jesus emphasizes the value of gathering in His name."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 2, 2, 2, 'kjv', 'Jesus was called', 9, 25, 'greek', 'Called - A Divine Invitation', 'καλέω (kaleo, G2564) - to call, to invite. This verb is significant as it indicates an invitation, not just a summons. It reflects how Jesus is approachable and desires to be part of human experiences, like celebrations, which foreshadows His ministry of inclusion and grace.', 'textformat.abc', '[{"type": "strongs", "reference": "G2564", "description": "Strong''s number for the Greek term translated as ''called.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');