INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(43, 13, 13, 13, 'kjv', 'Ye call me Master and Lord: and ye say well; for so I am.', 0, 57, 'theology', 'Christ''s Authority in Service', 'This passage suggests that recognizing Jesus as ''Master'' entails a responsibility to serve others, reflecting His example. Many scholars believe that true leadership in the Christian context is rooted in humility and service, as demonstrated by Christ washing the disciples'' feet. This challenges conventional views of power and authority.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 13, 13, 'kjv', 'Ye call me Master and Lord: and ye say well; for so I am.', 0, 57, 'question', 'Reflection on Authority', 'How does acknowledging Jesus as ''Master and Lord'' influence your daily decisions and interactions with others? In what ways can you embody His example of servant leadership in your own life?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 14, 14, 'kjv', 'If I then, your Lord and Master, have washed your feet; ye also ought to wash one another''s feet.', 0, 97, 'connection', 'Service in Humility', 'This call to wash one another''s feet resonates with Galatians 5:13, where Paul encourages believers to serve one another in love. Both passages emphasize the importance of humility and service as central to the Christian life. By following Christ''s example, we fulfill the law of love, showcasing that true leadership is demonstrated through acts of service.', 'link', '[{"type": "crossReference", "reference": "Galatians 5:13", "description": "Encouragement to serve one another in love."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 14, 14, 'kjv', 'If I then, your Lord and Master, have washed your feet; ye also ought to wash one another''s feet.', 0, 97, 'greek', 'Key Term: ''Master''', 'κύριος (kyrios, G2962) - lord, master, owner. This term highlights Jesus'' authority and position as Lord. By using this term, Jesus emphasizes that true authority includes humility and service, challenging conventional notions of leadership.', 'textformat.abc', '[{"type": "strongs", "reference": "G2962", "description": "Strong''s number for ''\u03ba\u03cd\u03c1\u03b9\u03bf\u03c2''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 14, 14, 'kjv', 'ye also ought to wash one another''s feet.', 56, 97, 'theology', 'Christian Community and Service', 'This passage suggests that the act of washing feet symbolizes the broader Christian principle of mutual service and humility. Many scholars believe that this reflects the nature of the Christian community, where believers are called to embody Christ''s love through humble service to each other, fostering unity and compassion.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 14, 14, 'kjv', 'ye also ought to wash one another''s feet.', 56, 97, 'question', 'Application of Service', 'In what practical ways can you demonstrate humility and service to those around you, as Jesus did? Consider specific actions you can take this week to embody this teaching in your relationships.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 15, 15, 'kjv', 'For I have given you an example,', 0, 32, 'theology', 'Servanthood as Example', 'This passage suggests that Jesus'' act of washing the disciples'' feet is a profound demonstration of servanthood. Many scholars believe this exemplifies the call for Christians to emulate Christ''s humility and love in their interactions with others, reinforcing the idea that true leadership in the Kingdom of God is marked by service rather than authority.', 'person.2.fill', '[{"type": "commentary", "reference": "John 13:14-15", "description": "Commentaries often highlight the theme of servanthood in this passage."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 15, 15, 'kjv', 'that ye should do as I have done to you.', 33, 73, 'connection', 'Call to Action', 'This phrase connects with Matthew 20:26-28, where Jesus teaches that greatness in His kingdom comes through serving others. This connection matters because it underscores the radical nature of Jesus'' teachings on leadership and discipleship, inviting believers to reflect on their own practices of service.', 'link', '[{"type": "crossReference", "reference": "Matthew 20:26-28", "description": "Jesus emphasizes servanthood as the hallmark of true leadership."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 15, 15, 'kjv', 'I have given you an example,', 4, 32, 'greek', 'Greek Insight', 'παραδείγμα (paradeigma, G3857) - example, pattern, model. This term indicates that Jesus not only teaches but also embodies the principles He instructs. It implies that His actions serve as a model for His followers to imitate in their daily lives.', 'textformat.abc', '[{"type": "strongs", "reference": "G3857", "description": "Strong''s number for ''example'' which enhances understanding of the term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 15, 15, 'kjv', 'that ye should do as I have done to you.', 33, 73, 'question', 'Reflective Application', 'In what ways can you practically apply the example Jesus set in your daily interactions with others? How does this challenge or affirm your understanding of leadership and service within your community?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 16, 16, 'kjv', 'The servant is not greater than his lord;', 32, 73, 'connection', 'Servanthood in Scripture', 'This phrase echoes the teaching found in Matthew 10:24-25 where Jesus explains that a disciple is not above his teacher. This connection highlights the principle of humility and the importance of recognizing one''s position in relation to Christ. Understanding our role as servants can reshape our approach to ministry and community.', 'link', '[{"type": "crossReference", "reference": "Matthew 10:24-25", "description": "Jesus teaches about the role of disciples in relation to their teacher."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 16, 16, 'kjv', 'neither he that is sent greater than he that sent him.', 74, 128, 'theology', 'Authority and Mission', 'This passage emphasizes the theological concept of authority and representation. Many scholars believe this reflects the relationship within the Trinity, where the sent (the Son) is in complete submission to the sender (the Father). This serves as a model for believers in their obedience to Christ''s commission, affirming the importance of aligning one''s actions with divine authority.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 16, 16, 'kjv', 'Verily, verily, I say unto you,', 0, 31, 'greek', 'Emphasis in Speech', 'ἀμήν ἀμήν (amēn amēn, G281) - truly, truly. The repetition of ''verily'' serves to emphasize the truth of what follows. This phrase is used by Jesus to capture the attention of His listeners, indicating that what He is about to say is of utmost importance, signaling a shift to a profound teaching.', 'textformat.abc', '[{"type": "strongs", "reference": "G281", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 16, 16, 'kjv', 'The servant is not greater than his lord;', 32, 73, 'question', 'Understanding Our Role', 'In what ways do you recognize the authority of Christ in your life, and how does that influence your service to others? Reflecting on this can deepen understanding of humility and the call to serve as Christ served.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 17, 17, 'kjv', 'happy are ye if ye do them.', 25, 52, 'connection', 'Joy in Obedience', 'This phrase connects to James 1:22, which states, ''But be ye doers of the word, and not hearers only...'' This connection is important as it emphasizes the necessity of action in the Christian faith, illustrating that true happiness comes from living out the teachings of Christ, not merely knowing them.', 'link', '[{"type": "crossReference", "reference": "James 1:22", "description": "Highlights the importance of being active participants in faith."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 17, 17, 'kjv', 'If ye know these things, happy are ye if ye do them.', 0, 52, 'greek', 'Key Greek Terms', 'γνωρίζω (gnōrízō, G1097) - to know, recognize, understand. This term underlines that knowledge of Christ''s teachings carries a responsibility to act upon them, suggesting that understanding leads to a deeper state of blessedness when coupled with action.', 'textformat.abc', '[{"type": "strongs", "reference": "G1097", "description": "Greek word indicating knowledge and understanding."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 17, 17, 'kjv', 'happy are ye if ye do them.', 25, 52, 'theology', 'Blessedness in Action', 'Many scholars believe this statement reflects the broader biblical theme that true happiness and blessedness are found in obedience to God''s commands. This aligns with the Beatitudes in Matthew 5, which describe the characteristics of those who are blessed in the Kingdom of God.', 'sparkles', '[{"type": "commentary", "reference": "Matthew 5:3-12", "description": "The Beatitudes as a foundation of Christian blessedness."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 17, 17, 'kjv', 'If ye know these things, happy are ye if ye do them.', 0, 52, 'question', 'Understanding Action and Happiness', 'What does it mean to truly know Christ''s teachings if it does not lead to action? How can our understanding of these principles influence our daily choices and interactions with others?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 18, 18, 'kjv', 'I speak not of you all: I know whom I have chosen:', 0, 50, 'theology', 'Divine Election', 'This phrase underscores the doctrine of divine election, which suggests that God chooses individuals for specific purposes. Many scholars believe this reflects God''s sovereignty in salvation, indicating that while Jesus is aware of those who will betray Him, He still extends grace and opportunity to all. This highlights the tension between divine foreknowledge and human free will.', 'person.2.fill', '[{"type": "commentary", "reference": "John Calvin, Institutes of the Christian Religion", "description": "Calvin discusses the nature of God''s choosing in relation to grace."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 18, 18, 'kjv', 'but that the scripture may be fulfilled, He that eateth bread with me hath lifted up his heel against me.', 51, 156, 'connection', 'Scriptural Fulfillment', 'This statement connects directly to Psalm 41:9, which reads, ''Even my close friend, someone I trusted, one who shared my bread, has turned against me.'' This connection matters as it emphasizes the prophetic nature of Jesus'' ministry and the fulfillment of Old Testament scriptures, showcasing how betrayal was foretold and illustrates the depths of human treachery.', 'link', '[{"type": "crossReference", "reference": "Psalm 41:9", "description": "A direct prophecy that Jesus alludes to regarding betrayal."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 18, 18, 'kjv', 'He that eateth bread with me', 92, 120, 'greek', 'Significance of ''eateth''', 'ἔσθω (esthō, G2068) - to eat, consume. This term not only refers to the physical act of sharing a meal but also symbolizes fellowship and intimacy. In the context of this verse, it highlights the betrayal of a close companion, making the act of Judas'' treachery all the more poignant.', 'textformat.abc', '[{"type": "strongs", "reference": "G2068", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 18, 18, 'kjv', 'I know whom I have chosen:', 24, 50, 'question', 'Understanding Divine Choice', 'What does it mean for Jesus to know whom He has chosen in the context of His ministry? How does this knowledge influence our understanding of discipleship and calling today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 19, 19, 'kjv', 'Now I tell you before it come', 0, 29, 'connection', 'Foretelling Events', 'This phrase relates to Isaiah 46:10, where God claims, ''I make known the end from the beginning.'' Both verses emphasize the divine ability to predict future events, reinforcing the authority of Jesus as the Messiah. The connection highlights the prophetic nature of Christ''s ministry and the fulfillment of Scripture.', 'link', '[{"type": "crossReference", "reference": "Isaiah 46:10", "description": "God''s declaration of foreknowledge"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 19, 19, 'kjv', 'when it is come to pass', 37, 60, 'theology', 'Promise of Fulfillment', 'Many scholars believe this phrase highlights the reliability of Jesus'' words and the certainty of God''s promises. It suggests that believers can trust in the prophecies of Christ, which ultimately affirm His divine identity and mission. This assurance also invites a deeper faith in the unfolding of God''s plan.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 19, 19, 'kjv', 'ye may believe that I am he', 62, 89, 'greek', 'Identity of Christ', 'ἐγώ εἰμι (ego eimi, G1473 G1510) - I am, to be. This phrase is significant as it echoes God''s self-identification in Exodus 3:14 (''I AM WHO I AM''). Jesus employs this language to assert His divine nature and authority, inviting belief in His identity as the Messiah.', 'textformat.abc', '[{"type": "strongs", "reference": "G1473", "description": "First person pronoun used by Jesus"}, {"type": "strongs", "reference": "G1510", "description": "To be, indicating existence"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 19, 19, 'kjv', 'Now I tell you before it come', 0, 29, 'question', 'Understanding Prophecy', 'What does it mean for you personally that Jesus foretells events to strengthen your faith? How can this assurance shape your understanding of God''s control over your life and the future?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 20, 20, 'kjv', 'He that receiveth whomsoever I send receiveth me;', 32, 81, 'connection', 'Sending and Receiving', 'This phrase resonates with Matthew 10:40, where Jesus states, ''He who receives you receives me, and he who receives me receives the one who sent me.'' Both passages emphasize the authority of Jesus'' messengers and the profound connection between Jesus, His followers, and God the Father. Understanding this connection deepens our appreciation for the roles of discipleship and evangelism.', 'link', '[{"type": "crossReference", "reference": "Matthew 10:40", "description": "Jesus emphasizes the importance of receiving His messengers."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 20, 20, 'kjv', 'Verily, verily, I say unto you,', 0, 31, 'theology', 'Authority of Christ''s Words', 'The repeated phrase ''verily, verily'' (or ''truly, truly'') underscores the significance of what follows. Many scholars believe this is a marker of Christ''s divine authority, signaling that His words are not merely suggestions but absolute truth. This highlights the call for the disciples to take His teachings seriously, reflecting the weight of His message.', 'sparkles', '[{"type": "commentary", "reference": "The New International Commentary on the New Testament", "description": "Discusses the significance of ''verily, verily'' in Jesus'' teachings."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 20, 20, 'kjv', 'and he that receiveth me receiveth him that sent me.', 82, 134, 'greek', 'Key Greek Terms', 'δέχομαι (dechomai, G1209) - to receive, accept. This term emphasizes openness and acceptance, crucial for understanding the relational aspect of faith. It illustrates the action required from believers to welcome Christ and His message, indicating that such acceptance brings one into communion with God the Father.', 'textformat.abc', '[{"type": "strongs", "reference": "G1209", "description": "Strong''s number for the Greek term ''to receive.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 20, 20, 'kjv', 'He that receiveth whomsoever I send receiveth me;', 32, 81, 'question', 'Implications of Reception', 'What does it mean to truly receive those whom Christ sends? In what ways can we actively embrace and support those who carry His message today? Reflecting on this may challenge us to consider our role in the body of Christ and how we respond to His call through others.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 21, 21, 'kjv', 'he was troubled in spirit', 26, 51, 'theology', 'Jesus'' Human Emotion', 'This phrase illustrates the genuine human emotions of Jesus, highlighting the depth of His experience. Many scholars believe this moment reveals both His divine foreknowledge and His profound empathy for His disciples. It suggests that the betrayal will not only affect the relationship between Jesus and Judas but also the emotional well-being of Jesus Himself, affirming the dual nature of Christ as both fully God and fully man.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 21, 21, 'kjv', 'one of you shall betray me', 115, 141, 'connection', 'Foreshadowing Betrayal', 'This declaration from Jesus foreshadows the betrayal that is central to the Passion narrative. It connects to Zechariah 11:12-13, which speaks of a shepherd being betrayed for thirty pieces of silver. Understanding this connection enriches our comprehension of the prophetic nature of Jesus'' life and the fulfillment of Old Testament prophecy in the New Testament.', 'link', '[{"type": "crossReference", "reference": "Zechariah 11:12-13", "description": "Prophecy of betrayal and its implications."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 21, 21, 'kjv', 'Verily, verily, I say unto you', 78, 108, 'greek', 'Emphasis in Jesus'' Words', 'ἀμήν ἀμήν (amēn amēn, G281) - truly, certainly. This repeated phrase emphasizes the certainty and importance of what follows. In the context of Jesus'' announcement of betrayal, it serves to underscore the weight of this revelation, urging the disciples to take His words seriously and reflect on their implications.', 'textformat.abc', '[{"type": "strongs", "reference": "G281", "description": "Strong''s number for ''amen'', indicating truth and affirmation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 21, 21, 'kjv', 'When Jesus had thus said', 0, 24, 'question', 'What precedes this moment?', 'What do the preceding words of Jesus reveal about His mission and the nature of His ministry? How does understanding the context of His statement deepen our insight into the significance of betrayal within the scope of redemption? Reflecting on this can illuminate the interplay between divine foreknowledge and human free will.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 22, 22, 'kjv', 'Then the disciples looked one on another, doubting of whom he spake.', 0, 68, 'connection', 'Disciples'' Confusion', 'This moment reflects a similar theme found in Matthew 26:22, where the disciples express their uncertainty about who will betray Jesus. Such parallels highlight the shared human experience of doubt and fear in the face of impending betrayal. Understanding this connection enriches our comprehension of the disciples'' emotional turmoil during the Last Supper.', 'link', '[{"type": "crossReference", "reference": "Matthew 26:22", "description": "Another account of the disciples questioning who would betray Jesus."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 22, 22, 'kjv', 'Then the disciples looked one on another, doubting of whom he spake.', 0, 68, 'theology', 'Betrayal and Community', 'This passage illustrates the fragility of community among the disciples when faced with betrayal. Many scholars believe that this moment serves as a theological reflection on how betrayal can create doubt and division within a community, reminding Christians of the importance of unity and vigilance in faith.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 22, 22, 'kjv', 'Then the disciples looked one on another, doubting of whom he spake.', 0, 68, 'question', 'Reflecting on Doubt', 'What does this moment of doubt reveal about the nature of trust within your own spiritual community? How can we address uncertainty and betrayal in a way that fosters deeper connections and understanding?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 22, 22, 'kjv', 'Then the disciples looked one on another, doubting of whom he spake.', 0, 68, 'greek', 'Doubt in Greek Context', 'δοκέω (dokeō, G1380) - to seem, to think, to suppose. This term indicates a deeper level of questioning, suggesting that the disciples were not only confused but also actively engaging in speculation about Jesus'' words. This adds depth to their emotional state, emphasizing their struggle to comprehend the gravity of the situation.', 'textformat.abc', '[{"type": "strongs", "reference": "G1380", "description": "Greek term for ''to seem'' or ''to think''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 23, 23, 'kjv', 'leaning on Jesus'' bosom', 14, 37, 'greek', 'Greek Term Analysis', 'The phrase ''leaning on'' is translated from the Greek word ἄνθειν (anthen, G3456) - to recline or rest upon. This imagery conveys intimacy and trust, illustrating the close relationship between Jesus and His beloved disciple. Such physical proximity symbolizes spiritual closeness, emphasizing the depth of love and connection present in their relationship.', 'textformat.abc', '[{"type": "strongs", "reference": "G3456", "description": "Greek term for ''leaning'' or ''reclining''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 23, 23, 'kjv', 'whom Jesus loved', 60, 76, 'connection', 'Relationship Dynamics', 'This phrase resonates with John 19:26, where Jesus entrusts His mother to this beloved disciple. The repeated emphasis on Jesus'' love for this disciple underscores the theme of intimate relationships in the Gospel, inviting readers to consider how they relate to Christ. It highlights the call to embody love within the community of believers.', 'link', '[{"type": "crossReference", "reference": "John 19:26", "description": "Jesus entrusts His mother to the disciple whom He loved, showing deep relational bonds."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 23, 23, 'kjv', 'leaning on Jesus'' bosom', 14, 37, 'theology', 'Intimacy with Christ', 'This passage suggests that true discipleship involves a relational closeness with Jesus. Many scholars believe that the act of leaning against Jesus symbolizes the believer''s need for dependence on Him for spiritual nourishment and guidance. This intimacy is foundational for understanding our identity as beloved children of God.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Insights on the intimacy and dependence of disciples on Christ."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 23, 23, 'kjv', 'whom Jesus loved', 60, 76, 'question', 'Reflecting on Love', 'What does it mean for you to be considered ''loved'' by Jesus? How does this understanding of being His beloved disciple influence your relationships with others and your spiritual walk?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 24, 24, 'kjv', 'Simon Peter therefore beckoned to him,', 0, 38, 'connection', 'Peter''s Role in Inquiry', 'Peter''s action of beckoning another disciple echoes instances in which he takes initiative in the Gospels, such as in Matthew 14:28-29 when he asks to walk on water. This connection highlights Peter''s character as a seeker of understanding and truth, demonstrating a proactive approach to Jesus'' teachings.', 'link', '[{"type": "crossReference", "reference": "Matthew 14:28-29", "description": "Peter asks Jesus to walk on water, showcasing his impulsive yet faithful nature."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 24, 24, 'kjv', 'that he should ask who it should be', 39, 74, 'theology', 'Seeking Revelation', 'This moment reflects the theological principle of seeking divine revelation through inquiry. Many scholars believe that asking questions is integral to faith growth, emphasizing the importance of dialogue with God and among believers in understanding His will.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 24, 24, 'kjv', 'who it should be of whom he spake.', 58, 92, 'greek', 'Understanding ''spake''', 'ἐλάλησεν (elalesen, G2980) - spoke, said. This term emphasizes the communicative nature of Jesus'' ministry, revealing the importance of His words in the context of relationship and revelation. The act of speaking is central to both the teaching and the unfolding of the narrative.', 'textformat.abc', '[{"type": "strongs", "reference": "G2980", "description": "This Greek verb indicates direct speech or communication."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 24, 24, 'kjv', 'Simon Peter therefore beckoned to him, that he should ask', 0, 57, 'question', 'Who Do We Approach?', 'In what ways do we seek clarification from others, particularly from those we trust in matters of faith? How does Peter''s action encourage us to foster open dialogue in our spiritual communities, especially concerning challenging teachings?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 25, 25, 'kjv', 'He then lying on Jesus'' breast saith unto him, Lord, who is it?', 0, 63, 'connection', 'Intimacy with Christ', 'The act of leaning on Jesus'' breast suggests profound intimacy and trust, akin to John 1:18 where it states that Jesus is in the bosom of the Father. This connection highlights the close relationship between Jesus and His disciples, emphasizing how believers are invited into a relational knowledge of Christ.', 'link', '[{"type": "crossReference", "reference": "John 1:18", "description": "This verse establishes the closeness of Jesus'' relationship with the Father."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 25, 25, 'kjv', 'He then lying on Jesus'' breast saith unto him, Lord, who is it?', 0, 63, 'greek', 'Key Greek Term', 'ἐπὶ (epi, G1909) - upon, on, at; indicating a position of closeness and resting. This term underscores the physical and emotional closeness of the disciple to Jesus, enhancing the understanding of their relationship in this pivotal moment.', 'textformat.abc', '[{"type": "strongs", "reference": "G1909", "description": "Greek term indicating closeness or position."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 13, 25, 25, 'kjv', 'He then lying on Jesus'' breast saith unto him, Lord, who is it?', 0, 63, 'theology', 'The Nature of Revelation', 'This moment illustrates a key theological principle: God reveals Himself to those who seek Him. The act of asking ''who is it?'' reflects the disciple''s desire for understanding and truth. Many scholars believe that this reveals the nature of divine revelation—intimate and relational—where God desires to disclose Himself to those who are close to Him.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the relational aspect of divine revelation."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 13, 25, 25, 'kjv', 'He then lying on Jesus'' breast saith unto him, Lord, who is it?', 0, 63, 'question', 'Seeking Truth', 'What does the act of asking ''who is it?'' reveal about our own pursuit of truth in our relationship with Christ? How can we emulate John''s example of seeking clarity and understanding in our faith journey?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini');