INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(43, 14, 13, 13, 'kjv', 'that the Father may be glorified in the Son', 56, 99, 'greek', 'Glorification in the Trinity', 'δοξασθῇ (doxasthē, G1392) - be glorified. This term emphasizes the divine purpose of glorification tied to Jesus'' mission. The phrase illustrates the interconnectedness of the Father and the Son, where the Son''s works and answered prayers serve to reveal and magnify the Father''s glory, showcasing the unity of the Trinity.', 'textformat.abc', '[{"type": "strongs", "reference": "G1392", "description": "Strong''s number for the Greek term for glorification"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 13, 13, 'kjv', 'whatsoever ye shall ask in my name', 4, 38, 'question', 'Understanding Asking in His Name', 'What does it truly mean to ask in Jesus'' name, and how does this shape our understanding of prayer? Consider how this directive influences your approach to seeking God''s will and the importance of aligning your requests with His character and purposes.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 14, 14, 'kjv', 'If ye shall ask any thing in my name, I will do it.', 0, 51, 'connection', 'Prayer in Jesus'' Name', 'This verse connects closely with John 15:7, where Jesus states, ''If you abide in me, and my words abide in you, ask whatever you wish, and it will be done for you.'' Both verses emphasize the power and efficacy of prayer when aligned with Christ''s will, reflecting the intimate relationship believers have with Him. This relationship is foundational for understanding the nature of answered prayer.', 'link', '[{"type": "crossReference", "reference": "John 15:7", "description": "Connects the concept of asking in Jesus'' name to the necessity of abiding in Him."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 14, 14, 'kjv', 'If ye shall ask any thing in my name, I will do it.', 0, 51, 'theology', 'The Power of His Name', 'Many scholars believe that asking in Jesus'' name signifies more than a mere formula; it reflects the believer''s alignment with His character and purpose. This suggests that prayer is not about selfish desires but about seeking to fulfill God''s will. Hence, this passage invites believers to cultivate a heart that desires God''s glory through their requests.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Provides insight into the significance of asking in Jesus'' name."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 14, 14, 'kjv', 'If ye shall ask any thing in my name, I will do it.', 0, 51, 'greek', 'Meaning of ''ask''', 'αἰτέω (aiteō, G154) - to ask, request, or demand. This term conveys a sense of earnestness in seeking something from God. It implies that the act of asking is not casual but is rooted in a deep desire for communion with God, highlighting the relational aspect of prayer in the believer''s life.', 'textformat.abc', '[{"type": "strongs", "reference": "G154", "description": "The original Greek term emphasizing the earnestness of the request."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 14, 14, 'kjv', 'If ye shall ask any thing in my name, I will do it.', 0, 51, 'question', 'Understanding Asking in His Name', 'What does it mean to ask ''in my name''? How does this understanding influence your prayer life and expectations when approaching God? Consider how aligning your requests with Jesus'' character affects the nature of your petitions.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 15, 15, 'kjv', 'If ye love me, keep my commandments.', 0, 36, 'connection', 'Love and Obedience', 'This verse echoes the sentiment found in 1 John 5:3, which states, ''For this is the love of God, that we keep his commandments: and his commandments are not grievous.'' The connection highlights the idea that true love for Christ manifests in obedience to His teachings, reinforcing the belief that love and action are inseparable in the Christian faith.', 'link', '[{"type": "crossReference", "reference": "1 John 5:3", "description": "Reinforces the relationship between love and obedience."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 15, 15, 'kjv', 'If ye love me, keep my commandments.', 0, 36, 'theology', 'Obedience as Expression of Love', 'This passage suggests that obedience to Christ''s commandments is a fundamental expression of love for Him. Many scholars believe this reflects the broader Christian teaching that faith is not merely intellectual assent but is demonstrated through tangible actions that align with God''s will, emphasizing a relational faith.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 15, 15, 'kjv', 'keep my commandments.', 15, 36, 'greek', 'Commandments in Greek', 'ἐντολή (entolē, G1785) - command, order, injunction. This term indicates a directive from authority, implying that Christ''s commandments are not mere suggestions but are essential for the life of a believer, emphasizing obedience as a critical aspect of discipleship.', 'textformat.abc', '[{"type": "strongs", "reference": "G1785", "description": "Strong''s number for the Greek term for commandments."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 15, 15, 'kjv', 'If ye love me, keep my commandments.', 0, 36, 'question', 'Understanding Love''s Demands', 'What does it mean to truly love Christ in a way that compels us to keep His commandments? In what areas of life might we struggle to align our actions with His teachings, and how can we intentionally seek to bridge that gap?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 16, 16, 'kjv', 'I will pray the Father, and he shall give you another Comforter', 4, 67, 'connection', 'Prayer and the Trinity', 'This phrase emphasizes the relationship within the Trinity, where Jesus intercedes with the Father on behalf of believers. A related passage is Romans 8:26-27, which discusses the Spirit''s role in intercession. Understanding this connection highlights the cooperative work of the Father, Son, and Holy Spirit in the believer''s life.', 'link', '[{"type": "crossReference", "reference": "Romans 8:26-27", "description": "Discusses the intercession of the Spirit."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 16, 16, 'kjv', 'another Comforter', 50, 67, 'greek', 'Significance of ''Comforter''', 'παράκλητος (paraklētos, G3875) - advocate, helper, comforter. This term signifies not just a consoler, but one who empowers and advocates for believers. The promise of another Comforter indicates that the Holy Spirit will continue the work of Christ in guiding and supporting the disciples.', 'textformat.abc', '[{"type": "strongs", "reference": "G3875", "description": "Strong''s number for ''Comforter''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 16, 16, 'kjv', 'that he may abide with you for ever', 69, 104, 'theology', 'Eternal Presence of the Spirit', 'This passage suggests the doctrine of the indwelling of the Holy Spirit, a key aspect of Christian theology. Many scholars believe this indicates that believers are never alone and have constant access to divine guidance and comfort. This assurance of the Spirit''s eternal presence is foundational to spiritual growth.', 'sparkles', '[{"type": "commentary", "reference": "John Calvin''s Commentary on John", "description": "Calvin discusses the permanence of the Spirit''s presence."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 16, 16, 'kjv', 'I will pray the Father', 4, 26, 'question', 'Understanding Intercessory Prayer', 'What does it mean for Jesus to pray to the Father on our behalf? How does this shape your understanding of prayer and its power in your life? Reflecting on Christ''s intercession can encourage deeper engagement in prayer.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 17, 17, 'kjv', 'Even the Spirit of truth;', 0, 25, 'greek', 'Spirit in Greek', 'πνεῦμα (pneuma, G4151) - spirit, breath, wind. This term emphasizes the Spirit''s role as the life-giving presence of God, highlighting the dynamic and active nature of the Holy Spirit in the believer''s life, distinguishing it from mere abstract concepts of spirituality.', 'textformat.abc', '[{"type": "strongs", "reference": "G4151", "description": "Strong''s Greek number for ''spirit''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 17, 17, 'kjv', 'whom the world cannot receive, because it seeth him not, neither knoweth him:', 26, 103, 'connection', 'World vs. Believers', 'This phrase resonates with 1 Corinthians 2:14, which states, "But the natural man receiveth not the things of the Spirit of God: for they are foolishness unto him." This connection highlights the contrast between those who are spiritually discerned and those who remain blinded to divine truths, reinforcing the necessity of spiritual awakening.', 'link', '[{"type": "crossReference", "reference": "1 Corinthians 2:14", "description": "Illustrates the world''s inability to understand spiritual matters."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 17, 17, 'kjv', 'but ye know him; for he dwelleth with you, and shall be in you.', 104, 167, 'theology', 'Indwelling of the Spirit', 'This passage suggests the doctrine of the indwelling of the Holy Spirit, a foundational belief in Christian theology. Many scholars believe that this event, fully realized at Pentecost (Acts 2), signifies God''s intimate involvement in the lives of believers, offering guidance, comfort, and empowerment for living a life aligned with God''s will.', 'sparkles', '[{"type": "commentary", "reference": "Acts 2", "description": "Describes the fulfillment of the promise of the Holy Spirit."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 17, 17, 'kjv', 'for he dwelleth with you, and shall be in you.', 121, 167, 'question', 'Personal Reflection on Indwelling', 'What does it mean for the Holy Spirit to dwell within you? How does this truth influence your daily decisions and interactions with others, especially in moments of challenge or uncertainty?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 18, 18, 'kjv', 'I will not leave you comfortless: I will come to you.', 0, 53, 'connection', 'Promise of Presence', 'This promise is reminiscent of Matthew 28:20, where Jesus assures His disciples, ''I am with you always, even to the end of the age.'' Both verses emphasize the continual presence of Christ with His followers, providing comfort and reassurance amid trials. This connection underscores the theme of divine companionship throughout Scripture.', 'link', '[{"type": "crossReference", "reference": "Matthew 28:20", "description": "Jesus'' promise of His ongoing presence."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 18, 18, 'kjv', 'I will not leave you comfortless: I will come to you.', 0, 53, 'greek', 'Comfortless in Greek', 'ἀ orphanous (orphanous, G3737) - comfortless, bereft of aid. This term highlights the depth of abandonment that the disciples might feel in Jesus'' absence. By stating He will not leave them ''orphaned,'' Jesus assures them of His ongoing support through the Holy Spirit, reinforcing the intimate relationship believers have with Christ.', 'textformat.abc', '[{"type": "strongs", "reference": "G3737", "description": "Greek term for ''comfortless.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 18, 18, 'kjv', 'I will not leave you comfortless: I will come to you.', 0, 53, 'theology', 'Doctrine of the Holy Spirit', 'This passage suggests that Jesus'' promise of coming to His followers manifests through the Holy Spirit. Many scholars believe this reflects the doctrine of the Trinity, where the presence of God is with believers in various forms. The assurance of the Spirit as ''Comforter'' emphasizes the active role of God in providing comfort and guidance in the life of a believer.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discussion on the role of the Holy Spirit."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 18, 18, 'kjv', 'I will not leave you comfortless: I will come to you.', 0, 53, 'question', 'Understanding Divine Comfort', 'What does it mean for you personally to know that Jesus will not leave you comfortless? How does this assurance shape your reliance on Him in times of trouble and uncertainty?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 19, 19, 'kjv', 'Yet a little while, and the world seeth me no more;', 0, 51, 'connection', 'Temporal Separation', 'This phrase connects to Matthew 28:20, where Jesus promises, ''I am with you always, to the end of the age.'' While the world may not see Him post-resurrection, believers experience His presence. This juxtaposition highlights a key theme of the Gospel: the invisible reality of Christ''s reign in the lives of His followers.', 'link', '[{"type": "crossReference", "reference": "Matthew 28:20", "description": "Jesus'' promise of His enduring presence."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 19, 19, 'kjv', 'but ye see me: because I live, ye shall live also.', 52, 102, 'theology', 'Christ''s Resurrection and Our Life', 'This passage suggests that Christ''s resurrection is the foundation for the believer''s spiritual life. Many scholars believe that the promise of living because He lives reflects the doctrine of regeneration, where believers are given new life through faith in Christ (cf. 2 Corinthians 5:17). This underscores the transformative power of encountering the risen Christ.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 19, 19, 'kjv', 'because I live, ye shall live also.', 67, 102, 'greek', 'Living in Him', 'ζάω (zao, G2198) - to live, to breathe, to be among the living. This term emphasizes not just physical existence but spiritual vitality and communion with God. The promise of life here denotes a profound relationship with Christ, indicating that true life is found in Him.', 'textformat.abc', '[{"type": "strongs", "reference": "G2198", "description": "Strong''s number for the Greek term ''to live''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 19, 19, 'kjv', 'Yet a little while, and the world seeth me no more;', 0, 51, 'question', 'Understanding Christ''s Absence', 'What does it mean for the world to not see Christ, while believers do? How does this distinction shape your understanding of faith and perception of reality? Reflect on how spiritual sight differs from physical sight in your own life.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 20, 20, 'kjv', 'I am in my Father', 31, 48, 'connection', 'Divine Relationship', 'This phrase connects to John 10:30, where Jesus states, ''I and my Father are one.'' This relationship emphasizes the unity and divine nature of Jesus, affirming His authority and divine mission. Understanding this connection deepens our comprehension of the Trinity and the intimate relationship within God''s nature.', 'link', '[{"type": "crossReference", "reference": "John 10:30", "description": "Emphasizes the unity of Jesus and the Father."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 20, 20, 'kjv', 'ye in me, and I in you', 54, 76, 'theology', 'Indwelling Presence', 'This passage suggests a profound theological truth about the indwelling of Christ in believers. Many scholars believe this reflects the doctrine of union with Christ, where believers are not only connected to Jesus but also partake in His life. This relationship is foundational for understanding salvation and sanctification.', 'sparkles', '[{"type": "commentary", "reference": "The New International Commentary on the New Testament", "description": "Explores the implications of the believer''s union with Christ."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 20, 20, 'kjv', 'At that day', 0, 11, 'question', 'Understanding ''That Day''', 'What does ''that day'' refer to in the context of knowing Christ''s presence? Is it a future event, or does it also signify a present realization of faith? Reflect on how this knowledge shapes your daily walk with Christ and informs your understanding of spiritual intimacy.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 20, 20, 'kjv', 'ye shall know', 12, 25, 'greek', 'Knowledge in Greek', 'γινώσκω (ginōskō, G1097) - to know, understand, recognize. This term implies a deep, experiential knowledge, contrasting mere intellectual acknowledgment. It emphasizes that true knowledge of Christ involves relational intimacy, not just cognitive understanding.', 'textformat.abc', '[{"type": "strongs", "reference": "G1097", "description": "Strong''s entry for the Greek term for knowledge."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 21, 21, 'kjv', 'He that hath my commandments, and keepeth them,', 0, 47, 'connection', 'Commandments and Love', 'This phrase resonates with John 15:10, where Jesus emphasizes obedience to His commandments as a sign of love. The connection highlights that true love for Christ is demonstrated through action, not merely words. It reinforces the idea that love is tied to obedience in the Christian faith.', 'link', '[{"type": "crossReference", "reference": "John 15:10", "description": "Jesus teaches that keeping His commandments is essential to remaining in His love."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 21, 21, 'kjv', 'he it is that loveth me: and he that loveth me shall be loved of my Father,', 48, 123, 'theology', 'Reciprocal Love', 'This passage suggests a profound theological truth about the reciprocal nature of love between Christ, the Father, and believers. Many scholars believe it emphasizes that love is not one-sided; keeping Christ''s commandments leads to an experience of divine love, illustrating the intimate relationship believers have with the Trinity.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 21, 21, 'kjv', 'I will love him, and will manifest myself to him.', 128, 177, 'greek', 'Manifestation of Christ', 'εμφανίζω (emphanizō, G1718) - to manifest, reveal, make visible. This term underscores the personal and intimate nature of Christ''s relationship with those who love Him. The promise of manifestation indicates that believers will experience Christ''s presence and revelation in their lives as a direct result of their love and obedience.', 'textformat.abc', '[{"type": "strongs", "reference": "G1718", "description": "Strong''s number for the Greek word meaning ''to manifest''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 21, 21, 'kjv', 'He that hath my commandments, and keepeth them,', 0, 47, 'question', 'What Does Obedience Entail?', 'What does it mean to truly ''keep'' Christ''s commandments in our daily lives? Reflecting on this may lead to a deeper understanding of how our actions align with our faith and how we can express our love for Christ through obedience.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 22, 22, 'kjv', 'Judas saith unto him, not Iscariot, Lord, how is it that thou wilt manifest thyself unto us, and not unto the world?', 0, 116, 'connection', 'Manifestation of Christ', 'This question from Judas highlights a key theme in John''s Gospel regarding the selective revelation of Jesus. A relevant cross-reference is Matthew 11:25, where Jesus thanks the Father for hiding truths from the wise and revealing them to little children. This connection matters as it emphasizes the divine choice in revealing Himself to those who seek Him sincerely, contrasting with those who reject Him.', 'link', '[{"type": "crossReference", "reference": "Matthew 11:25", "description": "Jesus speaks of divine revelation to the humble."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 22, 22, 'kjv', 'how is it that thou wilt manifest thyself unto us, and not unto the world?', 42, 116, 'theology', 'Selective Revelation', 'This passage suggests a doctrine of selective revelation, where Jesus chooses to manifest Himself to His followers rather than to the world at large. Many scholars believe that this reflects a deeper spiritual intimacy between Christ and believers, as noted in John 10:14-15, where Jesus describes Himself as the Good Shepherd who knows His sheep. This emphasizes the relational aspect of faith.', 'sparkles', '[{"type": "crossReference", "reference": "John 10:14-15", "description": "Jesus'' relationship with His followers as the Good Shepherd."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 22, 22, 'kjv', 'how is it that thou wilt manifest thyself unto us, and not unto the world?', 42, 116, 'question', 'Understanding Divine Manifestation', 'What does it mean for Jesus to manifest Himself specifically to believers? How does this selective revelation impact our understanding of our relationship with Christ in contrast to the view of the world? Reflecting on this question may deepen one''s appreciation for the personal nature of faith and divine communication.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 22, 22, 'kjv', 'Lord, how is it that thou wilt manifest thyself unto us, and not unto the world?', 36, 116, 'greek', 'Greek Term for Manifest', 'φανερόω (phaneroō, G5319) - to make manifest, to reveal. This term is significant as it denotes the act of revealing or making known something that is hidden. In the context of this verse, it highlights the special revelation of Christ to His disciples, suggesting a deeper understanding and relationship that is not available to those outside the faith.', 'textformat.abc', '[{"type": "strongs", "reference": "G5319", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 23, 23, 'kjv', 'If a man love me, he will keep my words:', 34, 74, 'theology', 'Love and Obedience', 'This passage suggests that true love for Christ is demonstrated through obedience to His teachings. Many scholars believe this principle underscores the relational aspect of faith, where love is not merely emotional but is expressed through action. This aligns with the broader biblical theme that love for God is intertwined with following His commandments (see 1 John 5:3).', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 23, 23, 'kjv', 'my Father will love him,', 79, 103, 'connection', 'Divine Love Connection', 'This phrase connects to Romans 5:8, where it is stated that God demonstrates His love for us through Christ''s sacrifice. The linkage emphasizes that God''s love is conditional upon our relationship with Christ, reinforcing the notion that our obedience invites divine love and presence into our lives.', 'link', '[{"type": "crossReference", "reference": "Romans 5:8", "description": "God''s love demonstrated through Christ"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 23, 23, 'kjv', 'we will come unto him, and make our abode with him.', 108, 159, 'greek', 'Abode (μονή)', 'μονή (monē, G3438) - abode, dwelling place. This term conveys not just a physical presence but an intimate, ongoing relationship. It reflects the promise of Christ and the Father establishing a lasting communion with believers, which is central to the New Covenant experience.', 'textformat.abc', '[{"type": "strongs", "reference": "G3438", "description": "Greek term for ''abode''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 23, 23, 'kjv', 'If a man love me, he will keep my words:', 34, 74, 'question', 'What is True Love?', 'In what ways can you actively demonstrate your love for Christ in your daily life? Consider how obedience to His teachings might manifest in your actions and decisions.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 24, 24, 'kjv', 'He that loveth me not keepeth not my sayings:', 0, 45, 'theology', 'Love and Obedience', 'This passage suggests a deep connection between love for Christ and obedience to His teachings. Many scholars believe that genuine love for Jesus is evidenced by a commitment to uphold His words, indicating that true discipleship requires an active response to His commands. This aligns with the broader biblical theme found in 1 John 5:3, which states that love for God is demonstrated through keeping His commandments.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 24, 24, 'kjv', 'and the word which ye hear is not mine, but the Father''s which sent me.', 46, 117, 'connection', 'Divine Authority', 'This phrase echoes the concept of divine authority present throughout Scripture. It connects to John 12:49, where Jesus states, ''For I have not spoken on my own authority, but the Father who sent me has given me a commandment.'' This reinforces the idea that Jesus'' words carry the weight of God''s authority, establishing the necessity of heeding His teachings as they reflect the will of the Father.', 'link', '[{"type": "crossReference", "reference": "John 12:49", "description": "Jesus speaks of His authority coming from the Father."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 24, 24, 'kjv', 'He that loveth me not keepeth not my sayings:', 0, 45, 'question', 'Examining Our Love', 'What does this statement reveal about the nature of your relationship with Christ? In what ways can you evaluate your adherence to His teachings as a reflection of your love for Him? Consider how this might challenge or encourage you in your daily walk of faith.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 24, 24, 'kjv', 'keepeth not my sayings:', 22, 45, 'greek', 'Understanding ''Keepeth''', 'τηρεῖ (tēreī, G5083) - to keep, to observe, to guard. This term implies more than mere adherence; it involves active engagement in preserving and valuing Christ''s teachings. The use of this word emphasizes the responsibility of believers to not only hear but also to implement His words into their lives.', 'textformat.abc', '[{"type": "strongs", "reference": "G5083", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 25, 25, 'kjv', 'These things have I spoken unto you, being yet present with you.', 0, 64, 'connection', 'Jesus'' Presence and Teaching', 'This verse highlights the significance of Jesus'' physical presence during His ministry, echoing themes found in Matthew 28:20, where Jesus promises to be with His followers until the end of the age. This connection emphasizes the continuity of His teachings and presence in the life of believers, reinforcing the assurance that His words remain relevant beyond His earthly ministry.', 'link', '[{"type": "crossReference", "reference": "Matthew 28:20", "description": "Jesus promises His continuous presence with believers."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 25, 25, 'kjv', 'These things have I spoken unto you, being yet present with you.', 0, 64, 'greek', 'Key Greek Term', 'λέγω (lego, G3004) - to say, speak, tell. This term underlines the authority and intentionality behind Jesus'' words. It indicates not just communication but an impartation of truth that challenges and transforms the hearer, reflecting the importance of His teachings during His earthly ministry.', 'textformat.abc', '[{"type": "strongs", "reference": "G3004", "description": "Strong''s number for the Greek term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(43, 14, 25, 25, 'kjv', 'These things have I spoken unto you, being yet present with you.', 0, 64, 'theology', 'The Importance of Teaching', 'This passage suggests that Jesus'' teachings are foundational for the faith of His followers. The act of speaking while present indicates a relational aspect of discipleship. Many scholars believe this reflects the importance of community and personal relationship in understanding spiritual truths, which is a central theme in Christian doctrine.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(43, 14, 25, 25, 'kjv', 'These things have I spoken unto you, being yet present with you.', 0, 64, 'question', 'Reflecting on Jesus'' Words', 'What does it mean for us today that Jesus spoke these words while physically present? How does recognizing His physical presence in teaching shape our understanding of His messages and their application in our lives?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini');