INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(45, 10, 2, 2, 'kjv', 'zeal of God', 40, 51, 'greek', 'Greek Term for Zeal', 'ζηλος (zelos, G2205) - zeal, enthusiasm, fervor. This term conveys a strong emotional commitment or passion, which can be positive or negative depending on its focus. In the context of Romans 10:2, it highlights the intensity of Israel''s pursuit of God, but underscores that passion must be paired with accurate understanding.', 'textformat.abc', '[{"type": "strongs", "reference": "G2205", "description": "Strong''s number for ''zeal''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 2, 2, 'kjv', 'have', 33, 37, 'question', 'Submission to Righteousness', 'What does it mean to submit oneself to the righteousness of God in a practical sense? How can we ensure our own zeal does not overshadow our need for divine truth and alignment with God''s will?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 3, 3, 'kjv', 'ignorant of God''s righteousness', 15, 46, 'connection', 'Righteousness Defined', 'This phrase connects to Isaiah 64:6, which states that our righteousness is like filthy rags. It emphasizes the futility of human attempts to achieve righteousness apart from God. Understanding God''s righteousness is crucial for salvation and highlights the need for divine grace.', 'link', '[{"type": "crossReference", "reference": "Isaiah 64:6", "description": "Illustrates the inadequacy of human righteousness in contrast to God''s standard."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 3, 3, 'kjv', 'going about to establish their own righteousness', 52, 100, 'theology', 'Human Effort vs. Divine Grace', 'This passage suggests a theological tension between human effort and divine grace. Many scholars believe that the desire to establish one''s own righteousness stems from a misunderstanding of grace and reliance on works. This reflects the broader Christian teaching that salvation is through faith in Christ alone, not by works (Ephesians 2:8-9).', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 3, 3, 'kjv', 'have not submitted themselves unto the righteousness of God', 102, 161, 'greek', 'Submission in Greek', 'ὑποτάσσω (hypotassō, G5293) - to submit, to place under. This term indicates a voluntary act of yielding to authority. Understanding this word deepens the realization that true righteousness requires humility and submission to God''s will, rather than self-reliance.', 'textformat.abc', '[{"type": "strongs", "reference": "G5293", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 3, 3, 'kjv', 'going about to establish their own righteousness', 52, 100, 'question', 'Personal Righteousness Reflection', 'What are some ways I might be trying to establish my own righteousness instead of submitting to God''s? How does this affect my understanding of grace and my relationship with God?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 4, 4, 'kjv', 'For Christ is the end of the law for righteousness to every one that believeth.', 0, 79, 'connection', 'Fulfillment of the Law', 'This verse connects closely with Galatians 3:24-25, where Paul describes the law as a tutor leading us to Christ. By stating that Christ is the end of the law for righteousness, it emphasizes that faith in Him fulfills the law''s requirements, liberating believers from its bondage. This underscores the transition from the law to grace through faith.', 'link', '[{"type": "crossReference", "reference": "Galatians 3:24-25", "description": "Paul explains the law''s role in bringing us to Christ."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 4, 4, 'kjv', 'end of the law', 18, 32, 'greek', 'Understanding ''End''', 'τέλος (telos, G5056) - end, purpose, goal. This term indicates not just a conclusion but a fulfillment or goal. In this context, Christ embodies the ultimate purpose of the law, highlighting that the law''s intention was always to point to Him, culminating in the righteousness available through faith.', 'textformat.abc', '[{"type": "strongs", "reference": "G5056", "description": "Strong''s number for the Greek term ''telos''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 4, 4, 'kjv', 'to every one that believeth.', 51, 79, 'theology', 'Universal Offer of Salvation', 'This phrase suggests a pivotal doctrine in Christianity: salvation through faith is available to all, regardless of background or prior adherence to the law. Many scholars believe this equality before God reflects the inclusivity of the Gospel, emphasizing that belief in Christ, rather than works of the law, is the basis for righteousness.', 'person.2.fill', '[{"type": "commentary", "reference": "Romans 3:22", "description": "Paul reiterates that righteousness comes through faith for all who believe."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 4, 4, 'kjv', 'For Christ is the end of the law', 0, 32, 'question', 'What does ''end'' imply?', 'In what ways does understanding Christ as the ''end'' of the law reshape your understanding of His role in your life? Consider how this affects your view of legalism versus grace and how it informs your relationship with God.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 5, 5, 'kjv', 'the righteousness which is of the law', 21, 58, 'connection', 'Old Testament Foundations', 'This phrase connects to Leviticus 18:5, which states, ''You shall therefore keep my statutes and my rules; if a person does them, he shall live by them.'' This connection is significant as it shows the continuity of God''s requirement for righteousness from the Law of Moses to Paul''s teaching in Romans. It emphasizes that the law provides a standard, but also highlights the limitations of relying solely on the law for righteousness.', 'link', '[{"type": "crossReference", "reference": "Leviticus 18:5", "description": "Leviticus shows the foundational basis for living by the law."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 5, 5, 'kjv', 'the man which doeth those things', 65, 97, 'theology', 'Faith vs. Works', 'This passage suggests a contrast between righteousness achieved through law (works) and that of faith, which is central to Christian doctrine. Many scholars believe that Paul highlights this distinction to show that while obedience to the law is important, it cannot bring true righteousness or salvation, which is obtained through faith in Christ (cf. Romans 3:20).', 'sparkles', '[{"type": "commentary", "reference": "Romans 3:20", "description": "Romans 3:20 expands on the futility of law for salvation."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 5, 5, 'kjv', 'shall live by them', 98, 116, 'greek', 'Key Greek Term', 'ζήσεται (zēsetai, G2198) - shall live. This term implies not just physical life but spiritual vitality. In the context of the law, it evokes the idea that life is contingent upon adherence to the law, a concept that Paul later critiques as insufficient for true life in Christ.', 'textformat.abc', '[{"type": "strongs", "reference": "G2198", "description": "Strong''s number for the Greek term related to living."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 5, 5, 'kjv', 'the man which doeth those things', 65, 97, 'question', 'Understanding Righteousness', 'What does it mean to truly ''do'' the law in your life? How do we reconcile this with the New Testament teaching that righteousness comes through faith? Reflecting on this can deepen your understanding of the balance between law and grace in your faith journey.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 6, 6, 'kjv', 'Who shall ascend into heaven?', 87, 116, 'connection', 'Heavenly Ascent', 'This phrase echoes Psalm 24:3, which asks, ''Who shall ascend the hill of the Lord?'' This connection highlights the difficulty and holiness associated with approaching God. It suggests that human efforts alone cannot achieve access to divine righteousness, underscoring the necessity of faith in Christ.', 'link', '[{"type": "crossReference", "reference": "Psalm 24:3", "description": "A Psalm reflecting the holiness required to approach God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 6, 6, 'kjv', 'the righteousness which is of faith', 4, 39, 'theology', 'Faith''s Righteousness', 'This passage suggests that righteousness is not obtained through law or works but through faith in Christ. This aligns with the Reformation principle of ''sola fide,'' emphasizing that faith alone is sufficient for justification before God. Many theologians argue that this underscores the transformative power of faith in the believer''s life.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 6, 6, 'kjv', 'Say not in thine heart', 63, 85, 'greek', 'Inner Dialogue', 'καρδία (kardia, G2588) - heart, inner self, mind. This term emphasizes the inner thoughts and motivations of an individual. In this context, it indicates that true faith involves an inward belief rather than mere external expressions or queries about Christ''s work.', 'textformat.abc', '[{"type": "strongs", "reference": "G2588", "description": "Strong''s number for the Greek word ''heart.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 6, 6, 'kjv', 'Who shall', 87, 96, 'question', 'Christ''s Accessibility', 'What does it mean to rely on Christ''s work rather than attempting to bring Him down from heaven? How does this understanding of Christ''s divine accessibility through faith shape our relationship with Him?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 7, 7, 'kjv', 'Who shall descend into the deep?', 4, 36, 'connection', 'Connection to Jonah', 'This phrase recalls the story of Jonah, who descended into the depths of the sea. Jonah 2:2 states, ''Out of the belly of Sheol I cried, and you heard my voice.'' The connection emphasizes the theme of resurrection and deliverance, as both Christ and Jonah faced death and returned, highlighting God''s power to save.', 'link', '[{"type": "crossReference", "reference": "Jonah 2:2", "description": "Illustrates a similar descent and cry for salvation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 7, 7, 'kjv', 'to bring up Christ again from the dead.', 47, 86, 'theology', 'Resurrection Theology', 'This passage underlines the central Christian doctrine of the resurrection. Many scholars believe that this emphasizes Christ''s victory over death, which is foundational for the faith as noted in 1 Corinthians 15:14, where Paul states, ''And if Christ has not been raised, then our preaching is in vain.''', 'sparkles', '[{"type": "commentary", "reference": "1 Corinthians 15:14", "description": "Highlights the importance of resurrection in Christian faith."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 7, 7, 'kjv', 'Or, Who shall descend into the deep?', 0, 36, 'question', 'Understanding Depths of Despair', 'What does this ''deep'' represent in our own lives? In what ways do we find ourselves in spiritual depths, and how does the promise of resurrection empower us to seek hope and restoration?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 7, 7, 'kjv', 'to bring up Christ again from the dead.', 47, 86, 'greek', 'Greek Term for ''bring up''', 'ἀναστήσω (anastēsō, G450) - I will raise up, cause to stand up. This verb emphasizes the act of resurrection, indicating not just a return to life, but a restoration to a new state of existence, central to Paul''s message in Romans.', 'textformat.abc', '[{"type": "strongs", "reference": "G450", "description": "Indicates the significance of resurrection in Christian teaching."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 8, 8, 'kjv', 'The word is nigh thee, even in thy mouth, and in thy heart:', 19, 78, 'connection', 'Proximity of God''s Word', 'This phrase echoes Deuteronomy 30:14, where Moses emphasizes that God''s commands are near to the people, indicating accessibility. This connection highlights the belief that God''s truth is not distant or unattainable but rather close and personal, enabling a direct relationship with Him.', 'link', '[{"type": "crossReference", "reference": "Deuteronomy 30:14", "description": "Moses speaks of God''s commands being near to Israel."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 8, 8, 'kjv', 'the word of faith, which we preach;', 88, 123, 'theology', 'Nature of Faith', 'This phrase suggests that faith is not just a feeling but a declaration and belief in the truth about Jesus Christ. Many scholars believe this encapsulates the essence of the gospel message, which is to be proclaimed and confessed, reinforcing the communal aspect of faith in Christ.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 8, 8, 'kjv', 'the word of faith, which we preach;', 88, 123, 'greek', 'Key Greek Term', 'λόγος (logos, G3056) - word, reason, discourse. The term ''logos'' emphasizes the communicative nature of faith as it conveys God''s revelation and truth, underscoring how faith is articulated and shared among believers.', 'textformat.abc', '[{"type": "strongs", "reference": "G3056", "description": "Strong''s number for ''word''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 8, 8, 'kjv', 'even in thy mouth, and in thy heart:', 42, 78, 'question', 'Heart and Mouth Connection', 'What does it mean for faith to reside both in our hearts and be expressed through our mouths? How does this duality shape our understanding of authentic faith and its expression in our daily lives?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 9, 9, 'kjv', 'confess with thy mouth the Lord Jesus', 19, 56, 'connection', 'Confession in Faith', 'This phrase connects to Matthew 10:32, where Jesus states, ''Whoever acknowledges me before others, I will also acknowledge before my Father in heaven.'' Both passages emphasize the importance of verbal acknowledgment of faith as essential for salvation. This highlights the public aspect of faith and the necessity of confessing Christ openly, which is crucial in the life of a believer.', 'link', '[{"type": "crossReference", "reference": "Matthew 10:32", "description": "Jesus emphasizes the importance of acknowledging Him."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 9, 9, 'kjv', 'believe in thine heart', 68, 90, 'greek', 'Heart in Greek Thought', 'καρδία (kardia, G2588) - heart, mind, inner self. In Greek thought, the heart encompasses not just emotions but the entire inner life, including thoughts and will. This reinforces the idea that genuine belief in Christ must stem from a deep, personal conviction and commitment, not just surface-level agreement.', 'textformat.abc', '[{"type": "strongs", "reference": "G2588", "description": "Strong''s number for ''heart''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 9, 9, 'kjv', 'thou shalt be saved', 131, 150, 'theology', 'Salvation Through Faith', 'This passage encapsulates the core Christian doctrine of salvation by faith, a concept affirmed in Ephesians 2:8-9 which states that we are saved by grace through faith, not by works. Many scholars believe that this verse underscores the simplicity and accessibility of the Gospel, affirming that salvation is available to all who genuinely confess and believe.', 'sparkles', '[{"type": "crossReference", "reference": "Ephesians 2:8-9", "description": "Affirms salvation by grace through faith."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 9, 9, 'kjv', 'if thou shalt confess with thy mouth', 5, 41, 'question', 'Role of Confession', 'What does it mean for you personally to confess Christ with your mouth? In what ways can you make your faith known to others, and how does this confession impact your daily life and relationships?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 10, 10, 'kjv', 'with the heart man believeth unto righteousness', 4, 51, 'connection', 'Faith and Righteousness', 'This phrase connects to Genesis 15:6, where Abraham''s belief in God was credited as righteousness. It highlights the biblical principle that faith is foundational to being deemed righteous before God. Additionally, Hebrews 11:1 elaborates on faith as assurance and conviction, emphasizing its importance in a believer''s life.', 'link', '[{"type": "crossReference", "reference": "Genesis 15:6", "description": "Abraham''s faith credited as righteousness."}, {"type": "crossReference", "reference": "Hebrews 11:1", "description": "Definition of faith as assurance and conviction."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 10, 10, 'kjv', 'with the mouth confession is made unto salvation', 57, 105, 'theology', 'Confession and Salvation', 'This passage underscores the necessity of verbal confession in the process of salvation, aligning with the doctrine of soteriology. Many scholars believe that this reflects the belief in the power of spoken faith to affirm one''s relationship with Christ, as seen in Matthew 10:32, which states that acknowledging Christ before others is essential for salvation.', 'person.2.fill', '[{"type": "crossReference", "reference": "Matthew 10:32", "description": "Acknowledgment of Christ as a condition for salvation."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 10, 10, 'kjv', 'believeth unto righteousness', 23, 51, 'greek', 'Key Greek Term', 'δικαιοσύνη (dikaiosynē, G1343) - righteousness, justice. This term embodies the concept of being in right standing with God, which is a central theme in Pauline theology, emphasizing that righteousness is obtained through faith rather than works.', 'textformat.abc', '[{"type": "strongs", "reference": "G1343", "description": "Strong''s number for righteousness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 10, 10, 'kjv', 'For with the heart man believeth', 0, 32, 'question', 'Heart and Belief', 'What does it mean to believe with the heart, and how does this understanding influence our daily faith practices? Reflecting on this can deepen your recognition of the heart''s role in genuine belief and relationship with God.', 'heart.text.square', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 11, 11, 'kjv', 'Whosoever believeth on him shall not be ashamed.', 25, 73, 'connection', 'Cross-Reference to Isaiah', 'This phrase echoes Isaiah 28:16, which states, ''He that believeth shall not make haste.'' Both verses emphasize the assurance and steadfastness of faith in God. This connection highlights the continuity of God''s promise throughout Scripture that those who trust in Him will find security and will not be disappointed.', 'link', '[{"type": "crossReference", "reference": "Isaiah 28:16", "description": "Affirms the theme of faith and assurance in God''s promises."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 11, 11, 'kjv', 'Whosoever believeth on him shall not be ashamed.', 25, 73, 'greek', 'Key Greek Term', 'ἐπαισχύνομαι (epaischynomai, G1870) - to be ashamed, to feel shame. This term suggests a deep emotional response to failure or disgrace, contrasting with the confidence that faith in Christ provides. The use of this verb emphasizes that true belief results in assurance rather than shame.', 'textformat.abc', '[{"type": "strongs", "reference": "G1870", "description": "Indicates the emotional aspect of shame in the context of faith."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 11, 11, 'kjv', 'Whosoever believeth on him shall not be ashamed.', 25, 73, 'theology', 'Assurance of Salvation', 'This verse reflects the doctrine of assurance, which is widely accepted among Christians. Many scholars believe that faith in Christ guarantees salvation, freeing believers from shame and judgment. This assurance encourages believers to live boldly in their faith, knowing they are accepted by God.', 'person.2.fill', '[{"type": "commentary", "reference": "John Calvin''s ''Institutes of the Christian Religion''", "description": "Discusses the assurance of salvation through faith."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 11, 11, 'kjv', 'Whosoever believeth on him shall not be ashamed.', 25, 73, 'question', 'Reflection on Belief', 'What does it mean to truly believe in Christ in a way that leads to no shame? How can understanding this assurance influence your daily life and interactions with others?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 12, 12, 'kjv', 'there is no difference between the Jew and the Greek', 4, 56, 'connection', 'Unity in Christ', 'This phrase resonates with Galatians 3:28, which states that in Christ, there is neither Jew nor Greek, emphasizing the equality of all believers. This connection highlights Paul''s broader theological theme of unity among diverse groups in the body of Christ, affirming that salvation is available to everyone, regardless of ethnic or cultural background.', 'link', '[{"type": "crossReference", "reference": "Galatians 3:28", "description": "Affirms equality among believers"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 12, 12, 'kjv', 'the same Lord over all', 62, 84, 'theology', 'God''s Universal Lordship', 'This passage suggests the universal authority of Christ as Lord, a foundational Christian doctrine. Many scholars believe that this reflects the belief in God''s impartiality in salvation, aligning with the idea found in Acts 10:34-35, where Peter acknowledges that God shows no favoritism. This reinforces the message that Christ''s lordship extends equally to all who believe.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 12, 12, 'kjv', 'rich unto all that call upon him', 88, 120, 'greek', 'Rich Grace', 'πλουσίου (plousiou, G4145) - rich, abundant. This term indicates not only material wealth but also a depth of spiritual abundance. The richness of the Lord''s grace signifies that all who call upon Him receive generously, highlighting God''s willingness to bless and provide for all believers, transcending cultural and social barriers.', 'textformat.abc', '[{"type": "strongs", "reference": "G4145", "description": "Strong''s number for ''rich''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 12, 12, 'kjv', 'call upon him', 107, 120, 'question', 'Calling on His Name', 'What does it mean to truly ''call upon'' the Lord? In the context of this verse, consider how this act represents both a recognition of need and a response of faith. How does this shape your understanding of prayer and reliance on God in your daily life?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 13, 13, 'kjv', 'whosoever shall call upon the name of the Lord', 4, 50, 'connection', 'Universal Call to Salvation', 'This phrase echoes Joel 2:32, which states, ''And it shall come to pass that everyone who calls on the name of the Lord shall be saved.'' This connection highlights the continuity of God''s redemptive plan from the Old Testament to the New Testament, emphasizing that salvation is accessible to all, regardless of background or ethnicity.', 'link', '[{"type": "crossReference", "reference": "Joel 2:32", "description": "Prophetic affirmation of salvation for all who call on God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 13, 13, 'kjv', 'shall be saved', 51, 65, 'greek', 'Key Greek Term', 'σώζω (sōzō, G4982) - to save, to deliver, to make whole. This term not only refers to spiritual salvation but also encompasses physical and emotional healing, showing the comprehensive nature of salvation that Jesus offers.', 'textformat.abc', '[{"type": "strongs", "reference": "G4982", "description": "Greek term for salvation with broad implications."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 13, 13, 'kjv', 'call upon the name of the Lord', 20, 50, 'theology', 'The Importance of Calling', 'Many scholars believe that calling upon the name of the Lord signifies not just a verbal acknowledgment but an act of faith and reliance on God for salvation. This reflects a personal relationship with God, where one actively seeks His presence and support in their life.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explores the significance of calling upon God in faith."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 13, 13, 'kjv', 'whosoever shall call upon the name of the Lord', 4, 50, 'question', 'Who is Included?', 'In light of the promise that ''whosoever shall call upon the name of the Lord shall be saved,'' who do you believe is included in ''whosoever''? How does this understanding affect your view of outreach and evangelism?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 14, 14, 'kjv', 'How then shall they call on him in whom they have not believed?', 0, 63, 'theology', 'Faith and Calling', 'This passage emphasizes the relationship between faith and the act of calling on the Lord. It suggests that true calling is rooted in belief, aligning with the doctrine of justification by faith (Romans 5:1). Many scholars believe this highlights the necessity of a personal faith response before one can truly invoke God''s name for salvation.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(45, 10, 14, 14, 'kjv', 'how shall they believe in him of whom they have not heard?', 68, 126, 'connection', 'The Importance of Hearing', 'This phrase connects with Romans 10:17, which states that faith comes by hearing, and hearing by the word of God. This connection underscores the significance of the preached word in fostering belief. It highlights the necessity of evangelism and the proclamation of the Gospel as a means for individuals to encounter Christ.', 'link', '[{"type": "crossReference", "reference": "Romans 10:17", "description": "Faith arises from hearing the Gospel message."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 14, 14, 'kjv', 'how shall they hear without a preacher?', 131, 170, 'question', 'Role of Preachers', 'What does this verse reveal about the responsibility of believers to share the Gospel? It prompts reflection on how each Christian can participate in God''s mission of evangelism. Are we actively engaging in conversations that lead others to hear about Christ?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(45, 10, 14, 14, 'kjv', 'How then shall they call on him in whom they have not believed?', 0, 63, 'greek', 'Key Greek Term', 'πιστεύω (pisteuō, G4100) - to believe, to have faith. This term underscores the essential act of belief in Christ as the foundation for calling upon Him. It highlights the active response required from individuals in the context of salvation.', 'textformat.abc', '[{"type": "strongs", "reference": "G4100", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');