INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(52, 5, 15, 15, 'kjv', 'See that none render evil for evil unto any man;', 0, 48, 'greek', 'Key Greek Term', 'κακία (kakia, G2556) - malice, wickedness, depravity. This term emphasizes the moral quality of actions that believers are urged to avoid, illustrating the seriousness of ethical conduct in Christian life.', 'textformat.abc', '[{"type": "strongs", "reference": "G2556", "description": "Strong''s number for the Greek term related to evil."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 15, 15, 'kjv', 'but ever follow that which is good, both among yourselves, and to all men.', 49, 123, 'question', 'Application of Goodness', 'What practical steps can you take to actively pursue goodness in your interactions with others, especially when faced with unkindness or evil?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 16, 16, 'kjv', 'Rejoice evermore.', 0, 17, 'connection', 'Joy in Scripture', 'The command to ''rejoice evermore'' resonates with Philippians 4:4, which states, ''Rejoice in the Lord always: and again I say, Rejoice.'' Both verses emphasize the importance of joy as a continual state of being for believers, regardless of circumstances. This connection highlights the biblical theme that joy is rooted in one''s relationship with God rather than external conditions.', 'link', '[{"type": "crossReference", "reference": "Philippians 4:4", "description": "Another exhortation for continuous joy."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 16, 16, 'kjv', 'Rejoice evermore.', 0, 17, 'greek', 'Greek Insight', 'χαίρετε (chairete, G5463) - rejoice, be glad. This term conveys a sense of joy that is both a command and a state of being. In the New Testament, it often reflects a deep, abiding joy rooted in faith and connection to God, urging believers to maintain a joyful disposition as part of their spiritual walk.', 'textformat.abc', '[{"type": "strongs", "reference": "G5463", "description": "Strong''s number for the Greek term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 16, 16, 'kjv', 'Rejoice evermore.', 0, 17, 'theology', 'Theology of Joy', 'Many scholars believe that the call to ''rejoice evermore'' reflects a theological understanding that joy is a fruit of the Spirit (Galatians 5:22). This suggests that true joy is not merely an emotional state but a spiritual gift that believers cultivate through their relationship with Christ and obedience to His teachings. Joy serves as a testimony to the transformative power of faith.', 'sparkles', '[{"type": "commentary", "reference": "Galatians 5:22", "description": "Discusses joy as a fruit of the Spirit."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 16, 16, 'kjv', 'Rejoice evermore.', 0, 17, 'question', 'Reflection on Joy', 'What does it mean to you to ''rejoice evermore'' in the midst of life''s challenges? Consider how this directive can be applied in your daily circumstances and how it reflects your trust in God''s sovereignty and goodness.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 17, 17, 'kjv', 'Pray without ceasing.', 0, 21, 'connection', 'Prayer in Scripture', 'The exhortation to ''pray without ceasing'' resonates with Luke 18:1, where Jesus teaches the importance of persistent prayer through the parable of the persistent widow. Additionally, Romans 12:12 encourages believers to be ''faithful in prayer.'' These references underscore the significance of an ongoing dialogue with God, which strengthens our faith and reliance on Him.', 'link', '[{"type": "crossReference", "reference": "Luke 18:1", "description": "Illustrates the importance of persistence in prayer."}, {"type": "crossReference", "reference": "Romans 12:12", "description": "Encourages faithfulness in prayer."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 17, 17, 'kjv', 'Pray without ceasing.', 0, 21, 'greek', 'Understanding ''Pray''', 'προσεύχομαι (proseuchomai, G4336) - to pray, to make a vow. This term emphasizes the earnestness and commitment involved in prayer, suggesting a deep, personal engagement with God rather than a mere ritual. The call to pray ''without ceasing'' implies a lifestyle of constant communication with the Divine.', 'textformat.abc', '[{"type": "strongs", "reference": "G4336", "description": "Strong''s number for the Greek word for prayer."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 17, 17, 'kjv', 'Pray without ceasing.', 0, 21, 'theology', 'The Essence of Prayer', 'Many scholars believe that ''pray without ceasing'' highlights the necessity of maintaining an ongoing relationship with God. This reflects the Christian understanding of prayer not as a duty but as a vital aspect of communion with the Holy Spirit. The continuous nature of prayer aligns with the belief that God desires to be intimately involved in every aspect of our lives.', 'person.2.fill', '[{"type": "commentary", "reference": "The New Bible Commentary", "description": "Discusses the nature of prayer in the believer''s life."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 17, 17, 'kjv', 'Pray without ceasing.', 0, 21, 'question', 'Reflecting on Prayer', 'What does it mean to you to ''pray without ceasing'' in your daily life? How can this command shape your understanding of your relationship with God? Consider the ways you can incorporate prayer into various moments of your day.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 18, 18, 'kjv', 'In every thing give thanks:', 0, 27, 'connection', 'Thankfulness in Scripture', 'This phrase connects with Philippians 4:6, which encourages believers not to be anxious but to present their requests to God with thanksgiving. Both verses emphasize the importance of maintaining a thankful attitude in all circumstances, reflecting a deep trust in God''s sovereignty and goodness. Such attitudes cultivate joy and peace in the believer''s life, aligning with God''s will.', 'link', '[{"type": "crossReference", "reference": "Philippians 4:6", "description": "Encouragement to present requests with thanksgiving"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 18, 18, 'kjv', 'for this is the will of God', 28, 55, 'theology', 'Understanding God''s Will', 'Many scholars believe that this passage reveals a fundamental aspect of God''s will for believers: to cultivate a constant attitude of gratitude. This aligns with broader biblical themes that portray thankfulness as a response to God''s grace and mercy, highlighting that recognizing God''s blessings is central to spiritual maturity and alignment with His purposes.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Insight on thankfulness reflecting God''s will"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 18, 18, 'kjv', 'In every thing give thanks:', 0, 27, 'greek', 'Key Greek Term', 'εὐχαριστία (eucharistia, G2169) - thankfulness, gratitude. This term emphasizes the act of expressing gratitude not just in good times but throughout all circumstances, suggesting a profound acknowledgment of God''s continual provision and grace. The practice of thanksgiving is integral to Christian life, reflecting a heart aligned with God''s purposes.', 'textformat.abc', '[{"type": "strongs", "reference": "G2169", "description": "Greek term for thankfulness"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 18, 18, 'kjv', 'for this is the will of God', 28, 55, 'question', 'Reflecting on God''s Will', 'What does it mean to you that giving thanks in every situation is described as God''s will? How can you apply this truth to challenging circumstances in your life, and what changes might it prompt in your perspective on daily events?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 19, 19, 'kjv', 'Quench not the Spirit.', 0, 22, 'connection', 'Spirit''s Work in Believers', 'This phrase echoes Ephesians 4:30, which warns against grieving the Holy Spirit. Both verses highlight the importance of our relationship with the Spirit and how our actions can affect His work in us. Understanding that we can either hinder or cultivate the Spirit''s presence encourages believers to remain sensitive to His leading.', 'link', '[{"type": "crossReference", "reference": "Ephesians 4:30", "description": "Warning against grieving the Holy Spirit."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 19, 19, 'kjv', 'Quench not the Spirit.', 0, 22, 'greek', 'Greek Term Insight', 'σβέννυμι (sbennymi, G4570) - to extinguish, quench. This term suggests the act of putting out a fire, which is a metaphor for hindering the passionate work of the Holy Spirit in believers'' lives. Recognizing this can lead to a deeper understanding of how we can actively nurture the Spirit''s fire rather than allowing it to diminish.', 'textformat.abc', '[{"type": "strongs", "reference": "G4570", "description": "Greek term meaning to extinguish."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 19, 19, 'kjv', 'Quench not the Spirit.', 0, 22, 'theology', 'Role of the Holy Spirit', 'This verse suggests that the Holy Spirit plays a vital role in the believer''s life, guiding, empowering, and sanctifying. Many scholars believe that failing to acknowledge or respond to the Spirit''s promptings can lead to spiritual stagnation. The call to not quench the Spirit emphasizes the need for ongoing openness and responsiveness to His work.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the significance of the Holy Spirit''s role."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 19, 19, 'kjv', 'Quench not the Spirit.', 0, 22, 'question', 'Reflecting on Openness', 'What practices in your life might be quenching the work of the Spirit? Are there areas where you feel prompted to be more open to His guidance? Reflecting on these questions can lead to a more vibrant and responsive faith as you seek to cultivate the Spirit''s presence in your daily life.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 20, 20, 'kjv', 'Despise not prophesyings.', 0, 25, 'connection', 'Prophecy in the Church', 'This exhortation resonates with 1 Corinthians 14:39, where Paul encourages believers to desire prophesying, emphasizing its value in edifying the church. Both passages highlight the importance of not dismissing prophetic messages, which are vital for spiritual growth and guidance within the community of faith.', 'link', '[{"type": "crossReference", "reference": "1 Corinthians 14:39", "description": "Paul encourages the pursuit of prophetic gifts."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 20, 20, 'kjv', 'Despise not prophesyings.', 0, 25, 'greek', 'Understanding ''Prophesyings''', 'προφητεία (propheteia, G4394) - prophecy, the speaking forth of divine revelations. This term emphasizes the prophetic role in conveying God''s messages, underscoring the necessity of receiving such words with openness and discernment.', 'textformat.abc', '[{"type": "strongs", "reference": "G4394", "description": "Strong''s number for the Greek word for prophecy."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 20, 20, 'kjv', 'Despise not prophesyings.', 0, 25, 'theology', 'Role of Prophecy', 'Many scholars believe that this verse reflects the doctrine of the ongoing relevance of spiritual gifts, particularly prophecy, in the life of the church. Prophecy serves as a means of divine communication, aiding believers in their spiritual journey and community life, rather than being seen as obsolete or irrelevant.', 'sparkles', '[{"type": "commentary", "reference": "The New International Commentary on the New Testament", "description": "Explores the significance of prophecy in the early church."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 20, 20, 'kjv', 'Despise not prophesyings.', 0, 25, 'question', 'Value of Prophecy', 'How can we cultivate an attitude that values prophetic insights in our lives and community? What steps can we take to ensure we are receptive to messages that may come from unexpected sources?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 21, 21, 'kjv', 'Prove all things;', 0, 17, 'connection', 'Testing for Truth', 'The call to ''prove all things'' resonates with 1 John 4:1, where believers are advised to test the spirits to see whether they are from God. This connection emphasizes the importance of discernment in the Christian faith, ensuring that teachings align with biblical truth.', 'link', '[{"type": "crossReference", "reference": "1 John 4:1", "description": "Encourages believers to discern between true and false teachings."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 21, 21, 'kjv', 'hold fast that which is good.', 18, 47, 'theology', 'Embracing Goodness', 'This exhortation reflects the Christian doctrine of moral responsibility, where believers are called to cling to goodness as a reflection of their faith in Christ. Many scholars believe this conveys the idea of actively choosing to embody virtues that align with God''s character.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 21, 21, 'kjv', 'Prove all things;', 0, 17, 'greek', 'Critical Examination', 'δοκιμάζω (dokimazo, G1381) - to test, examine, prove. This term suggests an active engagement in discerning truth, reminding believers that faith is not blind but involves critical thinking and evaluation.', 'textformat.abc', '[{"type": "strongs", "reference": "G1381", "description": "Strong''s number for the Greek word meaning to test or examine."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 21, 21, 'kjv', 'hold fast that which is good.', 18, 47, 'question', 'Defining Goodness', 'What criteria do you use to define ''that which is good'' in your life? Reflecting on this can help clarify how closely your values align with biblical teachings.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 22, 22, 'kjv', 'Abstain from all appearance of evil.', 0, 36, 'connection', 'Connection to Other Teachings', 'This instruction parallels 1 Peter 2:11, which urges believers to abstain from fleshly lusts that war against the soul. Both passages highlight the importance of maintaining a lifestyle that reflects one''s faith in Christ, suggesting that our actions should not just avoid sin but also appear righteous to others. This is crucial for witness and integrity in the Christian life.', 'link', '[{"type": "crossReference", "reference": "1 Peter 2:11", "description": "Calls believers to live honorably among non-believers."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 22, 22, 'kjv', 'Abstain from all appearance of evil.', 0, 36, 'theology', 'Moral Responsibility', 'This verse emphasizes the believer''s responsibility in maintaining a moral witness. Many scholars believe that abstaining from even the appearance of evil reflects the doctrine of sanctification, where believers are called to be set apart for God''s purposes. This highlights the holistic approach to holiness that encompasses thoughts, actions, and public perception.', 'person.2.fill', '[{"type": "commentary", "reference": "Expositor''s Bible Commentary", "description": "Discusses the implications of living a sanctified life."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 22, 22, 'kjv', 'appearance of evil', 17, 35, 'greek', 'Original Language Insight', 'εἶδος (eidos, G1491) - form, appearance, or kind. This term emphasizes not just the evil itself but its outward manifestation. The use of ''appearance'' suggests that believers must be vigilant about how their actions and choices are perceived, as they can influence others'' understanding of the faith.', 'textformat.abc', '[{"type": "strongs", "reference": "G1491", "description": "Strong''s number for the Greek term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 22, 22, 'kjv', 'Abstain from all appearance of evil.', 0, 36, 'question', 'Reflect on Your Witness', 'What steps can you take to ensure that your actions do not merely avoid sin but also do not give the impression of wrongdoing? How can this principle shape your daily decisions and interactions with others?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 23, 23, 'kjv', 'the very God of peace', 4, 25, 'connection', 'God as Peace', 'This phrase connects to Philippians 4:7, which states that the peace of God surpasses all understanding. The reference emphasizes God''s nature as the source of peace, reinforcing the idea that true sanctification comes from Him alone. Understanding God as the God of peace is crucial for believers seeking wholeness and assurance in their spiritual journey.', 'link', '[{"type": "crossReference", "reference": "Philippians 4:7", "description": "Describes God''s peace that guards our hearts."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 23, 23, 'kjv', 'sanctify you wholly', 26, 45, 'greek', 'Sanctification Defined', 'ἁγιάζω (hagiazo, G37) - to make holy, consecrate, or set apart. This Greek term highlights the process of sanctification, indicating a continual action where God actively purifies believers. The wholeness referred to emphasizes that sanctification encompasses every aspect of a person''s life—spirit, soul, and body.', 'textformat.abc', '[{"type": "strongs", "reference": "G37", "description": "Strong''s number for the Greek word used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 23, 23, 'kjv', 'your whole spirit and soul and body', 62, 97, 'theology', 'Holistic Sanctification', 'This passage suggests a holistic view of humanity, recognizing the interplay between spirit, soul, and body. Many scholars believe this reflects the comprehensive nature of salvation, where every part of the believer is preserved and transformed for God''s glory. This understanding encourages believers to seek wholeness in every aspect of their lives as they await Christ''s return.', 'sparkles', '[{"type": "commentary", "reference": "MacArthur, John. The MacArthur New Testament Commentary: 1 Thessalonians.", "description": "Discussion on holistic sanctification."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 23, 23, 'kjv', 'blameless unto the coming of our Lord Jesus Christ', 111, 161, 'question', 'Living Blamelessly', 'What does it mean to be ''blameless'' as we await Christ''s coming? This prompts a reflection on personal integrity and spiritual readiness. In what ways can we actively pursue a life that reflects the holiness of God in anticipation of His return?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 24, 24, 'kjv', 'Faithful is he that calleth you, who also will do it.', 0, 53, 'connection', 'God''s Faithfulness', 'This phrase connects with Hebrews 10:23, which states, ''Let us hold fast the profession of our faith without wavering; (for he is faithful that promised).'' The emphasis on God''s faithfulness in both passages underscores the assurance believers have in His promises. It encourages believers to remain steadfast in their faith, knowing that God will fulfill His calling.', 'link', '[{"type": "crossReference", "reference": "Hebrews 10:23", "description": "Encouragement to hold fast to faith due to God''s faithfulness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 24, 24, 'kjv', 'he that calleth you', 12, 31, 'greek', 'Divine Calling', 'καλέω (kaléō, G2564) - to call, summon. This term emphasizes God''s active role in initiating a relationship with believers. It reflects the personal nature of divine calling, suggesting that each believer is chosen for a unique purpose within God''s plan.', 'textformat.abc', '[{"type": "strongs", "reference": "G2564", "description": "Greek term for calling, highlighting God''s initiative."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 24, 24, 'kjv', 'who also will do it.', 33, 53, 'theology', 'God''s Sovereignty', 'This statement suggests that God not only calls but also empowers believers to fulfill their calling. Many scholars believe this reflects the doctrine of sanctification, where God works in the lives of believers to accomplish His purposes. This assurance can provide comfort in times of doubt, reinforcing the belief that God equips those He calls.', 'sparkles', '[{"type": "commentary", "reference": "The New American Commentary on 1 Thessalonians", "description": "Discusses the implications of God''s active role in sanctification."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 24, 24, 'kjv', 'Faithful is he', 0, 14, 'question', 'Understanding Faithfulness', 'What does it mean for God to be ''faithful'' in your life? Reflect on specific instances where you have seen His faithfulness in fulfilling His promises. How does this understanding of God''s character influence your trust in Him during challenging times?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 25, 25, 'kjv', 'Brethren, pray for us.', 0, 22, 'connection', 'Call to Intercession', 'Paul''s request for prayer echoes James 5:16, where it states, ''The effectual fervent prayer of a righteous man availeth much.'' This connection highlights the importance of communal prayer in the early Church and suggests that intercession for one another is a vital aspect of Christian fellowship and support.', 'link', '[{"type": "crossReference", "reference": "James 5:16", "description": "Emphasizes the power and necessity of prayer among believers."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 25, 25, 'kjv', 'Brethren, pray for us.', 0, 22, 'greek', 'Meaning of ''Pray''', 'προσεύχομαι (proseuchomai, G4336) - to pray, to make a vow. This term indicates not just a request for communication with God but also suggests a deeper commitment and earnestness in seeking divine intervention, reflecting the communal aspect of prayer in the Christian community.', 'textformat.abc', '[{"type": "strongs", "reference": "G4336", "description": "Strong''s number for the Greek term related to prayer."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 25, 25, 'kjv', 'Brethren, pray for us.', 0, 22, 'theology', 'The Role of Community in Prayer', 'This verse illustrates the theological principle of communal intercession, suggesting that prayer is not merely an individual activity but a collective responsibility. Many scholars believe that this reflects the early Church''s understanding of unity and mutual support among believers, essential for spiritual growth and resilience.', 'person.2.fill', '[{"type": "commentary", "reference": "New International Commentary on the New Testament", "description": "Discusses the communal aspects of prayer in the early Church."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 25, 25, 'kjv', 'Brethren, pray for us.', 0, 22, 'question', 'Reflecting on Intercession', 'What does it mean to you personally to ''pray for us'' within your community? How can you actively participate in the spiritual support of others in your church or fellowship, reflecting the call to prayer that Paul emphasizes?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 26, 26, 'kjv', 'Greet all the brethren with an holy kiss.', 0, 41, 'connection', 'Cultural Greeting Practices', 'This verse connects to Romans 16:16, where Paul instructs believers to greet one another with a holy kiss. This cultural practice reflects the warmth and unity of the early church, emphasizing the importance of fellowship and love among believers. Such greetings symbolize a deeper spiritual bond and mutual respect within the Christian community.', 'link', '[{"type": "crossReference", "reference": "Romans 16:16", "description": "Another instance of greeting with a holy kiss, showing early church customs."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 26, 26, 'kjv', 'Greet all the brethren with an holy kiss.', 0, 41, 'greek', 'Holy Kiss in Greek', 'ἄγιον (hagion, G40) - holy, sacred. The use of ''holy'' in this context underscores the purity and significance of the greeting. It indicates that the act of greeting is not merely a social formality but a sacred expression of love and fellowship among believers, reflecting their relationship with God.', 'textformat.abc', '[{"type": "strongs", "reference": "G40", "description": "Strong''s number for ''holy'', emphasizing the spiritual nature of the greeting."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 26, 26, 'kjv', 'Greet all the brethren with an holy kiss.', 0, 41, 'theology', 'Significance of Fellowship', 'Many scholars believe that the command to greet with a holy kiss illustrates the theological concept of community in the body of Christ. This passage highlights the importance of expressing love and unity among believers, which is foundational to Christian doctrine. The practice serves as a visible sign of the spiritual connection believers share through Christ.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explores the significance of fellowship and love within the Christian community."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 26, 26, 'kjv', 'Greet all the brethren with an holy kiss.', 0, 41, 'question', 'Reflecting on Fellowship', 'How does the practice of greeting one another with a holy kiss inform our understanding of community within the church today? In what ways can we express love and unity in our interactions with fellow believers?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 27, 27, 'kjv', 'I charge you by the Lord', 0, 24, 'theology', 'Authority in Apostolic Charge', 'The phrase ''I charge you by the Lord'' highlights the authority by which Paul speaks. This reflects the apostolic tradition where the apostles invoke Christ''s authority in their teachings, emphasizing the seriousness of their message. Many scholars believe that this insistence on reading the epistle signifies the communal nature of faith in the early church, where teaching was integral to church life and unity.', 'person.2.fill', '[{"type": "commentary", "reference": "1 Timothy 4:13", "description": "Paul encourages Timothy to devote himself to the public reading of Scripture."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(52, 5, 27, 27, 'kjv', 'this epistle be read', 30, 50, 'connection', 'Importance of Public Reading', 'This directive connects to Colossians 4:16, where Paul instructs that his letters be read in the churches. The communal reading of Scripture was vital for doctrinal unity and spiritual growth in early Christianity, fostering a shared understanding of the faith among believers.', 'link', '[{"type": "crossReference", "reference": "Colossians 4:16", "description": "Paul''s instruction on the public reading of letters to encourage community."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 27, 27, 'kjv', 'all the holy brethren', 56, 77, 'greek', 'Understanding ''Holy Brethren''', 'ἁγίους (hagious, G40) - holy, set apart. This term emphasizes the sanctity and identity of believers as those consecrated to God. Paul''s use of ''holy brethren'' indicates a close-knit community of faith, reflecting their shared calling and purity in Christ.', 'textformat.abc', '[{"type": "strongs", "reference": "G40", "description": "Strong''s number for ''holy'', indicating set apart for God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(52, 5, 27, 27, 'kjv', 'I charge you by the Lord', 0, 24, 'question', 'Reflecting on Authority', 'What does it mean for you to receive this charge by the Lord? How does understanding the authority behind this epistle shape your approach to reading and applying Scripture in your life and community?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini');