INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(58, 11, 20, 20, 'kjv', 'By faith Isaac blessed Jacob and Esau concerning things to come.', 0, 64, 'greek', 'Understanding ''faith''', 'πίστις (pistis, G4102) - faith, trust, belief. This Greek term denotes not just a belief in God but also a trusting relationship characterized by commitment. In this context, Isaac''s faith reflects a deep trust in God''s promises regarding his descendants, emphasizing the relational aspect of faith that transcends mere intellectual assent.', 'textformat.abc', '[{"type": "strongs", "reference": "G4102", "description": "Strong''s number for ''faith'' in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 20, 20, 'kjv', 'By faith Isaac blessed Jacob and Esau concerning things to come.', 0, 64, 'question', 'Reflecting on Isaac''s Faith', 'What does it mean to bless others ''concerning things to come''? How can we, like Isaac, exercise faith in our blessings for future generations? Consider how your faith influences the way you pray for and encourage those who follow you.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 21, 21, 'kjv', 'worshipped, leaning upon the top of his staff.', 74, 120, 'greek', 'Worship and Posture', 'προσκυνέω (proskyneō, G4352) - to worship, to bow down. This word indicates a deep reverence and submission to God, reflecting Jacob''s recognition of God''s sovereignty even in his final moments.', 'textformat.abc', '[{"type": "strongs", "reference": "G4352", "description": "Greek term for worship"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 21, 21, 'kjv', 'By faith Jacob, when he was a dying, blessed both the sons of Joseph;', 0, 69, 'theology', 'Blessing as a Covenant Act', 'Many scholars believe this act of blessing encapsulates the essence of covenant theology, where blessings confirm God''s promises to His people. Jacob''s faith highlights the continuity of God''s covenant through his descendants, illustrating the significance of faith-based legacy.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Insights on covenant blessings"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 21, 21, 'kjv', 'By faith Jacob, when he was a dying, blessed both the sons of Joseph;', 0, 69, 'question', 'Legacy of Faith', 'What does it mean to pass on blessings of faith to future generations, as Jacob did? How can we actively engage in this practice within our families and communities?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 21, 21, 'kjv', 'By faith Jacob, when he was a dying, blessed both the sons of Joseph;', 0, 69, 'connection', 'Jacob''s Blessing Legacy', 'This passage connects with Genesis 48:14-16, where Jacob explicitly blesses Joseph''s sons, Ephraim and Manasseh, demonstrating the significance of faith in the passing of blessings. The act symbolizes not only familial continuity but also the divine favor that continues through generations. Understanding the context of Jacob''s blessings enriches our grasp of the faith that motivated his actions.', 'link', '[{"type": "crossReference", "reference": "Genesis 48:14-16", "description": "Jacob blesses Joseph''s sons, showing faith in God''s promises."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 21, 21, 'kjv', 'and worshipped, leaning upon the top of his staff.', 70, 120, 'theology', 'Worship in Weakness', 'This action of Jacob worshipping while leaning on his staff illustrates a powerful theological theme: true worship can occur in moments of physical weakness and impending death. Many scholars believe this emphasizes the necessity of reliance on God even in our frailest states, reflecting the Christian teaching that faith often shines brightest amid adversity.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 21, 21, 'kjv', 'By faith Jacob, when he was a dying, blessed both the sons of Joseph;', 0, 69, 'question', 'Faith in Dying Moments', 'What does Jacob''s act of blessing his grandsons reveal about the nature of faith at the end of life? How can we emulate his example in our own circumstances, especially when facing uncertainty or mortality?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 21, 21, 'kjv', 'By faith Jacob, when he was a dying, blessed both the sons of Joseph;', 0, 69, 'greek', 'Understanding ''Blessed''', 'εὐλόγησε (eulogēse, G2127) - blessed. The term implies not just a spoken blessing but a deep invocation of God''s favor and presence over the recipients. This highlights the weight and significance of Jacob''s actions, as they were rooted in a faith that extended beyond his life.', 'textformat.abc', '[{"type": "strongs", "reference": "G2127", "description": "Greek term for ''blessed,'' indicating a profound act of invoking divine favor."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 22, 22, 'kjv', 'By faith Joseph, when he died, made mention of the departing of the children of Israel;', 0, 87, 'connection', 'Joseph''s Prophetic Faith', 'Joseph''s mention of the departing of the children of Israel connects with Exodus 13:19, where Moses takes Joseph''s bones with him. This connection highlights Joseph''s faith in God''s promise of deliverance, foreshadowing the future exodus of Israel from Egypt. It illustrates the importance of holding onto God''s promises even beyond one''s lifetime.', 'link', '[{"type": "crossReference", "reference": "Exodus 13:19", "description": "Moses carries Joseph''s bones, fulfilling Joseph''s command."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 22, 22, 'kjv', 'and gave commandment concerning his bones.', 88, 130, 'theology', 'Hope in Resurrection', 'Joseph''s command regarding his bones reflects a profound belief in God''s future promises. Many scholars believe this act symbolizes hope in resurrection and God''s faithfulness to His covenant people. It emphasizes that even in death, Joseph looked forward to a time when God''s plan for Israel would be realized, encouraging believers to trust in God''s eternal promises.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 22, 22, 'kjv', 'By faith Joseph, when he died, made mention of the departing of the children of Israel;', 0, 87, 'question', 'Faith Beyond Death', 'What does Joseph''s faith in the departing of Israel reveal about the nature of hope in God''s promises? How can we apply his example of faith to our own lives, especially regarding our legacy and the impact of our beliefs beyond our lifetime?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 22, 22, 'kjv', 'and gave commandment concerning his bones.', 88, 130, 'greek', 'Commandment Significance', 'ἐντολή (entolē, G1785) - commandment, order. This Greek term highlights the authoritative nature of Joseph''s directive. It emphasizes the seriousness with which he viewed the future of his people and the promise of their return to the land of Canaan, reinforcing the importance of obedience to God''s will.', 'textformat.abc', '[{"type": "strongs", "reference": "G1785", "description": "Strong''s number for the term ''commandment''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 23, 23, 'kjv', 'By faith Moses, when he was born', 0, 32, 'connection', 'Moses'' Birth and Protection', 'This reference to Moses'' birth connects with Exodus 2:1-2, where his mother hides him to protect him from Pharaoh''s decree. This act signifies divine protection and foreshadows Moses'' future role as deliverer of Israel. Additionally, Acts 7:20 highlights his extraordinary upbringing, emphasizing God''s hand in his life from the very beginning.', 'link', '[{"type": "crossReference", "reference": "Exodus 2:1-2", "description": "Details the circumstances surrounding Moses'' birth and concealment."}, {"type": "crossReference", "reference": "Acts 7:20", "description": "Speaks to Moses'' divine purpose from birth."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 23, 23, 'kjv', 'hid three months of his parents', 38, 69, 'greek', 'Faith in Action', 'κρύπτω (kryptō, G2928) - to hide, conceal. This term underscores the active faith of Moses'' parents, as they took deliberate action to protect him from harm. Their choice to hide him speaks volumes about their trust in God''s plan amidst danger.', 'textformat.abc', '[{"type": "strongs", "reference": "G2928", "description": "Strong''s number for the Greek word for ''hide.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 23, 23, 'kjv', 'because they saw he was a proper child', 71, 109, 'theology', 'Divine Favor and Purpose', 'Many scholars believe that the phrase ''proper child'' reflects a sense of divine favor and purpose attributed to Moses even at birth. This aligns with the theological view that God has specific plans for individuals, which can be evident from the very start of their lives, warranting parental faith and courage.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 23, 23, 'kjv', 'and they were not afraid of the king''s commandment', 111, 161, 'question', 'Courage Amidst Fear', 'What does it mean to act in faith rather than fear, especially in the face of authority? How can we apply this principle in our own lives when confronted with difficult decisions or societal pressures?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 24, 24, 'kjv', 'refused to be called the son of Pharaoh''s daughter;', 43, 94, 'greek', 'Refused Definition', 'ἀρνέομαι (arneomai, G720) - to deny, refuse, or reject. This term signifies a deliberate choice, indicating that Moses made a conscious decision to reject his privileged position in Pharaoh''s household. Such a refusal illustrates profound faith and commitment to God''s people over worldly status.', 'textformat.abc', '[{"type": "strongs", "reference": "G720", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 24, 24, 'kjv', 'By faith Moses, when he was come to years, refused to be called the son of Pharaoh''s daughter;', 0, 94, 'theology', 'Faith and Identity', 'This passage suggests that true faith often involves difficult choices regarding identity and allegiance. Many scholars believe that Moses'' refusal reflects a larger theological principle: believers are called to prioritize their identity in Christ over worldly affiliations. This aligns with Paul''s teaching in Philippians 3:20 about citizenship in heaven, reminding us to evaluate where our true allegiance lies.', 'sparkles', '[{"type": "crossReference", "reference": "Philippians 3:20", "description": "Believers'' citizenship in heaven."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 24, 24, 'kjv', 'By faith Moses, when he was come to years, refused to be called the son of Pharaoh''s daughter;', 0, 94, 'connection', 'Moses'' Identity Choices', 'Moses'' decision to refuse his royal status echoes the sentiment in 2 Corinthians 6:17, which calls believers to separate from worldly influences. This connection emphasizes the theme of identity in Christ versus worldly identity, illustrating how Moses prioritized his spiritual heritage over earthly privilege.', 'link', '[{"type": "crossReference", "reference": "2 Corinthians 6:17", "description": "Calls believers to separate from worldly influences."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 24, 24, 'kjv', 'refused to be called the son of Pharaoh''s daughter;', 43, 94, 'theology', 'Choosing Suffering over Comfort', 'This passage suggests that Moses'' refusal to be identified as Pharaoh''s daughter signifies a profound theological truth: the call to discipleship often involves the rejection of worldly comforts and privileges. Many scholars believe this reflects Jesus'' teaching in Matthew 16:24 about taking up one''s cross to follow Him.', 'sparkles', '[{"type": "commentary", "reference": "Matthew 16:24", "description": "Jesus teaches about the cost of discipleship."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 24, 24, 'kjv', 'By faith Moses, when he was come to years, refused to be called the son of Pharaoh''s daughter;', 0, 94, 'greek', 'Faith in Action', 'πίστις (pistis, G4102) - faith, belief, trust. This term underscores that Moses'' actions were motivated by faith, emphasizing that true faith often requires active choices against societal norms. His refusal to embrace his Egyptian identity is a powerful testament to his commitment to God''s people.', 'textformat.abc', '[{"type": "strongs", "reference": "G4102", "description": "Strong''s number for ''faith'' in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 24, 24, 'kjv', 'refused to be called the son of Pharaoh''s daughter;', 43, 94, 'question', 'Identity and Choices', 'What does it mean to refuse societal labels and choose a different identity in light of your faith? How can this challenge you to evaluate the influences and identities you embrace in your own life?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 25, 25, 'kjv', 'Choosing rather to suffer affliction with the people of God', 0, 59, 'question', 'What Does Affliction Teach Us?', 'In what ways does choosing to suffer with God''s people deepen our understanding of faith? Reflecting on Moses'' decision invites consideration of how communal suffering shapes our identity as believers and strengthens our resolve in spiritual matters.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 25, 25, 'kjv', 'the pleasures of sin for a season;', 75, 109, 'greek', 'Pleasures of Sin', 'ἀμαρτία (hamartia, G266) - sin, wrongdoing. This term encompasses not just acts of sin but also the nature of sin itself, highlighting the moral and spiritual separation from God that comes with choosing sin over righteousness.', 'textformat.abc', '[{"type": "strongs", "reference": "G266", "description": "Greek term for sin"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 25, 25, 'kjv', 'Choosing rather to suffer affliction with the people of God, than to enjoy the pleasures of sin for a season;', 0, 109, 'connection', 'Suffering with God''s People', 'This verse resonates with 1 Peter 4:13, which encourages believers to rejoice in sharing Christ''s sufferings. Additionally, Romans 8:17 states that we are heirs with Christ, provided we suffer with Him. These connections highlight the value and honor in enduring hardship alongside fellow believers, reinforcing the idea that suffering can lead to greater spiritual rewards.', 'link', '[{"type": "crossReference", "reference": "1 Peter 4:13", "description": "Encouragement to rejoice in sharing Christ''s sufferings."}, {"type": "crossReference", "reference": "Romans 8:17", "description": "Affirms the unity of suffering with Christ and its implications."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 25, 25, 'kjv', 'pleasures of sin for a season;', 79, 109, 'theology', 'Temporal vs. Eternal Values', 'This passage suggests a fundamental Christian teaching about the contrast between temporal pleasures and eternal values. Many scholars believe that the fleeting nature of sin''s pleasures often leads to spiritual emptiness, while choosing to align with God''s people brings lasting fulfillment. This reflects the broader biblical theme that true joy is found in obedience and relationship with God rather than in worldly pursuits.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 25, 25, 'kjv', 'suffer affliction with the people of God,', 19, 60, 'greek', 'Key Greek Terms', 'πάσχειν (paschein, G3958) - to suffer, bear, endure. This term emphasizes the active experience of suffering, suggesting that it is not merely passive but involves a conscious choice to endure hardship for a greater purpose.', 'textformat.abc', '[{"type": "strongs", "reference": "G3958", "description": "Strong''s number for the term ''suffer.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 25, 25, 'kjv', 'Choosing rather to suffer affliction', 0, 36, 'question', 'The Cost of Choice', 'What does it mean to actively choose suffering over temporary pleasures? In what ways might this choice reflect your values and priorities in your own life? Consider how this decision shapes your identity as part of God''s people and your understanding of sacrifice.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 26, 26, 'kjv', 'Esteeming the reproach of Christ greater riches than the treasures in Egypt', 0, 75, 'question', 'Value of Reproach', 'What does it mean to esteem the reproach of Christ as greater than earthly treasures? How can this understanding influence our daily choices regarding wealth and status in the light of eternal values?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 26, 26, 'kjv', 'for he had respect unto the recompence of the reward', 77, 129, 'greek', 'Recompense in Greek', 'ἀποδοχή (apodoxe, G5885) - recompense, return, reward. This term highlights the idea of receiving something in return for one''s actions and underscores the belief in divine justice and reward for faithfulness.', 'textformat.abc', '[{"type": "strongs", "reference": "G5885", "description": "Greek word meaning recompense or reward."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 26, 26, 'kjv', 'Esteeming the reproach of Christ greater riches than the treasures in Egypt', 0, 75, 'connection', 'Reproach of Christ', 'This phrase connects to Philippians 3:8, where Paul states that he considers everything a loss compared to the worth of knowing Christ. Both passages emphasize the value of spiritual wealth over earthly treasures, illustrating a common theme in Scripture that true fulfillment comes from a relationship with God rather than material possessions. This connection enriches our understanding of sacrifice in the Christian faith.', 'link', '[{"type": "crossReference", "reference": "Philippians 3:8", "description": "Paul''s valuation of knowing Christ over all else."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 26, 26, 'kjv', 'for he had respect unto the recompence of the reward', 77, 129, 'theology', 'Eternal Rewards', 'This phrase suggests that Moses looked beyond immediate circumstances to the eternal reward promised by God. Many scholars believe this reflects the Christian doctrine of eternal rewards, as seen in Matthew 5:12, where Jesus teaches about the rewards for enduring persecution. This perspective encourages believers to maintain faith in trials, knowing that God''s ultimate reward is far greater than earthly suffering.', 'sparkles', '[{"type": "crossReference", "reference": "Matthew 5:12", "description": "Jesus'' teaching on rewards for persecution."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 26, 26, 'kjv', 'greater riches than the treasures in Egypt', 33, 75, 'greek', 'Riches in Greek', 'πλούτος (ploutos, G4149) - wealth, riches, abundance. This term signifies not just material wealth but also spiritual abundance. The use of ''greater riches'' in this context highlights the contrast between transient earthly wealth and the lasting spiritual wealth found in faith and relationship with Christ.', 'textformat.abc', '[{"type": "strongs", "reference": "G4149", "description": "Strong''s number for the Greek term for riches."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 26, 26, 'kjv', 'Esteeming the reproach of Christ', 0, 32, 'question', 'Value of Reproach', 'What does it mean to consider the reproach of Christ as valuable? In what ways might we be called to embrace discomfort or ridicule for our faith today? Reflecting on this can help us understand the cost of discipleship and the true treasures that come from following Christ.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 27, 27, 'kjv', 'By faith he forsook Egypt', 0, 25, 'connection', 'Faith and Forsaking', 'This phrase connects to Exodus 2:14, where Moses chooses to identify with his people rather than enjoy the privileges of the Egyptian court. His act of faith in leaving Egypt underscores the theme of choosing spiritual over earthly wealth. It illustrates the transformative power of faith that compels believers to make sacrifices for a greater purpose.', 'link', '[{"type": "crossReference", "reference": "Exodus 2:14", "description": "Moses'' choice to reject his Egyptian identity"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 27, 27, 'kjv', 'not fearing the wrath of the king', 27, 60, 'theology', 'Courage in Faith', 'Many scholars believe this portion highlights the theological principle that faith empowers believers to confront fear with courage. The fear of earthly authorities can often inhibit obedience to God, yet true faith provides the strength to act righteously despite potential repercussions. This reflects the larger biblical theme of divine protection for those who stand firm in their faith.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 27, 27, 'kjv', 'for he endured, as seeing him who is invisible', 62, 108, 'greek', 'Seeing the Invisible', 'ἀοράτου (aorátou, G517) - invisible, unseen. This term emphasizes the concept of faith as a means to perceive spiritual realities that are not visible to the physical eye. This aligns with 2 Corinthians 4:18, where Paul encourages believers to focus on the eternal rather than the temporal, reinforcing that true understanding and assurance come from faith in God.', 'textformat.abc', '[{"type": "strongs", "reference": "G517", "description": "The Greek term for invisible, highlighting the unseen aspects of faith"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 27, 27, 'kjv', 'By faith he forsook Egypt', 0, 25, 'question', 'What does true faith require?', 'What does it mean to forsake ''Egypt'' in your own life? Consider in what ways God might be calling you to step away from familiar comforts or societal pressures in order to pursue a deeper relationship with Him. How does this align with your understanding of faith as an active choice?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 28, 28, 'kjv', 'Through faith he kept the passover, and the sprinkling of blood, lest he that destroyed the firstborn should touch them.', 0, 120, 'connection', 'Passover Significance', 'This verse references the Passover, which is central to the Exodus narrative in Exodus 12:13. In that passage, the blood of the lamb protects the Israelites from the plague that struck Egypt. This connection underscores the theme of faith and divine protection, illustrating how the Old Testament foreshadows Christ''s sacrificial death, which provides salvation for believers today (1 Corinthians 5:7).', 'link', '[{"type": "crossReference", "reference": "Exodus 12:13", "description": "The original Passover event highlighting the significance of blood for protection."}, {"type": "crossReference", "reference": "1 Corinthians 5:7", "description": "Paul identifies Christ as our Passover lamb, reinforcing the theological connection."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 28, 28, 'kjv', 'the sprinkling of blood', 40, 63, 'greek', 'Sprinkling in Greek', 'ῥάπισμα (rhapisma, G4472) - sprinkling. This term relates to the ritualistic act of applying blood, which signifies purification and atonement in the context of the sacrificial system. Understanding this word enriches our grasp of the Passover''s significance and its anticipation of Christ''s ultimate sacrifice.', 'textformat.abc', '[{"type": "strongs", "reference": "G4472", "description": "Strong''s number for the Greek term ''sprinkling.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 28, 28, 'kjv', 'lest he that destroyed the firstborn should touch them.', 65, 120, 'theology', 'Divine Judgment and Mercy', 'This passage suggests a profound theological concept of judgment and mercy. Many scholars believe that the act of sprinkling blood represents God''s mercy in the face of impending judgment. It illustrates the necessity of faith in God''s provision for salvation, a theme that resonates throughout Christian doctrine, emphasizing that faith in Christ''s sacrifice protects believers from eternal judgment.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 28, 28, 'kjv', 'kept the passover', 17, 34, 'question', 'Faith and Obedience', 'What does it mean to ''keep the Passover'' in today''s context? How can we apply the principles of faith and obedience demonstrated by the Israelites to our own lives, especially in understanding God''s deliverance from sin?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 29, 29, 'kjv', 'By faith they passed through the Red sea as by dry land:', 0, 56, 'connection', 'Cross-Reference to Exodus', 'This phrase directly connects to Exodus 14:22, which describes the miraculous crossing of the Red Sea. The Israelites'' faith in God''s deliverance led them to walk through the sea on dry land, demonstrating trust in divine intervention. This event not only showcases God''s power but also serves as a foundational moment for Israel''s identity as His chosen people.', 'link', '[{"type": "crossReference", "reference": "Exodus 14:22", "description": "The account of the Israelites crossing the Red Sea."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 29, 29, 'kjv', 'which the Egyptians assaying to do were drowned.', 57, 105, 'theology', 'Judgment and Salvation', 'This passage illustrates a common biblical theme of judgment and salvation. The Egyptians'' attempt to follow the Israelites into the Red Sea, resulting in their drowning, signifies God''s judgment on unbelief and oppression. Conversely, the Israelites'' safe passage signifies salvation through faith. Many scholars believe this foreshadows the ultimate salvation offered through Christ, as seen in Romans 6:23, where the wages of sin lead to death, but the gift of God is eternal life.', 'sparkles', '[{"type": "commentary", "reference": "Romans 6:23", "description": "The contrast between death due to sin and the gift of eternal life."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(58, 11, 29, 29, 'kjv', 'By faith they passed through the Red sea as by dry land:', 0, 56, 'question', 'Faith in Action', 'What does it mean to act in faith, as the Israelites did at the Red Sea? How can we apply their example of trusting God''s promises in our own lives, especially in situations that seem impossible?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 29, 29, 'kjv', 'By faith they passed through the Red sea as by dry land:', 0, 56, 'greek', 'Understanding ''passed through''', 'διέρχομαι (diérchomai, G1330) - to go through, pass through. This term emphasizes not just the act of crossing but suggests a deliberate movement through a challenging situation, symbolizing faith that leads to deliverance.', 'textformat.abc', '[{"type": "strongs", "reference": "G1330", "description": "Strong''s number for the term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 30, 30, 'kjv', 'compassed about seven days', 57, 83, 'question', 'Importance of Perseverance', 'What does the act of compassing Jericho for seven days teach us about perseverance in our own faith journeys? How might we apply the concept of sustained faith in our daily challenges?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 30, 30, 'kjv', 'fell down', 30, 39, 'greek', 'Significance of ''Fell Down''', 'καταπίπτω (katapiptō, G2706) - to fall down, collapse, or fall flat. This term emphasizes not just a physical collapse but also symbolizes the dismantling of barriers that prevent God''s people from entering into His promise.', 'textformat.abc', '[{"type": "strongs", "reference": "G2706", "description": "Indicates the Greek word''s meaning and usage in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(58, 11, 30, 30, 'kjv', 'By faith the walls of Jericho fell down', 0, 39, 'connection', 'Faith and Divine Intervention', 'This event parallels Joshua 6:20, where the Israelites'' obedience to God''s command results in the walls collapsing. The connection emphasizes that faith, when coupled with action, aligns believers with divine purposes. The miraculous nature of the walls falling illustrates God''s sovereignty in delivering His people.', 'link', '[{"type": "crossReference", "reference": "Joshua 6:20", "description": "Details the fall of Jericho''s walls as a result of Israel''s obedience."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');