INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(62, 5, 5, 5, 'kjv', 'Who is he that overcometh the world', 0, 35, 'question', 'Understanding the Overcomer', 'What does it mean to ''overcome the world'' in the context of your daily challenges? How can belief in Jesus as the Son of God empower you to face life''s obstacles and temptations?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 5, 5, 'kjv', 'he that believeth', 41, 58, 'greek', 'Key Greek Term', 'πιστεύων (pisteuōn, G4100) - believing, having faith. This term emphasizes a continuous action and trust in Christ, suggesting that overcoming the world is not a one-time event but an ongoing lifestyle of faith.', 'textformat.abc', '[{"type": "strongs", "reference": "G4100", "description": "Strong''s number for the Greek term for believing."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 6, 6, 'kjv', 'This is he that came by water and blood, even Jesus Christ; not by water only, but by water and blood.', 0, 102, 'connection', 'Connection to Baptism and Crucifixion', 'This phrase connects to Matthew 3:16-17, where Jesus is baptized in water, and John 19:34, which describes blood and water flowing from Jesus'' side during His crucifixion. These events underscore the significance of both water and blood in Christ''s mission of redemption, illustrating the fullness of His sacrifice and the initiation of His ministry.', 'link', '[{"type": "crossReference", "reference": "Matthew 3:16", "description": "Jesus'' baptism in water"}, {"type": "crossReference", "reference": "John 19:34", "description": "Blood and water from Jesus'' side"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 6, 6, 'kjv', 'And it is the Spirit that beareth witness, because the Spirit is truth.', 103, 174, 'theology', 'Role of the Holy Spirit', 'This passage highlights the essential role of the Holy Spirit in affirming the truth of Jesus Christ. Many scholars believe this reflects the doctrine of the Trinity, where the Spirit testifies to the reality of Jesus'' dual nature as both divine and human. The Spirit''s witness is foundational for understanding the truth of the Gospel and the believer''s assurance in faith.', 'sparkles', '[{"type": "commentary", "reference": "MacArthur, John. The MacArthur New Testament Commentary: 1 John.", "description": "Discusses the role of the Holy Spirit in affirming truth"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 6, 6, 'kjv', 'This is he that came by water and blood, even Jesus Christ;', 0, 59, 'greek', 'Significance of ''came''', 'ἐλθών (elthōn, G2064) - to come, to go. The term signifies a purposeful arrival, emphasizing that Jesus intentionally entered human history to fulfill God''s plan of salvation. This choice of word reinforces the idea that His coming was not accidental but divinely orchestrated.', 'textformat.abc', '[{"type": "strongs", "reference": "G2064", "description": "Greek word for ''came''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 6, 6, 'kjv', 'not by water only, but by water and blood.', 60, 102, 'question', 'Understanding Dual Testimony', 'Why does John emphasize that Jesus came ''by water and blood''? In what ways does this dual testimony impact our understanding of salvation and the nature of Christ? Reflecting on this can deepen our appreciation for the completeness of Jesus'' sacrifice and the assurance it brings to believers.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 7, 7, 'kjv', 'the Father, the Word, and the Holy Ghost', 48, 88, 'connection', 'Trinitarian Evidence', 'This phrase highlights the concept of the Trinity, which is foundational in Christian theology. Cross-references such as Matthew 28:19, where Jesus commands baptism in the name of the Father, Son, and Holy Spirit, further affirm this triadic relationship. Understanding the Trinity is crucial for grasping the nature of God as three distinct persons yet one essence.', 'link', '[{"type": "crossReference", "reference": "Matthew 28:19", "description": "Jesus'' commission emphasizes the Trinity."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 7, 7, 'kjv', 'these three are one', 94, 113, 'theology', 'Unity in Diversity', 'This statement encapsulates the doctrine of the Trinity, where the Father, the Word (Jesus), and the Holy Spirit are distinct yet unified. Many scholars believe this unity emphasizes the cooperative work of the Trinity in creation and redemption, reinforcing the belief that God is both relational and singular in essence.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 7, 7, 'kjv', 'three that bear record in heaven', 14, 46, 'greek', 'Key Greek Term', 'μαρτυρέω (martureō, G3140) - to bear witness, to testify. This term underlines the authoritative nature of the testimony being provided by the Father, the Word, and the Holy Spirit, emphasizing the divine validation of Christ''s identity and mission.', 'textformat.abc', '[{"type": "strongs", "reference": "G3140", "description": "Strong''s number for ''bear witness''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 7, 7, 'kjv', 'the Father, the Word, and the Holy Ghost', 48, 88, 'question', 'Understanding the Trinity', 'What does it mean for the Father, the Word, and the Holy Ghost to be one? How does this understanding of unity in diversity impact your relationship with God and your view of His work in the world?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 8, 8, 'kjv', 'the spirit, and the water, and the blood', 48, 88, 'connection', 'Witness of the Elements', 'This phrase resonates with John 3:5, where Jesus emphasizes the necessity of being born of water and the Spirit. The connection highlights the significance of these elements in the believer''s experience of salvation and transformation. Both passages underscore the collaborative role of physical and spiritual realms in affirming faith.', 'link', '[{"type": "crossReference", "reference": "John 3:5", "description": "Jesus discusses being born of water and Spirit."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 8, 8, 'kjv', 'these three agree in one', 94, 118, 'theology', 'Unity of Witness', 'This passage suggests a profound unity in the testimony of the Spirit, water, and blood, reflecting the doctrine of the Trinity. Many scholars believe this agreement among the witnesses signifies the cohesive nature of God''s revelation and the inseparable work of the Father, Son, and Holy Spirit in salvation.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 8, 8, 'kjv', 'the spirit, and the water, and the blood', 48, 88, 'greek', 'Key Greek Terms', 'πνεῦμα (pneuma, G4151) - spirit; ὕδωρ (hudor, G5204) - water; αἷμα (haima, G129) - blood. Each term carries significant theological weight, indicating the essential elements in the life of a believer and the sacrificial nature of Christ''s atonement.', 'textformat.abc', '[{"type": "strongs", "reference": "G4151", "description": "Strong''s for ''spirit''"}, {"type": "strongs", "reference": "G5204", "description": "Strong''s for ''water''"}, {"type": "strongs", "reference": "G129", "description": "Strong''s for ''blood''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 8, 8, 'kjv', 'these three agree in one', 94, 118, 'question', 'Understanding Agreement', 'What does it mean for the spirit, the water, and the blood to ''agree in one''? How does this unity inform your understanding of the Christian faith and the testimony of Jesus Christ?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 9, 9, 'kjv', 'the witness of God is greater', 34, 63, 'connection', 'God''s Testimony in Scripture', 'This phrase resonates with John 5:36, where Jesus states, ''But I have a greater witness than John''s; for the works which the Father has given Me to finish—the very works that I do—bear witness of Me, that the Father has sent Me.'' Both passages emphasize the supreme authority of God''s testimony over human witness, highlighting the divine validation of Jesus as the Son of God.', 'link', '[{"type": "crossReference", "reference": "John 5:36", "description": "Jesus asserts the superiority of God''s witness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 9, 9, 'kjv', 'the witness of God which he hath testified of his Son', 77, 130, 'theology', 'The Nature of God''s Testimony', 'This passage suggests that God''s testimony about His Son is foundational to Christian faith. Many scholars believe that this underscores the relational aspect of God''s witness, as it is not merely a declaration but an affirmation of the relationship between the Father and the Son, reinforcing the doctrine of the Trinity and the divinity of Christ.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 9, 9, 'kjv', 'If we receive the witness of men', 0, 32, 'question', 'Human vs Divine Witness', 'What does it mean to receive the witness of men, and how does that compare to receiving God''s witness? In what ways do we allow human opinions to overshadow the truth of God''s testimony about His Son in our lives?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 9, 9, 'kjv', 'the witness of God which he hath testified of his Son', 77, 130, 'greek', 'Key Greek Term', 'μαρτυρία (martyria, G3141) - witness, testimony. This term emphasizes the act of bearing witness, which is central to the Christian message, indicating that God''s testimony about Jesus is both authoritative and essential for believers.', 'textformat.abc', '[{"type": "strongs", "reference": "G3141", "description": "Strong''s number for ''witness''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 10, 10, 'kjv', 'He that believeth on the Son of God hath the witness in himself', 0, 63, 'connection', 'Belief and Inner Witness', 'This phrase connects to Romans 8:16, which states, ''The Spirit himself bears witness with our spirit that we are children of God.'' Both verses emphasize the internal affirmation of faith through the Spirit, suggesting that belief in Christ not only involves external acknowledgment but also an inner transformation and assurance.', 'link', '[{"type": "crossReference", "reference": "Romans 8:16", "description": "The Spirit''s witness in believers"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 10, 10, 'kjv', 'he that believeth not God hath made him a liar', 65, 111, 'theology', 'Implications of Unbelief', 'This passage suggests that rejecting God''s testimony about His Son equates to calling God a liar. Many scholars believe this highlights the seriousness of disbelief, as it not only denies the truth of God''s revelation but also fundamentally distorts one''s relationship with Him, emphasizing the necessity of faith for salvation.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 10, 10, 'kjv', 'the record that God gave of his Son', 138, 173, 'greek', 'Understanding ''record''', 'μαρτυρία (martyria, G3141) - testimony, witness. This term signifies the authoritative witness that God provides about His Son, reinforcing the reliability of the message of Christ''s identity and mission. The use of this word emphasizes the importance of accepting God''s testimony in matters of faith.', 'textformat.abc', '[{"type": "strongs", "reference": "G3141", "description": "Strong''s number for ''martyria''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 10, 10, 'kjv', 'He that believeth on the Son of God hath the witness in himself', 0, 63, 'question', 'Personal Witness of Faith', 'What does it mean to have ''the witness in himself''? Consider how your personal experiences of faith and the Holy Spirit''s presence affirm your belief in Christ. How does this inner witness shape your understanding of your identity as a believer?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 11, 11, 'kjv', 'this is the record, that God hath given to us eternal life, and this life is in his Son.', 4, 92, 'connection', 'Eternal Life in John', 'This phrase echoes John 3:16, where it states that God gave His only Son for eternal life. The connection highlights the consistent message of salvation through faith in Christ. Understanding this link deepens our appreciation of God''s grace and the centrality of Jesus in the gift of eternal life.', 'link', '[{"type": "crossReference", "reference": "John 3:16", "description": "For God so loved the world that He gave His only Son, that whoever believes in Him should not perish but have eternal life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 11, 11, 'kjv', 'eternal life', 50, 62, 'greek', 'Greek Term for Life', 'ζωή (zōē, G2222) - life, vitality. This term encompasses not just physical existence but a quality of life that is spiritually rich and eternal, reflecting a relationship with God through Christ. This emphasizes the transformative nature of the life believers receive.', 'textformat.abc', '[{"type": "strongs", "reference": "G2222", "description": "Strong''s number for the Greek term for life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 11, 11, 'kjv', 'given to us eternal life', 38, 62, 'theology', 'Gift of Eternal Life', 'This passage suggests that eternal life is a gift from God, not something earned through works. Many scholars believe this reflects the doctrine of grace, emphasizing that salvation is solely through faith in Jesus Christ, aligning with Ephesians 2:8-9.', 'sparkles', '[{"type": "commentary", "reference": "Ephesians 2:8-9", "description": "For by grace you have been saved through faith, and that not of yourselves; it is the gift of God."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 11, 11, 'kjv', 'this life is in his Son.', 68, 92, 'question', 'Source of Life', 'What does it mean that life is found ''in his Son''? How does this understanding impact your relationship with Christ and your view of eternal life? Reflecting on this can deepen your faith and understanding of the significance of being united with Jesus.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 12, 12, 'kjv', 'He that hath the Son hath life;', 0, 31, 'theology', 'Life in Christ', 'This passage emphasizes the fundamental Christian belief that eternal life is found exclusively in Jesus Christ. Many scholars believe this reflects the doctrine of salvation, asserting that relationship with the Son is essential for spiritual vitality. The contrast between possessing the Son and possessing life underscores the centrality of Christ in the believer''s experience of salvation.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 12, 12, 'kjv', 'and he that hath not the Son of God hath not life.', 32, 82, 'connection', 'Consequences of Rejection', 'This phrase connects to John 3:36, which states, ''Whoever believes in the Son has eternal life; whoever rejects the Son will not see life.'' The connection matters as it highlights the serious implications of rejecting Christ. Both verses collectively emphasize that life, both now and eternally, is contingent upon one''s relationship with Jesus.', 'link', '[{"type": "crossReference", "reference": "John 3:36", "description": "Related teaching on belief in the Son and eternal life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 12, 12, 'kjv', 'He that hath the Son hath life;', 0, 31, 'greek', 'Understanding ''Hath''', 'ἔχει (echei, G2192) - to have, hold, possess. This term implies an active possession, indicating that having the Son is not merely a passive state but an ongoing relationship that brings life. The use of this verb suggests a continuous, living connection with Christ that is essential for spiritual existence.', 'textformat.abc', '[{"type": "strongs", "reference": "G2192", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 12, 12, 'kjv', 'He that hath the Son hath life;', 0, 31, 'question', 'Life''s Source Inquiry', 'What does it mean for you personally to ''have the Son'' in your life? How does this relationship influence your daily living and perspective on eternal life?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 13, 13, 'kjv', 'that ye may know that ye have eternal life', 81, 123, 'theology', 'Assurance of Salvation', 'This passage emphasizes the believers'' assurance of eternal life through faith in Christ. Many scholars believe that the certainty of salvation is foundational to Christian faith, as indicated in Romans 8:16-17, where the Spirit confirms our identity as children of God. This assurance encourages believers to live confidently in their relationship with God, knowing that their salvation is secure.', 'sparkles', '[{"type": "crossReference", "reference": "Romans 8:16-17", "description": "Affirms the assurance of being children of God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 13, 13, 'kjv', 'that ye may believe on the name of the Son of God', 129, 178, 'connection', 'Faith in Christ', 'The phrase highlights the importance of faith in Jesus as central to receiving eternal life. This connects to John 20:31, where belief in Jesus is also presented as the purpose of the Gospel of John. Understanding this relationship between belief and eternal life reinforces the call to trust in Christ''s redemptive work.', 'link', '[{"type": "crossReference", "reference": "John 20:31", "description": "Purpose of the Gospel centered on believing in Jesus."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 13, 13, 'kjv', 'These things have I written unto you', 0, 36, 'question', 'Purpose of the Writing', 'What specific truths or experiences have led you to believe in the name of the Son of God? Reflecting on the reasons behind your faith can deepen your understanding of the assurance of eternal life articulated in this letter. How does recognizing the author''s intention shape your perspective on your own faith journey?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 13, 13, 'kjv', 'that ye may know that ye have eternal life', 81, 123, 'greek', 'Key Greek Term', 'γινώσκω (ginōskō, G1097) - to know, understand, perceive. This term emphasizes a deep, relational knowledge rather than mere intellectual assent. In the context of eternal life, it suggests that knowing Christ intimately leads to the assurance of salvation.', 'textformat.abc', '[{"type": "strongs", "reference": "G1097", "description": "Strong''s number for the Greek term meaning to know."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 14, 14, 'kjv', 'this is the confidence that we have in him', 4, 46, 'theology', 'Confidence in Prayer', 'This phrase underscores a foundational Christian belief regarding the assurance believers have in their relationship with God. Many scholars believe that this confidence stems from the understanding of God''s nature as loving and responsive to His children. It reflects the intimate relationship between believers and Christ, affirming that prayer is not merely a ritual, but a genuine communication with a trustworthy Father. This assurance encourages believers to engage in prayer boldly.', 'sparkles', '[{"type": "commentary", "reference": "1 John 3:21-22", "description": "These verses elaborate on the confidence we have before God in prayer."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 14, 14, 'kjv', 'if we ask any thing according to his will', 54, 95, 'connection', 'Alignment with God''s Will', 'This phrase connects with James 4:3, which states that asking with the wrong motives can hinder prayers being answered. Understanding God''s will is essential in prayer. This highlights the importance of seeking alignment with God''s desires rather than our own. The connection emphasizes that prayer is not just about our requests but aligning our hearts to God''s purpose.', 'link', '[{"type": "crossReference", "reference": "James 4:3", "description": "Warning against asking with wrong motives."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 14, 14, 'kjv', 'he heareth us', 97, 110, 'greek', 'Heareth - ἀκούω', 'ἀκούω (akouō, G191) - to hear, listen, comprehend. This term conveys not only the act of hearing but also the understanding and attentiveness of God to our prayers. It reassures believers that God is not distant; He is actively listening to our requests, thereby fostering a deeper trust in His responsiveness to our needs.', 'textformat.abc', '[{"type": "strongs", "reference": "G191", "description": "Strong''s number for the Greek term meaning ''to hear.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 14, 14, 'kjv', 'if we ask any thing according to his will', 54, 95, 'question', 'Understanding God''s Will', 'What does it mean for our requests to align with God''s will? How can we discern His will in our lives, especially when making personal petitions to Him in prayer? Reflecting on this question can deepen our understanding of prayer and our relationship with God.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 15, 15, 'kjv', 'if we know that he hear us', 4, 30, 'connection', 'Confidence in Prayer', 'This phrase resonates with John 14:13-14, where Jesus assures believers that whatever they ask in His name, He will do it. The connection emphasizes the assurance and confidence believers can have in their relationship with Christ, reinforcing the power of prayer aligned with His will.', 'link', '[{"type": "crossReference", "reference": "John 14:13-14", "description": "Jesus promises to answer prayers made in His name."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 15, 15, 'kjv', 'whatsoever we ask', 32, 49, 'greek', 'Understanding ''Ask''', 'αἰτέω (aiteō, G154) - to ask, request, or demand. This term indicates a strong sense of petitioning, suggesting that believers are encouraged to approach God with their needs confidently and expectantly, knowing He is attentive to their requests.', 'textformat.abc', '[{"type": "strongs", "reference": "G154", "description": "Greek term for ''ask'' used in this context."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 15, 15, 'kjv', 'we know that we have the petitions that we desired of him', 51, 108, 'theology', 'Assurance of Answered Prayer', 'This passage suggests a foundational aspect of Christian faith: the assurance that God hears and responds to prayer. Many scholars believe this reflects the doctrine of God''s faithfulness, indicating that when prayers align with His will, they are met with a positive response, fostering a deep trust in divine providence.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 15, 15, 'kjv', 'we know that we have the petitions that we desired of him', 51, 108, 'question', 'Reflecting on God''s Promises', 'What does it mean to you to ''know'' that God answers your prayers? How can this assurance influence your prayer life and confidence in His will?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 16, 16, 'kjv', 'If any man see his brother sin a sin which is not unto death, he shall ask, and he shall give him life for them that sin not unto death.', 0, 136, 'connection', 'Intercession for Others', 'This passage relates closely to James 5:16, which encourages believers to pray for one another so that they may be healed. The connection emphasizes the communal aspect of faith where believers are called to intercede for each other in times of sin and struggle, reflecting Christ''s role as our intercessor (Hebrews 7:25).', 'link', '[{"type": "crossReference", "reference": "James 5:16", "description": "Encouragement for prayer among believers."}, {"type": "crossReference", "reference": "Hebrews 7:25", "description": "Christ as our intercessor."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 16, 16, 'kjv', 'There is a sin unto death: I do not say that he shall pray for it.', 137, 203, 'theology', 'Understanding Sin Unto Death', 'Many scholars believe that the ''sin unto death'' refers to a conscious and deliberate rejection of God''s grace, where one turns away from faith. This aligns with the concept of blasphemy against the Holy Spirit, which is unforgivable (Mark 3:29). The passage suggests a limit to intercessory prayer, emphasizing the seriousness of persistent unrepentance.', 'sparkles', '[{"type": "commentary", "reference": "John MacArthur Commentary", "description": "Insight on the nature of sin unto death."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 16, 16, 'kjv', 'If any man see his brother sin a sin which is not unto death, he shall ask, and he shall give him life for them that sin not unto death.', 0, 136, 'question', 'Role of Community in Sin', 'What does this passage reveal about our responsibility to our fellow believers when they sin? How can we balance the act of confronting sin with the grace of interceding for their restoration?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 17, 17, 'kjv', 'All unrighteousness is sin: and there is a sin not unto death.', 0, 62, 'connection', 'Cross-reference to Romans', 'Romans 6:23 states, ''For the wages of sin is death; but the gift of God is eternal life through Jesus Christ our Lord.'' This connection emphasizes the severity of sin and its consequences, as well as the hope of salvation through Christ. In the context of 1 John 5:17, it highlights that while all unrighteousness is indeed sin, not all sin leads to spiritual death due to the redemptive work of Christ.', 'link', '[{"type": "crossReference", "reference": "Romans 6:23", "description": "Illustrates the consequences of sin in contrast to the hope of eternal life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 17, 17, 'kjv', 'All unrighteousness is sin:', 0, 27, 'theology', 'Nature of Sin', 'This passage underscores the comprehensive nature of sin, affirming that any act of unrighteousness is contrary to God''s will. Many scholars believe this verse reflects the biblical doctrine of original sin, which suggests that all human beings are inclined to sin due to the Fall. This understanding helps believers recognize their need for grace and the necessity of Christ''s atoning sacrifice.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the pervasive nature of sin and its implications for humanity."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(62, 5, 17, 17, 'kjv', 'there is a sin not unto death.', 32, 62, 'question', 'Understanding Sin''s Consequences', 'What does it mean for there to be a ''sin not unto death''? How can we discern which sins fall into this category? Reflecting on this can lead to deeper insights into God''s mercy and the distinction between sins that can be forgiven and those that reflect a willful rejection of God. This understanding may shape how we approach our own sins and those of others.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 17, 17, 'kjv', 'All unrighteousness is sin:', 0, 27, 'greek', 'Understanding ''Unrighteousness''', 'ἀδικία (adikia, G93) - unrighteousness, injustice. This term highlights not just the act of sin but the moral failure associated with it. The use of ''unrighteousness'' in 1 John 5:17 conveys the idea that every form of injustice is fundamentally against God''s character, reinforcing the need for believers to pursue righteousness in their lives.', 'textformat.abc', '[{"type": "strongs", "reference": "G93", "description": "Strong''s number for the Greek term for unrighteousness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(62, 5, 18, 18, 'kjv', 'whosoever is born of God sinneth not', 13, 49, 'theology', 'Nature of the New Birth', 'This phrase emphasizes the transformative nature of being ''born of God.'' Many scholars believe that this highlights the concept of regeneration, which is a fundamental Christian doctrine asserting that through faith in Christ, believers are made new and empowered to live righteously. This aligns with teachings in John 3:3, where Jesus speaks of being ''born again'' as essential to see the Kingdom of God.', 'person.2.fill', '[{"type": "commentary", "reference": "John 3:3", "description": "Jesus'' teaching on being born again."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');