INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(2, 25, 36, 36, 'kjv', 'one beaten work of pure gold', 69, 97, 'theology', 'Symbolism of Unity', 'This phrase suggests a theological concept of unity within diversity, reflecting the nature of the Body of Christ (1 Corinthians 12:12-27). Just as the candlestick is crafted as a single piece, believers are called to be united in purpose and spirit, emphasizing the importance of harmony within the church.', 'person.2.fill', '[{"type": "crossReference", "reference": "1 Corinthians 12:12-27", "description": "Paul''s teaching on the unity of the Body of Christ."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 25, 36, 36, 'kjv', 'Their knops and their branches shall be of the same', 0, 51, 'question', 'Understanding Unity', 'What does it mean for the knops and branches to be of the same material? How can this principle of unity apply to our relationships within the church community today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 36, 36, 'kjv', 'pure gold', 88, 97, 'greek', 'Significance of Gold', 'זָהָב (zahav, H2091) - gold, a precious metal symbolizing purity and divinity in Scripture. Its use in the Tabernacle signifies the holiness of God''s presence and the value of the worship offered to Him.', 'textformat.abc', '[{"type": "strongs", "reference": "H2091", "description": "Strong''s number for the Hebrew word for gold."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 37, 37, 'kjv', 'the seven lamps thereof', 20, 43, 'connection', 'Symbolism of Seven', 'The reference to ''seven lamps'' can be cross-referenced with Revelation 1:12, where the seven lampstands symbolize the seven churches. This connection emphasizes the idea of divine illumination and guidance for God''s people, paralleling the role of the lamps in the Tabernacle as sources of light in the presence of God.', 'link', '[{"type": "crossReference", "reference": "Revelation 1:12", "description": "The seven lampstands represent the churches in John''s vision."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 37, 37, 'kjv', 'that they may give light', 85, 109, 'theology', 'Light as Divine Presence', 'Many scholars believe that the act of lighting the lamps symbolizes God''s presence among His people. In biblical theology, light often represents purity, truth, and divine revelation, suggesting that the lamps serve as a means for the Israelites to connect with God''s holiness.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 25, 37, 37, 'kjv', 'the seven lamps thereof', 20, 43, 'greek', 'Lamp in Greek', 'λύχνος (luchnos, G3088) - lamp, light. This term emphasizes the functional role of the lamps, which is to provide illumination. The use of this specific word highlights the importance of light in the context of worship and the sacred space of the Tabernacle.', 'textformat.abc', '[{"type": "strongs", "reference": "G3088", "description": "Strong''s number for the Greek word for lamp."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 37, 37, 'kjv', 'that they may give light', 85, 109, 'question', 'Purpose of Light', 'What does it mean for us today that God desires to provide light in our lives? How can we reflect this divine light to others in our community and the world?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 38, 38, 'kjv', 'And the tongs thereof, and the snuffdishes thereof, shall be of pure gold.', 0, 74, 'connection', 'Gold''s Symbolism in Scripture', 'The mention of ''pure gold'' highlights the sacredness and value associated with items used in worship. This connects to 1 Peter 1:7, which speaks of faith being more precious than gold. Both passages emphasize the idea that spiritual offerings and tools for worship should reflect the highest standards of purity and value in the sight of God.', 'link', '[{"type": "crossReference", "reference": "1 Peter 1:7", "description": "Discusses the value of faith compared to gold."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 38, 38, 'kjv', 'And the tongs thereof, and the snuffdishes thereof, shall be of pure gold.', 0, 74, 'greek', 'Understanding ''Gold''', 'זָהָב (zahav, H2091) - gold, a precious metal often associated with divinity and purity in ancient texts. Its use in the Tabernacle signifies the holiness of the worship space and the seriousness of the rituals performed therein.', 'textformat.abc', '[{"type": "strongs", "reference": "H2091", "description": "Strong''s number for the Hebrew word for gold."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 38, 38, 'kjv', 'And the tongs thereof, and the snuffdishes thereof, shall be of pure gold.', 0, 74, 'theology', 'The Role of Worship Tools', 'This passage suggests that the physical tools used in worship, such as tongs and snuffdishes, are significant in facilitating a holy experience. Many scholars believe that the quality of these items reflects the reverence with which we approach God, indicating that our offerings, both material and spiritual, should be of the highest quality.', 'sparkles', '[{"type": "commentary", "reference": "Exodus 25:38", "description": "Discusses the significance of materials in sacred worship."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 25, 38, 38, 'kjv', 'And the tongs thereof, and the snuffdishes thereof, shall be of pure gold.', 0, 74, 'question', 'Reflection on Worship Quality', 'What does the requirement for ''pure gold'' in worship utensils teach us about how we should approach our own acts of worship today? Do we offer God our best, or do we give Him what is left over?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 39, 39, 'kjv', 'Of a talent of pure gold shall he make it, with all these vessels.', 0, 66, 'connection', 'Value of Gold in Worship', 'The mention of ''a talent of pure gold'' connects to 1 Chronicles 29:4, where King David prepared a talent of gold for the temple. This highlights the importance of offering valuable materials for worship, emphasizing that God deserves the best from His people.', 'link', '[{"type": "crossReference", "reference": "1 Chronicles 29:4", "description": "David''s offering for the temple construction."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 39, 39, 'kjv', 'Of a talent of pure gold shall he make it, with all these vessels.', 0, 66, 'theology', 'God''s Standards in Worship', 'This verse suggests that God has specific standards regarding what is used in worship. Many scholars believe that the use of pure gold symbolizes purity and holiness, reflecting God''s character and the reverence due to Him in worship practices.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 25, 39, 39, 'kjv', 'Of a talent of pure gold shall he make it, with all these vessels.', 0, 66, 'question', 'What does ''pure gold'' signify?', 'In what ways does the requirement of ''pure gold'' challenge our understanding of what we offer to God? How can we ensure that our offerings—be they material or spiritual—reflect His holiness and purity?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 39, 39, 'kjv', 'Of a talent of pure gold shall he make it, with all these vessels.', 0, 66, 'greek', 'Understanding ''Talent''', 'טַלֶּנְט (talent, H3603) - a unit of weight and value, often used to indicate a significant amount. This emphasizes the monetary and symbolic weight of the offerings made to God.', 'textformat.abc', '[{"type": "strongs", "reference": "H3603", "description": "Strong''s number for talent in Hebrew."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 40, 40, 'kjv', 'after their pattern, which was shewed thee in the mount.', 29, 85, 'connection', 'Heavenly Blueprint', 'This idea of following a divine pattern connects to Hebrews 8:5, which states that the earthly tabernacle was a copy and shadow of what is in heaven. The meticulous instructions given to Moses emphasize the importance of obedience to God''s design, reflecting His holiness and order. This connection underscores the significance of worship as being aligned with God''s revealed will.', 'link', '[{"type": "crossReference", "reference": "Hebrews 8:5", "description": "Highlights the heavenly model of the tabernacle."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 40, 40, 'kjv', 'make them after their pattern, which was shewed thee in the mount.', 19, 85, 'greek', 'Pattern in Greek', 'ταύτης (tautēs, G3778) - this, of this kind. The term emphasizes the specific nature of the instructions given. It indicates that the designs are not arbitrary but divinely ordained. This underscores the importance of adherence to God''s commands in worship and service.', 'textformat.abc', '[{"type": "strongs", "reference": "G3778", "description": "Greek term for ''this'', indicating specificity."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 25, 40, 40, 'kjv', 'And look that thou make them after their pattern, which was shewed thee in the mount.', 0, 85, 'theology', 'God''s Holiness and Order', 'Many scholars believe that this passage reflects the principle of divine order in worship. The specific instructions highlight God''s desire for His people to approach Him in a manner that reflects His holiness. It suggests that worship is not only about the heart but also about aligning with God’s prescribed way, which is a key aspect of the covenant relationship.', 'sparkles', '[{"type": "commentary", "reference": "Expositors Bible Commentary", "description": "Discusses the importance of divine order in worship."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 25, 40, 40, 'kjv', 'that thou make them after their pattern', 9, 48, 'question', 'Understanding Divine Patterns', 'What does it mean for us today to follow God''s pattern in our worship and lives? In what ways can we ensure that our expressions of faith align with His revealed will, as exemplified in Scripture?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 1, 1, 'kjv', 'fine twined linen, and blue, and purple, and scarlet', 61, 113, 'connection', 'Symbolism of Colors', 'The colors mentioned here—blue, purple, and scarlet—are rich in symbolism throughout Scripture. For instance, blue often represents heaven (Numbers 15:38), purple is associated with royalty (John 19:2), and scarlet can signify sin and redemption (Isaiah 1:18). This connection reflects the divine nature of the tabernacle as a place where heaven meets earth, suggesting that it serves a dual purpose of worship and representation of Christ''s kingship and redemptive work.', 'link', '[{"type": "crossReference", "reference": "Numbers 15:38", "description": "The blue thread in the tassels symbolizes God''s commandments."}, {"type": "crossReference", "reference": "Isaiah 1:18", "description": "Scarlet represents sin but also God''s promise of cleansing."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 1, 1, 'kjv', 'with cherubims of cunning work shalt thou make them', 115, 166, 'theology', 'Cherubim as Guardians', 'Many scholars believe that cherubim represent the guardianship of God''s holiness. Their presence in the tabernacle emphasizes the sacredness of the space where God dwells among His people. This reflects the theological concept that access to God requires reverence and acknowledgment of His holiness, as seen in Ezekiel 10, where cherubim are depicted as attendants to God''s glory. The intricate craftsmanship denotes the importance of beauty and skill in worship.', 'sparkles', '[{"type": "commentary", "reference": "Ezekiel 10", "description": "Cherubim are depicted as guardians of God''s glory."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 1, 1, 'kjv', 'thou shalt make the tabernacle', 9, 39, 'question', 'Purpose of the Tabernacle', 'What does it mean to us today that God instructed the making of the tabernacle? This command demonstrates God''s desire to dwell among His people. How does this understanding influence our view of community worship and personal devotion? In what ways can we create spaces in our lives that reflect God''s presence and holiness?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 2, 2, 'kjv', 'The length of one curtain shall be eight and twenty cubits', 0, 58, 'connection', 'Dimensions in Scripture', 'The specific measurements of the curtains mirror the detailed construction specifications in 1 Kings 6:2 for Solomon''s temple, emphasizing God''s desire for beauty and order in worship spaces. This connection illustrates continuity in God''s instructions for sacred spaces throughout Israel''s history.', 'link', '[{"type": "crossReference", "reference": "1 Kings 6:2", "description": "Details the dimensions of Solomon''s temple, showing a pattern in God''s design."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 2, 2, 'kjv', 'the breadth of one curtain four cubits', 64, 102, 'theology', 'Symbolism of Dimensions', 'The precise measurements of the tabernacle curtains suggest that God is a God of order and detail. Many scholars believe this reflects the importance of structure in worship and the need for believers to approach God with reverence and intentionality, as seen in the broader context of biblical worship.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 2, 2, 'kjv', 'every one of the curtains shall have one measure', 108, 156, 'question', 'Uniformity in Worship', 'What does the uniformity of the curtains signify about God''s expectations for His people in worship? How does this principle of unity apply to the diverse body of Christ today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 2, 2, 'kjv', 'The length of one curtain shall be eight and twenty cubits', 0, 58, 'greek', 'Cultural Context of Measurements', 'In Hebrew, ''cubit'' translates to ''ammah'' (H0520), which was a common measurement in ancient Near Eastern cultures. Understanding this term helps shed light on the practicality of the tabernacle''s design within its historical context, indicating that God communicated with His people in relatable terms.', 'textformat.abc', '[{"type": "strongs", "reference": "H0520", "description": "The Hebrew word for cubit, a standard measurement of length."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 3, 3, 'kjv', 'The five curtains shall be coupled together one to another;', 0, 59, 'connection', 'Unity in Worship', 'This concept of coupling the curtains together reflects the unity necessary in worship. In Ephesians 4:3, Paul emphasizes maintaining the unity of the Spirit in the bond of peace. The coupling of the curtains symbolizes how the community of believers is to be united in purpose and worship, just as the components of the tabernacle are combined to create a sacred space.', 'link', '[{"type": "crossReference", "reference": "Ephesians 4:3", "description": "Emphasis on unity within the church"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 3, 3, 'kjv', 'and other five curtains shall be coupled one to another.', 60, 116, 'theology', 'Divine Order in Construction', 'Many scholars believe that the specific instructions for the tabernacle''s construction illustrate God''s desire for order and structure in worship. This passage suggests that God values both beauty and functionality in the design of sacred spaces, which can be seen as a reflection of His character and holiness.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 3, 3, 'kjv', 'The five curtains shall be coupled together one to another;', 0, 59, 'question', 'Reflecting on Community', 'What does it mean for us today to be ''coupled together'' in faith and worship? How can we actively support and connect with one another in our spiritual journeys, just as the curtains are bound together to form the tabernacle?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 4, 4, 'kjv', 'loops of blue', 20, 33, 'connection', 'Symbolism of Blue', 'The use of blue loops can be cross-referenced with Numbers 15:38, where Israelites are instructed to attach blue tassels to their garments. This connection highlights the significance of blue as a color representing heavenly authority and divine commandments, reinforcing the sacred nature of the tabernacle and its furnishings.', 'link', '[{"type": "crossReference", "reference": "Numbers 15:38", "description": "Instructions for blue tassels to remember God''s commandments."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 4, 4, 'kjv', 'from the selvedge', 67, 84, 'theology', 'Significance of Selvedge', 'Many scholars believe that the mention of ''selvedge'' signifies the importance of the edges or boundaries in God''s design. This concept can relate to the idea of holiness and separation, indicating that the tabernacle is set apart for divine purposes and should not be treated casually.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 4, 4, 'kjv', 'coupling of the second', 180, 202, 'question', 'Understanding Divine Connection', 'What does it mean for us to be ''coupled'' with one another in faith as the curtains are coupled in the tabernacle? How does this imagery of connection influence our understanding of community within the body of Christ?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 5, 5, 'kjv', 'Fifty loops shalt thou make in the one curtain, and fifty loops shalt thou make in the edge of the curtain that is in the coupling of the second; that the loops may take hold one of another.', 0, 190, 'connection', 'Symbolism of Fifty', 'The use of ''fifty'' in this context can be connected to the concept of Jubilee in Leviticus 25:10, which occurs every fifty years and represents liberation and restoration. This connection highlights the significance of the tabernacle as a place of God''s presence and a symbol of freedom for His people, reinforcing the idea that the physical union of the curtains reflects a deeper spiritual unity among the Israelites.', 'link', '[{"type": "crossReference", "reference": "Leviticus 25:10", "description": "The Jubilee year signifies freedom and restoration."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 5, 5, 'kjv', 'Fifty loops shalt thou make in the one curtain, and fifty loops shalt thou make in the edge of the curtain that is in the coupling of the second; that the loops may take hold one of another.', 0, 190, 'theology', 'Unity in Community', 'This passage suggests that the meticulous design of the tabernacle emphasizes the importance of unity among God''s people. The fifty loops that connect the curtains can be seen as a metaphor for the interconnectedness of the community of faith. Many scholars believe that this reflects the New Testament teaching found in 1 Corinthians 12:12-27, where the body of Christ is described as one unit made up of many parts, each essential for the whole.', 'sparkles', '[{"type": "crossReference", "reference": "1 Corinthians 12:12-27", "description": "The body of Christ illustrates unity in diversity."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 5, 5, 'kjv', 'that the loops may take hold one of another.', 146, 190, 'question', 'Reflection on Connection', 'In what ways do the ''loops'' that connect the curtains parallel our relationships within the Church today? How might this imagery of connection challenge us to foster deeper bonds among fellow believers?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 6, 6, 'kjv', 'fifty taches of gold', 20, 40, 'connection', 'Gold in Scripture', 'The use of gold in the tabernacle, specifically in the taches, connects to other instances of gold representing divinity and purity in Scripture. For example, in Revelation 21:18, the New Jerusalem is described as having streets of pure gold. This continuity emphasizes the sacredness of God''s dwelling places and the value of His presence among His people.', 'link', '[{"type": "crossReference", "reference": "Revelation 21:18", "description": "Describes the New Jerusalem with streets of gold, indicating God''s holiness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 6, 6, 'kjv', 'couple the curtains together', 46, 74, 'theology', 'Unity in Worship', 'This directive to couple the curtains together reflects the theological concept of unity among God''s people. Many scholars believe this foreshadows the unity found in Christ as expressed in Ephesians 4:3, where believers are urged to maintain the unity of the Spirit. The physical coupling of the curtains serves as a symbol of spiritual cohesion within the community of faith.', 'sparkles', '[{"type": "commentary", "reference": "Ephesians 4:3", "description": "Encourages believers to maintain unity in the Spirit."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 6, 6, 'kjv', 'it shall be one tabernacle', 96, 122, 'question', 'Meaning of ''One''', 'What does it mean for the tabernacle to be ''one''? In light of God''s intention for His presence to dwell among His people, how does this concept of unity inform our understanding of the Church as the body of Christ today? How does recognizing ourselves as a part of this ''one tabernacle'' challenge us to pursue harmony and fellowship?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 7, 7, 'kjv', 'curtains of goats’ hair', 20, 43, 'connection', 'Goat''s Hair Significance', 'The use of goats'' hair in the tabernacle covering connects to Leviticus 16:10, where a goat is used as a scapegoat for the atonement of Israel''s sins. This symbolizes the sacrificial system and foreshadows Christ''s ultimate sacrifice, establishing a deeper understanding of God''s provision for atonement through Christ.', 'link', '[{"type": "crossReference", "reference": "Leviticus 16:10", "description": "The scapegoat imagery highlights the significance of sacrifice in the atonement process."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 7, 7, 'kjv', 'upon the tabernacle', 61, 80, 'theology', 'God''s Dwelling Place', 'This verse suggests that the tabernacle serves as a physical manifestation of God''s presence among His people. Many scholars believe that the structure and materials used in the tabernacle reflect God''s holiness and the need for separation from sin, emphasizing the importance of approaching God with reverence.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Henry discusses the significance of the tabernacle as a holy space for divine encounter."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 7, 7, 'kjv', 'eleven curtains shalt thou make', 82, 113, 'question', 'Why Eleven Curtains?', 'What might the choice of eleven curtains signify in the context of Israel''s journey? Could it represent the completeness of God''s provision for His people during their wilderness experience? Reflecting on this could provide insight into how God meets our needs even in transitional seasons.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 8, 8, 'kjv', 'The length of one curtain shall be thirty cubits, and the breadth of one curtain four cubits: and the eleven curtains shall be all of one measure.', 0, 146, 'connection', 'Measurement in Scripture', 'The specified measurements of the curtains in Exodus 26:8 resonate with the dimensions given for the Holy City in Revelation 21:15-17. Both passages highlight divine precision in design, underscoring that God values order and beauty in His dwelling places. This connection emphasizes the continuity of God''s plan from the Tabernacle to the New Jerusalem.', 'link', '[{"type": "crossReference", "reference": "Revelation 21:15-17", "description": "Revelation discusses the dimensions of the New Jerusalem, paralleling the divine measurements in Exodus."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 8, 8, 'kjv', 'The length of one curtain shall be thirty cubits, and the breadth of one curtain four cubits: and the eleven curtains shall be all of one measure.', 0, 146, 'theology', 'Unity in Worship', 'The uniformity in the measurements of the curtains suggests a theological principle of unity in worship. Many scholars believe this reflects the idea that God desires His people to come together in a unified manner when approaching Him. The consistent dimensions symbolize the shared identity and purpose of the community of Israel as they worship in the Tabernacle.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary on the Whole Bible", "description": "Henry discusses the significance of unity in worship as reflected in the structure of the Tabernacle."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 8, 8, 'kjv', 'The length of one curtain shall be thirty cubits, and the breadth of one curtain four cubits: and the eleven curtains shall be all of one measure.', 0, 146, 'question', 'Reflecting on Divine Design', 'What does the specific measurement of the curtains reveal about God''s attention to detail in His designs? In what ways can we apply the principle of careful planning in our own lives as we seek to honor God?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 8, 8, 'kjv', 'The length of one curtain shall be thirty cubits, and the breadth of one curtain four cubits: and the eleven curtains shall be all of one measure.', 0, 146, 'greek', 'Significance of ''Measure''', 'מִּשְׁקָל (mishkal, H4975) - measure, weight, standard. This Hebrew term emphasizes the importance of measurement in the construction of sacred spaces, indicating that God''s dwelling is established with precision and intentionality.', 'textformat.abc', '[{"type": "strongs", "reference": "H4975", "description": "Strong''s number for the term ''measure'' used in the context of construction."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 9, 9, 'kjv', 'five curtains by themselves', 22, 49, 'connection', 'Five as a Symbol', 'The number five often symbolizes grace in biblical literature. For instance, in 1 Corinthians 15:10, Paul reflects on God''s grace in his life. This coupling of five curtains could signify the grace that surrounds the tabernacle, representing God''s presence among His people.', 'link', '[{"type": "crossReference", "reference": "1 Corinthians 15:10", "description": "Paul''s acknowledgment of grace in his life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 9, 9, 'kjv', 'six curtains by themselves', 55, 81, 'theology', 'Theological Significance of Six', 'Many scholars believe that the number six represents imperfection or incompleteness, as it falls short of seven, which denotes divine perfection. This distinction may highlight the need for divine intervention to complete the tabernacle''s function, suggesting that human efforts alone are insufficient.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 26, 9, 9, 'kjv', 'double the sixth curtain', 93, 117, 'greek', 'Doubling in Hebrew Context', 'The Hebrew word for ''double'' here is ''כָּפַר'' (kaphar, H3722) which can imply covering or atoning. This may suggest a deeper layer where the act of doubling the sixth curtain could symbolize a covering for sin, foreshadowing the atonement found in Christ.', 'textformat.abc', '[{"type": "strongs", "reference": "H3722", "description": "Strong''s number for ''\u05db\u05b8\u05bc\u05e4\u05b7\u05e8'' used in contexts of covering and atoning."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 9, 9, 'kjv', 'forefront of the tabernacle', 125, 152, 'question', 'Significance of the Forefront', 'Why is the sixth curtain specifically placed at the forefront of the tabernacle? What does this reveal about God''s desire for His presence to be visible and accessible to His people? Reflect on how the visibility of God''s dwelling encourages a deeper relationship with Him.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 26, 10, 10, 'kjv', 'fifty loops on the edge', 20, 43, 'connection', 'Symbolism of Fifty', 'The number fifty often signifies freedom and liberation in biblical contexts, as seen in the Year of Jubilee (Leviticus 25:10). The loops on the curtains may symbolize the means by which God''s presence is established among His people, representing freedom from sin and the establishment of a new covenant. This connection enriches our understanding of God''s desire for intimacy with His people.', 'link', '[{"type": "crossReference", "reference": "Leviticus 25:10", "description": "Describes the Year of Jubilee, a significant biblical event associated with freedom."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');