INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(2, 28, 21, 21, 'kjv', 'the names of the children of Israel, twelve, according to their names', 29, 98, 'connection', 'Tribes of Israel Connection', 'This phrase echoes the covenantal relationship established with the twelve tribes of Israel throughout Scripture. For example, Genesis 49:28 lists the twelve tribes and their blessings, highlighting God''s promise and identity tied to each tribe. This connection emphasizes the continuity of God''s plan and His personal relationship with His chosen people.', 'link', '[{"type": "crossReference", "reference": "Genesis 49:28", "description": "List of the twelve tribes and their blessings."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 21, 21, 'kjv', 'like the engravings of a signet', 100, 131, 'theology', 'Signet''s Significance', 'The imagery of a signet is significant in biblical theology, as it represents authority, identity, and authenticity. Many scholars believe this signifies that each tribe is uniquely recognized and has a distinct role within God''s covenantal community. The signet also symbolizes God''s sovereignty over Israel, marking them as His chosen people.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the symbolism of the signet in relation to God''s covenant."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 21, 21, 'kjv', 'every one with his name shall they be according to the twelve tribes', 133, 201, 'question', 'Names and Identity Reflection', 'What does it mean for us today to bear the names of God’s people? How does recognizing our identity in Christ as part of His chosen people shape our understanding of community and purpose in the Church?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 22, 22, 'kjv', 'chains at the ends of wreathen work of pure gold', 41, 89, 'connection', 'Connection to the Tabernacle', 'The mention of ''wreathen work of pure gold'' in Exodus 28:22 connects to the intricate craftsmanship described in Exodus 25:31-40 regarding the Menorah. Both emphasize the importance of beauty and purity in the sacred items of worship, reflecting God''s glory and the care required in His service.', 'link', '[{"type": "crossReference", "reference": "Exodus 25:31-40", "description": "Describes the detailed craftsmanship of the Menorah, highlighting artistic elements in the Tabernacle."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 22, 22, 'kjv', 'upon the breastplate chains', 20, 47, 'theology', 'Symbolism of the Breastplate', 'The breastplate worn by the high priest symbolizes the intercession of the people before God. Many scholars believe that the chains represent the connection between the priest and the tribes of Israel, reinforcing the notion that the high priest carries the burdens and identities of the people into the presence of God.', 'person.2.fill', '[{"type": "commentary", "reference": "Exodus 28:15-30", "description": "Discusses the significance of the high priest''s breastplate and its role in intercession."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 22, 22, 'kjv', 'wreathen work of pure gold', 63, 89, 'greek', 'Gold''s Significance', 'χρυσός (chrysos, G5550) - gold, symbolizes divinity, purity, and value. In the context of the breastplate''s chains, the use of pure gold signifies the holiness of the priestly office and the sacredness of the worship practices, indicating that what is offered to God must be of the highest quality.', 'textformat.abc', '[{"type": "strongs", "reference": "G5550", "description": "Strong''s number for gold, indicating its spiritual significance in biblical texts."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 22, 22, 'kjv', 'chains at the ends', 41, 59, 'question', 'Meaning of Connection', 'What does it mean for the high priest to have chains at the ends of the breastplate? How does this imagery of connection influence our understanding of Christ as our intercessor today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 23, 23, 'kjv', 'two rings of gold', 41, 58, 'connection', 'Symbolism of Gold', 'Gold is often associated with divinity and purity in Scripture. In Exodus 25:17, the mercy seat is made of pure gold, emphasizing its sacred nature. The use of gold rings here suggests the importance of the breastplate as a holy object, meant to bear the names of the tribes of Israel before God. This connection highlights the significance of the priestly garments in mediating between God and His people.', 'link', '[{"type": "crossReference", "reference": "Exodus 25:17", "description": "Gold''s significance in the construction of sacred objects"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 23, 23, 'kjv', 'two rings of gold', 41, 58, 'greek', 'Gold in Scripture', 'The Hebrew word for gold here is ''זָהָב'' (zahav, H2091) - gold, a precious metal used extensively for items of value and significance in the temple and priestly garments, symbolizing divine glory and holiness.', 'textformat.abc', '[{"type": "strongs", "reference": "H2091", "description": "Strong''s number for the Hebrew word for gold"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 23, 23, 'kjv', 'two rings', 41, 50, 'theology', 'Priestly Mediation', 'This passage suggests a theological understanding of the priest''s role in Israel. The two rings enable the breastplate to be securely attached, signifying the priest''s duty to carry the names of the tribes before God, thus acting as a mediator. Many scholars believe this foreshadows the ultimate mediation of Christ, who bears our names before the Father (Hebrews 7:25).', 'figure.mind.and.body', '[{"type": "crossReference", "reference": "Hebrews 7:25", "description": "Christ''s role as our mediator"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 23, 23, 'kjv', 'two rings of gold', 41, 58, 'question', 'Significance of the Rings', 'What might the number ''two'' signify in this context? Considering the dual roles of the priest as both representative of the people and servant of God, how does this duality reflect on our understanding of intercession in prayer?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 24, 24, 'kjv', 'And thou shalt put the two wreathen chains of gold in the two rings which are on the ends of the breastplate.', 0, 109, 'connection', 'High Priest''s Garments', 'This verse connects to Exodus 39:15-21, which describes the assembly of the high priest''s garments, emphasizing the significance of the breastplate and its attachments. The chains and rings symbolize the connection between God and His people, as the high priest bears the names of the tribes on his heart, representing their collective identity before God.', 'link', '[{"type": "crossReference", "reference": "Exodus 39:15-21", "description": "Further details on the high priest''s garments and their symbolism."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 24, 24, 'kjv', 'in the two rings which are on the ends of the breastplate.', 51, 109, 'theology', 'Mediation and Representation', 'Many scholars believe this passage underscores the theological concept of mediation. The high priest, adorned with the breastplate and its chains, serves as a mediator between God and the Israelites. This foreshadows the ultimate mediation of Christ, who intercedes for humanity before the Father.', 'sparkles', '[{"type": "commentary", "reference": "Hebrews 4:14-16", "description": "Discusses Christ as our high priest and mediator."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 24, 24, 'kjv', 'the two rings which are on the ends of the breastplate.', 54, 109, 'question', 'Role of the Breastplate', 'What does the presence of the rings signify about the high priest''s responsibility in representing the tribes of Israel? How does this understanding of representation deepen your grasp of Christ''s role as our intercessor?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 25, 25, 'kjv', 'the other two ends of the two wreathen chains', 4, 49, 'connection', 'Connection to Levitical Garments', 'This phrase recalls the intricate design of the priestly garments described in Exodus 28. The use of wreathen chains symbolizes the connection between God and His people through the priesthood. This recalls how the Levites were set apart for service, a theme also explored in Leviticus 8:7-9, where Aaron and his sons are consecrated as priests. Understanding these connections deepens our appreciation for the role of the priesthood in mediating between God and humanity.', 'link', '[{"type": "crossReference", "reference": "Leviticus 8:7-9", "description": "Describes the consecration of Aaron and his sons as priests."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 25, 25, 'kjv', 'and put them on the shoulderpieces of the ephod', 87, 134, 'theology', 'Priestly Representation', 'The reference to the shoulderpieces of the ephod signifies the weight of responsibility and representation that the high priest bears for the people of Israel. Many scholars believe this symbolizes Christ''s ultimate role as our High Priest, who bears our burdens and intercedes on our behalf (Hebrews 4:14-16). This passage suggests that just as the priest carries the names of the tribes on his shoulders, Christ carries us in His heart and before the Father.', 'person.2.fill', '[{"type": "crossReference", "reference": "Hebrews 4:14-16", "description": "Describes Christ as our High Priest."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 25, 25, 'kjv', 'the two ouches', 71, 85, 'greek', 'Understanding ''ouches''', 'אֶשְׁתָּה (esh''tah, H7857) - ouches, clasps. This term refers to the clasps that secure the ephod and symbolize the binding of the priest to his sacred duties. The term implies a connection that is both functional and spiritual, as the high priest is bound to serve God and the people. Recognizing the significance of these ouches enhances our understanding of the priestly role within the covenant community.', 'textformat.abc', '[{"type": "strongs", "reference": "H7857", "description": "Strong''s number for the Hebrew word for ouches."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 25, 25, 'kjv', 'put them on the shoulderpieces of the ephod', 91, 134, 'question', 'Reflect on Priestly Role', 'What does it mean for us today that the high priest carried the names of the tribes on his shoulders? How does this imagery shape our understanding of Christ''s intercessory role in our lives? Reflecting on this can deepen our appreciation for the personal and communal aspects of faith.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 26, 26, 'kjv', 'two rings of gold', 20, 37, 'connection', 'Connection to Offerings', 'The mention of ''two rings of gold'' recalls the use of gold in offerings for the Tabernacle, as seen in Exodus 25:3-7. The gold signifies not only value but also the glory of God. This connection emphasizes the importance of precious materials in creating items for divine service, reflecting our own offerings to God in worship.', 'link', '[{"type": "crossReference", "reference": "Exodus 25:3-7", "description": "Details the materials for the Tabernacle."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 26, 26, 'kjv', 'the breastplate in the border thereof', 84, 121, 'theology', 'Significance of the Breastplate', 'The breastplate, worn by the high priest, symbolizes the representation of the people before God. It contained the names of the twelve tribes, highlighting that God is intimately concerned with His people. Many scholars believe this reflects the concept of intercession, as the high priest carried their burdens into the presence of God.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the symbolism of the high priest''s garments."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 26, 26, 'kjv', 'upon the two ends of the breastplate', 63, 99, 'question', 'Purpose of Placement', 'Why are the rings placed specifically ''upon the two ends of the breastplate''? What does this arrangement teach us about the connection between priestly duties and the importance of being anchored in God''s presence? Reflecting on this may enhance our understanding of how we are to approach God with reverence and intentionality.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 27, 27, 'kjv', 'two other rings of gold', 4, 27, 'connection', 'Symbolism of Gold', 'Gold often symbolizes divinity and purity in Scripture. In Revelation 21:18-21, the streets of the New Jerusalem are made of pure gold, highlighting its heavenly significance. This connection emphasizes the sacredness of the priestly garments and their function in approaching God.', 'link', '[{"type": "crossReference", "reference": "Revelation 21:18-21", "description": "Describes the divine nature and purity of gold in the heavenly context."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 27, 27, 'kjv', 'the curious girdle of the ephod', 182, 213, 'theology', 'Significance of the Girdle', 'The ''curious girdle'' is often seen as a representation of the strength and support of the priesthood. Many scholars believe it symbolizes the binding nature of the covenant between God and His people, emphasizing the importance of being firmly anchored in faith and duty.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 27, 27, 'kjv', 'the two sides of the ephod underneath', 67, 104, 'greek', 'Ephod in Hebrew', 'אֵפוֹד (ephod, H646) - a priestly garment worn by the high priest, symbolizing the mediatorial role in approaching God. The ephod serves as a reminder of the high priest''s responsibility to intercede for the people.', 'character.book.closed', '[{"type": "strongs", "reference": "H646", "description": "Strong''s number for the term ''ephod''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 27, 27, 'kjv', 'two other rings of gold', 4, 27, 'question', 'Purpose of the Rings', 'What does the presence of these ''two other rings'' reveal about God''s desire for order and structure in worship? How can we apply this understanding of divine organization to our own lives and community practices?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 28, 28, 'kjv', 'bind the breastplate by the rings thereof', 15, 56, 'connection', 'Binding and Commitment', 'The act of binding the breastplate to the ephod reflects the deeper commitment of the High Priest to carry the burdens of the people before God. This is echoed in 1 Peter 5:7, where believers are encouraged to cast their cares on God, who cares for them. Just as the High Priest was physically attached to the breastplate, believers are spiritually connected to Christ, who bears their burdens.', 'link', '[{"type": "crossReference", "reference": "1 Peter 5:7", "description": "This verse emphasizes God''s care for His people."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 28, 28, 'kjv', 'with a lace of blue', 85, 104, 'theology', 'Symbolism of Blue', 'The use of blue in the binding lace is significant, often associated with heavenly realities and divine revelation. Many scholars believe that blue symbolizes the connection between the earthly and the divine, serving as a reminder of God''s covenant and presence among His people. This aligns with the broader theme in Scripture of the sacredness of God''s instructions and the importance of obedience.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 28, 28, 'kjv', 'that the breastplate be not loosed from the ephod', 164, 213, 'question', 'Stability in Our Faith', 'What does it mean for our spiritual lives if we are not ''loosed'' from our commitment to Christ? This passage prompts reflection on the importance of remaining steadfast in our faith and how we can ensure that our relationship with God is secure and enduring, much like the binding of the breastplate to the ephod.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 29, 29, 'kjv', 'the names of the children of Israel', 21, 56, 'connection', 'Names Represent Identity', 'This phrase connects to Revelation 7:4, where the names of the tribes of Israel are sealed for protection. The inclusion of these names on Aaron''s breastplate signifies that the identity of the people of God is carried into the presence of God, emphasizing His covenant relationship with them. This highlights the importance of representation in the divine service.', 'link', '[{"type": "crossReference", "reference": "Revelation 7:4", "description": "Sealing of the tribes signifies God''s protection over His people."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 29, 29, 'kjv', 'upon his heart', 88, 102, 'theology', 'Heart as Center of Affection', 'Many scholars believe that ''heart'' in biblical literature often symbolizes the seat of emotions and intentions. In this context, Aaron bearing the names of Israel upon his heart illustrates his deep connection and responsibility for the people before God, embodying the priestly role of intercession and care. This reflects the broader theological theme of God’s desire for His leaders to love and prioritize their communities.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the significance of the heart in representing one''s commitments."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 29, 29, 'kjv', 'a memorial before the Lord continually', 146, 184, 'question', 'Significance of Memorials', 'What does it mean for Aaron''s representation of Israel to serve as a memorial before the Lord? Consider how this concept of memorial influences our understanding of intercession. How do our prayers and actions serve as reminders of our commitments to God and one another?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 30, 30, 'kjv', 'the Urim and the Thummim', 50, 74, 'connection', 'Urim and Thummim Reference', 'The Urim and Thummim are mentioned again in Leviticus 8:8, where they are associated with the priestly garments. This connection is significant as it underlines the importance of divine guidance in matters of judgment and decision-making for Israel. The presence of these items in the breastplate symbolizes God''s active role in guiding His people through the high priest.', 'link', '[{"type": "crossReference", "reference": "Leviticus 8:8", "description": "Reference to the use of Urim and Thummim in priestly duties."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 30, 30, 'kjv', 'upon his heart', 208, 222, 'theology', 'Heart and Judgment', 'This phrase suggests the deep connection between the priest''s role and the emotional and spiritual state of Israel. Many scholars believe that bearing the names of the tribes on his heart illustrates God''s intimate relationship with His people and highlights the importance of love and compassion in leadership, as described in 1 Peter 5:2-3.', 'person.2.fill', '[{"type": "commentary", "reference": "1 Peter 5:2-3", "description": "Discusses leadership in a loving and compassionate manner."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 30, 30, 'kjv', 'bear the judgment of the children of Israel', 164, 207, 'question', 'Judgment Bearing Reflection', 'What does it mean for a leader to ''bear the judgment'' of their people? How might this principle apply to modern leaders in the church or community? Reflecting on this can illuminate the weight of responsibility that comes with leadership and the calling to represent others before God.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 31, 31, 'kjv', 'the robe of the ephod all of blue', 20, 53, 'connection', 'Symbolism of Blue', 'The color blue in Scripture often symbolizes heaven and divine authority. For instance, in Numbers 15:38-39, the Israelites are instructed to wear blue tassels on their garments to remember the commandments of the Lord. This connection emphasizes the significance of the ephod''s robe, as it represents the priest''s role in mediating between God and His people.', 'link', '[{"type": "crossReference", "reference": "Numbers 15:38-39", "description": "Instructions for blue tassels symbolizing obedience to God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 31, 31, 'kjv', 'the robe of the ephod all of blue', 20, 53, 'theology', 'Priestly Identity', 'The robe of the ephod signifies the special identity of the high priest. Many scholars believe that the blue color represents not only the priest''s role but also the holiness required for approaching God. This reflects the broader theological theme of separation between the sacred and the common, emphasizing the need for purity in those who serve the Lord.', 'person.2.fill', '[{"type": "commentary", "reference": "Expositor''s Bible Commentary", "description": "Discusses the significance of priestly garments."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 31, 31, 'kjv', 'the robe of the ephod all of blue', 20, 53, 'question', 'Reflecting on Holiness', 'What does the requirement of a blue robe for the high priest reveal about God''s standards for holiness? How might this challenge us to consider our own preparation and purity when we approach God in worship today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 31, 31, 'kjv', 'the robe of the ephod all of blue', 20, 53, 'greek', 'Understanding ''Ephod''', 'אֵפוֹד (ephod, H646) - a priestly garment that signifies the role of the high priest. The ephod is crucial for the priest''s duties, especially in making decisions through the Urim and Thummim, representing divine guidance.', 'textformat.abc', '[{"type": "strongs", "reference": "H646", "description": "Strong''s number for the Hebrew term ''ephod''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 32, 32, 'kjv', 'an hole in the top of it, in the midst thereof:', 19, 66, 'connection', 'Priestly Garments', 'The mention of the hole in the robe connects to the significance of priestly garments in the broader context of Levitical duties. In Leviticus 8:7, the garments of the priest are described as part of their consecration, highlighting the holiness required for approaching God. This connection emphasizes the importance of preparing oneself to serve in God’s presence.', 'link', '[{"type": "crossReference", "reference": "Leviticus 8:7", "description": "Describes the garments of the priest during consecration."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 32, 32, 'kjv', 'it shall have a binding of woven work round about the hole of it,', 67, 132, 'theology', 'Symbolism of Binding', 'The binding of woven work signifies the strength and unity of the priesthood. Many scholars believe this symbolizes the interconnectedness of the community of believers and their collective representation before God. This idea is echoed in 1 Peter 2:9, where believers are called a ''royal priesthood,'' linking the Old Testament priestly function to the New Testament church.', 'sparkles', '[{"type": "crossReference", "reference": "1 Peter 2:9", "description": "Describes believers as a royal priesthood."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 32, 32, 'kjv', 'as it were the hole of an habergeon,', 133, 169, 'greek', 'Habergeon Meaning', 'χιτών (chitōn, G5509) - garment, tunic. The term ''habergeon'' refers to a protective garment, which can symbolize the spiritual protection God provides to His people. This idea parallels Ephesians 6:11, where believers are instructed to put on the full armor of God.', 'textformat.abc', '[{"type": "strongs", "reference": "G5509", "description": "Refers to a garment or tunic."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 32, 32, 'kjv', 'that it be not rent.', 170, 190, 'question', 'Significance of Integrity', 'What does it mean for our spiritual lives if we are not to be ''rent'' or torn? This phrase can lead to a reflection on the importance of maintaining unity and integrity within the body of Christ, as emphasized in 1 Corinthians 12:25-26. How do we ensure that we are not divided in our mission and purpose?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 33, 33, 'kjv', 'pomegranates of blue, and of purple, and of scarlet', 47, 98, 'connection', 'Symbolism of Colors', 'The colors blue, purple, and scarlet are rich in symbolism throughout Scripture. For example, blue often represents divinity (Numbers 15:38-39), purple signifies royalty (Judges 8:26), and scarlet is associated with sin and redemption (Isaiah 1:18). This connection highlights the multifaceted nature of the priesthood and its role in mediating between God and the people.', 'link', '[{"type": "crossReference", "reference": "Numbers 15:38-39", "description": "Instructions for tassels, highlighting the use of blue."}, {"type": "crossReference", "reference": "Isaiah 1:18", "description": "Scarlet representing sin and the invitation to redemption."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 33, 33, 'kjv', 'bells of gold', 133, 146, 'theology', 'Significance of Sound', 'The inclusion of gold bells on the hem of the robe serves a theological purpose. Many scholars believe it signifies the importance of the priest''s ministry being audible, representing transparency and accountability in their service before God. This auditory element reminds the community of God''s presence and the priest''s role as an intermediary.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 33, 33, 'kjv', 'pomegranates of blue, and of purple, and of scarlet', 47, 98, 'question', 'Reflecting on Symbolism', 'What do the pomegranates and their colors reveal about the nature of worship in the Israelite community? How might these symbols inform our understanding of our own worship practices today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 33, 33, 'kjv', 'beneath upon the hem of it', 4, 30, 'greek', 'Hebrew Insight', 'כְּנָף (k''naf, H3671) - wing, edge, or border. This term often refers to the edges of garments, symbolizing protection and authority. The use of this word in the context of the priestly robe emphasizes the sacred boundaries and the priest''s role in safeguarding the holiness of the worship space.', 'textformat.abc', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 34, 34, 'kjv', 'A golden bell and a pomegranate, a golden bell and a pomegranate, upon the hem of the robe round about.', 0, 103, 'connection', 'Symbolism of Bells and Pomegranates', 'The repeated imagery of ''golden bell and a pomegranate'' resonates with other scriptural uses of bells and pomegranates, notably in 1 Kings 7:18, where they adorn the pillars of Solomon''s temple. This highlights a continuity of sacred symbolism in Israel''s worship, linking the priestly garments in Exodus to the grandeur of the temple, representing divine presence and holiness.', 'link', '[{"type": "crossReference", "reference": "1 Kings 7:18", "description": "Reference to pomegranates and bells in temple design."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 34, 34, 'kjv', 'A golden bell and a pomegranate, a golden bell and a pomegranate, upon the hem of the robe round about.', 0, 103, 'greek', 'Significance of ''Golden''', 'χρυσός (chrysos, G5552) - gold; represents purity and divinity in Scripture. The use of gold in the priest''s garments underscores the holiness required of those who serve in God''s presence, symbolizing their role in mediating between God and the people.', 'textformat.abc', '[{"type": "strongs", "reference": "G5552", "description": "Strong''s number for Greek term for gold."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(2, 28, 34, 34, 'kjv', 'A golden bell and a pomegranate, a golden bell and a pomegranate, upon the hem of the robe round about.', 0, 103, 'theology', 'Priestly Role and Holiness', 'This passage suggests that the bells and pomegranates on Aaron’s robe serve a dual purpose: they signify the beauty and splendor of God''s holiness while also serving a practical function in the priest''s ministry. The sound of the bells as Aaron moves indicates his ongoing presence before the Lord, emphasizing the importance of being attentive to God''s holiness in worship. (Source: The New Bible Commentary)', 'sparkles', '[{"type": "commentary", "reference": "The New Bible Commentary", "description": "Commentary on the significance of the priest''s garments."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(2, 28, 34, 34, 'kjv', 'A golden bell and a pomegranate, a golden bell and a pomegranate, upon the hem of the robe round about.', 0, 103, 'question', 'Reflecting on Sacred Symbols', 'What do the symbols of the golden bell and pomegranate reveal about the nature of worship in your life? How can recognizing the sacredness in everyday elements enhance your awareness of God''s presence?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini');