INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 3, 6, 6, 'kjv', 'I will not be afraid of ten thousands of people, that have set themselves against me round about.', 0, 97, 'greek', 'Understanding ''Afraid''', 'φόβος (phobos, G5401) - fear, dread, terror. The term indicates not just a feeling of fear but a deep-seated anxiety that can overwhelm. Understanding this word helps to grasp the intensity of the psalmist''s declaration of confidence against fear, as he proclaims his trust in God’s protection.', 'textformat.abc', '[{"type": "strongs", "reference": "G5401", "description": "Gloss on the Greek term for fear"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 3, 7, 7, 'kjv', 'Arise, O Lord; save me, O my God:', 0, 33, 'connection', 'Call for Divine Intervention', 'This plea echoes Psalm 44:26, where the psalmist similarly implores God to rise and help in times of distress. It highlights the theme of relying on God''s power for salvation, reinforcing the understanding that true deliverance comes from Him alone.', 'link', '[{"type": "crossReference", "reference": "Psalm 44:26", "description": "Another instance of crying out for God''s intervention."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 3, 7, 7, 'kjv', 'for thou hast smitten all mine enemies upon the cheek bone;', 34, 93, 'theology', 'God''s Judgment on Enemies', 'This phrase reflects the biblical motif of God as a warrior against unrighteousness. Many scholars believe it symbolizes God''s vindication and protection over His people, asserting His authority and justice in dealing with opposition.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 3, 7, 7, 'kjv', 'thou hast broken the teeth of the ungodly.', 94, 136, 'greek', 'Significance of ''broken''', 'שָׁבַר (shavar, H7665) - to break, destroy. This word conveys a forceful action against evil, emphasizing not just restraint but a decisive victory over the ungodly. Such imagery reinforces the assurance of God''s protective power.', 'textformat.abc', '[{"type": "strongs", "reference": "H7665", "description": "Strong''s number for the Hebrew term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 3, 7, 7, 'kjv', 'save me, O my God:', 15, 33, 'question', 'Understanding Salvation''s Nature', 'What does it mean to seek God''s salvation in times of trouble? How does this reflect our own understanding of dependency on God during our struggles? Reflecting on this can deepen our appreciation for God''s role as our savior.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 3, 8, 8, 'kjv', 'Salvation belongeth unto the Lord:', 0, 34, 'connection', 'Divine Salvation Theme', 'This phrase connects to Exodus 14:13, where Moses tells the Israelites, ''Fear ye not, stand still, and see the salvation of the Lord.'' Both passages emphasize that salvation is an act of God, reinforcing the belief in His sovereignty and power to save His people in times of trouble.', 'link', '[{"type": "crossReference", "reference": "Exodus 14:13", "description": "Demonstrates the theme of divine salvation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 3, 8, 8, 'kjv', 'thy blessing is upon thy people.', 35, 67, 'theology', 'God''s Blessing Assurance', 'Many scholars believe this statement reflects the covenantal relationship between God and His people, where His blessings are contingent upon their faithfulness. This assurance of blessing signifies God''s favor and presence among His followers, reinforcing the idea that His blessings are a response to His people''s reliance on Him.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 3, 8, 8, 'kjv', 'Salvation belongeth unto the Lord:', 0, 34, 'greek', 'Key Term in Salvation', 'יְשׁוּעָה (yeshuah, H3444) - salvation, deliverance. This Hebrew term emphasizes the concept of being saved from danger or distress, underscoring the theme of divine intervention. The use of this term in the context of Psalm 3 highlights the psalmist''s reliance on God for rescue.', 'textformat.abc', '[{"type": "strongs", "reference": "H3444", "description": "Strong''s number for the Hebrew term for salvation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 3, 8, 8, 'kjv', 'thy blessing is upon thy people.', 35, 67, 'question', 'Understanding Divine Blessing', 'What does it mean for God''s blessing to be upon His people, and how can we actively seek to align our lives with His will to receive that blessing?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 1, 1, 'kjv', 'Hear me when I call, O God of my righteousness:', 0, 47, 'connection', 'Call for Divine Attention', 'This plea resonates with Psalm 55:16-17, where the psalmist also seeks God''s attention in distress. The connection highlights a consistent theme in the Psalms: the believer''s urgent cry for God''s intervention during troubling times. Recognizing God as ''our righteousness'' emphasizes reliance on His justice and goodness in moments of need.', 'link', '[{"type": "crossReference", "reference": "Psalm 55:16-17", "description": "A similar call for God\u2019s attention in distress."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 1, 1, 'kjv', 'thou hast enlarged me when I was in distress;', 48, 93, 'theology', 'God''s Deliverance', 'This phrase suggests the theological concept of divine deliverance, where God not only hears our cries but actively responds by providing relief and growth in times of trouble. Many scholars believe this reflects the transformative nature of God''s mercy, leading to spiritual enlargement and deeper faith through trials.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 4, 1, 1, 'kjv', 'have mercy upon me, and hear my prayer.', 94, 133, 'question', 'Seeking God''s Mercy', 'In what ways do you seek God''s mercy in your life? How does understanding His willingness to hear our prayers shape your approach to prayer? Reflect on specific moments where you felt God''s mercy in response to your petitions and how that influences your relationship with Him.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 2, 2, 'kjv', 'O ye sons of men, how long will ye turn my glory into shame?', 0, 60, 'connection', 'Turning Glory into Shame', 'This phrase resonates with Psalm 69:19-20, where the psalmist laments being dishonored and shamed by his enemies. Both passages highlight the deep emotional pain that comes from seeing one''s honor turned into disgrace. Understanding this shared theme helps us recognize the persistent struggle between righteousness and the scorn of the ungodly.', 'link', '[{"type": "crossReference", "reference": "Psalm 69:19-20", "description": "Another lament of dishonor and shame."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 2, 2, 'kjv', 'how long will ye love vanity, and seek after leasing?', 61, 114, 'theology', 'Vanity in Human Pursuits', 'This passage suggests a critique of earthly pursuits that lack true value, reflecting the biblical theme of vanity (Hebrew ''hevel''). Many scholars believe that this speaks to the human tendency to prioritize transient gains over spiritual integrity, aligning with Ecclesiastes 1:2, which declares all is vanity. This invites reflection on what we truly value.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 4, 2, 2, 'kjv', 'O ye sons of men, how long will ye turn my glory into shame?', 0, 60, 'question', 'Reflecting on Honor', 'In what ways do we inadvertently turn God’s glory into shame through our actions or attitudes? How can we align our lives more closely with His honor and ensure our pursuits reflect His righteousness?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 2, 2, 'kjv', 'how long will ye love vanity, and seek after leasing?', 61, 114, 'greek', 'Understanding ''Vanity''', 'הֶבֶל (hevel, H1892) - vanity, breath, vapor. This term conveys the idea of something transient and insubstantial. In the context of this verse, it underscores the futility of chasing after things that do not last, encouraging believers to focus on what is eternal.', 'textformat.abc', '[{"type": "strongs", "reference": "H1892", "description": "Strong''s number for the term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 3, 3, 'kjv', 'the Lord hath set apart him that is godly for himself', 14, 67, 'theology', 'God''s Special Relationship', 'This verse highlights the idea that God has a unique relationship with the godly, indicating His active choice to sanctify and protect them. Many scholars believe this reflects the broader biblical theme of God''s covenant relationship with His people, as seen in passages like Deuteronomy 14:2, where Israel is called a special treasure to the Lord. This sets a precedent for understanding how God distinguishes the righteous for His purposes.', 'person.2.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 3, 3, 'kjv', 'the Lord will hear when I call unto him', 69, 108, 'connection', 'Assurance of God''s Listening', 'This phrase connects with 1 John 5:14, which states that if we ask anything according to His will, He hears us. The assurance that God listens to the prayers of the godly is a recurring theme in Scripture, emphasizing the importance of maintaining a righteous relationship with Him to experience His attentive response.', 'link', '[{"type": "crossReference", "reference": "1 John 5:14", "description": "Affirms that God listens to the prayers of those aligned with His will."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 3, 3, 'kjv', 'set apart him that is godly', 28, 55, 'greek', 'Meaning of ''Set Apart''', 'חָדַל (chadal, H2820) - to cease, to withdraw. This term indicates a divine separation, emphasizing how God intentionally chooses and withdraws a person for His purposes. This highlights the sacred nature of being godly and the privilege of being distinguished by God for His work.', 'textformat.abc', '[{"type": "strongs", "reference": "H2820", "description": "Strong''s number for the Hebrew term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 3, 3, 'kjv', 'But know that the Lord hath set apart him that is godly for himself', 0, 67, 'question', 'Understanding God''s Choice', 'What implications does it have for your life to know that God has set you apart as His own? How does this understanding of being chosen affect your daily actions and your relationship with others?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 4, 4, 'kjv', 'Stand in awe, and sin not:', 0, 26, 'theology', 'Awe and Sin', 'This phrase emphasizes the importance of reverence toward God, suggesting that true awe can lead to righteous living. Many scholars believe that a proper understanding of God''s holiness can deter sinful behavior, reflecting a common theme in Scripture where fear of the Lord is linked to wisdom (Proverbs 9:10).', 'sparkles', '[{"type": "commentary", "reference": "Proverbs 9:10", "description": "The fear of the Lord is the beginning of wisdom."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 4, 4, 4, 'kjv', 'commune with your own heart upon your bed,', 27, 69, 'question', 'Self-Reflection', 'What does it mean to ''commune with your own heart''? How can this practice deepen your relationship with God and guide your decisions? Reflect on moments of stillness and how they might lead to greater clarity in your spiritual journey.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 4, 4, 'kjv', 'and be still.', 70, 83, 'connection', 'Stillness in Scripture', 'The call to be still resonates with Psalm 46:10, ''Be still, and know that I am God.'' This connection emphasizes the importance of stillness in experiencing God''s presence and understanding His sovereignty in our lives. Both verses encourage a pause from life''s chaos to focus on God.', 'link', '[{"type": "crossReference", "reference": "Psalm 46:10", "description": "Encouragement to recognize God''s authority through stillness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 4, 4, 'kjv', 'Stand in awe, and sin not:', 0, 26, 'greek', 'Original Language Insight', 'יָרֵא (yare, H3372) - to fear, to revere. This Hebrew word for ''stand in awe'' conveys a sense of deep respect and reverence for God, which is foundational to a godly life. It suggests that awe is more than mere fear; it is a recognition of God''s holiness and power.', 'textformat.abc', '[{"type": "strongs", "reference": "H3372", "description": "Strong''s number for the Hebrew word used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 5, 5, 'kjv', 'Offer the sacrifices of righteousness,', 0, 38, 'connection', 'Sacrifices of Righteousness', 'This phrase echoes the call in Psalm 51:17, which states, ''The sacrifices of God are a broken spirit; a broken and contrite heart, O God, thou wilt not despise.'' Both verses emphasize that true sacrifices are not merely physical offerings but involve a heart aligned with God''s will. This connection deepens our understanding of what it means to approach God with sincerity and authenticity.', 'link', '[{"type": "crossReference", "reference": "Psalm 51:17", "description": "Highlights the nature of true sacrifices to God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 5, 5, 'kjv', 'and put your trust in the Lord.', 39, 70, 'theology', 'Trust in God', 'Many scholars believe that this call to trust in the Lord reflects a central tenet of biblical faith, emphasizing reliance on God''s character and promises. This concept aligns with Proverbs 3:5-6, which instructs believers to trust in the Lord with all their heart. Such trust is foundational for a faithful relationship with God and is often linked with the idea of righteousness.', 'person.2.fill', '[{"type": "commentary", "reference": "Proverbs 3:5-6", "description": "Provides insight into the importance of trust in God."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 4, 5, 5, 'kjv', 'Offer the sacrifices of righteousness,', 0, 38, 'question', 'Understanding Righteous Sacrifice', 'What does it mean to offer sacrifices of righteousness in your daily life? How can you align your actions and intentions with God''s will, reflecting true righteousness in your conduct?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 5, 5, 'kjv', 'and put your trust in the Lord.', 39, 70, 'greek', 'Trust in Greek', 'πίστις (pistis, G4102) - faith, trust, confidence. This term suggests not only belief but also a deep relational trust in God. It emphasizes that faith involves commitment and reliance on God''s faithfulness.', 'textformat.abc', '[{"type": "strongs", "reference": "G4102", "description": "Strong''s number for the term ''trust''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 6, 6, 'kjv', 'Who will shew us any good?', 24, 50, 'connection', 'Seeking Goodness', 'This phrase resonates with Psalm 34:12-13, which asks, ''What man is he that desireth life, and loveth many days, that he may see good?'' Both passages underscore a universal human longing for goodness and fulfillment. The connection highlights the consistent biblical theme of seeking divine favor and the good life that comes from following God.', 'link', '[{"type": "crossReference", "reference": "Psalm 34:12-13", "description": "Explores the desire for goodness and divine favor."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 6, 6, 'kjv', 'Lord, lift thou up the light of thy countenance upon us.', 51, 107, 'theology', 'Divine Favor', 'Many scholars believe this plea reflects the ancient Hebrew understanding of God''s countenance as a symbol of His presence and favor. In Numbers 6:25, the priestly blessing states, ''The Lord make His face shine upon you.'' This suggests that experiencing God''s light brings joy and peace, emphasizing the importance of divine relationship in a believer''s life.', 'person.2.fill', '[{"type": "commentary", "reference": "Numbers 6:25", "description": "Highlights the significance of God''s favor."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 4, 6, 6, 'kjv', 'Lord, lift thou up the light of thy countenance upon us.', 51, 107, 'greek', 'Countenance in Hebrew', 'פָּנִים (panim, H6440) - face, presence. This term signifies more than just a physical face; it represents one''s character and presence. Invoking God''s ''countenance'' implies a desire for His active involvement and approval in our lives, reinforcing the relational aspect of faith.', 'textformat.abc', '[{"type": "strongs", "reference": "H6440", "description": "Strong''s number for the Hebrew term for face."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 6, 6, 'kjv', 'Who will shew us any good?', 24, 50, 'question', 'Seeking God''s Goodness', 'In moments of doubt, how do you seek the good that only God can provide? What does it mean to look to Him for assurance rather than to the world around you?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 7, 7, 'kjv', 'Thou hast put gladness in my heart', 0, 34, 'theology', 'Source of True Joy', 'This phrase highlights the divine source of joy in a believer''s life. Many scholars believe that true gladness transcends material blessings, as it is rooted in a relationship with God. This joy is often contrasted with the fleeting satisfaction derived from earthly abundance, suggesting that spiritual fulfillment is of greater value than physical prosperity.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 4, 7, 7, 'kjv', 'more than in the time that their corn and their wine increased', 36, 98, 'connection', 'Contrast with Material Blessings', 'This verse can be cross-referenced with Ecclesiastes 2:10-11, where the author reflects on the emptiness of pursuing pleasure and wealth. The connection emphasizes that while material gain can bring temporary joy, the gladness from God is enduring and far superior. It serves as a reminder that spiritual joy does not depend on external circumstances.', 'link', '[{"type": "crossReference", "reference": "Ecclesiastes 2:10-11", "description": "Reflection on the futility of earthly pursuits"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 7, 7, 'kjv', 'gladness in my heart', 14, 34, 'greek', 'Understanding Gladness', 'שָׂשׂוֹן (sasón, H7797) - joy, gladness, rejoicing. This Hebrew word conveys a sense of deep, abiding joy that stems from God’s presence. It indicates that true gladness is not merely emotional but is rooted in the believer''s spiritual state.', 'textformat.abc', '[{"type": "strongs", "reference": "H7797", "description": "Strong''s number for the Hebrew term related to joy"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 7, 7, 'kjv', 'more than in the time that their corn and their wine increased', 36, 98, 'question', 'Value of Spiritual Joy', 'What does it mean to experience joy that surpasses earthly abundance? How can we cultivate this deeper sense of gladness in our own lives, especially when faced with the pursuit of material gain?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 8, 8, 'kjv', 'I will both lay me down in peace, and sleep: for thou, Lord, only makest me dwell in safety.', 0, 92, 'connection', 'Connection to Safety Scriptures', 'This verse connects with Isaiah 26:3, which states, ''You will keep him in perfect peace, whose mind is stayed on You, because he trusts in You.'' Both verses emphasize the peace that comes from reliance on God. Additionally, Psalm 121:3-4 reassures that God will not allow our foot to slip, highlighting His role as our protector. This underscores the theme of divine safety that allows for true rest.', 'link', '[{"type": "crossReference", "reference": "Isaiah 26:3", "description": "Highlights God''s promise of peace."}, {"type": "crossReference", "reference": "Psalm 121:3-4", "description": "Emphasizes God as a protector."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 8, 8, 'kjv', 'lay me down in peace', 12, 32, 'theology', 'Peace as a Gift', 'This passage suggests that peace is not merely the absence of conflict but a gift from God. Many scholars believe that the biblical concept of peace (Hebrew ''shalom'') encompasses wholeness and security, suggesting a deep spiritual state that comes from trusting in God''s provision and care. It indicates the believer''s reliance on God for safety and peace of mind.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses peace as a divine gift."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 4, 8, 8, 'kjv', 'thou, Lord, only makest me dwell in safety.', 49, 92, 'greek', 'Safety in Greek', 'סָלַם (salam, H7999) - to be safe, secure, or at peace. This term underlines that true safety and security are found solely in the Lord. The use of ''only'' emphasizes the exclusivity of God''s protection, reminding believers that no earthly assurance can provide the same lasting safety that faith in God does.', 'textformat.abc', '[{"type": "strongs", "reference": "H7999", "description": "Indicates the original Hebrew term for safety."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 4, 8, 8, 'kjv', 'I will both lay me down in peace, and sleep', 0, 43, 'question', 'Reflecting on Rest', 'What does it mean to ''lay down in peace'' when facing life''s uncertainties? How can trusting in God''s safety transform your approach to rest and sleep amidst challenges?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 5, 1, 1, 'kjv', 'Give ear to my words, O Lord, consider my meditation.', 0, 53, 'connection', 'Prayer and Listening', 'This verse resonates with 1 Peter 3:12, which states, ''For the eyes of the Lord are on the righteous and his ears are attentive to their prayer.'' Both passages emphasize the importance of prayer and the attentiveness of God to the supplicant. This connection illustrates the relational aspect of prayer, where the believer is assured that their words and thoughts are valued by God.', 'link', '[{"type": "crossReference", "reference": "1 Peter 3:12", "description": "Both verses emphasize God''s attentiveness to prayer."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 5, 1, 1, 'kjv', 'consider my meditation.', 30, 53, 'greek', 'Meditation Explained', 'הָגָה (hāgâ, H1897) - to meditate, ponder, speak. This Hebrew term suggests not only silent contemplation but also the act of vocalizing one''s thoughts. In this context, it indicates the depth of the psalmist''s prayer life, where meditation involves both reflection and expression before God.', 'textformat.abc', '[{"type": "strongs", "reference": "H1897", "description": "Strong''s number for the Hebrew term related to meditation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 5, 1, 1, 'kjv', 'Give ear to my words, O Lord, consider my meditation.', 0, 53, 'theology', 'God''s Attentiveness', 'This verse underscores the theological concept of divine attentiveness. Many scholars believe that the psalmist''s plea illustrates a core belief that God is not distant but actively engaged in the lives of believers, listening to their prayers and meditations. This reflects the broader Christian teaching about the personal relationship between God and humanity, emphasizing that God hears and responds to our inner thoughts and desires.', 'person.2.fill', '[{"type": "commentary", "reference": "The New Bible Commentary", "description": "Discusses the relational aspect of prayer in Psalms."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 5, 1, 1, 'kjv', 'Give ear to my words, O Lord, consider my meditation.', 0, 53, 'question', 'Reflecting on Prayer', 'What does it mean to you that God is asked to ''give ear'' to our words? How does this understanding of God''s attentiveness shape your approach to prayer and meditation?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 5, 2, 2, 'kjv', 'Hearken unto the voice of my cry, my King, and my God: for unto thee will I pray.', 0, 81, 'connection', 'Prayer and Divine Attention', 'This verse resonates with 1 Peter 3:12, which states, ''For the eyes of the Lord are on the righteous and His ears are attentive to their prayer.'' Both passages emphasize the importance of seeking God''s attention in prayer, highlighting His responsiveness to the cries of His people. Understanding this connection reassures believers of God''s constant presence and willingness to listen.', 'link', '[{"type": "crossReference", "reference": "1 Peter 3:12", "description": "Emphasizes God''s attentiveness to prayer."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 5, 2, 2, 'kjv', 'my King, and my God', 34, 53, 'theology', 'Christ as King and God', 'This phrase reflects the dual nature of Christ in Christian theology, acknowledging Him both as sovereign ruler (''my King'') and divine being (''my God''). Many scholars believe this underscores the intimate relationship between believers and Christ, where He not only reigns over all but also engages personally with individuals. This relationship invites believers to approach Him with both reverence and intimacy.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the significance of Christ''s dual role."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 5, 2, 2, 'kjv', 'for unto thee will I pray.', 55, 81, 'question', 'The Purpose of Prayer', 'What does it mean to pray ''unto thee''? How does this phrase shape our understanding of the intent and focus of our prayers? Reflecting on this can deepen your approach to prayer, encouraging a more intentional connection with God as you articulate your needs and desires before Him.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 5, 2, 2, 'kjv', 'my King, and my God', 34, 53, 'greek', 'Divine Titles in Prayer', 'אֱלֹהִים (Elohim, H430) - God, deity; this term emphasizes God''s power and authority. The use of ''my King'' acknowledges His sovereign rule over creation. Understanding these terms enriches one''s prayer life by recognizing the full scope of God''s character.', 'textformat.abc', '[{"type": "strongs", "reference": "H430", "description": "Strong''s number for Elohim, signifying divine authority."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 5, 3, 3, 'kjv', 'My voice shalt thou hear in the morning, O Lord;', 0, 48, 'connection', 'Morning Prayer Connection', 'This phrase resonates with Psalm 88:13, where the psalmist declares, ''But I, O Lord, cry unto thee; and in the morning shall my prayer prevent thee.'' This connection emphasizes the importance of beginning each day with a heartfelt prayer, setting a tone of dependence on God from the outset.', 'link', '[{"type": "crossReference", "reference": "Psalm 88:13", "description": "Another instance of morning prayer highlighting its significance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 5, 3, 3, 'kjv', 'in the morning will I direct my prayer unto thee,', 49, 98, 'question', 'Focus of Morning Prayer', 'What does it mean to ''direct'' my prayer to God each morning? How can intentionality in prayer affect my relationship with Him and my perspective throughout the day?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini');