INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 10, 13, 13, 'kjv', 'Thou wilt not require it.', 66, 91, 'greek', 'Heart''s Deception', 'לֹא יִדְרֹשׁ (lo'' yidrosh, H1819) - to demand, to require. This Hebrew term emphasizes the belief that God will not seek justice or accountability for the wicked''s actions. It reflects a dangerous self-deception that leads to moral and spiritual apathy.', 'textformat.abc', '[{"type": "strongs", "reference": "H1819", "description": "Strong''s number for ''require'' in this context."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 14, 14, 'kjv', 'Thou hast seen it; for thou beholdest mischief and spite, to requite it with thy hand:', 0, 86, 'connection', 'God''s Awareness of Injustice', 'This phrase echoes the sentiment found in Exodus 3:7, where God declares, ''I have surely seen the affliction of my people.'' Both verses emphasize God''s active observation of human suffering and wrongdoing. This connection highlights the assurance that God is not distant but is aware and responsive to the plight of the oppressed.', 'link', '[{"type": "crossReference", "reference": "Exodus 3:7", "description": "God''s acknowledgment of Israel''s suffering"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 14, 14, 'kjv', 'the poor committeth himself unto thee;', 87, 125, 'theology', 'Dependence on God', 'This passage suggests a key theological principle: the reliance of the poor on God for justice and provision. Many scholars believe this reflects the broader theme of the Psalms, where the marginalized seek refuge in God. This dependence highlights God''s role as a protector and advocate for those without means.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 10, 14, 14, 'kjv', 'thou art the helper of the fatherless.', 126, 164, 'greek', 'God as Helper', 'עָזַר (azar, H5826) - to help, assist. This Hebrew term emphasizes God''s active role in providing support. The use of ''helper'' illustrates God''s commitment to the vulnerable, reinforcing the biblical theme of divine advocacy for the marginalized, particularly orphans.', 'textformat.abc', '[{"type": "strongs", "reference": "H5826", "description": "Strong''s number for ''help''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 14, 14, 'kjv', 'the poor committeth himself unto thee;', 87, 125, 'question', 'Trust in God''s Provision', 'What does it mean to truly ''commit'' oneself to God in times of distress, especially as the poor do in this verse? How can we apply this understanding of trust in our own lives when facing challenges?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 15, 15, 'kjv', 'Break thou the arm of the wicked', 0, 32, 'theology', 'Divine Justice', 'This phrase suggests a plea for divine intervention against injustice. Many scholars believe this reflects the biblical theme of God''s active role in punishing evil and protecting the innocent, as seen throughout Scripture, particularly in Psalms. The imagery of ''breaking the arm'' symbolizes the removal of strength and power from the wicked, emphasizing God''s sovereignty over all earthly authority.', 'sparkles', '[{"type": "commentary", "reference": "Psalms 37:17", "description": "This verse similarly portrays the Lord sustaining the righteous while the wicked will face downfall."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 10, 15, 15, 'kjv', 'seek out his wickedness till thou find none', 51, 94, 'connection', 'Pursuit of Righteousness', 'This call to ''seek out wickedness'' echoes the themes found in Jeremiah 17:10, where God examines the heart and mind. This connection matters as it illustrates God''s thorough and just nature, seeking to eradicate evil completely. Understanding this can deepen our appreciation for the righteousness God desires for His creation and the lengths to which He will go to ensure justice.', 'arrow.triangle.branch', '[{"type": "crossReference", "reference": "Jeremiah 17:10", "description": "God''s promise to search and reward according to actions."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 15, 15, 'kjv', 'Break thou the arm of the wicked', 0, 32, 'question', 'Understanding Divine Intervention', 'What does it mean for God to ''break the arm'' of the wicked in your own life? In what ways can you identify the ''wicked'' influences around you that God might be calling you to confront or remove? Reflecting on this can lead to a deeper understanding of spiritual warfare and reliance on God''s strength in facing evil.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 15, 15, 'kjv', 'seek out his wickedness till thou find none', 51, 94, 'greek', 'Understanding ''Wickedness''', 'רָשָׁע (rasha, H7563) - wicked, guilty, criminal. This Hebrew term reflects not just the act of wrongdoing but also the moral state of the individual. This word choice emphasizes the seriousness of wickedness and its implications for both personal and communal morality, underscoring the need for divine justice.', 'textformat.abc', '[{"type": "strongs", "reference": "H7563", "description": "Strongs number for the Hebrew word for wicked."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 16, 16, 'kjv', 'The Lord is King for ever and ever', 0, 34, 'theology', 'Eternal Kingship of God', 'This phrase emphasizes the eternal nature of God''s sovereignty, a central theme in biblical theology. Many scholars believe that God''s kingship is not bound by time or circumstance, affirming His ultimate authority over all creation. This reflects the belief in God''s unchanging nature and His enduring rule, which provides comfort and assurance to believers amidst worldly chaos.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 10, 16, 16, 'kjv', 'the heathen are perished out of his land', 36, 76, 'connection', 'Judgment of the Nations', 'This phrase connects with Psalm 2:4-5, where it states, ''He that sitteth in the heavens shall laugh: the Lord shall have them in derision.'' Both passages highlight God''s ultimate justice against the wicked and the transitory nature of those who oppose Him. This connection underscores the assurance that God''s justice prevails and that His kingdom will ultimately be established.', 'link', '[{"type": "crossReference", "reference": "Psalm 2:4", "description": "Highlights God''s authority and judgment over nations."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 16, 16, 'kjv', 'The Lord is King for ever and ever', 0, 34, 'greek', 'King - βασιλεὺς', 'βασιλεὺς (basileus, G935) - king, ruler. This term signifies authority and governance, and in the context of God''s kingship, it emphasizes His supreme power and dominion over all creation. Understanding this term deepens the recognition of God''s role as the ultimate ruler, contrasting with earthly rulers.', 'textformat.abc', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 16, 16, 'kjv', 'The Lord is King for ever and ever', 0, 34, 'question', 'Reflection on God''s Rule', 'What does it mean for you personally to acknowledge that ''The Lord is King for ever and ever''? How does recognizing God''s eternal kingship influence your daily life and decisions, especially in times of uncertainty?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 17, 17, 'kjv', 'Lord, thou hast heard the desire of the humble:', 0, 47, 'connection', 'Hearing the Humble', 'This phrase connects with James 4:6, which states, ''God resisteth the proud, but giveth grace unto the humble.'' Both verses highlight God''s attentive response to the humble, emphasizing His favor towards those who are lowly in spirit. This reinforces the idea that God is relational and responsive, particularly to those who recognize their need for Him.', 'link', '[{"type": "crossReference", "reference": "James 4:6", "description": "Illustrates God''s grace towards the humble."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 17, 17, 'kjv', 'thou wilt prepare their heart,', 48, 78, 'theology', 'God''s Preparation of Hearts', 'Many scholars believe this phrase indicates God''s active role in transforming and preparing the hearts of His people. This aligns with the doctrine of regeneration, where God instills a new heart and spirit within believers (Ezekiel 36:26). It reflects God''s sovereignty in salvation and His intimate involvement in the spiritual growth of the humble.', 'sparkles', '[{"type": "commentary", "reference": "Ezekiel 36:26", "description": "References God''s transformative work in believers."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 10, 17, 17, 'kjv', 'thou wilt cause thine ear to hear:', 79, 113, 'greek', 'Hearing as a Divine Act', 'שָׁמַע (shama, H8085) - to hear, listen, obey. This Hebrew term emphasizes not just the act of hearing but also the responsiveness and obedience that follows. It highlights God''s nature as one who listens and acts upon the cries of the humble, affirming His commitment to justice and mercy.', 'textformat.abc', '[{"type": "strongs", "reference": "H8085", "description": "Hebrew word for ''to hear.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 17, 17, 'kjv', 'Lord, thou hast heard the desire of the humble:', 0, 47, 'question', 'Understanding Humility''s Role', 'What does it mean to be humble in the context of your own desires? How does recognizing your own need for God influence your prayer life and relationship with Him? Reflecting on this can deepen your understanding of dependence on God and His provision.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 18, 18, 'kjv', 'To judge the fatherless and the oppressed', 0, 41, 'connection', 'Judgment for the Vulnerable', 'This phrase connects to Isaiah 1:17, which calls for seeking justice and correcting oppression. Both passages emphasize God''s concern for vulnerable groups, suggesting that divine judgment involves advocating for the marginalized. The consistent biblical theme highlights that God''s justice is rooted in His compassion for the weak and oppressed.', 'link', '[{"type": "crossReference", "reference": "Isaiah 1:17", "description": "Calls for justice and care for the oppressed."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 18, 18, 'kjv', 'that the man of the earth may no more oppress', 43, 88, 'theology', 'God''s Justice Against Oppression', 'This passage suggests that God''s judgment is intended to eradicate oppression from the earth. Many scholars believe that God''s kingdom involves establishing justice and righteousness, where the oppressed are protected, and the wicked are held accountable. This reflects the Christian understanding of God''s nature as just and merciful.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 10, 18, 18, 'kjv', 'the fatherless', 9, 23, 'greek', 'Fatherless in Context', 'יָתוֹם (yathom, H3490) - orphan. The term highlights the vulnerability of children without parental protection, emphasizing God''s special concern for them in His justice. This aligns with broader biblical themes of God being a protector and defender of the weak.', 'textformat.abc', '[{"type": "strongs", "reference": "H3490", "description": "Strong''s number for ''orphan''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 10, 18, 18, 'kjv', 'the oppressed', 28, 41, 'question', 'Understanding Oppression', 'What does it mean for God to judge the oppressed, and how does this inform our responsibility toward those who suffer injustice? Reflecting on this can help us understand our role in advocating for justice and support for the marginalized in our communities.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 1, 1, 'kjv', 'In the Lord put I my trust:', 0, 27, 'theology', 'Trust in God', 'This phrase emphasizes the foundational Christian doctrine of trusting in God as the ultimate source of security and support. Many scholars believe that this reflects a covenantal relationship where faith in God is crucial for righteousness. Trusting in the Lord is often linked to His faithfulness and sovereignty, as seen in passages like Proverbs 3:5-6, which encourage believers to lean not on their own understanding.', 'person.2.fill', '[{"type": "commentary", "reference": "Proverbs 3:5-6", "description": "Encouragement to trust in the Lord."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 1, 1, 'kjv', 'how say ye to my soul, Flee as a bird to your mountain?', 28, 83, 'question', 'Facing Adversity', 'What does it reveal about our faith when external voices encourage us to flee rather than stand firm? This question invites reflection on how we respond to fear and doubt in our lives. Are we swayed by those who suggest retreating in the face of challenges, or do we hold steadfast in our trust in God’s protection and guidance?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 1, 1, 'kjv', 'put I my trust:', 12, 27, 'greek', 'Trust in Hebrew', 'בָּטַח (batach, H982) - to trust, confide, be confident. This Hebrew term encapsulates the idea of placing complete reliance on God. It implies not just a passive trust but an active confidence in God''s ability to protect and deliver. Understanding this term deepens our appreciation for the psalmist''s reliance on God amid threats.', 'textformat.abc', '[{"type": "strongs", "reference": "H982", "description": "Strong''s number for ''trust''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 1, 1, 'kjv', 'Flee as a bird to your mountain?', 51, 83, 'connection', 'Bird Imagery', 'The imagery of a bird fleeing to a mountain recalls similar themes in Scripture, such as in Psalm 55:6, where the psalmist expresses a desire to escape turmoil. This connection highlights the human instinct to seek refuge in times of danger. It serves as a reminder of the importance of finding our true refuge in God rather than in temporary solutions.', 'arrow.triangle.branch', '[{"type": "crossReference", "reference": "Psalm 55:6", "description": "Another instance of seeking refuge."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 2, 2, 'kjv', 'the wicked bend their bow', 9, 34, 'connection', 'Wickedness in Scripture', 'This phrase resonates with Proverbs 12:6, which states, ''The words of the wicked lie in wait for blood, but the mouth of the upright delivers them.'' Both verses illustrate the scheming nature of the wicked and their intent to harm the innocent. Understanding this connection helps highlight the persistent theme of the struggle between righteousness and wickedness throughout the Scriptures.', 'link', '[{"type": "crossReference", "reference": "Proverbs 12:6", "description": "This verse elaborates on the intentions of the wicked."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 2, 2, 'kjv', 'they make ready their arrow upon the string', 36, 79, 'greek', 'Symbolism of Arrows', 'The Hebrew word for ''arrow'' in this context is ''חֵץ'' (chets, H2611) - a projectile weapon symbolizing attack or harm. This imagery not only emphasizes the premeditated nature of the wicked''s actions but also serves as a metaphor for the spiritual warfare faced by the upright. The readiness of the arrow suggests imminent danger, urging the faithful to remain vigilant.', 'character.book.closed', '[{"type": "strongs", "reference": "H2611", "description": "Strong''s number for the Hebrew word for ''arrow.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 2, 2, 'kjv', 'that they may privily shoot at the upright in heart', 81, 132, 'theology', 'Nature of Spiritual Warfare', 'This verse suggests that the wicked often operate in secrecy, attacking the righteous when least expected. Many scholars believe this reflects the broader theological concept of spiritual warfare, where believers must be aware of unseen battles against evil. Ephesians 6:12 reinforces this notion, stating that our struggle is not against flesh and blood but against spiritual forces.', 'sparkles', '[{"type": "crossReference", "reference": "Ephesians 6:12", "description": "This verse elaborates on the nature of spiritual battles."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 11, 2, 2, 'kjv', 'the wicked bend their bow', 9, 34, 'question', 'Reflecting on the Nature of Evil', 'What does it mean for the wicked to ''bend their bow'' in our current context? How can we recognize subtle forms of wickedness in our lives and communities? This reflection prompts us to consider the active role that evil can play and our responsibility to stand firm in faith.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 3, 3, 'kjv', 'If the foundations be destroyed, what can the righteous do?', 0, 59, 'connection', 'Foundations in Scripture', 'This verse resonates with Matthew 7:24-27, where Jesus speaks of building one''s house on a rock versus sand. The image of foundations underscores the importance of stability in faith. Just as a house built on rock withstands storms, the righteous must rely on their strong foundation in God amidst chaos.', 'link', '[{"type": "crossReference", "reference": "Matthew 7:24-27", "description": "Jesus'' teaching on the importance of solid foundations in life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 3, 3, 'kjv', 'foundations', 7, 18, 'greek', 'Understanding Foundations', 'מְסִלּוֹת (mesillot, H4546) - foundations, paths. This Hebrew term emphasizes the idea of stability and security, suggesting that the fundamental principles of justice and righteousness are crucial for societal health. When these foundations are compromised, the moral fabric of society is threatened.', 'textformat.abc', '[{"type": "strongs", "reference": "H4546", "description": "Strong''s Hebrew number for foundations."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 3, 3, 'kjv', 'what can the righteous do?', 33, 59, 'theology', 'Righteousness Amid Crisis', 'This passage suggests that when foundational truths are eroded, the righteous are called to seek divine guidance and act with integrity. Many scholars believe that the role of the righteous is not merely to retreat but to engage in restoring justice and truth in society, reflecting God''s character in a tumultuous world.', 'figure.mind.and.body', '[{"type": "commentary", "reference": "The New American Commentary", "description": "Discusses the active role of the righteous in times of crisis."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 11, 3, 3, 'kjv', 'If the foundations be destroyed,', 0, 32, 'question', 'Reflecting on Foundations', 'In what ways can we identify the foundations of our faith and community today? How might we contribute to restoring and strengthening these foundations when they are under threat?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 4, 4, 'kjv', 'The Lord is in his holy temple', 0, 30, 'connection', 'God''s Presence', 'This phrase echoes the sentiment found in 1 Kings 8:27, where Solomon acknowledges that the heavens cannot contain God. This connection emphasizes the transcendence of the Lord, suggesting that while He is in His holy temple, His presence extends beyond physical confines. This understanding can bring comfort to believers, knowing that God is omnipresent and attentive to their circumstances.', 'link', '[{"type": "crossReference", "reference": "1 Kings 8:27", "description": "Solomon acknowledges God\u2019s transcendence."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 4, 4, 'kjv', 'his eyes behold, his eyelids try', 64, 96, 'theology', 'Divine Justice', 'This passage suggests that God is actively observing the actions of humanity, which reflects the theological concept of divine justice. Many scholars believe this aligns with the belief that God is not only aware of our actions but also evaluates them. This reinforces the idea that our moral choices have consequences and that God is a righteous judge.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 11, 4, 4, 'kjv', 'the Lord’s throne is in heaven', 32, 62, 'greek', 'Throne of God', 'θρόνος (thronos, G2362) - throne, seat of authority. This term underscores the sovereignty of God, indicating that His reign is established in heaven, far above earthly powers. Understanding this word helps believers recognize God’s ultimate authority over all creation.', 'textformat.abc', '[{"type": "strongs", "reference": "G2362", "description": "Greek word for throne."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 4, 4, 'kjv', 'the children of men', 98, 117, 'question', 'Human Accountability', 'What does it mean for God to ''try'' the children of men? How does this perspective influence our understanding of personal accountability and the moral choices we face daily? Reflecting on this can deepen our awareness of how our actions align with divine expectations.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 5, 5, 'kjv', 'The Lord trieth the righteous: but the wicked and him that loveth violence his soul hateth.', 0, 91, 'connection', 'Divine Testing', 'This verse echoes the sentiment found in James 1:12, which states that those who endure trial are blessed. Both passages highlight the refining nature of God''s tests for the righteous, suggesting that trials serve a purpose in spiritual growth. Additionally, Proverbs 15:9 correlates with the idea of the Lord''s disfavor towards violent individuals, reaffirming that God’s righteousness opposes wickedness.', 'link', '[{"type": "crossReference", "reference": "James 1:12", "description": "Highlights the blessing of enduring trials."}, {"type": "crossReference", "reference": "Proverbs 15:9", "description": "Affirms God''s opposition to wickedness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 5, 5, 'kjv', 'trieth', 9, 15, 'greek', 'Significance of Testing', 'נִסָּה (nissah, H5254) - to test, prove, try. This Hebrew word conveys the concept of examination or trial, which is integral to understanding God''s relationship with the righteous. The act of testing is not merely punitive but serves to strengthen faith and character, aligning with the broader biblical theme of divine refinement.', 'textformat.abc', '[{"type": "strongs", "reference": "H5254", "description": "Strong''s definition of the Hebrew term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 5, 5, 'kjv', 'the wicked and him that loveth violence his soul hateth.', 35, 91, 'theology', 'God''s Righteous Hatred', 'This passage suggests that God''s hatred towards wickedness is a reflection of His holiness and justice. Many scholars believe this aligns with the doctrine of divine retribution, where God responds to human actions with appropriate judgment. This understanding emphasizes that God''s love for righteousness inherently involves a rejection of evil, reinforcing the moral order established by His character.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the nature of God''s justice and holiness."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 11, 5, 5, 'kjv', 'The Lord trieth the righteous:', 0, 30, 'question', 'Purpose of Trials', 'What does it mean for us to understand that the Lord actively tests the righteous? How might this knowledge influence our response to challenges and adversity in our own lives? Reflecting on this can help clarify the purpose of our trials and encourage us to seek growth through them.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 6, 6, 'kjv', 'Upon the wicked he shall rain snares, fire and brimstone, and an horrible tempest: this shall be the portion of their cup.', 0, 122, 'connection', 'Judgment Imagery', 'This verse''s imagery of ''snares, fire and brimstone'' connects to Genesis 19:24, where God rained down sulfur and fire on Sodom and Gomorrah as judgment for wickedness. This connection emphasizes the seriousness of divine retribution against sin and highlights God''s holiness. Additionally, Revelation 21:8 mentions the fate of the ''cowardly, unbelieving, vile'' as a parallel warning of judgment, reinforcing the consequences of rejecting God''s righteousness.', 'link', '[{"type": "crossReference", "reference": "Genesis 19:24", "description": "God''s judgment on Sodom and Gomorrah"}, {"type": "crossReference", "reference": "Revelation 21:8", "description": "Fate of the wicked"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 6, 6, 'kjv', 'fire and brimstone', 38, 56, 'greek', 'Destructive Imagery', 'גָּחַל (gachol, H1501) - burning coals, live embers. This term evokes intense imagery of destruction and divine judgment. Its use here signifies not just physical destruction but also the spiritual consequences of wickedness, pointing to the ultimate fate of the wicked who oppose God''s ways.', 'textformat.abc', '[{"type": "strongs", "reference": "H1501", "description": "Burning coals, live embers"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 6, 6, 'kjv', 'this shall be the portion of their cup.', 83, 122, 'theology', 'Cup of Wrath', 'Many scholars believe the phrase ''the portion of their cup'' alludes to the biblical concept of divine judgment as a cup filled with wrath. This is echoed in Isaiah 51:17, where God''s people are warned about drinking the cup of His fury. It signifies the inevitable consequences of sin and the seriousness with which God takes justice, inviting reflection on God''s holiness and the weight of sin in our lives.', 'sparkles', '[{"type": "crossReference", "reference": "Isaiah 51:17", "description": "The cup of God''s wrath"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 11, 6, 6, 'kjv', 'he shall rain snares', 16, 36, 'question', 'Understanding Divine Justice', 'What does it mean for God to ''rain snares'' upon the wicked? How does this imagery help us understand the nature of divine justice and the consequences of living in opposition to God''s will? Reflecting on this can lead to deeper insights into the balance of justice and mercy in God''s character.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 7, 7, 'kjv', 'For the righteous Lord loveth righteousness;', 0, 44, 'connection', 'Righteousness in Scripture', 'This phrase connects well with Isaiah 61:8, where God expresses His love for justice and righteousness. It shows a consistent biblical theme that God values righteousness and actively engages in its promotion among His people. This connection emphasizes the importance of living a life that reflects God''s character.', 'link', '[{"type": "crossReference", "reference": "Isaiah 61:8", "description": "God''s love for justice and righteousness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 7, 7, 'kjv', 'his countenance doth behold the upright.', 45, 85, 'theology', 'God''s Omniscience', 'This passage suggests that God''s presence and attention are focused on those who are upright. Many scholars believe this reflects the doctrine of God''s omniscience, where He observes the hearts and actions of individuals. This reinforces the belief that living righteously is seen and valued by God, encouraging believers to pursue lives of integrity.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 11, 7, 7, 'kjv', 'the righteous Lord loveth righteousness;', 4, 44, 'greek', 'Righteousness Defined', 'צַדִּיק (tsaddiq, H6662) - righteous, just, lawful. This Hebrew term emphasizes not just moral correctness but also a covenant relationship with God. Understanding this term helps illuminate the depth of what it means for God to love righteousness; it is tied to His covenant faithfulness and the ethical behavior expected of His people.', 'textformat.abc', '[{"type": "strongs", "reference": "H6662", "description": "Strong''s number for ''righteous.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 11, 7, 7, 'kjv', 'For the righteous Lord loveth righteousness;', 0, 44, 'question', 'Understanding Divine Justice', 'What does it mean for God to love righteousness in the context of our daily lives? How does this understanding challenge or encourage us to pursue righteousness ourselves, especially in a world where injustice seems prevalent?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 12, 1, 1, 'kjv', 'Help, Lord; for the godly man ceaseth;', 0, 38, 'connection', 'Call for Divine Assistance', 'This plea for help resonates with other instances in Scripture where believers seek divine intervention. For example, Psalm 69:1 states, ''Save me, O God; for the waters are come in unto my soul.'' Both verses reflect a deep reliance on God during times of distress, highlighting the necessity of divine support in a world where the faithful seem to diminish.', 'link', '[{"type": "crossReference", "reference": "Psalm 69:1", "description": "A parallel cry for help from God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');