INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 25, 1, 1, 'kjv', 'Unto thee, O Lord, do I lift up my soul.', 0, 40, 'greek', 'Lift Up', 'נָשָׂא (nasa, H5375) - to lift, carry, or take away. This term emphasizes the act of raising one''s spirit or burdens to God, acknowledging His capability to support and redeem.', 'textformat.abc', '[{"type": "strongs", "reference": "H5375", "description": "Strong''s number for the Hebrew term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 2, 2, 'kjv', 'O my God, I trust in thee:', 0, 26, 'connection', 'Trust in God', 'This phrase echoes Proverbs 3:5-6, which advises believers to ''trust in the Lord with all thine heart.'' Both passages emphasize an unwavering reliance on God. The connection underscores the importance of trust as a foundational aspect of faith, especially in times of distress.', 'link', '[{"type": "crossReference", "reference": "Proverbs 3:5-6", "description": "Encourages full trust in God for guidance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 2, 2, 'kjv', 'let me not be ashamed,', 27, 49, 'theology', 'Shame and Faith', 'Many scholars believe that the request to not be ashamed reflects a broader theological principle that God’s faithfulness protects believers from disgrace. This aligns with Romans 10:11, which states that ''whoever believes in Him will not be put to shame.'' The idea of shame is linked with a lack of faith, while trust in God leads to confidence.', 'sparkles', '[{"type": "commentary", "reference": "Romans 10:11", "description": "Highlights trust in God as a source of confidence."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 2, 2, 'kjv', 'let not mine enemies triumph over me.', 50, 87, 'question', 'Enemies and Faith', 'In light of this plea, consider: How does trusting in God influence your perspective on conflicts and adversaries in your life? What does it mean to find victory in God even when facing opposition?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 2, 2, 'kjv', 'O my God, I trust in thee:', 0, 26, 'greek', 'Trust (ἔλπις)', 'πίστις (pistis, G4102) - faith, trust, belief. This term reflects a deep-seated confidence in God, suggesting that trust is not merely intellectual assent but a relational commitment that shapes a believer''s response to challenges.', 'textformat.abc', '[{"type": "strongs", "reference": "G4102", "description": "Indicates the Greek term for faith and trust."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 3, 3, 'kjv', 'Yea, let none that wait on thee be ashamed:', 0, 43, 'connection', 'Waiting on God', 'This phrase echoes Isaiah 49:23, which states, ''...for they shall not be ashamed that wait for me.'' Both verses emphasize the importance of trusting in God during trials. The connection highlights a consistent biblical theme: waiting on the Lord brings assurance and prevents shame.', 'link', '[{"type": "crossReference", "reference": "Isaiah 49:23", "description": "Emphasizes the theme of waiting on God and the assurance it brings."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 3, 3, 'kjv', 'let them be ashamed which transgress without cause.', 44, 95, 'theology', 'Shame and Righteousness', 'Many scholars believe that the concept of shame in Scripture is often linked to one''s relationship with God''s righteousness. This passage suggests that those who willingly transgress without just cause will face shame, indicating a moral and spiritual accountability that is foundational in Christian doctrine.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses themes of righteousness and shame in relation to sin."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 3, 3, 'kjv', 'let none that wait on thee be ashamed:', 5, 43, 'question', 'Understanding Waiting', 'What does it mean to wait on the Lord in your current circumstances? How does this active waiting shape your expectations and responses to challenges you face?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 3, 3, 'kjv', 'let them be ashamed which transgress without cause.', 44, 95, 'greek', 'Understanding ''Transgress''', 'פָּשַׁע (pasach, H6586) - to break away, to revolt. This term signifies a deliberate act of rebellion against God''s commandments, underscoring the seriousness of sin and the shame that follows such actions.', 'textformat.abc', '[{"type": "strongs", "reference": "H6586", "description": "Strong''s number for deeper study into the term ''transgress.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 4, 4, 'kjv', 'Shew me thy ways, O Lord;', 0, 25, 'connection', 'Seeking Divine Guidance', 'This phrase connects to Proverbs 3:5-6, which encourages trust in the Lord and promises guidance. Both passages emphasize the necessity of seeking God''s direction in our lives, highlighting that true understanding comes from aligning with His will. This pursuit of divine wisdom is foundational in the believer''s journey.', 'link', '[{"type": "crossReference", "reference": "Proverbs 3:5-6", "description": "Encouragement to trust in God''s guidance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 4, 4, 'kjv', 'teach me thy paths.', 26, 45, 'greek', 'Understanding ''Teach''', 'דָּרַךְ (darak, H1869) - to tread, to march, to journey. This Hebrew term emphasizes not just instruction but the process of walking in a specific way. It signifies an experiential learning where one engages in the path of righteousness, reflecting the holistic nature of divine teaching.', 'textformat.abc', '[{"type": "strongs", "reference": "H1869", "description": "Strong''s number for ''darak''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 4, 4, 'kjv', 'Shew me thy ways, O Lord;', 0, 25, 'theology', 'God''s Guidance in Life', 'This verse suggests the importance of divine revelation in a believer''s life. Many scholars believe that the quest for understanding God''s ways is fundamental to spiritual growth. It reflects the Christian doctrine that God desires to guide His people through His Word and Spirit, ensuring they walk in accordance with His truth.', 'sparkles', '[{"type": "commentary", "reference": "The Expositor''s Bible Commentary", "description": "Insights on the need for divine guidance."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 4, 4, 'kjv', 'teach me thy paths.', 26, 45, 'question', 'Reflecting on Divine Teaching', 'What specific paths is God inviting you to learn and walk in today? How can you actively engage with His teaching in your daily life, ensuring that your steps align with His will?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 5, 5, 'kjv', 'Lead me in thy truth, and teach me:', 0, 35, 'connection', 'Guidance in Truth', 'This plea for guidance parallels Psalm 119:105, which states, ''Thy word is a lamp unto my feet, and a light unto my path.'' Both verses emphasize the importance of divine instruction in navigating life’s complexities. The psalmist recognizes that true understanding and wisdom come from God, reinforcing the necessity of seeking His truth daily.', 'link', '[{"type": "crossReference", "reference": "Psalm 119:105", "description": "Highlights the theme of God''s word as guidance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 5, 5, 'kjv', 'for thou art the God of my salvation;', 36, 73, 'theology', 'Nature of Salvation', 'This phrase affirms a core Christian belief that God is the source of salvation. Many scholars believe this reflects the theological concept of grace, where salvation is not earned but given freely by God. This understanding encourages believers to rely on God''s mercy and not their own merits.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 5, 5, 'kjv', 'on thee do I wait all the day.', 74, 104, 'question', 'Waiting on God', 'What does it mean to wait on God throughout the day? Consider how this continual waiting impacts your daily decisions and interactions. How can you cultivate a posture of expectation and trust in God''s timing in your life?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 6, 6, 'kjv', 'tender mercies', 22, 36, 'greek', 'Hebrew Term Insight', 'The phrase ''tender mercies'' translates the Hebrew term ''רחמים'' (rachamim, H7356), which denotes deep compassion and mercy. It is often used to express the profound and nurturing love of God towards His people, reflecting His desire to protect and care for them. Understanding this term enriches our appreciation of God’s character as a compassionate Father.', 'textformat.abc', '[{"type": "strongs", "reference": "H7356", "description": "Strong''s number for the Hebrew term translated as ''tender mercies''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 6, 6, 'kjv', 'lovingkindnesses', 45, 61, 'connection', 'Cross-Reference to Covenant Love', 'This phrase connects to Psalm 136:1, which states, ''O give thanks unto the Lord; for he is good: for his mercy endureth forever.'' Both verses highlight God''s enduring lovingkindness, a crucial aspect of His covenant relationship with His people. This connection emphasizes that God''s love and mercy are foundational to His dealings with humanity.', 'link', '[{"type": "crossReference", "reference": "Psalm 136:1", "description": "Illustrates God''s enduring mercy and lovingkindness"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 6, 6, 'kjv', 'for they have been ever of old', 63, 93, 'theology', 'God''s Eternal Nature', 'This passage suggests that God''s attributes, such as mercy and lovingkindness, are eternal and unchanging. Many scholars believe this highlights the doctrine of God''s immutability, meaning He is consistent in character throughout time. This assurance can provide comfort and hope, knowing that God''s nature remains steady amidst life''s uncertainties.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 6, 6, 'kjv', 'Remember, O Lord', 0, 16, 'question', 'Divine Memory Reflection', 'What does it mean to ask God to ''remember''? How does this shape our understanding of His relationship with us? Reflecting on this question can lead to deeper insights into the nature of prayer and the assurance of God''s faithfulness and attentiveness to our pleas.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 7, 7, 'kjv', 'Remember not the sins of my youth, nor my transgressions:', 0, 57, 'connection', 'Cross-reference to Forgiveness', 'This plea echoes Psalm 103:12, which states, ''As far as the east is from the west, so far has he removed our transgressions from us.'' Both passages emphasize God''s desire to forgive and forget our sins, highlighting His mercy and grace. Connecting these verses reinforces the theme of divine forgiveness found throughout the Psalms.', 'link', '[{"type": "crossReference", "reference": "Psalm 103:12", "description": "Emphasizes God''s forgiveness of sins."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 7, 7, 'kjv', 'according to thy mercy remember thou me for thy goodness’ sake, O Lord.', 58, 129, 'theology', 'Understanding Divine Mercy', 'Many scholars believe this verse encapsulates the essence of God''s mercy as foundational to His character. It illustrates the theological concept that God’s mercy is not based on human merit but is rooted in His inherent goodness. This understanding encourages believers to approach God not with fear of condemnation but with trust in His compassionate nature.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the nature of God''s mercy."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 7, 7, 'kjv', 'Remember not the sins of my youth, nor my transgressions:', 0, 57, 'question', 'Reflecting on Past Sins', 'What does it mean to you that God chooses not to remember your past sins? How does this understanding of God''s mercy influence your current relationship with Him and your self-perception?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 7, 7, 'kjv', 'Remember not the sins of my youth, nor my transgressions:', 0, 57, 'greek', 'Hebrew Term for ''Transgressions''', 'פֶּשַׁע (pesha, H6588) - transgression, rebellion. This term conveys a sense of willful disobedience against God. Its usage here highlights the seriousness of the plea for forgiveness, as it underscores the nature of sin as not just mistakes but active rebellion against God’s commands.', 'textformat.abc', '[{"type": "strongs", "reference": "H6588", "description": "Strong''s number for the term ''transgression''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 8, 8, 'kjv', 'Good and upright is the Lord:', 0, 29, 'theology', 'Nature of God''s Goodness', 'This phrase highlights a fundamental aspect of God''s character—His goodness and uprightness. Many scholars believe that these attributes are essential for understanding God’s relationship with humanity, as they imply His moral perfection and faithfulness to His promises. This goodness is also foundational for His role as a teacher, indicating that divine instruction is rooted in love and righteousness.', 'sparkles', '[{"type": "commentary", "reference": "Psalms 119:68", "description": "This verse affirms that God is good and acts in goodness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 8, 8, 'kjv', 'therefore will he teach sinners in the way.', 30, 73, 'connection', 'Teaching the Way', 'This teaching aspect of God connects to Proverbs 3:5-6, where trusting in the Lord leads to guidance. The connection matters because it emphasizes that God’s instruction is accessible to all, including sinners, underscoring His desire for redemption and relationship.', 'link', '[{"type": "crossReference", "reference": "Proverbs 3:5-6", "description": "Trusting the Lord leads to His guidance."}, {"type": "crossReference", "reference": "Isaiah 30:21", "description": "God guides His people in the right way."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 8, 8, 'kjv', 'Good and upright is the Lord:', 0, 29, 'greek', 'Understanding ''Good''', 'ἀγαθός (agathos, G18) - good, virtuous, full of moral character. This term conveys the essence of God''s nature as fundamentally good, which is significant for believers as it assures them of His benevolent intentions and actions.', 'textformat.abc', '[{"type": "strongs", "reference": "G18", "description": "Strong''s number for the Greek word ''good''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 8, 8, 'kjv', 'therefore will he teach sinners in the way.', 30, 73, 'question', 'Reflecting on Divine Instruction', 'What does it mean for God to teach sinners, and how does this shape our understanding of His grace? Consider how this teaching reflects God''s desire to guide and transform lives, emphasizing the importance of repentance and learning in the spiritual journey.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 9, 9, 'kjv', 'The meek will he guide in judgment', 0, 34, 'connection', 'Meekness in Scripture', 'This phrase connects with Matthew 5:5, ''Blessed are the meek, for they shall inherit the earth.'' Both verses underscore the value of meekness in God''s eyes, emphasizing that those who demonstrate humility and gentleness are favored by Him and will receive guidance and blessings. This alignment between the Old and New Testament teachings highlights the continuity of God''s character and His expectations of His people.', 'link', '[{"type": "crossReference", "reference": "Matthew 5:5", "description": "Jesus'' Beatitudes affirming the blessedness of the meek."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 9, 9, 'kjv', 'and the meek will he teach his way', 36, 70, 'theology', 'Divine Guidance for the Humble', 'This passage suggests that God uniquely guides and instructs those who exhibit meekness. Many scholars believe that meekness is a prerequisite for receiving divine wisdom. The idea is rooted in the belief that humility opens one''s heart to learn and follow God''s ways, contrasting with the pride that often leads to spiritual blindness. This reflects a fundamental Christian teaching about the importance of humility in our relationship with God.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the importance of humility in receiving God''s guidance."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 9, 9, 'kjv', 'The meek will he guide in judgment', 0, 34, 'question', 'Reflecting on Meekness', 'What does it mean to be ''meek'' in the context of seeking God''s guidance? How can we cultivate a spirit of meekness in our daily lives to better understand and follow His judgment and path?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 9, 9, 'kjv', 'and the meek will he teach his way', 36, 70, 'greek', 'Understanding ''Teach''', 'לָמַד (lamad, H3925) - to teach, instruct, learn. This Hebrew word emphasizes the active role of God in imparting knowledge and direction to the meek. It signifies not just the transfer of information but the development of understanding and wisdom, highlighting God''s desire to guide those who humbly seek Him.', 'textformat.abc', '[{"type": "strongs", "reference": "H3925", "description": "Strong''s number for the Hebrew word for ''teach.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 10, 10, 'kjv', 'All the paths of the Lord are mercy and truth', 0, 45, 'connection', 'Mercy and Truth in Scripture', 'This phrase resonates with other scriptures, such as Exodus 34:6, where God describes Himself as ''merciful and gracious, slow to anger, and abounding in steadfast love and faithfulness.'' This connection highlights the consistent biblical theme of God''s character being rooted in mercy and truth. It reassures the believer of God’s unchanging nature and His commitment to guiding His people with love and integrity.', 'link', '[{"type": "crossReference", "reference": "Exodus 34:6", "description": "God''s self-revelation of mercy and faithfulness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 10, 10, 'kjv', 'All the paths of the Lord', 0, 25, 'theology', 'God''s Guidance', 'Many scholars believe this verse reflects the theological concept that God''s guidance is available to those who seek Him and obey His commandments. The paths symbolize the choices and directions in life, suggesting that following God''s ways leads to a life characterized by mercy and truth. This idea aligns with the broader biblical narrative that emphasizes obedience to God as a means of receiving His blessings.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explains the significance of following God''s paths."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 10, 10, 'kjv', 'mercy and truth', 30, 45, 'greek', 'Key Terms Defined', 'חֶסֶד (chesed, H2617) - mercy, loving-kindness, often denotes God''s covenant faithfulness. אֱמֶת (emet, H571) - truth, reliability, signifies the ultimate truthfulness of God’s nature and His promises. Together, these terms emphasize that God''s guidance is inherently loving and trustworthy.', 'textformat.abc', '[{"type": "strongs", "reference": "H2617", "description": "Strong''s definition of mercy."}, {"type": "strongs", "reference": "H571", "description": "Strong''s definition of truth."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 10, 10, 'kjv', 'unto such as keep his covenant and his testimonies', 46, 96, 'question', 'Covenant Relationship Inquiry', 'What does it mean to keep God''s covenant and testimonies in our daily lives? How does this commitment shape our understanding of mercy and truth in our relationship with Him? Reflecting on this may reveal areas where we can grow in faithfulness and obedience.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 11, 11, 'kjv', 'For thy name’s sake, O Lord,', 0, 28, 'connection', 'Divine Name Importance', 'The phrase ''for thy name''s sake'' resonates with Psalm 79:9, which states, ''Help us, O God of our salvation, for the glory of your name; deliver us, and atone for our sins, for your name''s sake!'' This connection emphasizes the significance of God''s name in the context of mercy and forgiveness, reinforcing the notion that God''s actions are motivated by His character and glory.', 'link', '[{"type": "crossReference", "reference": "Psalm 79:9", "description": "Highlights the importance of God''s name in seeking forgiveness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 11, 11, 'kjv', 'pardon mine iniquity;', 29, 50, 'theology', 'Concept of Forgiveness', 'This plea for pardon underlines a fundamental Christian doctrine regarding sin and grace. Many theologians emphasize that acknowledgment of one''s iniquity is the first step toward divine forgiveness. This passage suggests that recognition of our sinful nature is essential for receiving God''s grace, which is a core tenet of repentance in Christian teaching.', 'person.2.fill', '[{"type": "commentary", "reference": "The New Bible Commentary", "description": "Discusses the necessity of confession in the process of forgiveness."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 11, 11, 'kjv', 'for it is great.', 51, 67, 'question', 'Understanding Iniquity''s Weight', 'What does it mean for iniquity to be ''great'' in the context of our relationship with God? How does recognizing the gravity of our sins impact our understanding of God''s grace and forgiveness? Reflecting on this can deepen our appreciation for the magnitude of God''s mercy.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 12, 12, 'kjv', 'What man is he that feareth the Lord?', 0, 37, 'connection', 'Fear of the Lord', 'This phrase connects to Proverbs 1:7, which states, ''The fear of the Lord is the beginning of knowledge.'' The fear of the Lord denotes a reverential awe and respect that is foundational to wisdom and understanding. It illustrates that those who hold this fear are positioned to receive divine instruction and guidance.', 'link', '[{"type": "crossReference", "reference": "Proverbs 1:7", "description": "Proverbs emphasizes the importance of fearing the Lord in gaining knowledge."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 12, 12, 'kjv', 'him shall he teach in the way that he shall choose.', 38, 89, 'theology', 'Divine Guidance', 'This passage suggests that God actively instructs those who fear Him, indicating a relational dynamic between the believer and the divine. Many scholars believe that this reflects the broader theological concept of divine providence, where God not only guides but also chooses the paths for His followers based on their reverence for Him.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 12, 12, 'kjv', 'What man is he that feareth the Lord?', 0, 37, 'question', 'Understanding Fear', 'What does it mean to truly fear the Lord in today''s context? How can this reverence shape our daily decisions and interactions with others, especially when faced with moral dilemmas?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 12, 12, 'kjv', 'What man is he that feareth the Lord?', 0, 37, 'greek', 'Fear in Hebrew Context', 'יָרֵא (yare, H3372) - to fear, to revere. This term encapsulates not just fear in a negative sense but also a deep respect and reverence towards God, which is crucial for establishing a right relationship with Him.', 'textformat.abc', '[{"type": "strongs", "reference": "H3372", "description": "Strong''s number for the Hebrew word for fear."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 13, 13, 'kjv', 'His soul shall dwell at ease;', 0, 29, 'theology', 'Rest for the Soul', 'This phrase suggests a deep spiritual peace that comes from a relationship with God. Many scholars believe that when one fears the Lord and follows His ways, they experience a profound sense of rest and security, as reflected in Matthew 11:28-30, where Jesus invites the weary to find rest in Him. This rest is not merely physical but encompasses emotional and spiritual well-being.', 'sparkles', '[{"type": "commentary", "reference": "Matthew 11:28-30", "description": "Jesus offers rest to those burdened."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 13, 13, 'kjv', 'his seed shall inherit the earth.', 34, 67, 'connection', 'Inheritance Theme', 'This phrase echoes the promise made to Abraham in Genesis 13:15 and Psalm 37:11, where the meek shall inherit the earth. The connection matters as it emphasizes God''s covenantal faithfulness and the theme of divine inheritance in Scripture, indicating that those who live in accordance with God''s will are blessed not only themselves but also their descendants.', 'link', '[{"type": "crossReference", "reference": "Genesis 13:15", "description": "Promise of land to Abraham''s descendants."}, {"type": "crossReference", "reference": "Psalm 37:11", "description": "The meek shall inherit the earth."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 13, 13, 'kjv', 'His soul shall dwell at ease;', 0, 29, 'question', 'Understanding Spiritual Ease', 'What does it mean for one''s soul to dwell at ease in the context of trusting God? How might this understanding influence our daily decisions and interactions with others, especially in times of trouble?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 13, 13, 'kjv', 'and his seed shall inherit the earth.', 30, 67, 'greek', 'Heritage and Promise', 'זֶרַע (zera, H2233) - seed, offspring. This Hebrew term signifies not just biological descendants but also the spiritual legacy passed on. The concept of inheritance in biblical terms extends beyond material possessions to include the blessings and promises of God for future generations.', 'textformat.abc', '[{"type": "strongs", "reference": "H2233", "description": "Strong''s number for ''seed''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 14, 14, 'kjv', 'The secret of the Lord', 0, 22, 'theology', 'Divine Revelation', 'This phrase emphasizes the idea that God shares His intimate truths and plans with those who revere Him. Many scholars believe this indicates a relational aspect of faith, where understanding God''s will is reserved for the faithful, reflecting the covenant relationship emphasized throughout Scripture.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 25, 14, 14, 'kjv', 'with them that fear him', 26, 49, 'connection', 'Fear of the Lord', 'Proverbs 1:7 states, ''The fear of the Lord is the beginning of knowledge.'' This connection highlights that reverence for God is foundational to wisdom and understanding. The ''fear'' mentioned here is not terror but a profound respect that leads to a closer relationship with God.', 'link', '[{"type": "crossReference", "reference": "Proverbs 1:7", "description": "Emphasizes the importance of fearing the Lord for gaining knowledge."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 25, 14, 14, 'kjv', 'he will shew them his covenant', 55, 85, 'theology', 'Covenant Relationship', 'This part of the verse underscores the significance of God''s covenant with His people. Theologically, it suggests that those who honor God will receive deeper insights into His promises and commitments, signifying a special relationship that is marked by trust and obedience.', 'figure.mind.and.body', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini');