INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 78, 65, 65, 'kjv', 'as one out of sleep', 21, 40, 'theology', 'God''s Active Presence', 'This imagery suggests a moment of divine awakening that is crucial for understanding God''s active presence in human history. Many scholars believe this reflects the theological concept of God''s immanence, where He is not distant but deeply involved in the affairs of His creation, ready to act decisively when needed.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 78, 65, 65, 'kjv', 'like a mighty man that shouteth by reason of wine', 46, 95, 'greek', 'Mighty Man Insight', 'ἀνὴρ (anēr, G435) - man, husband, person. This term emphasizes strength and valor. The comparison of God to a ''mighty man'' illustrates His power and readiness to defend His people, evoking a sense of divine might that is both protective and assertive.', 'textformat.abc', '[{"type": "strongs", "reference": "G435", "description": "Strong''s number for ''man''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 65, 65, 'kjv', 'Then the Lord awaked as one out of sleep', 0, 40, 'question', 'Understanding Divine Action', 'What does it mean for God to ''awake'' in this context, and how does this shape our understanding of His timing in our lives? Reflect on how God''s intervention often comes at unexpected moments, and consider how this can influence your trust in His plans.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 66, 66, 'kjv', 'he smote his enemies in the hinder parts', 4, 44, 'connection', 'Divine Judgment', 'This phrase echoes the theme of divine judgment seen in other scriptures. For example, in Exodus 15:3, the Lord is described as a ''man of war,'' indicating His active role in defeating enemies. Additionally, Psalm 110:5 speaks of the Lord striking through kings in the day of His wrath, reinforcing the idea of God as a warrior against those who oppose Him. These connections highlight the seriousness of rebellion against God.', 'link', '[{"type": "crossReference", "reference": "Exodus 15:3", "description": "Describes the Lord as a warrior."}, {"type": "crossReference", "reference": "Psalm 110:5", "description": "Speaks of God''s judgment against kings."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 66, 66, 'kjv', 'he put them to a perpetual reproach', 46, 81, 'theology', 'Consequences of Rebellion', 'Many scholars believe that the idea of ''perpetual reproach'' highlights the long-lasting consequences of opposing God. This concept is significant in understanding the nature of divine justice; it shows that God not only punishes but also ensures that the memory of rebellion serves as a warning to future generations. This aligns with the broader biblical theme of God’s justice and the importance of living in alignment with His will.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the enduring consequences of divine judgment."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 78, 66, 66, 'kjv', 'he smote his enemies in the hinder parts', 4, 44, 'question', 'Understanding Divine Action', 'What does it mean for God to strike His enemies ''in the hinder parts''? How does this imagery of divine intervention shape our understanding of God’s protection over His people? Reflecting on this phrase can lead to deeper insights into the nature of God''s justice and the assurance that He stands against those who oppose righteousness.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 67, 67, 'kjv', 'Moreover he refused the tabernacle of Joseph, and chose not the tribe of Ephraim:', 0, 81, 'connection', 'Rejection of Ephraim', 'This verse reflects God''s selective choice among the tribes of Israel, which is echoed in Jeremiah 31:9 where God speaks of Ephraim as His firstborn, yet shows a preference for Judah in His plans. This connection highlights the theme of divine sovereignty in choosing leaders and the implications of such choices for the broader community of faith.', 'link', '[{"type": "crossReference", "reference": "Jeremiah 31:9", "description": "God''s relationship with Ephraim as His firstborn."}, {"type": "crossReference", "reference": "1 Chronicles 5:2", "description": "Judah''s preeminence among the tribes."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 67, 67, 'kjv', 'Moreover he refused the tabernacle of Joseph, and chose not the tribe of Ephraim:', 0, 81, 'theology', 'God''s Sovereign Choice', 'This passage suggests that God''s choices are not based on human standards or merit but on His divine purpose. Many scholars believe this reflects the theological principle of election, where God selects certain individuals or groups for specific roles in His redemptive plan, emphasizing His authority and grace in the unfolding of salvation history.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of God''s choice in the tribes."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 78, 67, 67, 'kjv', 'Moreover he refused the tabernacle of Joseph, and chose not the tribe of Ephraim:', 0, 81, 'question', 'Understanding Divine Choices', 'What does God''s refusal of the tabernacle of Joseph and the tribe of Ephraim teach us about His criteria for choosing leaders and communities today? How does this influence our understanding of God’s purpose in our own lives?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 67, 67, 'kjv', 'Moreover he refused the tabernacle of Joseph, and chose not the tribe of Ephraim:', 0, 81, 'greek', 'Understanding ''Refused''', 'ָמָא (ma''a, H3988) - to refuse, reject. This Hebrew term emphasizes the active decision of God in the rejection of Ephraim, highlighting the seriousness of His choices and the implications for the people involved.', 'textformat.abc', '[{"type": "strongs", "reference": "H3988", "description": "Strong''s number for the Hebrew term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 68, 68, 'kjv', 'the tribe of Judah, the mount Zion which he loved.', 10, 60, 'connection', 'Judah and Zion Connection', 'The choice of Judah and Zion resonates with Genesis 49:10, where it is prophesied that the scepter will not depart from Judah until Shiloh comes. This connection emphasizes the significance of Judah as the royal tribe and sets the stage for the Messianic lineage through David. Zion, as the chosen dwelling place of God, underscores His presence among His people, solidifying the theological importance of both Judah and Zion in the biblical narrative.', 'link', '[{"type": "crossReference", "reference": "Genesis 49:10", "description": "Prophecy regarding the tribe of Judah."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 68, 68, 'kjv', 'But chose the tribe of Judah', 0, 28, 'theology', 'Divine Choice and Sovereignty', 'This verse highlights the theme of God''s sovereignty in choosing His people. Many scholars believe that God''s selection of Judah reflects His divine plan for redemption, illustrating that His choices are based on His purpose rather than human merit. This doctrine of election emphasizes God''s grace and the unfolding of His redemptive narrative throughout Scripture.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Insights on God''s sovereignty in selection."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 78, 68, 68, 'kjv', 'the mount Zion which he loved.', 30, 60, 'question', 'Significance of God''s Love', 'What does it mean for God to love Zion? Considering that Zion represents His chosen dwelling and the heart of His people, how does this shape our understanding of God’s relationship with us today? Reflect on how recognizing Zion as a place of divine love influences your view of worship and community within the church.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 68, 68, 'kjv', 'the tribe of Judah', 10, 28, 'greek', 'Significance of ''Judah''', 'יְהוּדָה (Yehudah, H3063) - Judah, meaning ''praised.'' The name signifies the tribe''s role in leading praise to God. This term reflects not only the character of the tribe but also the expectation of the Messiah''s lineage, as Jesus is often referred to as the Lion of the tribe of Judah, further linking praise with redemption.', 'textformat.abc', '[{"type": "strongs", "reference": "H3063", "description": "Strong''s number for Judah."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 69, 69, 'kjv', 'And he built his sanctuary like high palaces,', 0, 45, 'connection', 'Divine Architecture', 'This phrase resonates with the imagery found in Psalm 27:4, where the psalmist expresses a desire to dwell in the house of the Lord. Both passages highlight the significance of God’s dwelling place, emphasizing its beauty and majesty. By comparing the sanctuary to ''high palaces,'' the text suggests a divine grandeur that transcends earthly structures, reinforcing God''s sovereignty and the sacredness of worship.', 'link', '[{"type": "crossReference", "reference": "Psalm 27:4", "description": "Highlights the desire to dwell in God''s house."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 69, 69, 'kjv', 'like the earth which he hath established for ever.', 46, 96, 'theology', 'Eternal Foundations', 'This passage suggests that God’s sanctuary is not only majestic but also eternal, mirroring the stability of creation itself. Many scholars believe this reflects the theological idea that God’s presence and His covenant with His people are enduring. This eternal aspect reassures believers of God’s unchanging nature and His commitment to His people across generations.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 78, 69, 69, 'kjv', 'built his sanctuary like high palaces,', 7, 45, 'greek', 'Sanctuary Significance', 'ναός (naos, G3485) - temple, sanctuary. Although this term is of Greek origin, its use highlights the concept of sacred space in Scripture. The term typically refers to the inner part of the temple, where the divine presence resides. By associating God''s sanctuary with ''high palaces,'' the text emphasizes the holiness and significance of the space where God meets His people.', 'text.book.closed', '[{"type": "strongs", "reference": "G3485", "description": "Refers to the sacred space in biblical contexts."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 69, 69, 'kjv', 'And he built his sanctuary like high palaces,', 0, 45, 'question', 'Understanding Divine Majesty', 'What does it mean for God to build His sanctuary ''like high palaces''? How does this imagery shape your understanding of worship and God''s presence in your life? Reflecting on the grandeur of God''s dwelling can deepen appreciation for the sacredness of communal and personal worship.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 70, 70, 'kjv', 'He chose David also his servant, and took him from the sheepfolds:', 0, 66, 'connection', 'David''s Anointing', 'This verse connects to 1 Samuel 16:11-13, where God instructs Samuel to anoint David as king after rejecting Saul. The choice of David, a shepherd, emphasizes God''s preference for the humble and the overlooked, showing that divine selection often defies human expectations. This narrative highlights the theme of God''s sovereignty in choosing leaders, reinforcing that the heart matters more than outward appearances.', 'link', '[{"type": "crossReference", "reference": "1 Samuel 16:11-13", "description": "The account of David''s anointing by Samuel."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 70, 70, 'kjv', 'He chose David also his servant, and took him from the sheepfolds:', 0, 66, 'theology', 'Divine Election', 'This verse reflects the theological concept of divine election, where God specifically chooses individuals for His purposes. Many scholars believe this serves as a precursor to the New Testament understanding of God''s chosen people and His plan for redemption through Christ, who also came from humble beginnings. This theme of God selecting the least likely to fulfill His plans encourages believers to trust in God''s wisdom and sovereignty.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discussion on the nature of divine election in Scripture."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 78, 70, 70, 'kjv', 'He chose David also his servant, and took him from the sheepfolds:', 0, 66, 'question', 'Understanding God''s Choice', 'What does it mean for God to choose someone from the sheepfolds for significant leadership? How does this challenge our perceptions of worthiness and capability in God''s service? Reflecting on David''s humble beginnings invites us to consider how God might be calling us to serve, regardless of our backgrounds.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 70, 70, 'kjv', 'He chose David also his servant, and took him from the sheepfolds:', 0, 66, 'greek', 'Servant Concept', 'עֶבֶד (ebed, H5650) - servant, slave. This term emphasizes the role of David as God''s chosen servant, which carries the connotation of being devoted and obedient to God''s will. Understanding this term deepens our appreciation of the humility and commitment expected from leaders in God''s kingdom.', 'character.book.closed', '[{"type": "strongs", "reference": "H5650", "description": "Strong''s number for the Hebrew term ''servant''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 71, 71, 'kjv', 'From following the ewes great with young', 0, 40, 'connection', 'David''s Past as Shepherd', 'This phrase recalls David''s humble beginnings as a shepherd, paralleling John 10:11 where Jesus refers to Himself as the Good Shepherd. Both shepherds demonstrate care and leadership, highlighting God''s choice of David as a leader who understands the needs of His people. This connection emphasizes the importance of servant leadership in both the Old and New Testaments.', 'link', '[{"type": "crossReference", "reference": "John 10:11", "description": "Jesus identifies Himself as the Good Shepherd, paralleling David''s role."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 71, 71, 'kjv', 'to feed Jacob his people', 56, 80, 'theology', 'Divine Shepherding', 'This passage suggests that God’s choice of David to shepherd Israel reflects His overarching care for His people. Many scholars believe this foreshadows Christ''s role as the ultimate shepherd, fulfilling the promise of guidance and provision. The imagery of feeding implies nurturing and protection, key themes in God’s covenant relationship with Israel.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 78, 71, 71, 'kjv', 'Israel his inheritance', 86, 108, 'greek', 'Inheritance Concept', 'κληρονομία (klēronomia, G2817) - inheritance, possession, heritage. This term underscores the theological significance of Israel as God’s chosen people, emphasizing the idea of a divine inheritance that involves both privilege and responsibility within the covenant community.', 'textformat.abc', '[{"type": "strongs", "reference": "G2817", "description": "Strong''s number for the Greek term meaning inheritance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 71, 71, 'kjv', 'to feed Jacob his people', 56, 80, 'question', 'Application of Leadership', 'What does it mean for us today to ''feed'' those we lead, whether in our families, communities, or workplaces? How can we embody the same care and integrity that David exemplified in his role as a shepherd?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 72, 72, 'kjv', 'according to the integrity of his heart', 15, 54, 'theology', 'Integrity in Leadership', 'This phrase highlights the importance of moral integrity in leadership, suggesting that a leader''s character profoundly impacts their ability to guide others. Many scholars believe that integrity is a foundational quality for godly leadership, as seen in 1 Timothy 3:2, which emphasizes that an overseer must be above reproach. This concept is crucial, as leaders are often seen as representatives of God''s character to their people.', 'person.2.fill', '[{"type": "crossReference", "reference": "1 Timothy 3:2", "description": "Qualifications for church leaders emphasizing integrity."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 72, 72, 'kjv', 'guided them by the skilfulness of his hands', 60, 103, 'connection', 'Skilfulness and Divine Guidance', 'The mention of ''skilfulness of his hands'' connects to Exodus 31:3, where God fills Bezalel with the Spirit of God, giving him skills for craftsmanship. This parallel is significant as it demonstrates that divine guidance is often expressed through practical skills and wisdom. It reminds us that God equips leaders with the necessary abilities to care for and guide His people effectively.', 'link', '[{"type": "crossReference", "reference": "Exodus 31:3", "description": "God''s empowerment for skilled work."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 72, 72, 'kjv', 'So he fed them', 0, 14, 'question', 'Reflection on Spiritual Nourishment', 'In what ways does the concept of ''feeding'' extend beyond physical sustenance to encompass spiritual nourishment? How does this understanding affect your view of the ways leaders, including Christ, provide for and nurture their followers?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 78, 72, 72, 'kjv', 'integrity of his heart', 32, 54, 'greek', 'Heart and Integrity in Scripture', 'כָּלִיל (kalil, H3632) - complete, whole, perfect. This Hebrew word conveys the idea of wholeness and completeness, emphasizing that true integrity involves a unified heart. This suggests that David''s leadership was characterized by a singular devotion to God and His people, reflecting a heart fully aligned with God''s will.', 'textformat.abc', '[{"type": "strongs", "reference": "H3632", "description": "Strong''s number for the Hebrew word."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 1, 1, 'kjv', 'O God, the heathen are come into thine inheritance;', 0, 51, 'connection', 'Heathen and Inheritance', 'This phrase echoes Deuteronomy 32:8-9, where God assigns nations their inheritance, emphasizing Israel''s unique status as His chosen people. The invasion by ''heathen'' signifies a profound loss of this divine protection and identity, showing how external forces threaten the sacred relationship between God and His people.', 'link', '[{"type": "crossReference", "reference": "Deuteronomy 32:8-9", "description": "Describes God''s division of nations and His inheritance of Israel."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 1, 1, 'kjv', 'thy holy temple have they defiled;', 52, 86, 'theology', 'Defilement of the Temple', 'Many scholars believe this verse symbolizes a profound theological crisis, where the defilement of the temple represents the abandonment of divine presence among the people. This reflects the notion of God''s holiness being violated, which is central to understanding the significance of worship and reverence in Israel''s covenant relationship with God.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 79, 1, 1, 'kjv', 'they have laid Jerusalem on heaps.', 87, 121, 'question', 'Jerusalem''s Desolation', 'What does it mean for a city, especially Jerusalem, to be laid on heaps? Consider how this destruction impacts the identity of the people of God. How does the physical state of Jerusalem reflect spiritual desolation, and what does this teach us about the consequences of turning away from God?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 2, 2, 'kjv', 'The dead bodies of thy servants', 0, 31, 'connection', 'Connection to Lamentations', 'This phrase echoes the lament found in Lamentations 4:10, where the dire situation of Jerusalem leads to extreme sorrow and suffering. The connection highlights a theme of mourning for God''s people and the deep loss experienced in times of national crisis. Both passages reflect a profound sense of grief over the fate of the faithful.', 'link', '[{"type": "crossReference", "reference": "Lamentations 4:10", "description": "A parallel verse that emphasizes the tragedy of the dead during the siege of Jerusalem."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 2, 2, 'kjv', 'given to be meat unto the fowls of the heaven', 42, 87, 'theology', 'Theological Implications of Defilement', 'Many scholars believe this imagery signifies utter defeat and dishonor, where the bodies of the righteous are treated with the utmost disrespect. This reflects the broader biblical theme of God''s judgment and the consequences of sin, suggesting that even in destruction, God''s sovereignty and the gravity of human sinfulness are underscored.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of disgrace in death as a result of sin."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 79, 2, 2, 'kjv', 'the flesh of thy saints unto the beasts of the earth', 89, 141, 'greek', 'Understanding ''saints''', 'חסידים (chassidim, H2610) - saints, the faithful ones. This term indicates not just the moral quality of the individuals but their relationship with God, signifying those who are set apart for divine service. Its use here emphasizes the tragedy of their demise, as they are revered figures whose sacrifice is a profound loss for the community.', 'textformat.abc', '[{"type": "strongs", "reference": "H2610", "description": "Strong''s number for the Hebrew term for saints."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 2, 2, 'kjv', 'The dead bodies of thy servants', 0, 31, 'question', 'Reflection on Servanthood', 'What does it mean to be a servant of God when facing persecution or suffering? How can understanding the fate of God''s servants here inspire us to persevere in our faith amidst trials?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 3, 3, 'kjv', 'Their blood have they shed like water', 0, 37, 'connection', 'Connection to Lamentations', 'The phrase ''shed like water'' evokes the imagery of widespread violence and loss. This can be connected to Lamentations 3:48-49, where it states, ''Mine eyes do fail with tears; my bowels are troubled.'' Both passages reflect the deep sorrow and devastation brought about by war and destruction. This connection highlights the gravity of the situation faced by Jerusalem and the emotional response it elicited from the faithful.', 'link', '[{"type": "crossReference", "reference": "Lamentations 3:48-49", "description": "A parallel expression of grief due to suffering."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 3, 3, 'kjv', 'shed like water', 22, 37, 'greek', 'Significance of ''shed''', 'שָׁדַף (shadaph, H8210) - to shed, pour out. This Hebrew term emphasizes the violent and careless manner in which blood is spilled. It conveys a sense of abundance and loss, likening the blood of the slain to a flowing stream. Such imagery underscores the tragic nature of the events in Jerusalem, where life is extinguished without regard.', 'textformat.abc', '[{"type": "strongs", "reference": "H8210", "description": "Strong''s number for the Hebrew term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 3, 3, 'kjv', 'there was none to bury them', 65, 92, 'theology', 'Theological Implications of Burial', 'Many scholars believe that the absence of burial rites signifies extreme disgrace and divine judgment. In ancient Israel, proper burial was crucial for honoring the dead and ensuring their peace in the afterlife. The lack of burial reflects not only the severity of the violence inflicted but also suggests that God''s favor has departed from His people, leading to their utter humiliation.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explains the significance of burial customs in ancient Israel."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 79, 3, 3, 'kjv', 'Their blood have they shed like water round about Jerusalem', 0, 59, 'question', 'Reflection on Violence and Mourning', 'What does the imagery of blood being shed like water reveal about the nature of violence and its impact on a community? How can this understanding inform our response to violence and loss in our own lives and communities today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 4, 4, 'kjv', 'We are become a reproach to our neighbours,', 0, 43, 'connection', 'Reproach in Scripture', 'This phrase echoes Jeremiah 20:8, where the prophet laments being a reproach to all. Like the Psalmist, Jeremiah experiences scorn from surrounding nations due to perceived failures. This connection underscores a theme of communal identity and suffering, suggesting that a nation''s moral and spiritual state can lead to external derision.', 'link', '[{"type": "crossReference", "reference": "Jeremiah 20:8", "description": "Another instance of reproach faced by God''s people."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 4, 4, 'kjv', 'a scorn and derision to them that are round about us.', 44, 97, 'theology', 'Group Identity and Shame', 'Many scholars believe this passage highlights how collective sin can lead to national shame, affecting the reputation of God''s people. The scorn from neighbors serves as a reminder of the covenantal relationship between Israel and God, where obedience results in blessing, and disobedience results in reproach (Deuteronomy 28:37).', 'person.2.fill', '[{"type": "commentary", "reference": "Deuteronomy 28:37", "description": "Discusses consequences of disobedience."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 79, 4, 4, 'kjv', 'We are become a reproach to our neighbours,', 0, 43, 'question', 'Impact of Reproach', 'In what ways does our personal or communal conduct reflect on our witness to others? How does the concept of being a ''reproach'' challenge us to live in a way that honors God and draws others to Him?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 4, 4, 'kjv', 'a scorn and derision to them that are round about us.', 44, 97, 'greek', 'Meaning of ''Scorn''', 'בוז (būz, H936) - to mock, deride, or scorn. This term conveys a deeper sense of contempt and signifies the emotional pain felt by the Israelites as they are subjected to mockery from surrounding nations due to their circumstances.', 'textformat.abc', '[{"type": "strongs", "reference": "H936", "description": "Strong''s number for the Hebrew term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 5, 5, 'kjv', 'How long, Lord? wilt thou be angry for ever?', 0, 44, 'connection', 'Question of Divine Patience', 'This plea echoes the sentiment found in Habakkuk 1:2, where the prophet also questions how long God will remain silent in the face of injustice. Both texts reflect a deep yearning for divine intervention and highlight the human experience of suffering and impatience. Understanding this connection enriches our grasp of the persistent human struggle with God''s timing in responding to distress.', 'link', '[{"type": "crossReference", "reference": "Habakkuk 1:2", "description": "The prophet expresses a similar cry for justice and understanding."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 5, 5, 'kjv', 'How long, Lord? wilt thou be angry for ever?', 0, 44, 'theology', 'Understanding God''s Anger', 'Many scholars believe that God''s anger, as depicted here, is not a capricious emotion but a response to sin and injustice. It serves as a reminder of His holiness and righteousness. This passage suggests that God''s anger is ultimately aimed at correction and restoration, which aligns with the broader biblical narrative of redemption through divine discipline.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explains the nature of divine anger in the context of sin."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 79, 5, 5, 'kjv', 'shall thy jealousy burn like fire?', 45, 79, 'greek', 'Understanding Jealousy', 'קִנְאָה (qin''ah, H7068) - jealousy, zeal. This term often conveys God''s passionate commitment to His covenant people. It signifies His desire for their fidelity and the serious consequences of turning away from Him. This understanding of divine jealousy deepens our appreciation for the intensity of God''s love and the seriousness with which He regards the relationship He has with His followers.', 'textformat.abc', '[{"type": "strongs", "reference": "H7068", "description": "Strong''s number for the Hebrew word translated as ''jealousy''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 5, 5, 'kjv', 'How long, Lord? wilt thou be angry for ever?', 0, 44, 'question', 'Reflecting on God''s Timing', 'What does it reveal about our relationship with God when we question His timing and anger? How can this verse guide us in moments of doubt when we feel abandoned in our struggles? Reflecting on these questions may deepen our trust in God''s ultimate plan and His timing, reminding us of His presence even in trials.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 79, 6, 6, 'kjv', 'Pour out thy wrath upon the heathen', 0, 35, 'connection', 'God''s Judgment on Nations', 'This phrase resonates with Jeremiah 10:25, where the prophet pleads for God''s wrath upon nations that do not know Him. Both verses reflect a deep concern for justice and divine retribution against those who reject God. Understanding this connection highlights the consistent biblical theme of God''s sovereignty over all nations and His righteous anger towards idolatry and injustice.', 'link', '[{"type": "crossReference", "reference": "Jeremiah 10:25", "description": "A similar plea for God''s judgment on nations that do not acknowledge Him."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');