INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 94, 23, 23, 'kjv', 'he shall bring upon them their own iniquity', 4, 47, 'connection', 'Divine Justice Theme', 'This phrase resonates with Galatians 6:7, which states, ''For whatever one sows, that will he also reap.'' Both verses emphasize the principle of divine justice, where individuals ultimately face the consequences of their actions. This connection highlights God''s role as both a just judge and a protector of the innocent.', 'link', '[{"type": "crossReference", "reference": "Galatians 6:7", "description": "Reinforces the principle of reaping what one sows."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 94, 23, 23, 'kjv', 'cut them off in their own wickedness', 59, 95, 'theology', 'Consequences of Sin', 'This passage suggests that God''s justice involves not only punishment but also a cessation of the wicked''s influence. Many scholars believe this reflects the doctrine of God''s holiness, where sin cannot coexist with His righteousness. Thus, the ultimate fate of the wicked serves as a sobering reminder of the seriousness of sin in light of God''s character.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 94, 23, 23, 'kjv', 'the Lord our God shall cut them off', 102, 137, 'question', 'Understanding Divine Action', 'What does it mean for the Lord to ''cut off'' the wicked? How does this shape our understanding of God''s justice and mercy? Reflecting on this question can lead to deeper insights about the nature of God as both a protector of righteousness and a judge of sin.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 1, 1, 'kjv', 'O come, let us sing unto the Lord:', 0, 34, 'connection', 'Invitation to Worship', 'This call to worship parallels Hebrews 10:25, which encourages believers not to forsake gathering together. The communal aspect of singing is significant in both passages, emphasizing the importance of collective praise. By coming together to sing, believers affirm their faith and encourage one another in their relationship with God.', 'link', '[{"type": "crossReference", "reference": "Hebrews 10:25", "description": "Encouragement to gather for worship"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 1, 1, 'kjv', 'let us make a joyful noise', 35, 61, 'theology', 'Joyful Expression of Faith', 'Many scholars believe that the phrase ''make a joyful noise'' reflects the theological concept of expressing joy in worship. This joy is not merely emotional but rooted in the acknowledgment of God''s greatness and salvation. The act of singing is a response to God''s character and actions, indicating that worship should be both heartfelt and exuberant.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 1, 1, 'kjv', 'to the rock of our salvation.', 62, 91, 'greek', 'Significance of ''Rock''', 'πέτρα (petra, G4073) - rock, cliff, boulder. This term emphasizes stability and strength, suggesting that God is a solid foundation for salvation. The imagery of God as a ''rock'' conveys safety and assurance, contrasting the instability of worldly things.', 'textformat.abc', '[{"type": "strongs", "reference": "G4073", "description": "Strong''s number for the Greek word ''rock''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 1, 1, 'kjv', 'let us sing unto the Lord:', 8, 34, 'question', 'Why Sing to the Lord?', 'What does it mean to sing unto the Lord as an act of worship? How does this practice shape our understanding of His character and our relationship with Him? Reflecting on the nature of God as the ''rock'' of our salvation, how does this influence the way we express our praise?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 2, 2, 'kjv', 'Let us come before his presence with thanksgiving,', 0, 50, 'connection', 'Thanksgiving in Worship', 'This phrase echoes 1 Thessalonians 5:18, which encourages believers to give thanks in all circumstances. The connection emphasizes the importance of gratitude in approaching God, suggesting that thanksgiving should be a foundational aspect of our worship and relationship with Him.', 'link', '[{"type": "crossReference", "reference": "1 Thessalonians 5:18", "description": "Encourages gratitude in all situations."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 2, 2, 'kjv', 'make a joyful noise unto him with psalms.', 55, 96, 'theology', 'Expression of Joy in Worship', 'This passage highlights the theological concept that worship is not merely internal but should be expressed outwardly through joyful noise and music. Many scholars believe that this reflects God''s desire for His people to engage fully in worship, embodying both joy and reverence as they come before Him.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 2, 2, 'kjv', 'Let us come before his presence with thanksgiving,', 0, 50, 'question', 'Approaching God''s Presence', 'What does it mean to come before God with thanksgiving, and how can this shape the way we approach Him in prayer and worship? How does recognizing His presence influence our attitude toward gratitude?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 2, 2, 'kjv', 'make a joyful noise unto him with psalms.', 55, 96, 'greek', 'Joyful Noise in Greek', 'φωνή (phōnē, G5456) - voice, sound, or noise. This term emphasizes the vocal aspect of worship, suggesting that our expressions of joy should be audible and vibrant, reflecting the heart''s delight in God''s presence.', 'textformat.abc', '[{"type": "strongs", "reference": "G5456", "description": "Greek term for voice or sound."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 3, 3, 'kjv', 'For the Lord is a great God,', 0, 28, 'connection', 'Divine Sovereignty', 'This phrase connects with 1 Chronicles 16:25, which states, ''For great is the Lord and greatly to be praised.'' Both verses affirm the unparalleled greatness of God, emphasizing His sovereignty and majesty over all creation. Recognizing God''s greatness is foundational for understanding His authority and our relationship with Him.', 'link', '[{"type": "crossReference", "reference": "1 Chronicles 16:25", "description": "Affirms God''s greatness and sovereignty."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 3, 3, 'kjv', 'and a great King above all gods.', 29, 61, 'theology', 'Supremacy of God', 'This passage suggests that God is not only great but holds supremacy over all other deities, which aligns with the doctrine of monotheism prevalent in Scripture. Many scholars believe this reflects the biblical worldview that recognizes no rivals to God''s authority, reinforcing the importance of worshiping Him alone.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 3, 3, 'kjv', 'For the Lord is a great God,', 0, 28, 'question', 'Understanding Divine Greatness', 'What does it mean for God to be ''great'' in your daily life? How can this understanding shape your worship and response to challenges? Reflecting on God''s greatness can lead to deeper trust and reliance on Him in all circumstances.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 3, 3, 'kjv', 'and a great King above all gods.', 29, 61, 'greek', 'Understanding ''King''', 'κύριος (kurios, G2962) - lord, master, owner. This term emphasizes authority and dominion. In this context, it highlights God''s rule over all spiritual entities, reinforcing His ultimate power and lordship.', 'textformat.abc', '[{"type": "strongs", "reference": "G2962", "description": "Indicates authority and mastery."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 4, 4, 'kjv', 'In his hand are the deep places of the earth', 0, 44, 'connection', 'Creation''s Depths', 'This phrase connects with Job 38:16, where God questions Job, asking, ''Have you entered into the springs of the sea, or walked in the recesses of the deep?'' Both verses emphasize God''s sovereignty over the depths of creation, highlighting His omnipotence. Understanding God''s control over the earth''s foundations encourages believers to trust in His power and wisdom.', 'link', '[{"type": "crossReference", "reference": "Job 38:16", "description": "God''s questioning of Job regarding the mysteries of creation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 4, 4, 'kjv', 'the strength of the hills is his also.', 46, 84, 'theology', 'God''s Sovereignty Over Creation', 'This passage suggests that both the deep places and the hills are under God''s authority, underscoring His sovereignty over all creation. Many scholars believe this reflects the biblical teaching that God is not only the creator of the earth but also its sustainer. This understanding can deepen our worship as we recognize God''s majesty in both the vastness of the seas and the grandeur of the mountains.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 4, 4, 'kjv', 'In his hand are the deep places of the earth', 0, 44, 'question', 'Understanding Divine Control', 'What does it mean for the deep places of the earth to be in God''s hand? How does this truth affect your understanding of His control over the challenges and uncertainties in your life? Reflecting on God''s sovereignty can provide peace amid turmoil.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 5, 5, 'kjv', 'The sea is his, and he made it: and his hands formed the dry land.', 0, 66, 'connection', 'Creation Affirmation', 'This verse echoes Genesis 1:10, where God declares the dry land ''Earth'' and the gathered waters ''Seas.'' The connection highlights God''s sovereignty and creative power over all creation. Recognizing God as the creator of both sea and land deepens our understanding of His authority and the inherent order in the universe.', 'link', '[{"type": "crossReference", "reference": "Genesis 1:10", "description": "Affirms God''s role as the creator of land and sea."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 5, 5, 'kjv', 'he made it', 20, 30, 'greek', 'Creative Authority', 'ἐποίησεν (epoiēsen, G4160) - made, created. This term emphasizes God''s active role in creation, indicating not just passive existence but intentional craftsmanship. Understanding this word deepens our appreciation of God''s creative power and His personal investment in the world.', 'textformat.abc', '[{"type": "strongs", "reference": "G4160", "description": "Indicates the action of making or creating."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 5, 5, 'kjv', 'his hands formed', 36, 52, 'theology', 'Divine Craftsmanship', 'This phrase suggests a personal, hands-on approach to creation. Many scholars believe it signifies God''s intimate involvement in shaping the world, contrasting the idea of a distant creator. This theological perspective invites believers to see God''s care and intentionality in the created order.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the personal nature of God''s creative work."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 5, 5, 'kjv', 'The sea is his', 0, 14, 'question', 'Ownership Reflection', 'What does it mean for you personally that ''the sea is his''? How does recognizing God''s ownership over creation influence your understanding of stewardship and your relationship with the environment?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 6, 6, 'kjv', 'O come, let us worship and bow down:', 0, 36, 'connection', 'Call to Worship', 'This phrase resonates with other calls to worship found in Scripture, such as Psalm 100:2 which invites believers to ''serve the Lord with gladness.'' Both passages emphasize a communal aspect of worship, urging the faithful to come together in reverence. This connection highlights the importance of worship not just as an individual act, but as a collective expression of faith.', 'link', '[{"type": "crossReference", "reference": "Psalm 100:2", "description": "Another invitation to communal worship."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 6, 6, 'kjv', 'let us kneel before the Lord our maker.', 37, 76, 'theology', 'Posture of Worship', 'The act of kneeling is a significant posture in worship, symbolizing humility and submission before God. Many scholars believe this physical act reflects an inner attitude of reverence and acknowledgment of God''s sovereignty. This passage suggests that worship involves both our hearts and our bodies, indicating the holistic nature of approaching God.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 6, 6, 'kjv', 'let us worship and bow down:', 8, 36, 'greek', 'Key Greek Terms', 'προσκυνέω (proskyneō, G4352) - to worship, to prostrate oneself. This term indicates an act of deep reverence, often involving physical submission. Its usage in this verse underscores the seriousness and depth of worship as a response to God''s majesty and authority.', 'textformat.abc', '[{"type": "strongs", "reference": "G4352", "description": "Greek term for worship."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 6, 6, 'kjv', 'let us kneel before the Lord our maker.', 37, 76, 'question', 'Reflections on Worship', 'What does it mean for you personally to kneel before the Lord? How does this physical act of submission reflect your relationship with God and your understanding of His creation? Consider how approaching God in worship affects your daily life and priorities.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 7, 7, 'kjv', 'For he is our God;', 0, 18, 'connection', 'Divine Identity', 'This phrase connects with Exodus 20:2, where God declares, ''I am the Lord your God,'' establishing a personal relationship with His people. Recognizing God’s identity as our God is foundational to understanding our place in His creation and His covenant with us.', 'link', '[{"type": "crossReference", "reference": "Exodus 20:2", "description": "God identifies Himself to His people."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 7, 7, 'kjv', 'the sheep of his hand.', 61, 83, 'theology', 'Shepherd Metaphor', 'This imagery reflects the pastoral care of God over His people, akin to the relationship depicted in John 10:14, where Jesus states, ''I am the good shepherd.'' Many scholars believe this highlights the intimate care and guidance God provides, assuring believers of His protective presence.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 7, 7, 'kjv', 'To day if ye will hear his voice,', 84, 117, 'question', 'Responding to God''s Voice', 'What does it mean to ''hear his voice'' in our daily lives? How can we cultivate a posture of listening to God amidst distractions? Reflecting on this can deepen our understanding of obedience and relationship with Him.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 7, 7, 'kjv', 'we are the people of his pasture,', 23, 56, 'greek', 'Pasture Metaphor', 'ποιμήν (poimēn, G4166) - shepherd, one who leads. This term emphasizes the role of God as a caring leader who guides His people, akin to a shepherd caring for their flock. It reinforces the idea of community and belonging in the faith.', 'textformat.abc', '[{"type": "strongs", "reference": "G4166", "description": "Strong''s number for ''shepherd.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 8, 8, 'kjv', 'Harden not your heart,', 0, 22, 'connection', 'Warning Against Hardness', 'This phrase echoes Hebrews 3:8, which warns believers not to harden their hearts as the Israelites did. The connection emphasizes the eternal relevance of this struggle against spiritual stubbornness and encourages vigilance in faith. It serves as a reminder that receptiveness to God''s voice is crucial for spiritual growth.', 'link', '[{"type": "crossReference", "reference": "Hebrews 3:8", "description": "A New Testament warning against hardening one''s heart."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 8, 8, 'kjv', 'as in the provocation,', 23, 45, 'theology', 'Nature of Provocation', 'The term ''provocation'' refers to the Israelites'' rebellion against God during their wilderness journey. Many scholars believe this illustrates the human tendency to challenge divine authority in times of trial. It reflects a broader theological theme of the need for faithfulness and trust in God''s provision, even amidst difficulties.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explains the significance of Israel''s rebellion."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 8, 8, 'kjv', 'and as in the day of temptation in the wilderness:', 46, 96, 'question', 'Reflecting on Temptation', 'What does it reveal about our faith when we face temptation as the Israelites did? How can we learn to respond to life''s challenges without hardening our hearts against God''s guidance? Reflecting on this can help us identify our own moments of temptation and the importance of remaining open to God''s leading.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 9, 9, 'kjv', 'When your fathers tempted me', 0, 28, 'connection', 'Historical Provocation', 'This phrase recalls the Israelites'' rebellion against God during the Exodus. Notably, it connects with Numbers 14:22, where God recounts their repeated testing in the wilderness. Understanding this historical context emphasizes the seriousness of their actions and God''s expectations of faithfulness from His people.', 'link', '[{"type": "crossReference", "reference": "Numbers 14:22", "description": "God recounts the Israelites'' testing of Him during the wilderness journey."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 9, 9, 'kjv', 'proved me', 30, 39, 'greek', 'Meaning of ''Proved''', 'דָּבַר (dabar, H1696) - to speak, declare, command. In this context, it conveys the notion of testing God’s character and promises, revealing a lack of trust. This term underscores the gravity of the Israelites’ actions as they questioned God''s fidelity despite experiencing His miracles.', 'textformat.abc', '[{"type": "strongs", "reference": "H1696", "description": "Strong''s number for the Hebrew word meaning ''to speak'' or ''declare''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 9, 9, 'kjv', 'saw my work', 45, 56, 'theology', 'Understanding God''s Work', 'This passage suggests that witnessing God''s miraculous deeds was not sufficient for the Israelites to maintain faith. Many scholars believe this challenges the notion that signs and wonders alone inspire belief, emphasizing the need for a heart aligned with God’s ways and the importance of faith beyond mere observation.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 9, 9, 'kjv', 'When your fathers tempted me', 0, 28, 'question', 'Reflections on Testing God', 'What does it reveal about human nature when we test or doubt God after experiencing His blessings? How can understanding this help us avoid similar pitfalls in our faith journey today?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 10, 10, 'kjv', 'Forty years long was I grieved with this generation,', 0, 52, 'connection', 'Wilderness Experience', 'This phrase connects to Hebrews 3:17, which references God''s grief over the disobedient generation in the wilderness. The Israelites'' prolonged disbelief serves as a warning to believers. Understanding this context emphasizes the seriousness of responding to God''s call and the consequences of hardening one''s heart.', 'link', '[{"type": "crossReference", "reference": "Hebrews 3:17", "description": "Highlights God''s response to Israel''s unbelief."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 10, 10, 'kjv', 'and said, It is a people that do err in their heart,', 53, 105, 'theology', 'Human Heart''s Deception', 'This passage suggests that the human heart can lead one astray. Theologically, it aligns with Jeremiah 17:9, which states that the heart is deceitful above all things. Many scholars believe that recognizing this truth is crucial for spiritual growth, as it prompts believers to seek God''s guidance and wisdom in their lives.', 'sparkles', '[{"type": "crossReference", "reference": "Jeremiah 17:9", "description": "Discusses the deceitfulness of the heart."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 10, 10, 'kjv', 'and they have not known my ways:', 106, 138, 'question', 'Knowledge of God''s Ways', 'What does it mean to truly know God''s ways? Reflecting on this can reveal areas in our lives where we may not fully understand or follow His guidance. How can we actively seek to learn and embody His ways in our daily decisions and actions?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 11, 11, 'kjv', 'Unto whom I sware in my wrath', 0, 29, 'connection', 'Divine Promises and Wrath', 'This phrase connects to Hebrews 3:18-19, where it states, ''And to whom did He swear that they would not enter His rest, but to those who did not obey?'' This connection emphasizes the seriousness of God''s promises and the consequences of disbelief, illustrating the weight of divine judgment when His people refuse to trust Him.', 'link', '[{"type": "crossReference", "reference": "Hebrews 3:18-19", "description": "Highlights the consequences of disobedience and the connection to God''s rest."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 11, 11, 'kjv', 'that they should not enter into my rest.', 30, 70, 'theology', 'Understanding God''s Rest', 'Many scholars believe that ''rest'' in this context symbolizes the ultimate peace and communion with God, which is a central theme in both the Old and New Testaments. This verse reflects the consequences of sin and disobedience, suggesting that true rest is reserved for those who trust and obey God, as seen in the promise of salvation through faith.', 'person.2.fill', '[{"type": "commentary", "reference": "Psalms Commentary by Charles Spurgeon", "description": "Discusses God''s rest as a spiritual state of peace."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 95, 11, 11, 'kjv', 'I sware in my wrath', 10, 29, 'greek', 'Hebrew Term for Wrath', 'חָרוּ (charah, H2734) - to be hot, to blaze up; often translated as ''wrath'' or ''anger.'' This term indicates a strong emotional response from God towards disobedience, emphasizing the seriousness of His covenant and the implications of breaking it.', 'textformat.abc', '[{"type": "strongs", "reference": "H2734", "description": "Strong''s definition and usage of the term for anger in Scripture."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 95, 11, 11, 'kjv', 'that they should not enter into my rest.', 30, 70, 'question', 'What is True Rest?', 'Considering the phrase ''my rest,'' how does this understanding of rest challenge or affirm your current spiritual state? In what ways might you be resisting God''s call to enter into a deeper relationship with Him?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 96, 1, 1, 'kjv', 'O sing unto the Lord a new song', 0, 31, 'connection', 'New Song Theme', 'The call to sing a ''new song'' resonates with similar exhortations found in Isaiah 42:10 and Revelation 5:9. In Isaiah, the people are encouraged to sing a fresh expression of praise for God''s deliverance, while in Revelation, the new song symbolizes the unique salvation accomplished through Christ. This connection emphasizes that our worship should continually reflect our experiences of God''s grace.', 'link', '[{"type": "crossReference", "reference": "Isaiah 42:10", "description": "Encouragement to sing a new song praising God''s deliverance."}, {"type": "crossReference", "reference": "Revelation 5:9", "description": "The new song as a symbol of salvation through Christ."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 96, 1, 1, 'kjv', 'sing unto the Lord', 2, 20, 'theology', 'Worship as Response', 'This verse underscores the theological principle that worship is an active response to God''s nature and deeds. Many scholars believe that singing is a communal act that not only glorifies God but also strengthens the community of believers, illustrating the importance of corporate worship in the life of faith.', 'sparkles', '[{"type": "commentary", "reference": "Wright, N.T. ''The Psalms as Prayer.''", "description": "Explores the significance of worship and community in the Psalms."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 96, 1, 1, 'kjv', 'all the earth', 53, 66, 'question', 'Universal Praise', 'What does it mean for all the earth to sing unto the Lord? How does this universal call to worship shape our understanding of God''s sovereignty and the mission of the church in proclaiming His glory to every nation?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 96, 2, 2, 'kjv', 'Sing unto the Lord, bless his name;', 0, 35, 'connection', 'Call to Worship', 'This phrase resonates with Psalm 100:2, which similarly calls for joyful worship and service to the Lord. Both passages emphasize the importance of active participation in praising God. The repetition across Scriptures highlights a communal aspect of worship, inviting all to acknowledge and celebrate God''s goodness.', 'link', '[{"type": "crossReference", "reference": "Psalm 100:2", "description": "Encourages joyful service and worship of the Lord."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 96, 2, 2, 'kjv', 'shew forth his salvation from day to day.', 36, 77, 'theology', 'Daily Declaration of Salvation', 'Many scholars believe this expression highlights the ongoing nature of God''s salvation, suggesting that believers are called to continually share their testimonies. This reflects the doctrine of proclaiming the Gospel, emphasizing that salvation is not merely a past event but a present reality that should be actively communicated.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 96, 2, 2, 'kjv', 'bless his name;', 20, 35, 'greek', 'Meaning of ''Bless''', 'εὐλογέω (eulogeō, G2127) - to bless, to praise. This term underscores the act of acknowledging God''s goodness and favor towards humanity. In the context of worship, it suggests an active response of gratitude and reverence, which is foundational in the life of a believer.', 'textformat.abc', '[{"type": "strongs", "reference": "G2127", "description": "Strong''s number for the Greek term used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');