INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 97, 2, 2, 'kjv', 'Clouds and darkness are round about him:', 0, 40, 'question', 'Understanding Divine Mystery', 'What does the presence of ''clouds and darkness'' signify about our understanding of God? How does this imagery challenge our perceptions of His nature and presence in our lives?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 2, 2, 'kjv', 'righteousness and judgment are the habitation of his throne.', 41, 101, 'greek', 'Key Greek Terms', 'δικαιοσύνη (dikaiosynē, G1343) – righteousness, justice; κρίσις (krisis, G2917) – judgment. These terms emphasize the moral perfection and authoritative decision-making that define God''s rule. Understanding these concepts can deepen our appreciation for His governance over creation.', 'textformat.abc', '[{"type": "strongs", "reference": "G1343", "description": "Strong''s definition of righteousness"}, {"type": "strongs", "reference": "G2917", "description": "Strong''s definition of judgment"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 3, 3, 'kjv', 'A fire goeth before him,', 0, 24, 'connection', 'Divine Presence in Fire', 'The imagery of fire preceding the Lord is echoed in Exodus 13:21, where the Lord leads the Israelites with a pillar of fire. This connection emphasizes God''s active presence and guidance, portraying Him as both protector and judge. The consuming nature of fire symbolizes divine purification and the seriousness of His judgment against His enemies.', 'link', '[{"type": "crossReference", "reference": "Exodus 13:21", "description": "God''s presence as a guiding fire"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 3, 3, 'kjv', 'burneth up his enemies round about.', 29, 64, 'theology', 'Judgment and Justice', 'This passage suggests that God''s judgment is both immediate and comprehensive. Many scholars believe it reflects the theological principle that divine justice will ultimately prevail over evil. The consuming fire represents not only the destruction of enemies but also the righteousness of God, affirming that He stands against injustice and oppression.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry\u2019s Commentary", "description": "Insights on divine judgment and justice"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 3, 3, 'kjv', 'A fire goeth before him,', 0, 24, 'greek', 'Fire as Divine Manifestation', 'אֵשׁ (esh, H784) - fire, flame. This Hebrew term conveys the intensity and purity of God''s presence. Fire is often associated with God''s holiness and the concept of divine refinement, indicating that His presence can both illuminate and consume, highlighting the dual nature of His character as loving and just.', 'textformat.abc', '[{"type": "strongs", "reference": "H784", "description": "Strong''s number for fire in Hebrew"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 3, 3, 'kjv', 'burneth up his enemies round about.', 29, 64, 'question', 'Reflecting on Divine Justice', 'What does it mean for God to ''burn up his enemies''? How can we reconcile this image of divine wrath with the notion of God''s love and mercy? In what ways does our understanding of justice shape our relationship with God and our response to evil in the world?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 4, 4, 'kjv', 'His lightnings enlightened the world', 0, 36, 'connection', 'Cross-reference to Revelation', 'The phrase ''His lightnings enlightened the world'' can be connected to Revelation 4:5, which describes lightening and thunder coming from the throne of God. This connection highlights God''s supreme power and presence, showing that His divine authority extends over all creation. Just as the lightning signifies His glory and might, it serves as a reminder of His judgment and the awe He inspires in humanity.', 'link', '[{"type": "crossReference", "reference": "Revelation 4:5", "description": "Describes God''s glory with thunder and lightening."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 4, 4, 'kjv', 'the earth saw, and trembled', 38, 65, 'theology', 'The Fear of the Lord', 'This passage illustrates the reverence and fear that God''s presence evokes in creation. Many scholars believe that ''the earth saw, and trembled'' reflects the biblical theme that true acknowledgment of God leads to a proper fear of Him, which is the beginning of wisdom (Proverbs 9:10). This fear is not merely terror but a profound respect for God''s holiness and sovereignty.', 'sparkles', '[{"type": "commentary", "reference": "Proverbs 9:10", "description": "Highlights the connection between fear of the Lord and wisdom."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 4, 4, 'kjv', 'His lightnings enlightened the world', 0, 36, 'greek', 'Significance of ''lightnings''', 'ἀστραπὴ (astrape, G796) - lightning, brightness. This term emphasizes the sudden and powerful nature of God''s revelation. In the biblical context, light often symbolizes truth and divine presence, suggesting that God''s revelations can illuminate dark places in our lives and the world around us.', 'textformat.abc', '[{"type": "strongs", "reference": "G796", "description": "Greek word for ''lightning.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 4, 4, 'kjv', 'the earth saw, and trembled', 38, 65, 'question', 'Earth''s Response to God', 'What does it mean for the earth to ''tremble'' in response to God''s light? How does this physical reaction of creation inform our understanding of God''s holiness and majesty? Reflect on how our own lives respond to the presence of God and whether we truly recognize His authority.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 5, 5, 'kjv', 'The hills melted like wax', 0, 25, 'connection', 'Hills in Scripture', 'The imagery of hills melting suggests a powerful transformation in God''s presence, similar to Micah 1:4, where mountains melt under the Lord''s coming. This emphasizes God''s sovereignty and the earth''s response to His might. Additionally, in Exodus 19:18, the mountain trembled at God''s descent, highlighting the awe and fear evoked by His presence.', 'link', '[{"type": "crossReference", "reference": "Micah 1:4", "description": "Highlights God''s power over creation."}, {"type": "crossReference", "reference": "Exodus 19:18", "description": "Demonstrates the earth''s reaction to God''s presence."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 5, 5, 'kjv', 'at the presence of the Lord', 26, 53, 'theology', 'God''s Immanence', 'This phrase reflects the theological concept of God''s immanence, suggesting that God''s presence is not only transcendent but also intimately involved in creation. Many scholars believe this underscores the transformative power of God''s holiness, which can radically alter the natural order, pointing to the need for reverence in His presence.', 'sparkles', '[{"type": "commentary", "reference": "The New Bible Commentary", "description": "Discusses God''s presence and its implications."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 5, 5, 'kjv', 'at the presence of the Lord of the whole earth', 55, 101, 'question', 'Global Sovereignty', 'Considering that the Lord is described as ''the Lord of the whole earth'', how does this understanding of His sovereignty influence our view of global events and our response to them? What does it mean to acknowledge His authority in a world that often seems chaotic?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 6, 6, 'kjv', 'The heavens declare his righteousness', 0, 37, 'connection', 'Heavens and Creation', 'This phrase echoes Genesis 1:14, where God creates the heavens as signs for seasons and days. Both passages highlight the role of creation in proclaiming God''s attributes. Understanding this connection enriches our appreciation of how the created order serves as a testament to God''s righteousness and glory.', 'link', '[{"type": "crossReference", "reference": "Genesis 1:14", "description": "Creation of the heavens as signs"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 6, 6, 'kjv', 'his righteousness', 20, 37, 'theology', 'Understanding Righteousness', 'Many scholars believe that ''righteousness'' here refers to God''s perfect moral character and His justice. This passage suggests that God''s actions in the world reflect His righteous nature, providing a foundation for believers to trust in His divine governance and moral order.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explains the nature of God''s righteousness"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 6, 6, 'kjv', 'all the people see his glory', 43, 71, 'question', 'Seeing God''s Glory', 'What does it mean for ''all the people'' to see God''s glory? In what ways do we observe His glory in our daily lives, and how does this visibility impact our faith and witness to others?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 6, 6, 'kjv', 'The heavens declare his righteousness', 0, 37, 'greek', 'Righteousness in Hebrew', 'צְדָקָה (tsedaqah, H6666) - righteousness, justice, integrity. This term emphasizes not only God''s justice but also the relational aspect of righteousness in His covenant with His people.', 'character.book.closed', '[{"type": "strongs", "reference": "H6666", "description": "Strong''s number for righteousness"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 7, 7, 'kjv', 'Confounded be all they that serve graven images, that boast themselves of idols: worship him, all ye gods.', 0, 106, 'connection', 'Idolatry and Confusion', 'This verse resonates with Isaiah 44:9-10, which states that those who make idols are nothing and their works are worthless. The connection emphasizes that reliance on idols leads to spiritual confusion and ultimately disappointment. It highlights the futility of idol worship compared to the glory of worshiping the one true God.', 'link', '[{"type": "crossReference", "reference": "Isaiah 44:9-10", "description": "Addresses the futility of idol-making."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 7, 7, 'kjv', 'graven images', 34, 47, 'greek', 'Idolatry in the Greek Context', 'לאֹ (lo, H3808) - not, no. While this Hebrew term is not directly in the Greek, it reflects the prohibition against idolatry found throughout Scripture. The concept of ''graven images'' is rooted in the broader biblical theme of God''s exclusive sovereignty and the rejection of false gods.', 'textformat.abc', '[{"type": "strongs", "reference": "H3808", "description": "Indicates the negation of idolatry."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 7, 7, 'kjv', 'worship him, all ye gods.', 81, 106, 'theology', 'Universal Call to Worship', 'This passage suggests a universal call to worship the one true God, reflecting the biblical doctrine of God''s supremacy over all creation. Many scholars believe this indicates that even the spiritual entities (gods) must recognize His ultimate authority, affirming the Christian teaching of monotheism and the futility of idolatry.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 7, 7, 'kjv', 'Confounded be all they that serve graven images', 0, 47, 'question', 'Reflecting on Idolatry', 'What does it mean to you personally to be ''confounded'' by the futility of idolatry? How does this shape your understanding of what it means to worship God alone in your life?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 8, 8, 'kjv', 'Zion heard, and was glad;', 0, 25, 'connection', 'Rejoicing in God''s Word', 'This phrase connects to Isaiah 52:7, which states, ''How beautiful upon the mountains are the feet of him that bringeth good tidings, that publisheth peace.'' Both passages highlight the joy that comes from receiving God''s message and the blessing of His presence. The gladness of Zion emphasizes the joy of the faithful community in response to God''s judgments and actions.', 'link', '[{"type": "crossReference", "reference": "Isaiah 52:7", "description": "Connecting the joy of Zion with the announcement of peace."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 8, 8, 'kjv', 'daughters of Judah rejoiced', 34, 61, 'theology', 'Community Rejoicing', 'The mention of the ''daughters of Judah'' signifies the inclusion of women in the community''s response to God''s judgments. Many scholars believe this reflects the broader theological principle of God''s grace being accessible to all members of the community, emphasizing unity and collective joy in divine justice and mercy.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 8, 8, 'kjv', 'because of thy judgments, O Lord.', 62, 95, 'question', 'Judgments and Our Response', 'What does it mean for us to rejoice in God''s judgments today? How can understanding His justice lead us to a deeper joy in our faith, especially in a world where justice seems lacking?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 8, 8, 'kjv', 'Zion heard, and was glad;', 0, 25, 'greek', 'Hearing and Gladness', 'שָׁמַע (shama, H8085) - to hear, listen, obey. This Hebrew word emphasizes not just auditory perception but active response. ''Hearing'' in a biblical context often implies a call to action or obedience, enhancing the understanding of Zion''s gladness as a response to divine revelation.', 'textformat.abc', '[{"type": "strongs", "reference": "H8085", "description": "Strong''s number for the Hebrew word for ''hear''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 9, 9, 'kjv', 'For thou, Lord, art high above all the earth:', 0, 45, 'connection', 'Divine Sovereignty', 'This phrase resonates with Isaiah 6:1, where the Lord is described as ''high and lifted up.'' Both verses emphasize God''s supreme authority and sovereignty over all creation. Recognizing God''s exalted position encourages believers to acknowledge His power in their lives and the world around them.', 'link', '[{"type": "crossReference", "reference": "Isaiah 6:1", "description": "Reveals God''s exalted position as sovereign."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 9, 9, 'kjv', 'thou art exalted far above all gods.', 46, 82, 'theology', 'God''s Supremacy', 'This assertion suggests a key theological principle: God''s supremacy over all other deities or idols. Many scholars believe this reflects the biblical theme of monotheism, where the Lord is portrayed as the one true God, contrasting with the false gods of the surrounding cultures. This distinction is crucial for understanding Israel''s covenantal relationship with God.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 9, 9, 'kjv', 'For thou, Lord, art high above all the earth:', 0, 45, 'question', 'Understanding God''s Exalted Nature', 'What does it mean for God to be ''high above all the earth''? How does this understanding of His exalted nature influence your daily decisions and interactions with others?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 9, 9, 'kjv', 'thou art exalted far above all gods.', 46, 82, 'greek', 'Understanding ''Exalted''', 'ὑψόω (hypsoō, G5312) - to raise up, exalt. This term conveys the idea of being lifted to a high position, emphasizing God''s unique status above any other deities or powers. It underscores the belief that no other gods can compare to His greatness.', 'textformat.abc', '[{"type": "strongs", "reference": "G5312", "description": "Strong''s number for the Greek term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 10, 10, 'kjv', 'Ye that love the Lord, hate evil:', 0, 33, 'connection', 'Loving God and Hating Evil', 'This phrase connects to Romans 12:9, which instructs believers to ''abhor what is evil; cling to what is good.'' The emphasis on loving the Lord and simultaneously hating evil reflects a profound moral dichotomy that is central to Christian ethics, highlighting that true love for God necessitates a rejection of evil.', 'link', '[{"type": "crossReference", "reference": "Romans 12:9", "description": "Paul''s exhortation on love and evil"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 10, 10, 'kjv', 'he preserveth the souls of his saints;', 34, 72, 'theology', 'God''s Preservation of Saints', 'Many scholars believe this phrase encapsulates the doctrine of divine preservation, suggesting that God actively protects and sustains His people throughout their spiritual journey. This aligns with 1 Peter 1:5, which assures believers that they are ''kept by the power of God through faith.''', 'sparkles', '[{"type": "commentary", "reference": "1 Peter 1:5", "description": "Assurance of God''s protective power"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 10, 10, 'kjv', 'he delivereth them out of the hand of the wicked.', 73, 122, 'question', 'Understanding Divine Deliverance', 'What does it mean for God to deliver us from the wicked? In what ways have you experienced His deliverance in your own life? Reflecting on this can deepen our understanding of God''s active role in safeguarding His followers from evil influences.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 11, 11, 'kjv', 'Light is sown for the righteous', 0, 31, 'connection', 'Light and Righteousness', 'This phrase connects well with John 8:12, where Jesus declares, ''I am the light of the world.'' The concept of light as a symbol of guidance and righteousness is prevalent in Scripture. It emphasizes that those who are righteous, aligned with God''s will, will receive illumination and direction in their lives. This connection highlights the importance of living in accordance with God''s commandments to experience His guidance.', 'link', '[{"type": "crossReference", "reference": "John 8:12", "description": "Jesus identifies Himself as the light that guides believers."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 11, 11, 'kjv', 'gladness for the upright in heart', 37, 70, 'theology', 'Joy of the Upright', 'Many scholars believe that the gladness mentioned here reflects a deeper theological truth about the joy that comes from living in harmony with God''s will. This aligns with the doctrine of joy in the Christian life, which is rooted in the presence of God and a right relationship with Him. The upright in heart experience a joy that transcends circumstances, grounded in their faith and the hope of salvation.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the joy of the righteous as a fruit of their relationship with God."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 11, 11, 'kjv', 'Light is sown for the righteous', 0, 31, 'greek', 'Understanding ''Light''', 'אֹור (or, H216) - light. In Hebrew, this word signifies illumination and is often used metaphorically for knowledge, guidance, and divine presence. The use of ''light'' here suggests that the righteous are not only recipients of physical light but also spiritual insight and clarity in their lives, reinforcing the idea of living under God''s favor.', 'textformat.abc', '[{"type": "strongs", "reference": "H216", "description": "Strong''s number for the Hebrew word for light."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 11, 11, 'kjv', 'gladness for the upright in heart', 37, 70, 'question', 'Source of Our Joy', 'What does it mean for us to experience gladness as the ''upright in heart''? How can we cultivate a heart that is aligned with God’s righteousness to fully embrace the joy He offers? Reflecting on this can help us understand the practical implications of living righteously in our daily lives.', 'heart.text.square', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 12, 12, 'kjv', 'Rejoice in the Lord, ye righteous;', 0, 34, 'connection', 'Rejoicing in the Lord', 'This phrase resonates with Philippians 4:4, which exhorts believers to ''Rejoice in the Lord always.'' Both passages emphasize the necessity of joy in the believer''s life, rooted in a relationship with God. The connection illustrates that joy is not merely an emotion but a response to understanding God’s character and His deeds.', 'link', '[{"type": "crossReference", "reference": "Philippians 4:4", "description": "Another exhortation to rejoice in the Lord."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 12, 12, 'kjv', 'give thanks at the remembrance of his holiness.', 39, 86, 'theology', 'Holiness and Thanksgiving', 'Many scholars believe that the act of remembering God’s holiness is foundational to worship and gratitude. This passage suggests that recognizing God''s pure and separate nature leads to a heartfelt response of thankfulness, aligning with the broader biblical teaching that worship is rooted in acknowledging God''s attributes.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the relationship between holiness and worship."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 97, 12, 12, 'kjv', 'Rejoice in the Lord, ye righteous;', 0, 34, 'question', 'Understanding Righteousness', 'What does it mean to be ''righteous'' in the context of rejoicing in the Lord? How does our understanding of righteousness influence our ability to rejoice in God''s presence?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 97, 12, 12, 'kjv', 'give thanks at the remembrance of his holiness.', 39, 86, 'greek', 'Understanding ''Remembrance''', 'ἀνάμνησις (anamnesis, G372) - remembrance, a calling to mind. This term highlights the importance of actively remembering God''s holiness, suggesting that such remembrance is a vital aspect of spiritual life and worship.', 'textformat.abc', '[{"type": "strongs", "reference": "G372", "description": "Strong''s number for the Greek term related to remembrance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 1, 1, 'kjv', 'O sing unto the Lord a new song;', 0, 32, 'connection', 'Call to Worship', 'This phrase echoes the call found in Revelation 5:9, where the redeemed sing a new song unto the Lamb. Both passages highlight the response of God''s people to His saving acts, emphasizing worship as a natural response to divine deliverance.', 'link', '[{"type": "crossReference", "reference": "Revelation 5:9", "description": "Reference to the new song sung by the redeemed."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 1, 1, 'kjv', 'for he hath done marvellous things:', 33, 68, 'theology', 'God''s Marvelous Works', 'This passage suggests that God''s acts of salvation are not only historical but also ongoing in the lives of believers. Many scholars believe that recognizing God''s marvelous deeds strengthens faith and inspires worship, as seen in the narratives throughout Scripture where divine intervention transforms lives.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 1, 1, 'kjv', 'his right hand, and his holy arm,', 69, 102, 'greek', 'Symbol of Strength', 'ἔκτασις (ektasis, G1625) - extension, stretching out. The imagery of God''s ''right hand'' signifies His power and authority in delivering His people, reflecting themes of divine strength found in many Psalms (e.g., Psalm 44:3).', 'textformat.abc', '[{"type": "strongs", "reference": "G1625", "description": "Greek term indicating extension or power."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 1, 1, 'kjv', 'hath gotten him the victory.', 103, 131, 'question', 'Understanding Victory', 'What does ''victory'' mean in the context of God''s character and His relationship with His people? How does this understanding affect our daily struggles and the assurance we have in Christ''s triumph over sin and death?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 2, 2, 'kjv', 'The Lord hath made known his salvation', 0, 38, 'connection', 'Salvation Revealed', 'This phrase connects to Isaiah 52:10, which states, ''The Lord has bared his holy arm in the sight of all the nations, and all the ends of the earth will see the salvation of our God.'' Both verses emphasize God''s public demonstration of salvation, portraying it as a universal revelation that invites all to witness His deliverance.', 'link', '[{"type": "crossReference", "reference": "Isaiah 52:10", "description": "Highlights God''s revelation of salvation to all nations."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 2, 2, 'kjv', 'his righteousness hath he openly shewed', 40, 79, 'theology', 'God''s Righteousness', 'This passage suggests that God''s righteousness is not hidden but is displayed openly for all to see. Many scholars believe this reflects the integral Christian teaching that God''s justice and moral perfection are foundational to His being, and He desires all humanity to acknowledge and respond to His righteous character.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 2, 2, 'kjv', 'in the sight of the heathen', 80, 107, 'question', 'Universal Audience', 'What does it mean for God''s salvation to be revealed ''in the sight of the heathen''? How does this understanding challenge or inspire your view of evangelism and the call to share the Gospel with those outside the faith?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 2, 2, 'kjv', 'The Lord hath made known his salvation', 0, 38, 'greek', 'Salvation in Greek', 'σωτηρία (soteria, G4991) - salvation, deliverance, or preservation. This term emphasizes not only the act of being saved but also the ongoing relationship and preservation that comes from God’s salvation.', 'textformat.abc', '[{"type": "strongs", "reference": "G4991", "description": "Strong''s number for the Greek term for salvation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 3, 3, 'kjv', 'He hath remembered his mercy and his truth toward the house of Israel:', 0, 70, 'connection', 'Divine Faithfulness', 'This phrase connects to Exodus 2:24, where God hears the cries of Israel and remembers His covenant. It highlights the continuity of God''s character in remembering His promises, crucial for understanding His relationship with Israel and, by extension, all believers. Additionally, it echoes Psalm 111:5, which celebrates God''s faithfulness in providing for those who fear Him.', 'link', '[{"type": "crossReference", "reference": "Exodus 2:24", "description": "God''s remembrance of His covenant with Israel."}, {"type": "crossReference", "reference": "Psalm 111:5", "description": "Celebration of God''s faithfulness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 3, 3, 'kjv', 'his mercy and his truth', 19, 42, 'greek', 'Key Hebrew Terms', 'חֶסֶד (chesed, H2617) - mercy, loving-kindness; אֱמֶת (emet, H571) - truth, faithfulness. These terms encapsulate God''s character. ''Chesed'' signifies His loyal love, while ''emet'' reflects His reliability. Together, they emphasize that God''s salvation is rooted in His unchanging nature.', 'textformat.abc', '[{"type": "strongs", "reference": "H2617", "description": "Strong''s number for chesed."}, {"type": "strongs", "reference": "H571", "description": "Strong''s number for emet."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');