INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 98, 3, 3, 'kjv', 'all the ends of the earth have seen the salvation of our God.', 71, 132, 'theology', 'Universal Salvation', 'This passage suggests the universality of God’s salvation, extending beyond Israel to all nations. Many scholars believe this reflects the mission of the Church to spread the Gospel, as seen in Matthew 28:19-20. The salvation offered through Christ is for everyone, emphasizing God''s desire for all people to come to Him.', 'sparkles', '[{"type": "crossReference", "reference": "Matthew 28:19-20", "description": "The Great Commission highlighting the global mission."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 3, 3, 'kjv', 'the house of Israel:', 50, 70, 'question', 'Understanding God''s People', 'What does it mean for us today to be part of God''s ''house'' or community? How does recognizing our identity as His chosen people influence our understanding of salvation and our responsibility towards others?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 4, 4, 'kjv', 'Make a joyful noise unto the Lord, all the earth:', 0, 49, 'connection', 'Universal Call to Worship', 'This phrase resonates with Psalm 100:1, which states, ''Make a joyful noise unto the Lord, all ye lands.'' Both passages invite all creation to engage in joyful worship, highlighting the universality of God''s sovereignty and the response He seeks from humanity. This connection emphasizes that worship is not limited to a particular group but is a communal act intended for all people, reflecting God''s desire for all to acknowledge Him.', 'link', '[{"type": "crossReference", "reference": "Psalm 100:1", "description": "Another call for joyful worship from all nations."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 4, 4, 'kjv', 'make a loud noise, and rejoice, and sing praise.', 50, 98, 'theology', 'The Nature of Praise', 'This passage suggests that praise is an active expression of joy and gratitude towards God. Many scholars believe that the repeated call to ''make a loud noise'' signifies a passionate and enthusiastic response to God''s goodness. Such expressions of worship reflect the inner transformation that acknowledging God''s salvation brings, reinforcing the idea that worship should be vibrant and heartfelt as a natural response to divine grace.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 4, 4, 'kjv', 'Make a joyful noise unto the Lord, all the earth:', 0, 49, 'question', 'What does joyful noise mean?', 'In what ways can you express a ''joyful noise'' in your daily life? Consider how this command invites not only vocal expressions but also actions that reflect joy in God’s presence. How does your understanding of joy influence your worship, and how might it affect others around you?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 4, 4, 'kjv', 'Make a joyful noise unto the Lord, all the earth:', 0, 49, 'greek', 'Joyful Noise in Original Language', 'רִנָּה (rinah, H7440) - a shout of joy or a joyful song. This Hebrew term captures the essence of exuberant praise, emphasizing that the noise made unto the Lord is not merely sound, but a deep expression of joy and celebration in His presence.', 'textformat.abc', '[{"type": "strongs", "reference": "H7440", "description": "Strong''s number for the Hebrew word for joyful noise."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 5, 5, 'kjv', 'Sing unto the Lord with the harp; with the harp, and the voice of a psalm.', 0, 74, 'connection', 'Joyful Worship in Song', 'This verse connects with 1 Chronicles 16:9, which encourages singing praises to God, reflecting the importance of music in worship. Additionally, Ephesians 5:19 highlights the role of singing in spiritual life, emphasizing that music can express our relationship with God. These connections illustrate that worship through music is a biblical practice that brings believers together in joy and reverence.', 'link', '[{"type": "crossReference", "reference": "1 Chronicles 16:9", "description": "Encouragement to sing praises to God."}, {"type": "crossReference", "reference": "Ephesians 5:19", "description": "Importance of singing in the spiritual life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 5, 5, 'kjv', 'Sing unto the Lord with the harp; with the harp, and the voice of a psalm.', 0, 74, 'greek', 'Musical Expression', 'ψαλμός (psalmos, G5568) - a song or hymn, especially one sung in praise. This term emphasizes the musical and poetic aspect of worship, highlighting that singing is not merely a ritual but a heartfelt expression of love and devotion to God.', 'textformat.abc', '[{"type": "strongs", "reference": "G5568", "description": "Strong''s number for ''psalmos''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 5, 5, 'kjv', 'Sing unto the Lord with the harp; with the harp, and the voice of a psalm.', 0, 74, 'theology', 'Worship as a Response', 'Many scholars believe this verse suggests that worship through music is a natural response to God''s goodness and mercy. It reflects a biblical understanding that praise is integral to our relationship with God, reinforcing the idea that worship is both a communal and personal act of devotion.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 5, 5, 'kjv', 'Sing unto the Lord with the harp; with the harp, and the voice of a psalm.', 0, 74, 'question', 'Personal Reflection on Worship', 'How do you incorporate music into your personal worship? Consider how the act of singing praises can deepen your relationship with God and express gratitude for His mercy and truth.', 'heart.text.square', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 6, 6, 'kjv', 'With trumpets and sound of cornet', 0, 33, 'connection', 'Celebration in Worship', 'The call to use trumpets in worship connects with Numbers 10:10, where trumpets are used to summon the people of Israel to worship and signal significant events. This highlights the importance of joyous sounds in expressing reverence and celebration before God. Such musical instruments serve to unify the congregation in praise, emphasizing that worship is both a communal and celebratory act.', 'link', '[{"type": "crossReference", "reference": "Numbers 10:10", "description": "Usage of trumpets in worship and gatherings."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 6, 6, 'kjv', 'make a joyful noise', 34, 53, 'theology', 'Joyful Worship', 'This verse reflects the doctrinal understanding of worship as an expression of joy and gratitude towards God. Many scholars believe that ''joyful noise'' signifies not just sound, but a heartfelt response to God''s majesty and sovereignty, indicating that true worship involves both emotion and action. This aligns with the broader biblical theme that worship is a joyful celebration of God''s attributes and deeds.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explores the nature of worship and joy."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 6, 6, 'kjv', 'before the Lord, the King', 54, 79, 'greek', 'Divine Kingship', 'κύριος (kyrios, G2962) - lord, master, sovereign. This term emphasizes God''s authority and position as King, reinforcing the concept that worship is directed towards a sovereign being who reigns over all creation. Recognizing God as ''the King'' invites worshippers to approach Him with reverence, acknowledging His rule and majesty.', 'textformat.abc', '[{"type": "strongs", "reference": "G2962", "description": "Strong''s number for ''lord'' in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 6, 6, 'kjv', 'make a joyful noise', 34, 53, 'question', 'Joy in Worship', 'What does it mean to you personally to ''make a joyful noise'' in your worship? How can you express your joy and gratitude towards God in your daily life? Reflecting on this can help illuminate ways to incorporate joy into your spiritual practices.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 7, 7, 'kjv', 'Let the sea roar, and the fulness thereof;', 0, 42, 'connection', 'Creation''s Response to God', 'This phrase echoes themes found in Job 38:8-11, where God questions Job about the sea''s boundaries. Both passages highlight the majesty of creation responding to its Creator. The sea, a powerful and chaotic element, symbolizes God''s control over nature. Recognizing this connection deepens our understanding of divine sovereignty and the call for all creation to praise Him.', 'link', '[{"type": "crossReference", "reference": "Job 38:8-11", "description": "God questions Job about the creation of the sea"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 7, 7, 'kjv', 'the fulness thereof;', 22, 42, 'theology', 'God''s Abundant Creation', 'The phrase ''the fulness thereof'' suggests the completeness and abundance of God''s creation. Many scholars believe this reflects the doctrine of creation ex nihilo, emphasizing that God created the world fully and perfectly. This understanding encourages believers to see the divine hand in the natural world and to appreciate the richness of life as a gift from God.', 'sparkles', '[{"type": "commentary", "reference": "Genesis 1:31", "description": "God saw all that He made, and it was very good"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 7, 7, 'kjv', 'Let the sea roar,', 0, 17, 'question', 'Nature''s Praise', 'What does it mean for the sea to ''roar'' in praise of the Lord? Consider how the natural world expresses its worship through the sounds and movements of waves. How might this imagery inspire you to reflect on your own expressions of worship in your life?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 8, 8, 'kjv', 'Let the floods clap their hands:', 0, 32, 'connection', 'Creation''s Response', 'This phrase parallels Romans 8:22, which states that the whole creation groans in anticipation of redemption. Both verses emphasize the idea that nature itself is responsive to God''s sovereignty and impending justice. Recognizing creation''s role in rejoicing highlights the interconnectedness of all life in God''s plan.', 'link', '[{"type": "crossReference", "reference": "Romans 8:22", "description": "Creation''s anticipation of redemption."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 8, 8, 'kjv', 'let the hills be joyful together', 33, 65, 'theology', 'Joyful Unity in Creation', 'This line suggests a theological understanding of unity in creation. Many scholars believe that the joyful response of the hills indicates a collective celebration of God''s righteousness and justice. This reflects the biblical theme of harmony within creation, where all elements participate in God''s divine order.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the unity of creation in worship."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 8, 8, 'kjv', 'Let the floods clap their hands:', 0, 32, 'question', 'Nature''s Praise', 'What does it mean for floods to ''clap their hands''? How does this imagery challenge our understanding of worship and praise? Reflecting on this can lead to deeper insights about how all of creation participates in glorifying God, and what that might look like in our own expressions of worship.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 8, 8, 'kjv', 'Let the floods clap their hands:', 0, 32, 'greek', 'Clapping Hands', 'Hebrew: מָחָא (machah, H4363) - to clap, strike, or beat. This term conveys a sense of joyful celebration and can be linked to the act of worship. The use of this word in the context of nature emphasizes that even the elements of creation express joy in response to God''s righteousness.', 'textformat.abc', '[{"type": "strongs", "reference": "H4363", "description": "Strong''s entry for the Hebrew term."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 9, 9, 'kjv', 'Before the Lord; for he cometh to judge the earth:', 0, 50, 'connection', 'Judgment of the Lord', 'This phrase connects with Revelation 20:12, which describes the great white throne judgment where the dead are judged according to their deeds. Both passages emphasize the certainty of divine judgment, underscoring God''s authority over all creation. Recognizing God''s role as judge helps believers understand the weight of their actions in this life.', 'link', '[{"type": "crossReference", "reference": "Revelation 20:12", "description": "Highlights the concept of divine judgment in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 9, 9, 'kjv', 'with righteousness shall he judge the world, and the people with equity.', 51, 123, 'theology', 'God''s Righteous Judgment', 'This passage suggests that God''s judgment is characterized by righteousness and equity, reflecting His nature as just and fair. Many scholars believe this reinforces the belief in God''s moral order in the universe. Such attributes assure believers that justice will ultimately prevail, offering hope amid worldly injustice.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 98, 9, 9, 'kjv', 'he cometh to judge the earth:', 21, 50, 'greek', 'Judgment in Greek', 'κριτής (kritēs, G2923) - judge, arbiter, ruler. The term indicates not only the act of judging but also the authority and role of the judge. Understanding this term deepens the meaning of God''s active role in administering justice, highlighting His sovereignty over creation.', 'textformat.abc', '[{"type": "strongs", "reference": "G2923", "description": "Strong''s number for the Greek word for judge."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 98, 9, 9, 'kjv', 'with righteousness shall he judge', 51, 84, 'question', 'Understanding Righteous Judgment', 'What does it mean for God to judge with righteousness, and how can this understanding impact our daily decisions and interactions with others? Reflecting on this can help us align our actions with God''s standards of justice and fairness in our lives.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 1, 1, 'kjv', 'The Lord reigneth; let the people tremble:', 0, 42, 'connection', 'Divine Sovereignty', 'This phrase resonates with Psalm 97:1, which similarly declares the Lord''s reign and evokes a response of reverence. In both verses, the emphasis on God’s kingship compels a reaction from humanity, highlighting the importance of recognizing His authority. This connection underscores a consistent biblical theme: the sovereignty of God over all creation.', 'link', '[{"type": "crossReference", "reference": "Psalm 97:1", "description": "Another psalm emphasizing God''s reign."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 1, 1, 'kjv', 'he sitteth between the cherubims;', 43, 76, 'greek', 'Cherubim Significance', 'כְּרוּבִים (kerubim, H3742) - cherubim, angelic beings associated with the presence of God. Their placement emphasizes God''s holiness and majesty, serving as guardians of His throne and the sanctity of His dwelling. Understanding their role enhances our grasp of divine worship and the seriousness of approaching God.', 'textformat.abc', '[{"type": "strongs", "reference": "H3742", "description": "Strong''s number for cherubim."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 1, 1, 'kjv', 'let the earth be moved.', 77, 100, 'theology', 'Cosmic Response to God', 'Many scholars believe this phrase reflects the profound impact of God''s authority on creation, suggesting that His reign is so powerful that it causes the very earth to tremble. This illustrates the biblical doctrine of creation’s responsiveness to God’s holiness, reinforcing the notion that all of creation is under His lordship and reacts to His presence.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explores the implications of God''s reign over creation."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 1, 1, 'kjv', 'The Lord reigneth; let the people tremble:', 0, 42, 'question', 'Response to God''s Reign', 'What does it mean for your daily life that ''The Lord reigneth''? In what ways can you express reverence and awe for His authority over your circumstances and the world around you?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 2, 2, 'kjv', 'The Lord is great in Zion;', 0, 26, 'connection', 'God''s Greatness', 'This phrase connects to Psalm 48:1, which states, ''Great is the Lord, and greatly to be praised in the city of our God, in the mountain of his holiness.'' Both verses emphasize the greatness of God specifically in Zion, reinforcing the idea that Zion is a central place of worship and divine presence. This connection highlights the importance of Zion in biblical theology as the site where God''s glory is uniquely manifested.', 'link', '[{"type": "crossReference", "reference": "Psalm 48:1", "description": "Another declaration of God''s greatness in Zion."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 2, 2, 'kjv', 'and he is high above all the people.', 27, 63, 'theology', 'God''s Sovereignty', 'This passage suggests a doctrine of God''s sovereignty over all nations and peoples. Many scholars believe that God''s exalted position signifies His authority and governance over creation, which is a central theme in both Old and New Testament theology. This sovereignty assures believers that God is ultimately in control, providing comfort and hope amid worldly chaos.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Commentary discussing God''s sovereignty in the Psalms."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 2, 2, 'kjv', 'The Lord is great in Zion;', 0, 26, 'question', 'Understanding Greatness', 'What does it mean for the Lord to be ''great'' in Zion? How does this understanding shape our view of worship and community within the church today? Reflecting on God’s greatness can lead us to a deeper appreciation of His presence and the significance of gathering in His name.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 2, 2, 'kjv', 'The Lord is great in Zion;', 0, 26, 'greek', 'Hebrew Context', 'יָהּ (Yah, H3050) - a shortened form of Yahweh, meaning ''the Lord.'' This name emphasizes God''s relational aspect and covenantal faithfulness, particularly significant in Zion, the chosen city. It reflects not just His greatness but His intimate involvement with His people.', 'character.book.closed', '[{"type": "strongs", "reference": "H3050", "description": "Strong''s number for the name of God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 3, 3, 'kjv', 'Let them praise thy great and terrible name;', 0, 44, 'connection', 'Praise in the Psalms', 'This phrase connects with Psalm 145:3, which states, ''Great is the Lord, and greatly to be praised; and his greatness is unsearchable.'' Both verses emphasize the imperative to praise God for His greatness, illustrating a foundational aspect of worship in the Psalms. This connection reinforces the theme of God''s majestic nature and the appropriate human response of reverence and adoration.', 'link', '[{"type": "crossReference", "reference": "Psalm 145:3", "description": "Highlights the importance of praising God''s greatness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 3, 3, 'kjv', 'great and terrible name;', 20, 44, 'theology', 'God''s Holiness', 'The juxtaposition of ''great'' and ''terrible'' in describing God''s name reflects a common theological understanding of God''s nature as both loving and just. Many scholars believe this duality illustrates the awe-inspiring power of God''s holiness, which commands both reverence and fear. This concept is foundational in Christian teaching, emphasizing the need for believers to approach God with both joy and serious respect.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 3, 3, 'kjv', 'for it is holy.', 45, 60, 'greek', 'Holiness in Hebrew', 'קָדוֹשׁ (qadosh, H6918) - holy, sacred, set apart. This term underlines the distinctiveness of God’s character and His separation from sin. The holiness of God is central to His identity, which informs how believers should live in response to His nature. Understanding the depth of this term enhances our appreciation of God''s perfection and the call for His followers to embody holiness in their lives.', 'textformat.abc', '[{"type": "strongs", "reference": "H6918", "description": "Strong''s number for the Hebrew word for holy."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 3, 3, 'kjv', 'Let them praise thy great and terrible name;', 0, 44, 'question', 'Understanding Praise', 'What does it mean to praise God''s ''great and terrible name'' in our daily lives? How can we reflect on His holiness in our worship practices, ensuring that our reverence aligns with the majesty of His character?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 4, 4, 'kjv', 'The king’s strength also loveth judgment', 0, 40, 'connection', 'Judgment in the Psalms', 'This phrase connects with Psalm 72:2, which states, ''He will judge your people in righteousness, your afflicted ones with justice.'' Both verses highlight God''s commitment to justice and righteousness as essential characteristics of His kingship. This connection emphasizes the divine nature of true leadership, rooted in moral order and fairness.', 'link', '[{"type": "crossReference", "reference": "Psalm 72:2", "description": "Another verse highlighting God''s judgment and righteousness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 4, 4, 'kjv', 'thou dost establish equity', 42, 68, 'greek', 'Understanding ''Equity''', 'צֶדֶק (tsedeq, H6664) - righteousness, justice. This Hebrew term conveys the idea of moral rightness and justice that God establishes. It speaks to God''s role as the ultimate judge who ensures that fairness and equity prevail, reflecting His character and will for His people.', 'textformat.abc', '[{"type": "strongs", "reference": "H6664", "description": "Strong''s number for the Hebrew term for righteousness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 4, 4, 'kjv', 'thou executest judgment and righteousness in Jacob', 70, 120, 'theology', 'God''s Justice in Israel', 'Many scholars believe that this passage illustrates the theological principle that God''s justice is not arbitrary but is rooted in His covenant relationship with Israel (Jacob). The execution of judgment and righteousness is a reflection of God''s faithfulness to His promises and His desire for His people to reflect His character in their lives.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the nature of God''s justice and its implications for His people."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 4, 4, 'kjv', 'The king’s strength also loveth judgment', 0, 40, 'question', 'Reflecting on Divine Leadership', 'What does it mean for us to embrace judgment and righteousness as qualities of our own leadership? In what ways can we reflect God''s justice in our interactions with others, especially in difficult situations?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 5, 5, 'kjv', 'Exalt ye the Lord our God, and worship at his footstool; for he is holy.', 0, 72, 'connection', 'Call to Worship', 'This verse resonates with Psalm 95:6, which also calls for worship: ''O come, let us worship and bow down; let us kneel before the Lord our Maker.'' Both passages emphasize the act of worship as a response to God''s holiness and majesty. This connection underscores the importance of recognizing God''s sovereignty and our humility before Him.', 'link', '[{"type": "crossReference", "reference": "Psalm 95:6", "description": "Another call to worship emphasizing reverence for God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 5, 5, 'kjv', 'worship at his footstool;', 31, 56, 'theology', 'Understanding the Footstool', 'The phrase ''his footstool'' suggests God''s sovereignty and the reverence we owe Him. Many scholars believe this refers to the Ark of the Covenant as God''s earthly throne (1 Chronicles 28:2), illustrating the significance of His presence among His people. This highlights the need for humility and awe in our worship, recognizing God’s majesty.', 'sparkles', '[{"type": "commentary", "reference": "1 Chronicles 28:2", "description": "References God''s presence and throne imagery."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 5, 5, 'kjv', 'for he is holy.', 57, 72, 'greek', 'Understanding Holiness', 'ἅγιος (hagios, G40) - holy, sacred, set apart. This term emphasizes the purity and separateness of God from all sin and imperfection. Understanding God''s holiness is crucial as it informs our worship and relationship with Him, calling us to live in a manner that reflects His character.', 'textformat.abc', '[{"type": "strongs", "reference": "G40", "description": "Strong''s number for the Greek term for holy."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 5, 5, 'kjv', 'Exalt ye the Lord our God,', 0, 26, 'question', 'Exalting God', 'What does it mean to truly exalt the Lord in our daily lives? How can we reflect His holiness in our actions and attitudes? This verse challenges us to consider the practical implications of our worship and how it influences our relationship with God and others.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 6, 6, 'kjv', 'Moses and Aaron among his priests', 0, 33, 'connection', 'Priestly Lineage', 'This reference to Moses and Aaron highlights their significant roles in Israel''s spiritual leadership. Cross-referencing Exodus 28:1, where God commands Aaron to serve as priest, emphasizes the divine appointment of his lineage to intercede for the people. Additionally, Hebrews 5:4 discusses the honor of being called to the priesthood, linking back to God''s established order for worship and mediation.', 'link', '[{"type": "crossReference", "reference": "Exodus 28:1", "description": "God''s command for Aaron''s priesthood."}, {"type": "crossReference", "reference": "Hebrews 5:4", "description": "The honor associated with the priesthood."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 6, 6, 'kjv', 'they called upon the Lord', 82, 107, 'theology', 'Prayer and Divine Response', 'Many scholars believe this phrase underscores the importance of prayer as a means of communication with God. It reflects the consistent Biblical theme that God responds to those who earnestly seek Him (James 4:8). This passage suggests that calling upon the Lord is an act of faith, demonstrating reliance on His character and willingness to respond.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 6, 6, 'kjv', 'he answered them', 113, 129, 'question', 'Understanding God''s Response', 'What does it mean for God to answer those who call upon Him? Reflecting on this aspect of divine interaction can deepen our understanding of prayer. Do we expect God''s answers to align with our desires, or are we open to His will being different from our own?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 6, 6, 'kjv', 'Samuel among them that call upon his name', 39, 80, 'greek', 'Calling on God''s Name', 'שֵׁם (shem, H8034) - name, reputation, fame. This Hebrew term emphasizes the significance of God''s name in the context of worship and prayer. Calling upon the name of the Lord is synonymous with invoking His presence and acknowledging His authority and character.', 'textformat.abc', '[{"type": "strongs", "reference": "H8034", "description": "Strong''s number for the Hebrew word ''name''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 7, 7, 'kjv', 'He spake unto them in the cloudy pillar', 0, 39, 'connection', 'God''s Guidance in Clouds', 'This phrase can be cross-referenced with Exodus 13:21, where God leads the Israelites by a pillar of cloud by day. The connection highlights God''s continuous guidance and presence with His people, emphasizing His desire to lead and protect them. The cloudy pillar symbolizes divine direction and assurance during their journey.', 'link', '[{"type": "crossReference", "reference": "Exodus 13:21", "description": "God leads the Israelites through the wilderness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');