INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 99, 7, 7, 'kjv', 'they kept his testimonies', 41, 66, 'theology', 'Importance of Obedience', 'This phrase suggests that obedience to God''s laws is crucial for maintaining a relationship with Him. Many scholars believe that keeping God''s testimonies reflects a commitment to His covenant and a means of experiencing His blessings. This underscores the theological principle that faith is demonstrated through action and adherence to divine instruction.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 7, 7, 'kjv', 'the ordinance that he gave them', 72, 103, 'greek', 'Understanding Ordinances', 'חֹק (choq, H2706) - statute, ordinance, decree. This Hebrew term emphasizes the binding nature of God''s commandments, suggesting that they are not merely suggestions but authoritative decrees meant to guide the community''s conduct and relationship with God.', 'textformat.abc', '[{"type": "strongs", "reference": "H2706", "description": "Meaning and significance of the Hebrew word for ''ordinance''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 7, 7, 'kjv', 'He spake unto them in the cloudy pillar', 0, 39, 'question', 'Divine Communication Reflection', 'What does it mean for God to speak to His people through visible signs like the cloudy pillar? How might God be communicating with us today in ways that require us to be attentive and responsive?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 8, 8, 'kjv', 'Thou answeredst them, O Lord our God', 0, 36, 'connection', 'Answering Prayer', 'This phrase echoes the promise found in Jeremiah 33:3, where God invites His people to call upon Him, assuring them He will answer. This emphasizes God''s faithfulness in responding to His people, encouraging believers to rely on Him in prayer.', 'link', '[{"type": "crossReference", "reference": "Jeremiah 33:3", "description": "God''s promise to answer when called upon"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 8, 8, 'kjv', 'thou wast a God that forgavest them', 38, 73, 'theology', 'Forgiveness in God', 'This phrase reflects the core Christian doctrine of forgiveness, highlighting God''s mercy towards His people. Many scholars believe that this underlines the necessity of repentance and God''s readiness to forgive, as echoed in 1 John 1:9, reinforcing the belief in His grace.', 'person.2.fill', '[{"type": "commentary", "reference": "1 John 1:9", "description": "God''s faithfulness to forgive when we confess"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 8, 8, 'kjv', 'though thou tookest vengeance of their inventions', 75, 124, 'question', 'Understanding Divine Justice', 'What does it reveal about God''s character when He forgives yet still takes vengeance? How does this balance between mercy and justice shape our understanding of sin and its consequences in our lives?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 8, 8, 'kjv', 'O Lord our God', 22, 36, 'greek', 'Divine Address', 'אֱלֹהֵינוּ (Eloheinu, H430) - our God. This term emphasizes the personal relationship between God and His people, indicating both His transcendence and immanence. It highlights the covenantal aspect of God''s engagement with Israel.', 'textformat.abc', '[{"type": "strongs", "reference": "H430", "description": "Strong''s entry for God in Hebrew"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 9, 9, 'kjv', 'Exalt the Lord our God,', 0, 23, 'connection', 'Call to Worship', 'This phrase resonates with 1 Chronicles 16:29, which states, ''Give to the Lord the glory due his name; bring an offering and come before him; worship the Lord in the splendor of his holiness.'' Both verses emphasize the imperative of exalting God and recognizing His majesty. This connection highlights the continuity in the call for worship throughout Scripture.', 'link', '[{"type": "crossReference", "reference": "1 Chronicles 16:29", "description": "A parallel call to glorify God in worship."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 9, 9, 'kjv', 'worship at his holy hill;', 28, 53, 'theology', 'Significance of the Holy Hill', 'Many scholars believe that ''holy hill'' refers to Mount Zion, symbolizing God''s presence and His covenant with Israel. This concept reflects the idea that true worship occurs in places designated by God, where His holiness is manifested. This theological underpinning underscores the importance of sacred spaces in worship.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the significance of holy sites in worship."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 99, 9, 9, 'kjv', 'for the Lord our God is holy.', 54, 83, 'greek', 'Understanding Holiness', 'קדוש (qadosh, H6918) - holy, sacred, set apart. This Hebrew term signifies not just moral purity but also the separateness of God from His creation. The insistence on God''s holiness in worship reminds believers of His transcendence and the reverence due to Him.', 'textformat.abc', '[{"type": "strongs", "reference": "H6918", "description": "Strong''s number for the Hebrew word for holy."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 99, 9, 9, 'kjv', 'Exalt the Lord our God,', 0, 23, 'question', 'Reflecting on Exaltation', 'What does it mean to truly exalt the Lord in our daily lives? How can we actively demonstrate our reverence for His holiness through our actions and choices?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 1, 1, 'kjv', 'Make a joyful noise unto the Lord, all ye lands.', 0, 48, 'connection', 'Joyful Worship', 'This verse connects with Psalm 95:1, which also calls for joyful singing to the Lord. Both passages emphasize a communal response to God’s greatness, highlighting that worship is an active expression of joy and gratitude. The exhortation to ''all ye lands'' indicates the universal scope of worship, inviting all nations to acknowledge and celebrate God''s sovereignty.', 'link', '[{"type": "crossReference", "reference": "Psalm 95:1", "description": "Calls for joyful singing to the Lord"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 1, 1, 'kjv', 'Make a joyful noise unto the Lord, all ye lands.', 0, 48, 'theology', 'Nature of Worship', 'Many scholars believe this verse encapsulates the essence of worship as an expression of joy and communal participation. The call for ''all ye lands'' reflects the inclusive nature of God''s kingdom, where worship transcends cultural and geographical boundaries. This aligns with the Great Commission in Matthew 28:19, where believers are tasked to make disciples of all nations, further emphasizing the universal call to worship.', 'sparkles', '[{"type": "commentary", "reference": "Matthew 28:19", "description": "The Great Commission emphasizes a call to all nations"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 100, 1, 1, 'kjv', 'Make a joyful noise unto the Lord, all ye lands.', 0, 48, 'question', 'Expression of Joy', 'What does it mean to make a ''joyful noise'' to the Lord in your daily life? Consider how your expressions of joy in worship reflect your relationship with God. Are there ways to incorporate more intentional acts of worship and gratitude in your routine that align with this call to joy?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 1, 1, 'kjv', 'Make a joyful noise unto the Lord, all ye lands.', 0, 48, 'greek', 'Joyful Noise', 'ἀγαλλιάω (agalliao, G21) - to rejoice, exult. This Greek term conveys a deep sense of joy that goes beyond mere happiness, suggesting exuberant praise. This aligns with the call in the verse for a joyful noise, emphasizing that our worship should be filled with heartfelt, exuberant expression.', 'textformat.abc', '[{"type": "strongs", "reference": "G21", "description": "Strong''s number for the Greek word for rejoice"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 2, 2, 'kjv', 'Serve the Lord with gladness:', 0, 29, 'theology', 'Joyful Service to God', 'This phrase emphasizes the attitude with which we approach our service to God. Many scholars believe that serving the Lord should be a joyful expression of gratitude rather than a mere obligation. This reflects the biblical principle that true worship involves the heart and spirit, aligning with the teachings found in Colossians 3:23, which encourages believers to work heartily, as for the Lord and not for men.', 'sparkles', '[{"type": "crossReference", "reference": "Colossians 3:23", "description": "Encouragement to serve with joy and dedication."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 100, 2, 2, 'kjv', 'come before his presence with singing.', 30, 68, 'connection', 'Worship and Presence', 'The act of coming before the Lord with singing connects with other scriptural references that highlight the importance of music in worship. For instance, Psalm 95:2 invites believers to come before His presence with thanksgiving and joyful songs. This connection underscores the communal and celebratory nature of worship, reinforcing the idea that singing is a vital expression of our relationship with God.', 'link', '[{"type": "crossReference", "reference": "Psalm 95:2", "description": "Another example of approaching God with joyful songs."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 2, 2, 'kjv', 'Serve the Lord with gladness:', 0, 29, 'question', 'Reflecting on Our Service', 'What does it mean for you to serve the Lord with gladness, especially in challenging times? How can you cultivate a spirit of joy in your daily acts of service, reflecting your relationship with Him?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 2, 2, 'kjv', 'come before his presence with singing.', 30, 68, 'greek', 'Singing in Worship', 'ἀγωγή (agōgē, G71) - leading, bringing, conducting. This term emphasizes the active role in bringing oneself into the presence of God, especially through worship. It conveys a sense of intentionality in approaching God, particularly through music, which is an essential aspect of corporate worship.', 'textformat.abc', '[{"type": "strongs", "reference": "G71", "description": "Greek term related to leading or bringing oneself."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 3, 3, 'kjv', 'Know ye that the Lord he is God:', 0, 32, 'connection', 'God''s Sovereignty Affirmed', 'This phrase echoes the declaration found in Deuteronomy 4:39, where it states, ''Know therefore this day, and consider it in thine heart, that the Lord he is God in heaven above, and upon the earth beneath: there is none else.'' This connection emphasizes the singularity and sovereignty of God, urging believers to recognize Him as the ultimate authority in their lives.', 'link', '[{"type": "crossReference", "reference": "Deuteronomy 4:39", "description": "Affirms God''s unique sovereignty"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 3, 3, 'kjv', 'it is he that hath made us, and not we ourselves;', 33, 82, 'theology', 'Creation and Dependence', 'This passage underscores the doctrine of creation, which teaches that humanity is created by God for His purposes. Many scholars believe this illustrates the inherent value and dignity of human life, as we are not self-made but are crafted by the Creator. This view promotes a sense of humility and dependence on God.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the implications of being created by God"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 100, 3, 3, 'kjv', 'we are his people, and the sheep of his pasture.', 83, 131, 'question', 'Identity in Christ', 'How does understanding ourselves as ''his people'' shape our identity and purpose? In what ways are we called to reflect the character of our Shepherd in our daily lives? This reflection can guide us in aligning our actions with our divine identity.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 4, 4, 'kjv', 'Enter into his gates with thanksgiving,', 0, 39, 'connection', 'Joyful Entrance', 'This phrase echoes the sentiment found in Philippians 4:6, which encourages believers to present their requests to God with thanksgiving. Both verses emphasize the importance of gratitude while approaching God, highlighting that a thankful heart is essential for true worship.', 'link', '[{"type": "crossReference", "reference": "Philippians 4:6", "description": "Encouragement to present requests with thanksgiving."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 4, 4, 'kjv', 'into his courts with praise:', 44, 72, 'theology', 'Worship and Praise', 'Many scholars believe this verse reflects the theology of worship in the Old Testament, where entering the courts of the temple symbolizes access to God''s presence. This access is often associated with communal worship, suggesting the importance of gathering together to praise God.', 'person.2.fill', '[{"type": "commentary", "reference": "The New Bible Commentary", "description": "Discusses the significance of temple worship."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 100, 4, 4, 'kjv', 'be thankful unto him,', 73, 94, 'question', 'Understanding Thankfulness', 'What does it mean to be thankful unto God in your daily life? How can you cultivate an attitude of gratitude that not only honors Him but also transforms your perspective on challenges?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 4, 4, 'kjv', 'and bless his name.', 95, 114, 'greek', 'Blessing His Name', 'εὐλογέω (eulogeo, G2127) - to bless, to praise. This term conveys the idea of speaking well of God, recognizing His attributes and deeds. The call to bless His name highlights the importance of acknowledging God’s character and works in our lives.', 'textformat.abc', '[{"type": "strongs", "reference": "G2127", "description": "Strong''s number for the Greek term for ''bless''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 5, 5, 'kjv', 'For the Lord is good;', 0, 21, 'connection', 'God''s Goodness', 'This phrase echoes Psalm 34:8, "O taste and see that the Lord is good: blessed is the man that trusteth in him." Both verses affirm the essential goodness of God, encouraging believers to experience and trust in His character. The connection reinforces the theme of gratitude and worship in approaching God.', 'link', '[{"type": "crossReference", "reference": "Psalm 34:8", "description": "Affirms the goodness of God and invites personal experience."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 5, 5, 'kjv', 'his mercy is everlasting;', 22, 47, 'theology', 'Understanding Mercy', 'Many scholars believe that ''mercy'' (chesed in Hebrew) refers to God''s steadfast love and covenant faithfulness. This passage emphasizes that God''s mercy is not temporary but everlasting, offering assurance of His enduring compassion towards His people. This concept is foundational in Christian theology, reflecting God''s unchanging nature.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the nature and significance of God''s mercy."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 100, 5, 5, 'kjv', 'and his truth endureth to all generations.', 48, 90, 'question', 'Timeless Truth', 'What does it mean for God''s truth to endure across generations? Consider how this assurance influences your understanding of Scripture and its relevance in today''s world. How might this timelessness inspire you to share your faith with others?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 100, 5, 5, 'kjv', 'his mercy is everlasting;', 22, 47, 'greek', 'Hebrew Term for Mercy', 'חֶסֶד (chesed, H2617) - lovingkindness, mercy, goodness. This term signifies a deep, loyal love that encompasses grace and compassion, foundational to understanding God''s relationship with His people.', 'textformat.abc', '[{"type": "strongs", "reference": "H2617", "description": "Strong''s number for ''chesed'', indicating its significance in the Old Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 1, 1, 'kjv', 'I will sing of mercy and judgment: unto thee, O Lord, will I sing.', 0, 66, 'connection', 'Mercy and Judgment Theme', 'This verse reflects a dual theme found in numerous Biblical texts, particularly in Isaiah 61:8, which speaks of God''s love for justice and righteousness. Additionally, Micah 6:8 emphasizes the importance of acting justly, loving mercy, and walking humbly with God. The connection underscores the holistic nature of God''s character, where mercy and justice coexist, inviting believers to reflect these qualities in their lives.', 'link', '[{"type": "crossReference", "reference": "Isaiah 61:8", "description": "Highlights God''s love for justice and mercy."}, {"type": "crossReference", "reference": "Micah 6:8", "description": "Emphasizes the call to live out justice and mercy."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 1, 1, 'kjv', 'I will sing of mercy and judgment: unto thee, O Lord, will I sing.', 0, 66, 'theology', 'Nature of Worship', 'This verse suggests that true worship involves both praise and a commitment to living righteously. Many scholars believe that the act of singing is not merely an expression of joy but also a declaration of allegiance to God''s attributes of mercy and justice. This dual focus in worship calls believers to align their lives with God''s character, which is essential for spiritual growth and community integrity.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 101, 1, 1, 'kjv', 'I will sing of mercy and judgment: unto thee, O Lord, will I sing.', 0, 66, 'question', 'Purpose of Singing', 'What does it mean for you to ''sing of mercy and judgment'' in your daily life? How can this expression of worship influence your actions and decisions, especially in contexts of fairness and compassion towards others?', 'heart.text.square', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 1, 1, 'kjv', 'I will sing of mercy and judgment: unto thee, O Lord, will I sing.', 0, 66, 'greek', 'Key Hebrew Terms', 'חסד (chesed, H2617) - mercy, kindness, love; and משפט (mishpat, H4941) - judgment, justice. These terms highlight the aspects of God''s character that the psalmist aims to celebrate through worship, illustrating the depth of God''s covenant love and the importance of justice in His kingdom.', 'textformat.abc', '[{"type": "strongs", "reference": "H2617", "description": "Strong''s number for mercy."}, {"type": "strongs", "reference": "H4941", "description": "Strong''s number for judgment."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 2, 2, 'kjv', 'I will behave myself wisely in a perfect way.', 0, 45, 'connection', 'Wisdom in Proverbs', 'This phrase evokes Proverbs 4:26, "Ponder the path of your feet; then all your ways will be sure." Both verses emphasize the importance of wise behavior in daily life, suggesting that a life aligned with God''s principles leads to a solid foundation. The connection highlights the biblical theme of wisdom as a guiding force in the believer''s life.', 'link', '[{"type": "crossReference", "reference": "Proverbs 4:26", "description": "Emphasizes the importance of wise living."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 2, 2, 'kjv', 'I will walk within my house with a perfect heart.', 77, 126, 'theology', 'Integrity in the Home', 'Many scholars believe this verse reflects the idea that true spirituality begins at home. The ''perfect heart'' suggests an integrity that is not merely external but deeply rooted in one''s inner being. This aligns with the New Testament teaching found in 1 Timothy 3:5, where a leader''s ability to manage their household reflects their capability to lead in the church. Such integrity is foundational for a life that honors God.', 'person.2.fill', '[{"type": "crossReference", "reference": "1 Timothy 3:5", "description": "Connects the management of one''s household to spiritual leadership."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 101, 2, 2, 'kjv', 'O when wilt thou come unto me?', 46, 76, 'question', 'Longing for God''s Presence', 'What does this yearning for God’s presence reveal about your relationship with Him? This phrase invites reflection on our own desire for intimacy with God. How might a more profound longing for His presence transform our daily lives and decisions, particularly in the context of our homes and personal conduct?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 2, 2, 'kjv', 'I will behave myself wisely in a perfect way.', 0, 45, 'greek', 'Original Language Insight', 'חָכְמָה (chokmah, H2451) - wisdom, skill, proficiency. This Hebrew term underscores the practical application of wisdom in everyday choices, suggesting that true wisdom is both a divine gift and a skill to be cultivated. It reflects an understanding that wisdom involves not just knowledge but also the ability to apply it in a ''perfect way.''', 'textformat.abc', '[{"type": "strongs", "reference": "H2451", "description": "Strong''s number for the Hebrew word for wisdom."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 3, 3, 'kjv', 'I will set no wicked thing before mine eyes:', 0, 44, 'connection', 'Avoiding Sinful Influences', 'This phrase echoes Job 31:1, where Job states, ''I made a covenant with mine eyes; why then should I think upon a maid?'' Both verses emphasize the importance of guarding one''s sight against temptation and wicked influences. This connection highlights a consistent biblical theme of the need for self-discipline in our thoughts and actions to maintain spiritual integrity.', 'link', '[{"type": "crossReference", "reference": "Job 31:1", "description": "Job''s commitment to purity of thought."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 3, 3, 'kjv', 'I hate the work of them that turn aside;', 45, 85, 'theology', 'The Nature of Righteousness', 'Many scholars believe that this declaration reflects a profound understanding of righteousness and the believer''s stance against sin. It suggests that hatred for wickedness is a necessary aspect of loving goodness. This aligns with the biblical teaching that believers are called to reject evil (Romans 12:9) and pursue holiness as part of their sanctification process.', 'sparkles', '[{"type": "commentary", "reference": "Romans 12:9", "description": "Teaching on genuine love and abhorring evil."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 101, 3, 3, 'kjv', 'I will set no wicked thing before mine eyes:', 0, 44, 'question', 'What Influences Shape Us?', 'In what ways do the things we expose ourselves to affect our spiritual health? This question invites reflection on the media, conversations, and environments we choose and how they align with our commitment to righteousness as indicated in this verse.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 3, 3, 'kjv', 'it shall not cleave to me.', 86, 112, 'greek', 'Understanding ''Cleave''', 'דָּבַק (dabaq, H1692) - to cling, to cleave. This Hebrew term implies a deep, intimate attachment. The psalmist''s declaration that wickedness will not cling to him suggests a proactive stance against sin, reinforcing the idea that believers should actively strive to avoid being entangled by sin''s allure.', 'textformat.abc', '[{"type": "strongs", "reference": "H1692", "description": "Strong''s number for the Hebrew word used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 4, 4, 'kjv', 'A froward heart shall depart from me:', 0, 37, 'theology', 'Nature of the Heart', 'This phrase suggests the importance of a pure heart in one''s relationship with God. Many scholars believe that a ''froward heart'' symbolizes disobedience and rebellion against God''s ways. The departure of such a heart reflects God''s call for integrity and moral uprightness in His followers. Maintaining a heart aligned with God’s principles is essential for spiritual growth and fellowship with Him.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 101, 4, 4, 'kjv', 'I will not know a wicked person.', 38, 70, 'connection', 'Separation from Evil', 'This assertion connects with 1 Corinthians 15:33, which states, ''Do not be deceived: Bad company corrupts good morals.'' The psalmist emphasizes the need for moral discernment and the avoidance of wicked influences, which is a recurring theme in Scripture. This connection underscores the call for believers to maintain purity in their associations to preserve their spiritual integrity.', 'link', '[{"type": "crossReference", "reference": "1 Corinthians 15:33", "description": "Admonition against corrupting influences"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 4, 4, 'kjv', 'A froward heart shall depart from me:', 0, 37, 'greek', 'Understanding ''Froward''', 'עִקֵּשׁ (iqesh, H6141) - froward, perverse. This Hebrew word indicates a heart that is difficult, rebellious, or contrary to God’s will. The psalmist''s declaration to reject such a heart highlights the necessity of aligning one’s inner being with God''s standards, fostering a life of righteousness and obedience.', 'textformat.abc', '[{"type": "strongs", "reference": "H6141", "description": "Strong''s number for froward"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 4, 4, 'kjv', 'I will not know a wicked person.', 38, 70, 'question', 'Reflecting on Relationships', 'What does it mean to ''know'' a wicked person in the context of your life? How can you apply the principle of discerning relationships to your daily interactions? Reflecting on this can help illuminate areas where you might need to set boundaries to maintain your spiritual health.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 5, 5, 'kjv', 'Whoso privily slandereth his neighbour, him will I cut off:', 0, 59, 'connection', 'Slander in Scripture', 'This phrase resonates with Proverbs 10:18, which states, ''He who conceals hatred has lying lips, and whoever spreads slander is a fool.'' The connection highlights the severity of slander in both passages, underscoring that slander not only harms relationships but also reflects a moral failing. The psalmist''s commitment to cut off such individuals emphasizes the need for integrity within a community.', 'link', '[{"type": "crossReference", "reference": "Proverbs 10:18", "description": "Proverbs warns against slander and its foolishness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 5, 5, 'kjv', 'him that hath an high look and a proud heart will not I suffer.', 60, 123, 'theology', 'Pride and Community', 'Many scholars believe that this passage reflects the biblical theme of humility versus pride. The condemnation of the proud heart aligns with James 4:6, which states, ''God resists the proud, but gives grace to the humble.'' This suggests that a community rooted in faith must prioritize humility, as pride can lead to division and conflict among its members.', 'person.2.fill', '[{"type": "crossReference", "reference": "James 4:6", "description": "James emphasizes the importance of humility."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 101, 5, 5, 'kjv', 'Whoso privily slandereth his neighbour, him will I cut off:', 0, 59, 'question', 'Reflecting on Slander', 'What does it mean to slander someone privately, and how can we guard against this behavior in our own lives? Reflecting on this verse, consider the impact of our words on others and how speaking ill of someone can undermine trust and community. How might we actively promote a culture of respect and encouragement instead?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 101, 6, 6, 'kjv', 'Mine eyes shall be upon the faithful of the land', 0, 48, 'connection', 'Faithful Observed', 'This phrase connects with 2 Chronicles 16:9, which states, ''For the eyes of the Lord run to and fro throughout the whole earth, to show Himself strong on behalf of those whose heart is loyal to Him.'' Both verses highlight God''s attentive care for the faithful, emphasizing His desire to support those who are steadfast in their devotion. This connection underscores the biblical theme of God''s active engagement in the lives of His followers.', 'link', '[{"type": "crossReference", "reference": "2 Chronicles 16:9", "description": "Demonstrates God\u2019s vigilance over the faithful."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');