INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(19, 118, 4, 4, 'kjv', 'Let them now that fear the Lord say, that his mercy endureth for ever.', 0, 70, 'greek', 'Key Greek Term: Mercy', 'ἔλεος (eleos, G1656) - mercy, compassion. This term encapsulates the essence of God''s character as one who shows kindness and compassion to His people. It encourages believers to understand that God''s mercy is active and continually present in their lives, inviting them to respond with gratitude and reverence.', 'textformat.abc', '[{"type": "strongs", "reference": "G1656", "description": "Strong''s number for the Greek word for mercy."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 5, 5, 'kjv', 'I called upon the Lord in distress:', 0, 35, 'connection', 'Calling in Distress', 'This phrase resonates with Psalm 34:17, which states, ''The righteous cry, and the Lord hears, and delivers them out of all their troubles.'' Both verses emphasize the theme of divine response to human distress, underscoring the importance of prayer during challenging times. This connection highlights God’s faithfulness in times of trouble.', 'link', '[{"type": "crossReference", "reference": "Psalm 34:17", "description": "Another Psalm emphasizing God''s response to the cries of the righteous."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 5, 5, 'kjv', 'the Lord answered me,', 36, 57, 'theology', 'God’s Faithful Response', 'Many scholars believe this phrase reflects the theological principle of God’s responsiveness to prayer. It suggests that prayer is not merely a ritual but a relational communication with God, who actively engages with His people. This underlines the belief that God is attentive and willing to intervene in the lives of those who call upon Him.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the nature of God''s response to prayer."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 5, 5, 'kjv', 'and set me in a large place.', 58, 86, 'greek', 'Large Place Meaning', 'χωρίον (chōrion, G5561) - place, space. This term conveys the idea of a spacious or open area, symbolizing relief and freedom from confinement. In the context of distress, it can represent God''s provision of safety and abundance, contrasting the feelings of oppression experienced in times of trouble.', 'textformat.abc', '[{"type": "strongs", "reference": "G5561", "description": "Strong''s number for the Greek word meaning place."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 5, 5, 'kjv', 'I called upon the Lord in distress:', 0, 35, 'question', 'Personal Distress Reflection', 'In what specific situations have you called upon the Lord in distress, and how have you experienced His presence or answers in those moments? Reflecting on personal experiences may deepen your understanding of God''s faithfulness in crisis.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 6, 6, 'kjv', 'The Lord is on my side;', 0, 23, 'connection', 'Divine Support', 'This phrase resonates with Romans 8:31, which states, ''If God be for us, who can be against us?'' Both verses emphasize the assurance of God''s support in the face of opposition. This connection highlights a central theme in Scripture: the divine presence that accompanies believers, encouraging them to stand firm in faith.', 'link', '[{"type": "crossReference", "reference": "Romans 8:31", "description": "Paul''s affirmation of God''s support."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 6, 6, 'kjv', 'I will not fear: what can man do unto me?', 24, 65, 'theology', 'Trust Over Fear', 'Many scholars believe this verse encapsulates the doctrine of divine sovereignty and human frailty. The assurance of God''s presence leads to a profound peace that enables believers to confront fears. This reflects a broader theological understanding that trust in God supersedes human threats, as seen in various psalms that declare God''s protective nature.', 'sparkles', '[{"type": "commentary", "reference": "The Expositor''s Bible Commentary", "description": "Discusses the theme of divine protection."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 6, 6, 'kjv', 'The Lord is on my side;', 0, 23, 'greek', 'Original Language Insight', 'κύριος (kyrios, G2962) - lord, master. This term emphasizes authority and ownership, suggesting that if the Lord is on one’s side, He is both protector and sovereign. Understanding this term enriches the reader’s grasp of the profound security found in God’s presence.', 'textformat.abc', '[{"type": "strongs", "reference": "G2962", "description": "Strong''s number for ''lord.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 6, 6, 'kjv', 'I will not fear: what can man do unto me?', 24, 65, 'question', 'Confronting Fear', 'What does it mean to live without fear in a world filled with uncertainties? This verse challenges us to consider how our trust in God can transform our response to human threats. In what ways can we actively remind ourselves of God’s presence in our daily lives?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 7, 7, 'kjv', 'The Lord taketh my part with them that help me', 0, 46, 'connection', 'Divine Support Theme', 'This phrase resonates with Romans 8:31, which states, ''If God be for us, who can be against us?'' Both verses emphasize God''s active support for His people, encouraging trust in His protection and assistance during adversities. Recognizing that the Lord takes our part reinforces the concept of divine advocacy in times of trouble.', 'link', '[{"type": "crossReference", "reference": "Romans 8:31", "description": "Highlights God''s supportive role for believers"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 7, 7, 'kjv', 'therefore shall I see my desire upon them that hate me', 48, 102, 'theology', 'Justice and Retribution', 'Many scholars believe this phrase reflects the biblical theme of divine justice. The psalmist expresses a confident expectation that God will bring about justice against those who oppose Him and His followers. This aligns with the broader theological understanding of God as a righteous judge who ultimately vindicates His people, as seen in passages like Isaiah 61:2.', 'sparkles', '[{"type": "commentary", "reference": "Isaiah 61:2", "description": "Illustrates God''s role in delivering justice"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 7, 7, 'kjv', 'The Lord taketh my part with them that help me', 0, 46, 'question', 'Understanding Divine Assistance', 'What does it mean for you personally to know that the Lord takes your part in your struggles? How can this assurance shape your interactions with those who oppose you or misunderstand your faith?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 8, 8, 'kjv', 'It is better to trust in the Lord', 0, 33, 'connection', 'Trust in the Lord', 'This phrase echoes Proverbs 3:5-6, which encourages believers to trust in the Lord with all their heart and not lean on their own understanding. The connection emphasizes the importance of divine guidance over human wisdom, reinforcing that true security comes from reliance on God.', 'link', '[{"type": "crossReference", "reference": "Proverbs 3:5-6", "description": "Encouragement to trust in the Lord for guidance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 8, 8, 'kjv', 'to trust in the Lord', 13, 33, 'greek', 'Trust in Hebrew', 'בָּטַח (batach, H982) - to trust, rely upon, or have confidence in. This term conveys a deep-seated assurance in God''s faithfulness, illustrating that trust in the Lord is not merely intellectual but involves a heartfelt reliance that shapes one''s actions and decisions.', 'textformat.abc', '[{"type": "strongs", "reference": "H982", "description": "Strong''s number for the Hebrew word for trust."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 8, 8, 'kjv', 'than to put confidence in man.', 34, 64, 'theology', 'Human Fallibility', 'Many scholars believe this verse highlights the inherent limitations and fallibility of humanity. In contrast to God''s perfect nature, human beings are prone to error and can disappoint. This theological reflection encourages believers to seek ultimate security in God rather than in fallible leaders or allies.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses trust in God over man."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 8, 8, 'kjv', 'It is better to trust in the Lord', 0, 33, 'question', 'Reflecting on Trust', 'What does it mean to actively trust in the Lord in your daily life? How can you discern when you are relying more on human wisdom than divine guidance?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 9, 9, 'kjv', 'It is better to trust in the Lord', 0, 33, 'connection', 'Trust in the Lord', 'This phrase echoes Proverbs 3:5, which advises believers to trust in the Lord with all their heart and not lean on their own understanding. This connection emphasizes the biblical theme of reliance on divine wisdom over human judgment, highlighting the importance of faith in God''s guidance.', 'link', '[{"type": "crossReference", "reference": "Proverbs 3:5", "description": "Encourages trust in God over personal understanding."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 9, 9, 'kjv', 'than to put confidence in princes', 34, 67, 'theology', 'Human Authority vs Divine Authority', 'This passage suggests a theological distinction between human authority and divine authority. Many scholars believe that while earthly leaders may have power, their decisions are fallible. In contrast, trust in God is portrayed as a source of true strength and security, aligning with the broader biblical narrative of God''s sovereignty over human affairs.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 9, 9, 'kjv', 'It is better to trust in the Lord', 0, 33, 'question', 'Trust and Reliance', 'What does it mean to place your trust in the Lord in practical terms? How might this trust influence your decisions when faced with challenging situations, especially in contexts where human authority seems strong?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 9, 9, 'kjv', 'than to put confidence in princes', 34, 67, 'greek', 'Trust and Confidence', 'πίστις (pistis, G4102) - faith, trust, confidence. This Greek word captures the essence of reliance not just as a mental assent but as a deep-seated trust in God''s promises, contrasting with the temporary and often unreliable nature of human leaders.', 'textformat.abc', '[{"type": "strongs", "reference": "G4102", "description": "Refers to the concept of faith and trust in the New Testament."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 10, 10, 'kjv', 'All nations compassed me about: but in the name of the Lord will I destroy them.', 0, 80, 'connection', 'Nations Surrounded', 'This verse resonates with Psalm 2:1-2, which speaks of the nations raging against the Lord and His Anointed. The connection underscores the tension between earthly powers and divine sovereignty. Just as the psalmist declares victory in the Lord''s name, believers are reminded that ultimate authority lies with God, not with human rulers or nations.', 'link', '[{"type": "crossReference", "reference": "Psalm 2:1-2", "description": "Highlights the theme of nations opposing God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 10, 10, 'kjv', 'in the name of the Lord', 36, 59, 'theology', 'Authority in God''s Name', 'This phrase emphasizes the theological concept of acting under God’s authority. Many scholars believe that invoking God''s name signifies reliance on His power and character, reinforcing the idea that victory comes through divine intervention rather than human strength.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 10, 10, 'kjv', 'All nations compassed me about:', 0, 31, 'question', 'Facing Opposition', 'How does recognizing that ''all nations compassed'' the psalmist inform your understanding of spiritual warfare today? In what ways can you apply the assurance of victory ''in the name of the Lord'' to your own struggles against opposition?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 10, 10, 'kjv', 'will I destroy them.', 60, 80, 'greek', 'Destruction in Context', 'שָׁמַד (shamad, H8045) - to destroy, exterminate. This Hebrew term conveys the seriousness of divine judgment against those who oppose God. Understanding this word enhances the interpretation of divine justice as portrayed in the psalm.', 'textformat.abc', '[{"type": "strongs", "reference": "H8045", "description": "Strong''s number for the Hebrew word used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 11, 11, 'kjv', 'They compassed me about; yea, they compassed me about:', 0, 54, 'connection', 'Surrounded by Adversaries', 'This phrase echoes the experience of David in 2 Samuel 5:17, where he also faced adversaries conspiring against him. Both instances highlight the feeling of being encircled by enemies, which serves to emphasize the believer''s reliance on God in times of distress. The repeated nature of the phrase underscores the intensity of the situation, drawing attention to the relentless opposition faced.', 'link', '[{"type": "crossReference", "reference": "2 Samuel 5:17", "description": "David faces opposition from the Philistines."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 11, 11, 'kjv', 'but in the name of the Lord I will destroy them.', 55, 103, 'theology', 'Power in God''s Name', 'This passage suggests that invoking the name of the Lord signifies reliance on divine authority rather than personal strength. Many scholars believe that the ''name'' represents God''s character and power, thus affirming that victory comes not through human means but through faith in God''s sovereignty. This aligns with the broader biblical theme of God''s intervention in human affairs.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 11, 11, 'kjv', 'but in the name of the Lord I will destroy them.', 55, 103, 'question', 'Authority in God''s Name', 'What does it mean to act ''in the name of the Lord'' in your daily challenges? How can this principle shape your approach to conflict and adversity, trusting in God''s power rather than your own?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 11, 11, 'kjv', 'They compassed me about; yea, they compassed me about:', 0, 54, 'greek', 'Original Language Insight', 'Hebrew: סָבַב (sabab, H5437) - to surround, encircle. This term signifies not just physical encirclement but also the overwhelming feeling of being besieged by troubles, emphasizing the intensity of the psalmist''s plight.', 'text.book.closed', '[{"type": "strongs", "reference": "H5437", "description": "Strong''s number for the Hebrew word used."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 12, 12, 'kjv', 'They compassed me about like bees', 0, 33, 'connection', 'Bees in Scripture', 'The imagery of being surrounded like bees evokes a sense of relentless attack. This parallels the experience of David in 2 Samuel 22:6, where he speaks of the waves of death surrounding him. The connection highlights the feeling of being engulfed by adversaries, yet reassures us of God''s deliverance when we call upon His name.', 'link', '[{"type": "crossReference", "reference": "2 Samuel 22:6", "description": "David describes his near-death experiences and God''s rescue."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 12, 12, 'kjv', 'they are quenched as the fire of thorns', 35, 74, 'theology', 'Temporary Nature of Opposition', 'This phrase suggests that opposition, like thorns, is transient and ultimately ineffective against the power of God. Many scholars believe this reflects the larger biblical theme that earthly troubles, though fierce, are fleeting in comparison to God''s eternal nature and salvation, as seen in Isaiah 40:8.', 'sparkles', '[{"type": "crossReference", "reference": "Isaiah 40:8", "description": "The word of our God stands forever, contrasting temporary struggles."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 12, 12, 'kjv', 'for in the name of the Lord I will destroy them', 76, 123, 'greek', 'Name of the Lord', 'יְהוָה (YHWH, H3068) - the proper name of God, often translated as ''LORD.'' This name signifies God''s eternal presence and covenant faithfulness. In this verse, invoking His name underscores the authority and power believers have in facing adversaries, echoing Philippians 2:10 where every knee bows to the name of Jesus.', 'textformat.abc', '[{"type": "strongs", "reference": "H3068", "description": "YHWH is the distinctive name of God in the Hebrew Scriptures."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 12, 12, 'kjv', 'for in the name of the Lord I will destroy them', 76, 123, 'question', 'Power in His Name', 'What does it mean to invoke ''the name of the Lord'' when facing challenges? How can we apply this principle in our own lives when confronting opposition or adversity? Reflecting on the authority of God''s name, what steps can you take to align your responses with His strength?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 13, 13, 'kjv', 'Thou hast thrust sore at me that I might fall: but the Lord helped me.', 0, 70, 'connection', 'Affliction and Divine Help', 'This verse resonates with 2 Corinthians 12:9, where Paul discusses how God''s grace is sufficient in weakness. Both passages highlight human vulnerability and the assurance of divine assistance. The connection emphasizes that God’s intervention is critical in moments of distress, reinforcing the message of reliance on divine strength amidst trials.', 'link', '[{"type": "crossReference", "reference": "2 Corinthians 12:9", "description": "Paul''s reflection on God''s grace in weakness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 13, 13, 'kjv', 'Thou hast thrust sore at me that I might fall: but the Lord helped me.', 0, 70, 'theology', 'God''s Sustaining Power', 'This passage suggests a crucial theological concept: God as the ultimate source of strength and salvation in adversity. Many scholars believe that the phrase ''but the Lord helped me'' encapsulates the essence of deliverance theology, where reliance on God is paramount during trials. This reflects the broader biblical theme of God’s faithfulness in providing support when believers face overwhelming challenges.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary on the Psalms", "description": "Insight into God''s sustaining power in trials."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 13, 13, 'kjv', 'but the Lord helped me.', 47, 70, 'greek', 'Help and Salvation', 'עָזַר (azar, H5826) - to help, support. This Hebrew term highlights the action of assistance and support from God. It underscores the idea that divine help is not passive but actively engages in delivering the believer from peril. This word''s significance lies in its frequent use throughout Scripture in contexts of rescue and salvation, reinforcing the assurance of God''s presence in times of trouble.', 'textformat.abc', '[{"type": "strongs", "reference": "H5826", "description": "Strong''s number for the Hebrew term meaning ''to help''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 13, 13, 'kjv', 'Thou hast thrust sore at me that I might fall:', 0, 46, 'question', 'Understanding Adversity', 'What does it mean to experience severe thrusts that aim to cause our downfall, and how can this shape our understanding of God''s role in our struggles? Reflecting on this can illuminate how adversity often serves as a backdrop for recognizing divine intervention and support.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 14, 14, 'kjv', 'The Lord is my strength', 0, 23, 'connection', 'Source of Strength', 'This phrase connects with Philippians 4:13, which states, ''I can do all things through Christ who strengthens me.'' Both verses highlight the idea that true strength for believers comes from God. Recognizing the Lord as our strength encourages reliance on His power rather than our own.', 'link', '[{"type": "crossReference", "reference": "Philippians 4:13", "description": "Emphasizes reliance on God''s strength in our lives."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 14, 14, 'kjv', 'and song', 24, 32, 'theology', 'Joy in Salvation', 'The phrase ''and song'' reflects a theological principle that joy is a response to God''s deliverance. Many scholars believe that worship through song is a vital aspect of expressing gratitude and joy for salvation, connecting to passages like Ephesians 5:19, where believers are encouraged to sing and make melody in their hearts to the Lord.', 'sparkles', '[{"type": "commentary", "reference": "Ephesians 5:19", "description": "Highlights the role of song in expressing the joy of salvation."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 14, 14, 'kjv', 'is become my salvation', 38, 60, 'greek', 'Meaning of Salvation', 'יְשׁוּעָה (yeshuah, H3468) - salvation, deliverance. This Hebrew word emphasizes the concept of God as the ultimate savior, reflecting His role in bringing deliverance. The use of ''is become'' suggests a dynamic and ongoing relationship where God is actively working in the believer''s life to bring about salvation.', 'textformat.abc', '[{"type": "strongs", "reference": "H3468", "description": "Strong''s number for the Hebrew word for salvation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 14, 14, 'kjv', 'The Lord is my strength and song, and is become my salvation.', 0, 61, 'question', 'Understanding My Salvation', 'What does it mean for the Lord to be both my strength and my song? How does recognizing Him as my personal source of salvation influence my daily life and worship?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 15, 15, 'kjv', 'The voice of rejoicing and salvation', 0, 36, 'connection', 'Rejoicing in Salvation', 'This phrase connects with Isaiah 12:3, which states, ''With joy you will draw water from the wells of salvation.'' Both verses emphasize the joy that accompanies salvation, highlighting a theme of divine deliverance that brings forth communal celebration. Understanding this connection enhances the reader''s appreciation of the joyful response to God''s saving acts.', 'link', '[{"type": "crossReference", "reference": "Isaiah 12:3", "description": "Highlights the theme of joy in salvation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 15, 15, 'kjv', 'the tabernacles of the righteous', 43, 75, 'theology', 'Righteousness and Community', 'Many scholars believe that ''tabernacles'' symbolizes the community of believers, where God''s presence dwells. This reflects a broader theological concept that the righteous are not isolated but are called to live in community, sharing in the blessings of God''s salvation. The idea of community worship reinforces the importance of collective rejoicing in God''s deliverance.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 15, 15, 'kjv', 'the right hand of the Lord doeth valiantly', 77, 119, 'greek', 'Valiant Acts of God', 'יָד (yad, H3027) - hand, power, strength. This Hebrew word indicates not just a physical hand but symbolizes God''s power and authority in action. The phrase implies that God''s might is actively involved in the lives of the righteous, demonstrating His protective and saving nature.', 'textformat.abc', '[{"type": "strongs", "reference": "H3027", "description": "Strong''s number for the Hebrew word for ''hand.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 15, 15, 'kjv', 'The voice of rejoicing and salvation', 0, 36, 'question', 'Understanding Our Response', 'What does it mean for your life to express ''the voice of rejoicing and salvation'' within your community? Reflect on how you can actively participate in celebrating God''s salvation, not only in personal worship but also in communal gatherings.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 16, 16, 'kjv', 'The right hand of the Lord is exalted', 0, 37, 'connection', 'Exalted Right Hand', 'This phrase connects with Psalm 89:13, which states, ''You have a mighty arm; strong is Your hand, and high is Your right hand.'' Both verses emphasize God''s power and authority, highlighting how His right hand symbolizes His strength and capability to save. This connection underscores the overarching theme of divine deliverance in the Psalms.', 'link', '[{"type": "crossReference", "reference": "Psalm 89:13", "description": "Illustrates God''s might and authority"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 16, 16, 'kjv', 'the right hand of the Lord doeth valiantly', 39, 81, 'theology', 'Divine Action and Strength', 'This passage suggests a theological understanding of God''s active involvement in the world. Many scholars believe that the mention of God''s right hand performing valiantly signifies His omnipotence and His willingness to act on behalf of His people, affirming the belief in God''s providence and protection throughout Scripture.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(19, 118, 16, 16, 'kjv', 'The right hand of the Lord is exalted', 0, 37, 'question', 'Understanding God''s Exaltation', 'What does it mean for God''s right hand to be exalted in your life? How can recognizing His strength and authority reshape your understanding of challenges you face?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 16, 16, 'kjv', 'the right hand of the Lord doeth valiantly', 39, 81, 'greek', 'Understanding ''Valiantly''', 'ἀνδρίζομαι (andrizómai, G4040) - to act like a man, to be bold or courageous. This term conveys the vigor and courage of God''s actions, suggesting that His interventions in our lives are not just powerful but also imbued with a purpose and intentionality that inspires us to trust in His might.', 'textformat.abc', '[{"type": "strongs", "reference": "G4040", "description": "Defines the Greek term for ''valiantly''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 17, 17, 'kjv', 'I shall not die, but live', 0, 25, 'connection', 'Life and Resurrection', 'This phrase resonates with Romans 8:38-39, where Paul expresses confidence in God''s love and the assurance of eternal life. It highlights the victory over death through faith, which is a central theme in Christian doctrine. Both passages emphasize that life, in a spiritual sense, transcends physical death, reinforcing the hope found in Christ.', 'link', '[{"type": "crossReference", "reference": "Romans 8:38-39", "description": "Paul''s assurance of God''s love and eternal life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(19, 118, 17, 17, 'kjv', 'I shall not die, but live', 0, 25, 'theology', 'Victory Over Death', 'Many scholars believe this declaration reflects a fundamental Christian belief in the resurrection and the eternal life promised through Christ. This verse underscores the transformative power of faith, which assures believers that death is not the end but a passage to a more profound existence with God.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini');