INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(20, 2, 12, 12, 'kjv', 'that speaketh froward things', 59, 87, 'greek', 'Froward Speech', 'עִקְשָׁה (iqsha, H6140) - frowardness, perversity. This Hebrew term conveys a sense of distortion and moral deviation. The use of ''froward'' emphasizes the intentionality behind such speech, suggesting that the evil man actively seeks to lead others astray. This insight deepens our understanding of the impact of speech on community and personal faith.', 'textformat.abc', '[{"type": "strongs", "reference": "H6140", "description": "Strong''s number for frowardness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 12, 12, 'kjv', 'To deliver thee from the way of the evil man', 0, 44, 'question', 'Understanding Deliverance', 'What does it mean to be delivered from the ''way of the evil man''? In what practical ways can we seek wisdom to recognize and avoid such influences in our lives? This reflection challenges us to consider our daily choices and the sources we allow to shape our beliefs and behaviors.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 13, 13, 'kjv', 'Who leave the paths of uprightness, to walk in the ways of darkness;', 0, 68, 'connection', 'Paths of Uprightness', 'This phrase connects to Isaiah 2:3, where the call to walk in the Lord''s ways is emphasized. Both passages highlight the importance of choosing a life aligned with divine wisdom over one that strays into darkness. The contrast serves to remind believers of the active choice involved in following God’s path.', 'link', '[{"type": "crossReference", "reference": "Isaiah 2:3", "description": "Calls people to walk in the ways of the Lord."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 13, 13, 'kjv', 'Who leave the paths of uprightness, to walk in the ways of darkness;', 0, 68, 'question', 'Reflecting on Choices', 'What does it mean to leave the paths of uprightness in your own life? Consider specific moments when you have faced choices between light and darkness. How do those choices reflect your understanding of righteousness and the consequences of straying from God''s ways?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 13, 13, 'kjv', 'Who leave the paths of uprightness, to walk in the ways of darkness;', 0, 68, 'theology', 'The Nature of Sin', 'This passage suggests that sin involves a deliberate choice to abandon righteousness for darkness. Many scholars believe this reflects a broader theological theme of free will, where individuals are called to actively choose God''s ways over the allure of sin, which leads to spiritual death (Romans 6:23).', 'figure.mind.and.body', '[{"type": "crossReference", "reference": "Romans 6:23", "description": "Highlights the consequences of sin."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 13, 13, 'kjv', 'Who leave the paths of uprightness, to walk in the ways of darkness;', 0, 68, 'greek', 'Paths of Uprightness', 'צִדְקָה (tsidqah, H6666) - righteousness, justice. This Hebrew term emphasizes not only moral uprightness but also the relational aspect of being in right standing with God. Understanding this term deepens the impact of leaving the paths of righteousness, as it signifies abandoning a relationship that aligns with divine justice.', 'textformat.abc', '[{"type": "strongs", "reference": "H6666", "description": "Strong''s number for righteousness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 14, 14, 'kjv', 'Who rejoice to do evil, and delight in the frowardness of the wicked;', 0, 69, 'connection', 'Connection to Psalm 1', 'This phrase echoes the teachings of Psalm 1:1, which warns against walking in the counsel of the wicked. Both passages highlight a stark contrast between the path of the righteous and the allure of evil. The celebration of wickedness in Proverbs 2:14 serves as a cautionary reminder of the spiritual dangers that come from delighting in sinful behavior.', 'link', '[{"type": "crossReference", "reference": "Psalm 1:1", "description": "Warns against the company of the wicked and the consequences of their ways."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 14, 14, 'kjv', 'rejoice to do evil', 4, 22, 'theology', 'The Nature of Sin', 'This phrase illustrates the perverse nature of sin, where individuals find joy in wrongdoing. Many scholars believe this reflects the fallen human condition, as articulated in Romans 7:15, where Paul expresses the struggle against sin. This joy in evil is a distortion of God''s intended order and highlights the need for redemption through Christ.', 'person.2.fill', '[{"type": "commentary", "reference": "Romans 7:15", "description": "Paul''s struggle with sin exemplifies the inner conflict of desiring good yet being drawn to evil."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 14, 14, 'kjv', 'delight in the frowardness of the wicked', 28, 68, 'question', 'Reflection on Delight', 'What does it mean to find delight in the actions of the wicked? How does this perspective affect our own choices and values? Reflecting on this can help us understand the importance of aligning our delights with God’s righteousness rather than the fleeting pleasures of sin.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 14, 14, 'kjv', 'the frowardness of the wicked', 39, 68, 'greek', 'Understanding ''Frowardness''', 'עִקְשָׁה (iqsha, H6141) - crookedness, perversion. This Hebrew word indicates a deviation from the straight path, representing moral and ethical corruption. The use of this term emphasizes the deliberate choice to embrace a life contrary to God’s design, highlighting the seriousness of such behavior.', 'textformat.abc', '[{"type": "strongs", "reference": "H6141", "description": "Strong''s number for the Hebrew word representing crookedness."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 15, 15, 'kjv', 'Whose ways are crooked, and they froward in their paths:', 0, 56, 'connection', 'Crooked Paths in Scripture', 'This phrase echoes the warning found in Isaiah 59:8, which describes the ways of the wicked as not knowing peace. Both passages highlight the moral and spiritual consequences of straying from righteousness. Understanding this connection emphasizes the serious nature of choosing paths that deviate from God''s wisdom.', 'link', '[{"type": "crossReference", "reference": "Isaiah 59:8", "description": "Similar theme regarding the consequences of wickedness"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 15, 15, 'kjv', 'Whose ways are crooked, and they froward in their paths:', 0, 56, 'greek', 'Understanding ''Crooked''', 'The term ''crooked'' can be linked to the Greek word ἐκκλίνω (ekklínō, G1577) which means to turn aside or deviate. This highlights the intentional choice to stray from a straight path, reflecting a deeper spiritual condition of rebellion against God''s truth.', 'textformat.abc', '[{"type": "strongs", "reference": "G1577", "description": "Greek term meaning to turn aside"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 15, 15, 'kjv', 'Whose ways are crooked, and they froward in their paths:', 0, 56, 'theology', 'The Nature of Sin', 'Many scholars believe this verse illustrates the inherent nature of sin as a deviation from God''s ways. The ''crooked'' paths symbolize the moral corruption that results from rejecting divine wisdom. This suggests that the pursuit of righteousness requires diligence in aligning one''s life with God''s truth.', 'sparkles', '[{"type": "commentary", "reference": "Proverbs 2:15", "description": "Commentary insights on the moral implications of deviating from God''s path"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 15, 15, 'kjv', 'Whose ways are crooked, and they froward in their paths:', 0, 56, 'question', 'Reflecting on Our Paths', 'What does it mean for our lives to have ''crooked'' ways? In what areas might we be straying from the paths of righteousness, and how can we seek God''s guidance to redirect our steps towards His truth?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 16, 16, 'kjv', 'To deliver thee from the strange woman', 0, 38, 'connection', 'Deliverance from Temptation', 'This phrase connects to 1 Corinthians 10:13, which assures believers that God provides a way of escape from temptation. The ''strange woman'' symbolizes seductive influences that lead one away from God’s path. Recognizing this parallel underscores the importance of divine protection and guidance in the face of moral challenges.', 'link', '[{"type": "crossReference", "reference": "1 Corinthians 10:13", "description": "God provides a way out of temptation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 16, 16, 'kjv', 'even from the stranger which flattereth with her words', 40, 94, 'question', 'Nature of Flattery', 'What does it reveal about human nature that we are often drawn to flattery, even when it leads us away from God? How can we discern genuine encouragement from deceptive words that may lead us into sin?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 16, 16, 'kjv', 'the strange woman', 21, 38, 'theology', 'Symbol of Spiritual Adultery', 'The ''strange woman'' often represents not just physical temptation but also spiritual unfaithfulness. Many scholars believe that this imagery reflects the broader biblical theme of Israel''s infidelity to God, as seen in texts like Jeremiah 3:20. This suggests that one’s loyalty to God is paramount, and straying can have dire spiritual consequences.', 'sparkles', '[{"type": "crossReference", "reference": "Jeremiah 3:20", "description": "Israel''s infidelity as a metaphor for spiritual unfaithfulness."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 16, 16, 'kjv', 'which flattereth with her words', 63, 94, 'greek', 'Flattery in Original Language', 'χαρίεντες (charientes, G5482) - to be gracious, pleasing, or charming. This term emphasizes the seductive nature of flattery, highlighting how smooth and enticing words can lead one astray from wisdom and righteousness.', 'textformat.abc', '[{"type": "strongs", "reference": "G5482", "description": "Greek term for flattery."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 17, 17, 'kjv', 'Which forsaketh the guide of her youth, and forgetteth the covenant of her God.', 0, 79, 'connection', 'Covenant Themes', 'This verse connects with Deuteronomy 6:12, which warns against forgetting God after receiving blessings. Both emphasize the danger of neglecting one''s foundational relationship with God. The idea of forsaking the ''guide of her youth'' suggests a departure from the wisdom and teachings instilled during formative years, highlighting the need for continual remembrance and adherence to God''s covenant.', 'link', '[{"type": "crossReference", "reference": "Deuteronomy 6:12", "description": "A warning against forgetting God''s covenant after blessings."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 17, 17, 'kjv', 'guide of her youth', 20, 38, 'theology', 'Role of Guidance', 'Many scholars believe that the ''guide of her youth'' reflects the importance of spiritual mentorship and the formative teachings of the early years. This suggests that the community and familial teachings play a crucial role in shaping one''s faith journey. The abandonment of this guide is indicative of a larger spiritual decline that can lead to destructive choices.', 'person.2.fill', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the significance of early spiritual teachings."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 17, 17, 'kjv', 'forgetteth the covenant of her God.', 44, 79, 'greek', 'Covenant Meaning', 'בְּרִית (berith, H1285) - covenant, treaty, alliance. This term emphasizes the binding agreement between God and His people, highlighting that forgetting the covenant implies a severed relationship. In biblical terms, a covenant carries the weight of commitment and loyalty, underscoring the seriousness of neglecting such a foundational aspect of faith.', 'textformat.abc', '[{"type": "strongs", "reference": "H1285", "description": "Strong''s number for the Hebrew word for covenant."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 17, 17, 'kjv', 'Which forsaketh the guide of her youth', 0, 38, 'question', 'Reflecting on Guidance', 'What does it mean to you to ''forsake the guide of your youth''? In what ways might you be neglecting the foundational teachings of your early faith, and how can you actively remember and uphold them in your current walk with God?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 18, 18, 'kjv', 'For her house inclineth unto death, and her paths unto the dead.', 0, 64, 'connection', 'Death''s Path in Scripture', 'This verse connects with Romans 6:23, which states, ''For the wages of sin is death, but the gift of God is eternal life through Jesus Christ our Lord.'' Both passages highlight the dire consequences of turning away from God''s wisdom, illustrating how sin leads to spiritual death. Understanding this connection emphasizes the importance of choosing the path of righteousness over the seductive allure of sin.', 'link', '[{"type": "crossReference", "reference": "Romans 6:23", "description": "Connects consequences of sin with spiritual death."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 18, 18, 'kjv', 'inclineth unto death', 14, 34, 'greek', 'Understanding ''inclineth''', 'κλίνει (klinei, G2578) - to lean, incline, or bend. This term signifies a deliberate direction or inclination toward death, depicting a choice. It illustrates the seductive nature of sin, suggesting that one must actively choose to turn away from life-giving paths. The imagery invites readers to consider the consequences of their choices.', 'textformat.abc', '[{"type": "strongs", "reference": "G2578", "description": "Greek word for ''inclineth'' highlighting the active choice."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 18, 18, 'kjv', 'her paths unto the dead.', 40, 64, 'theology', 'Theological Implications of ''Paths''', 'This passage suggests that the ''paths'' mentioned symbolize the choices and lifestyles one follows. Many scholars believe that the imagery of paths represents moral and spiritual direction, indicating that choosing the wrong path leads to spiritual death. This aligns with the broader biblical theme of the narrow versus broad way, as seen in Matthew 7:13-14.', 'sparkles', '[{"type": "crossReference", "reference": "Matthew 7:13-14", "description": "Illustrates the choice between paths leading to life or destruction."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 18, 18, 'kjv', 'For her house inclineth unto death, and her paths unto the dead.', 0, 64, 'question', 'Reflecting on Life Choices', 'What does it mean to recognize that certain choices lead to spiritual death? How can the imagery of ''paths'' guide you in evaluating the directions you are taking in your life? Consider what it looks like to actively choose the path of the righteous.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 19, 19, 'kjv', 'None that go unto her return again,', 0, 35, 'connection', 'Destruction of the Unwise', 'This phrase resonates strongly with Romans 6:23, which states, ''For the wages of sin is death; but the gift of God is eternal life through Jesus Christ our Lord.'' Just as the path of foolishness leads to irreversible consequences, so does sin lead to spiritual death. Both passages emphasize the finality of the choices one makes, highlighting the importance of choosing wisely.', 'link', '[{"type": "crossReference", "reference": "Romans 6:23", "description": "Connects the consequences of sin with the fate of the unwise."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 19, 19, 'kjv', 'neither take they hold of the paths of life.', 36, 80, 'theology', 'The Paths of Life', 'This passage suggests that true life is found in following God''s ways. Many scholars believe that the ''paths of life'' represent the teachings and wisdom of God, which lead to spiritual fulfillment and eternal life. This aligns with John 14:6, where Jesus declares, ''I am the way, the truth, and the life.'' The idea is that rejecting wisdom leads to death, while embracing it brings one closer to the divine.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explores the implications of choosing God''s paths."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 19, 19, 'kjv', 'None that go unto her return again,', 0, 35, 'question', 'Reflecting on Choices', 'What does this verse teach about the permanence of our choices? In what ways might we be tempted to stray from the paths of wisdom, and how can we ensure that we remain on the right path that leads to life?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 19, 19, 'kjv', 'neither take they hold of the paths of life.', 36, 80, 'greek', 'Path and Life in Greek', 'ἄγω (agō, G71) - to lead, bring, or carry. This verb implies not just a passive following, but an active engagement in moving towards or grasping the paths of life. Understanding the active nature of this word can deepen our comprehension of the urgency in seeking wisdom and life.', 'textformat.abc', '[{"type": "strongs", "reference": "G71", "description": "Strong''s number for the Greek term related to leading and following."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 20, 20, 'kjv', 'walk in the way of good men', 17, 44, 'connection', 'Path of Righteousness', 'This phrase connects to Psalm 1:1-3, which speaks about the blessed man who does not walk in the counsel of the wicked but delights in the law of the Lord. Both passages highlight the importance of choosing the right path and surrounding oneself with good influences. The emphasis on ''walking'' signifies an active, ongoing journey rather than a static state.', 'link', '[{"type": "crossReference", "reference": "Psalm 1:1-3", "description": "Contrast between the righteous and wicked"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 20, 20, 'kjv', 'keep the paths of the righteous', 50, 81, 'theology', 'Preservation of Righteousness', 'This passage suggests that maintaining the paths of the righteous is essential for spiritual health and stability. Many scholars believe that this reflects the broader biblical theme of perseverance in faith and moral living, where walking in righteousness leads to blessings and fulfillment of God''s promises. This aligns with the teaching found in Matthew 7:13-14 about the narrow path leading to life.', 'sparkles', '[{"type": "crossReference", "reference": "Matthew 7:13-14", "description": "Narrow path that leads to life"}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 20, 20, 'kjv', 'the way of good men', 25, 44, 'question', 'Reflection on Goodness', 'What characteristics define ''good men'' in the context of this verse, and how can I actively seek to embody those traits in my daily life? Reflecting on this may lead to deeper insights about personal integrity and the impact of one''s choices on others.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 20, 20, 'kjv', 'keep the paths of the righteous', 50, 81, 'greek', 'Greek Insight', 'רָשָׁע (rasha, H7563) - wicked, guilty. This term underscores the contrast between the righteous and the wicked, highlighting that to ''keep'' the paths of the righteous is to actively turn away from the ways of the guilty.', 'textformat.abc', '[{"type": "strongs", "reference": "H7563", "description": "Strong''s number for wicked"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 21, 21, 'kjv', 'the upright shall dwell in the land', 4, 39, 'connection', 'Upright in the Land', 'This phrase connects with Psalm 37:29, which states, ''The righteous shall inherit the land and dwell upon it forever.'' Both verses emphasize the blessed state of the upright and the promise of stability and reward in their lives. This reinforces the idea that righteousness leads to a lasting legacy.', 'link', '[{"type": "crossReference", "reference": "Psalm 37:29", "description": "A parallel verse highlighting the rewards for the righteous."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 21, 21, 'kjv', 'the perfect shall remain in it', 45, 75, 'theology', 'Perfection and Endurance', 'Many scholars believe that ''the perfect'' refers to those who strive for moral integrity and faithfulness to God. This reflects the Christian understanding of sanctification, where believers are called to pursue holiness. The promise that they shall ''remain in it'' suggests a divine assurance of their salvation and place in God''s kingdom.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 21, 21, 'kjv', 'the upright shall dwell in the land', 4, 39, 'question', 'What does ''upright'' mean?', 'In what ways can we embody the characteristics of the ''upright'' in our daily lives? How does this commitment affect our relationship with God and our community, and what practical steps can we take to ensure we are living in alignment with His ways?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 21, 21, 'kjv', 'the perfect shall remain in it', 45, 75, 'greek', 'Understanding ''Perfect''', 'תָּמִים (tamim, H8549) - complete, whole, unblemished. This Hebrew word conveys a sense of integrity and moral soundness, indicating that those who are considered ''perfect'' are those who are blameless and sincere in their conduct towards God and others.', 'textformat.abc', '[{"type": "strongs", "reference": "H8549", "description": "Strong''s number for the Hebrew word meaning perfect."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 22, 22, 'kjv', 'the wicked shall be cut off from the earth', 4, 46, 'connection', 'Judgment of the Wicked', 'This phrase connects strongly with Psalm 37:9, which states, ''For evildoers shall be cut off; but those who wait upon the Lord, they shall inherit the earth.'' Both passages emphasize the ultimate fate of the wicked versus the righteous, underscoring a consistent biblical theme of divine justice. Understanding this connection can deepen our awareness of God’s moral order.', 'link', '[{"type": "crossReference", "reference": "Psalm 37:9", "description": "Highlights the fate of the wicked in contrast to the righteous."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 22, 22, 'kjv', 'the transgressors shall be rooted out of it', 52, 95, 'theology', 'Rooted Out as Divine Justice', 'Many scholars believe this phrase illustrates the doctrine of divine retribution, where God actively removes those who oppose His ways from the land. This reflects the larger biblical narrative of God''s justice and the moral order established in creation, as noted in various theological commentaries. It serves as a warning and encouragement for believers to remain steadfast in righteousness.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the concept of divine justice and retribution."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 2, 22, 22, 'kjv', 'the wicked shall be cut off from the earth', 4, 46, 'question', 'Understanding Divine Judgment', 'What does it mean for the wicked to be ''cut off from the earth''? How does this understanding influence our view of justice and morality in our own lives? Reflecting on this can lead to a deeper understanding of God’s holiness and the seriousness of sin.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 2, 22, 22, 'kjv', 'the transgressors shall be rooted out of it', 52, 95, 'greek', 'Significance of ''Rooted Out''', 'נָשַׁל (nashal, H5370) - to be uprooted, to be removed. This term conveys a sense of complete removal and destruction, emphasizing the severity of the consequences for those who transgress against God''s laws. It serves as a potent reminder of the finality of divine judgment.', 'textformat.abc', '[{"type": "strongs", "reference": "H5370", "description": "Strong''s number for the Hebrew term used in this context."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 1, 1, 'kjv', 'My son, forget not my law;', 0, 26, 'connection', 'Parental Instruction', 'This phrase resonates with Deuteronomy 6:6-7, where parents are commanded to impress God''s commandments on their children. The connection emphasizes the importance of familial teaching in nurturing faith and obedience to God’s law, highlighting the enduring role of parental guidance in spiritual development.', 'link', '[{"type": "crossReference", "reference": "Deuteronomy 6:6-7", "description": "Emphasizes the importance of teaching God''s commandments to children."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 1, 1, 'kjv', 'but let thine heart keep my commandments:', 27, 68, 'theology', 'Heart in Scripture', 'Many scholars believe that the ''heart'' in biblical terms signifies the center of one''s being, encompassing emotions, will, and intellect. This reflects a holistic approach to obedience, suggesting that true adherence to God''s commandments arises not merely from external compliance but from an inner transformation and commitment to His ways.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses the significance of the heart in relation to obedience."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 1, 1, 'kjv', 'forget not my law;', 8, 26, 'question', 'The Nature of Forgetting', 'What does it mean to ''forget'' God''s law in the context of daily life? How can we actively remember and engage with His commandments in a world filled with distractions? Reflecting on these questions can deepen one''s commitment to living according to God''s statutes.', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 1, 1, 'kjv', 'let thine heart keep my commandments:', 31, 68, 'greek', 'Heart and Commandments', 'לב (lev, H3820) - heart, mind, will. This Hebrew term underscores the importance of the inner self in relation to God''s commandments, suggesting that adherence is rooted in a deep, personal commitment rather than mere external action.', 'textformat.abc', '[{"type": "strongs", "reference": "H3820", "description": "Strong''s number for the Hebrew word for heart."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 2, 2, 'kjv', 'length of days, and long life, and peace, shall they add to thee.', 4, 69, 'connection', 'Life Promises in Scripture', 'This phrase echoes the promises found in Deuteronomy 5:16, where honoring one''s parents is linked to prolonged life. Additionally, Psalm 37:11 speaks of the meek inheriting the earth and enjoying peace. These connections highlight a biblical theme where obedience to God’s commandments fosters a harmonious and extended life.', 'link', '[{"type": "crossReference", "reference": "Deuteronomy 5:16", "description": "Connecting honor for parents with long life."}, {"type": "crossReference", "reference": "Psalm 37:11", "description": "The meek shall enjoy peace."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 2, 2, 'kjv', 'length of days, and long life, and peace, shall they add to thee.', 4, 69, 'theology', 'Divine Rewards for Obedience', 'Many scholars believe this verse reflects the theological principle that obedience to God''s commands leads to divine blessings. The idea that following God''s law results in physical and spiritual well-being is foundational in biblical teaching, emphasizing that God''s decrees are not merely rules but pathways to a fulfilling life.', 'sparkles', '[{"type": "commentary", "reference": "Proverbs Commentary by Matthew Henry", "description": "Discusses the blessings of obedience in Proverbs."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 2, 2, 'kjv', 'length of days, and long life, and peace, shall they add to thee.', 4, 69, 'question', 'Understanding God''s Peace', 'What does it mean to experience ''peace'' as a result of following God''s commandments? How might this peace manifest in your daily life, and what steps can you take to prioritize God''s law in your decision-making?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 2, 2, 'kjv', 'length of days, and long life, and peace, shall they add to thee.', 4, 69, 'greek', 'Key Terms in Context', 'שָׁלוֹם (shalom, H7965) - peace, completeness, welfare. The term ''peace'' here signifies more than the absence of conflict; it encompasses wholeness and well-being in all aspects of life, which is a central theme in understanding God’s intention for humanity.', 'textformat.abc', '[{"type": "strongs", "reference": "H7965", "description": "The Hebrew word for peace, indicating a holistic state of well-being."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini');