INSERT INTO bible_insights (book_id, chapter, verse_start, verse_end, translation_id, segment_text, segment_start_char, segment_end_char, insight_type, title, content, icon, sources, quality_tier, is_interpretive, prompt_version, model)
VALUES
(20, 3, 16, 16, 'kjv', 'in her left hand riches and honour.', 41, 76, 'connection', 'Wealth and Honor', 'This idea connects with Proverbs 8:18, which states, ''Riches and honor are with me; enduring wealth and righteousness.'' The connection is significant as it highlights that true wealth is a byproduct of wisdom and righteousness, not merely material gain. This suggests that the pursuit of wisdom leads to a holistic understanding of prosperity that includes both physical and spiritual dimensions.', 'link', '[{"type": "crossReference", "reference": "Proverbs 8:18", "description": "Highlights the relationship between wisdom and true wealth."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 16, 16, 'kjv', 'Length of days is in her right hand;', 0, 36, 'question', 'Value of Wisdom', 'What does it mean for wisdom to be associated with ''length of days''? How might this understanding influence the way we prioritize learning and growing in wisdom in our daily lives?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 16, 16, 'kjv', 'and in her left hand riches and honour.', 37, 76, 'greek', 'Wealth in Context', 'כָּבוֹד (kavod, H3519) - honor, glory, weight. This Hebrew term often denotes not just material riches but also respect and dignity in the community. Understanding this term broadens the perspective on what ''riches'' truly entail, extending beyond the physical to include social and spiritual standing.', 'textformat.abc', '[{"type": "strongs", "reference": "H3519", "description": "Strong''s number for the Hebrew word for honor."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 17, 17, 'kjv', 'Her ways are ways of pleasantness', 0, 33, 'connection', 'Pleasant Ways in Scripture', 'This phrase connects to Psalm 16:11, which states, ''You make known to me the path of life; in your presence there is fullness of joy; at your right hand are pleasures forevermore.'' Both verses emphasize that following divine wisdom leads to joy and fulfillment, highlighting the intrinsic goodness of God''s guidance.', 'link', '[{"type": "crossReference", "reference": "Psalm 16:11", "description": "Illustrates the joy found in God''s presence."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 17, 17, 'kjv', 'all her paths are peace', 39, 62, 'theology', 'Paths of Peace', 'Many scholars believe that the ''paths of peace'' refer to the holistic well-being that comes from living according to God''s wisdom. This aligns with the broader biblical theme that God''s ways lead to true peace, as seen in Isaiah 26:3, which promises peace to those whose minds are steadfast.', 'sparkles', '[{"type": "commentary", "reference": "Isaiah 26:3", "description": "Emphasizes the peace granted to those who trust in God."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 17, 17, 'kjv', 'Her ways are ways of pleasantness', 0, 33, 'question', 'Reflecting on Pleasantness', 'What does it mean for God''s ways to be ''pleasant''? How can this understanding influence our daily decisions and interactions with others?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 17, 17, 'kjv', 'all her paths are peace', 39, 62, 'greek', 'Understanding ''Peace''', 'שָׁלוֹם (shalom, H7965) - peace, completeness, welfare. This Hebrew term encompasses more than just the absence of conflict; it implies wholeness and well-being, suggesting that true peace is found in alignment with God''s wisdom.', 'textformat.abc', '[{"type": "strongs", "reference": "H7965", "description": "Strong''s number for the Hebrew word for peace."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 18, 18, 'kjv', 'She is a tree of life to them that lay hold upon her:', 0, 53, 'connection', 'Cross-Reference to Genesis', 'This phrase echoes the imagery of the ''tree of life'' found in Genesis 2:9, where it symbolizes divine sustenance and eternal life. In both contexts, the tree represents a source of wisdom and fulfillment, reinforcing the importance of seeking wisdom as a means to spiritual vitality.', 'link', '[{"type": "crossReference", "reference": "Genesis 2:9", "description": "Reference to the tree of life in the Garden of Eden."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 18, 18, 'kjv', 'happy is every one that retaineth her.', 58, 96, 'question', 'Understanding True Happiness', 'What does it mean to be truly ''happy'' in the context of retaining wisdom? How does this understanding challenge modern definitions of happiness based on external circumstances?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 18, 18, 'kjv', 'tree of life', 9, 21, 'theology', 'Symbol of Life and Wisdom', 'The ''tree of life'' is a profound symbol within Scripture, representing not only physical life but spiritual vitality as well. Many scholars believe it signifies the connection between divine wisdom and human flourishing, indicating that true life comes from aligning oneself with God''s wisdom.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Explains the significance of wisdom as the source of life."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 18, 18, 'kjv', 'lay hold upon her', 35, 52, 'greek', 'Original Language Insight', 'חַזַּק (chazaq, H2388) - to be strong, to strengthen, to seize. This term emphasizes the active engagement required to grasp and hold onto wisdom, suggesting that it involves effort and commitment.', 'textformat.abc', '[{"type": "strongs", "reference": "H2388", "description": "Hebrew word for ''lay hold'' with its meaning."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 19, 19, 'kjv', 'The Lord by wisdom hath founded the earth;', 0, 42, 'connection', 'Creation Foundation', 'This phrase echoes Genesis 1:1, which states, ''In the beginning God created the heaven and the earth.'' Both passages highlight the foundational role of divine wisdom in creation. Understanding that God used wisdom to establish the earth underscores the intentionality and order in creation, providing believers with assurance of God''s sovereignty over the universe.', 'link', '[{"type": "crossReference", "reference": "Genesis 1:1", "description": "This verse describes God''s act of creation, showing His power and authority."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 19, 19, 'kjv', 'by understanding hath he established the heavens.', 43, 92, 'theology', 'Divine Order', 'This passage suggests that God''s understanding is integral to the establishment of the heavens. Many scholars believe this reflects the theological concept of God''s omniscience, indicating that His knowledge and wisdom are the basis for the cosmos''s structure and function. This understanding reassures believers of God''s control and purpose in the created order.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Henry emphasizes God''s wisdom and understanding as foundational to creation."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 19, 19, 'kjv', 'The Lord by wisdom', 0, 18, 'greek', 'Key Term Insight', 'חָכְמָה (chokmah, H2451) - wisdom, skill, discernment. This Hebrew word encompasses not just knowledge but practical application and moral insight. In this context, it highlights the skillful artistry with which God created the earth, suggesting that wisdom is vital to understanding the natural and moral order.', 'textformat.abc', '[{"type": "strongs", "reference": "H2451", "description": "Strong''s number for the Hebrew word for wisdom."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 19, 19, 'kjv', 'The Lord by wisdom hath founded the earth;', 0, 42, 'question', 'Reflect on Wisdom', 'What does it mean to you that the Lord used wisdom to create the earth? How does acknowledging God''s wisdom in creation influence your understanding of the world around you?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 20, 20, 'kjv', 'By his knowledge the depths are broken up, and the clouds drop down the dew.', 0, 76, 'connection', 'Creation and Sustenance', 'This verse connects to Genesis 1:9-10, where God gathers the waters and calls the dry land Earth. Both passages highlight God''s authority over creation, illustrating His wisdom in establishing natural order. Understanding this connection emphasizes the role of divine knowledge in sustaining life, which is a recurring theme throughout Scripture.', 'link', '[{"type": "crossReference", "reference": "Genesis 1:9-10", "description": "God''s organization of creation highlights His wisdom."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 20, 20, 'kjv', 'knowledge', 7, 16, 'greek', 'Understanding Knowledge', 'יָדַע (yada, H3045) - to know, understand, perceive. In the Hebrew context, this term encompasses a relational and experiential knowledge, suggesting that God''s wisdom is not merely intellectual but deeply intertwined with His character and actions in creation.', 'textformat.abc', '[{"type": "strongs", "reference": "H3045", "description": "Strong''s number for the Hebrew term for knowledge."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 20, 20, 'kjv', 'the depths are broken up', 17, 41, 'theology', 'Divine Authority Over Nature', 'Many scholars believe this phrase reflects God''s sovereign power over creation, illustrating how His knowledge directs the forces of nature. This suggests a theological understanding that God''s wisdom is foundational not just for creation but also for the ongoing order and harmony in the universe, reinforcing the idea that all aspects of life are under His control.', 'sparkles', '[{"type": "commentary", "reference": "Proverbs 3:20", "description": "Commentary insights on divine wisdom and authority."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 20, 20, 'kjv', 'the clouds drop down the dew', 47, 75, 'question', 'Nature''s Dependence on God', 'What does the imagery of clouds dropping dew reveal about our dependence on God''s provision? How might understanding this dependence influence our daily lives and our trust in His wisdom?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 21, 21, 'kjv', 'let not them depart from thine eyes:', 8, 44, 'connection', 'Eyes of the Heart', 'This phrase echoes the sentiment found in Psalms 119:11, where the psalmist states, ''Thy word have I hid in mine heart, that I might not sin against thee.'' Both verses emphasize the importance of internalizing wisdom and keeping it at the forefront of one''s mind to guide moral decisions. This connection highlights the necessity of a heart and mind aligned with God''s wisdom.', 'link', '[{"type": "crossReference", "reference": "Psalms 119:11", "description": "The importance of internalizing God''s word."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 21, 21, 'kjv', 'keep sound wisdom and discretion:', 45, 78, 'greek', 'Wisdom and Discretion', 'σοφία (sophia, G4678) - wisdom, skill. The term ''sound wisdom'' in this context suggests a depth of understanding that is practical and beneficial. It encourages the reader to not only acquire knowledge but to apply it wisely in everyday life, fostering a lifestyle that reflects God''s principles.', 'textformat.abc', '[{"type": "strongs", "reference": "G4678", "description": "Strong''s number for the Greek term for wisdom."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 21, 21, 'kjv', 'keep sound wisdom and discretion:', 45, 78, 'theology', 'The Role of Wisdom', 'Many scholars believe that wisdom is not merely intellectual knowledge but a divine attribute that reflects God''s character. This passage suggests that sound wisdom and discretion are essential for living a life that is pleasing to God and beneficial to oneself and others. The integration of wisdom into daily living is a foundational teaching in Christian doctrine.', 'sparkles', '[{"type": "commentary", "reference": "Proverbs Commentary by Matthew Henry", "description": "Henry discusses the nature of wisdom in Proverbs."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 21, 21, 'kjv', 'let not them depart from thine eyes:', 8, 44, 'question', 'Focus on Wisdom', 'What practices can you implement in your daily life to ensure that wisdom and discretion remain in your sight? Reflect on the ways you can prioritize these virtues and how they can influence your decisions and actions.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 22, 22, 'kjv', 'life unto thy soul', 17, 35, 'theology', 'Spiritual Life Source', 'This phrase emphasizes that wisdom and discretion are vital for spiritual vitality. Many scholars believe that true life, as referenced here, transcends mere existence; it speaks to a flourishing relationship with God. This idea is echoed in John 10:10, where Jesus speaks of giving abundant life. Understanding wisdom as essential sustenance for the soul can transform how believers pursue knowledge and discernment in their lives.', 'sparkles', '[{"type": "crossReference", "reference": "John 10:10", "description": "Jesus speaks of abundant life, linking spiritual vitality to divine wisdom."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 22, 22, 'kjv', 'grace to thy neck', 41, 58, 'connection', 'Symbolism of Grace', 'The imagery of grace as an ornamental adornment suggests that wisdom and discretion not only protect but also beautify the believer''s life. This resonates with Proverbs 1:9, where wisdom is described as a crown of glory. The connection underscores how embracing wisdom enriches one''s character and testimony, making grace an intrinsic part of the believer''s identity.', 'link', '[{"type": "crossReference", "reference": "Proverbs 1:9", "description": "Wisdom as an adornment that enhances one''s life and character."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 22, 22, 'kjv', 'So shall they be life unto thy soul, and grace to thy neck.', 0, 59, 'question', 'Understanding Life and Grace', 'What does it mean for wisdom and discretion to be ''life'' to your soul? In what ways can you actively cultivate these qualities in your daily life to experience the grace they bring? Reflecting on this text invites us to consider the practical implications of wisdom in our spiritual journey and how it manifests in our choices and relationships.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 22, 22, 'kjv', 'So shall they be life unto thy soul, and grace to thy neck.', 0, 59, 'greek', 'Key Greek Terms', 'חַיִּים (chayyim, H2416) - life. This term denotes not just physical existence but a holistic, vibrant life. The concept of ''grace'' in this context can also be linked to the Hebrew term חֵן (chen, H2580), which signifies favor and beauty, suggesting that wisdom brings a favorable and beautiful aspect to life.', 'textformat.abc', '[{"type": "strongs", "reference": "H2416", "description": "Indicates the richness of the term ''life'' in the Hebrew context."}, {"type": "strongs", "reference": "H2580", "description": "Highlights the significance of grace and favor in the believer''s life."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 23, 23, 'kjv', 'Then shalt thou walk in thy way safely,', 0, 39, 'connection', 'Safety in Guidance', 'This phrase connects with Psalms 119:105, ''Thy word is a lamp unto my feet, and a light unto my path.'' Both verses emphasize the importance of divine guidance in ensuring a safe and secure journey through life. The psalmist highlights the role of God’s word in illuminating our way, paralleling the idea that wisdom leads to safety.', 'link', '[{"type": "crossReference", "reference": "Psalms 119:105", "description": "Illustrates the importance of God''s guidance."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 23, 23, 'kjv', 'and thy foot shall not stumble.', 40, 71, 'theology', 'Divine Protection', 'This passage suggests a theological principle of divine protection for those who follow God''s wisdom. Many scholars believe that living according to God''s instructions provides spiritual stability and prevents moral or spiritual failure. The concept of not stumbling signifies faith in God''s promise to uphold His followers.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 23, 23, 'kjv', 'Then shalt thou walk in thy way safely,', 0, 39, 'question', 'Understanding ''Walk Safely''', 'What does it mean to ''walk in thy way safely'' in the context of your daily choices? How can you apply the wisdom and discretion mentioned in preceding verses to ensure that your path remains secure amidst life''s challenges?', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 23, 23, 'kjv', 'Then shalt thou walk in thy way safely,', 0, 39, 'greek', 'Key Term: Walk', 'הלך (halak, H1980) - to walk, go, come. This Hebrew term emphasizes the act of living one''s life in accordance with God''s directives. It signifies not just physical movement, but a lifestyle that embodies faithfulness and obedience to God’s wisdom.', 'textformat.abc', '[{"type": "strongs", "reference": "H1980", "description": "Strong''s number for the Hebrew term for ''walk''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 24, 24, 'kjv', 'When thou liest down, thou shalt not be afraid: yea, thou shalt lie down, and thy sleep shall be sweet.', 0, 103, 'connection', 'Peaceful Rest', 'This verse resonates with Psalm 4:8, which states, ''I will both lay me down in peace, and sleep: for thou, Lord, only makest me dwell in safety.'' Both passages emphasize the divine assurance of peace and safety during rest, highlighting the trust believers can place in God to provide security amidst uncertainties.', 'link', '[{"type": "crossReference", "reference": "Psalm 4:8", "description": "Similar themes of peace and safety during sleep."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 24, 24, 'kjv', 'thou shalt not be afraid', 22, 46, 'theology', 'Divine Assurance', 'Many scholars believe this phrase reflects a core aspect of biblical theology: the reassurance given by God to His people not to fear. This assurance is rooted in the promise of His presence and protection, as seen throughout Scripture (e.g., Isaiah 41:10). It suggests that trust in God alleviates fear, which is a foundational element of the believer''s relationship with Him.', 'person.2.fill', '[{"type": "commentary", "reference": "Isaiah 41:10", "description": "God''s promise to provide strength and support."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 24, 24, 'kjv', 'thy sleep shall be sweet.', 78, 103, 'question', 'Understanding Sweet Sleep', 'What does it mean for sleep to be ''sweet'' in the context of trusting God? Consider how this might reflect not only physical rest but also spiritual peace. How does your reliance on God impact your ability to experience restful sleep amidst life''s challenges?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 24, 24, 'kjv', 'When thou liest down', 0, 20, 'greek', 'Hebrew Insight', 'שָׁכַב (shakab, H7901) - to lie down, to rest. This term conveys not just the act of lying down physically but also implies a state of trust and surrender to God. In the context of this verse, it underscores the importance of resting in God’s promises and finding solace in His presence as we conclude our day.', 'textformat.abc', '[{"type": "strongs", "reference": "H7901", "description": "Hebrew word for ''to lie down.''"}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 25, 25, 'kjv', 'Be not afraid of sudden fear,', 0, 29, 'connection', 'Fear Not', 'This phrase echoes the command found in Isaiah 41:10, where God reassures His people not to fear because He is with them. The connection highlights the consistent biblical theme of God’s presence offering comfort in times of fear. It emphasizes that believers can trust in God''s protection amidst sudden and unexpected challenges.', 'link', '[{"type": "crossReference", "reference": "Isaiah 41:10", "description": "God''s reassurance to His people not to fear."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 25, 25, 'kjv', 'neither of the desolation of the wicked,', 30, 70, 'theology', 'Nature of Wickedness', 'This passage suggests that the desolation of the wicked serves as a warning of the consequences of sin. Many scholars believe that this reflects the doctrine of divine justice, wherein God allows visible consequences for wickedness. This can serve as a comfort to the righteous, reminding them that justice will ultimately prevail.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 25, 25, 'kjv', 'when it cometh.', 71, 86, 'question', 'Facing Unexpected Trials', 'What does it mean for us to not fear when unexpected trials arise? How can we cultivate a mindset that trusts in God’s sovereignty and protection, especially when faced with sudden challenges or the apparent success of the wicked?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 25, 25, 'kjv', 'Be not afraid of sudden fear,', 0, 29, 'greek', 'Understanding ''Fear''', 'φόβος (phobos, G5401) - fear, terror, dread. The term used here emphasizes a deep-rooted sense of anxiety that can arise suddenly. Understanding the nature of this fear helps us grasp the gravity of the admonition not to succumb to it, as God provides the strength to overcome such instincts.', 'textformat.abc', '[{"type": "strongs", "reference": "G5401", "description": "Strong''s definition of ''fear''."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 26, 26, 'kjv', 'For the Lord shall be thy confidence,', 0, 37, 'connection', 'Confidence in the Lord', 'This phrase resonates with Psalm 27:1, which states, ''The LORD is my light and my salvation; whom shall I fear?'' Both passages emphasize the idea that true security and assurance come from a relationship with God. This connection underscores the importance of placing our trust in Him, especially in times of uncertainty.', 'link', '[{"type": "crossReference", "reference": "Psalm 27:1", "description": "Highlights the theme of finding confidence in God."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 26, 26, 'kjv', 'shall keep thy foot from being taken.', 42, 79, 'theology', 'Divine Protection', 'This passage suggests that God not only provides confidence but actively protects His people from pitfalls and traps. Many scholars believe this reflects a broader biblical theme of God''s safeguarding presence, as seen in Psalm 91:11-12, where angels are commanded to guard the faithful. This assurance invites believers to trust in God''s providential care.', 'sparkles', '[{"type": "commentary", "reference": "Matthew Henry''s Commentary", "description": "Discusses God''s protective nature in the lives of believers."}]'::jsonb, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 26, 26, 'kjv', 'For the Lord shall be thy confidence,', 0, 37, 'question', 'Source of Confidence', 'What does it mean for the Lord to be your confidence in daily life? In what specific situations can you consciously rely on His strength rather than your own? Reflecting on this can deepen your understanding of God’s role as a source of stability and assurance.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 26, 26, 'kjv', 'and shall keep thy foot from being taken.', 38, 79, 'greek', 'Original Language Insight', 'טָפַס (ṭāpas, H8610) - to take, seize, or capture. This Hebrew term emphasizes the idea of being caught off guard or trapped. Understanding this word enriches our comprehension of God''s protective role, as He prevents us from falling into spiritual or moral dangers.', 'textformat.abc', '[{"type": "strongs", "reference": "H8610", "description": "Strong''s number for the term used in the phrase."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 27, 27, 'kjv', 'Withhold not good from them to whom it is due, when it is in the power of thine hand to do it.', 0, 94, 'connection', 'Related Teachings on Generosity', 'This verse parallels Galatians 6:10, which encourages believers to do good to all, especially to fellow believers. Both passages emphasize the moral imperative of generosity and the responsibility we have toward others when we have the means to help. The connection reminds us that our actions reflect our faith and the love we are called to demonstrate.', 'link', '[{"type": "crossReference", "reference": "Galatians 6:10", "description": "Encouragement to do good to all, especially those of the faith."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 27, 27, 'kjv', 'Withhold not good from them to whom it is due, when it is in the power of thine hand to do it.', 0, 94, 'theology', 'Moral Obligation to Act', 'This passage suggests that believers have a moral duty to provide for others when they are able. Many scholars believe this reflects God''s character, as He is generous and provides for our needs. It also aligns with the broader biblical theme of justice and care for the vulnerable, indicating that withholding good can be seen as a failure to live out God''s love and righteousness.', 'person.2.fill', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 27, 27, 'kjv', 'to whom it is due', 28, 45, 'greek', 'Understanding ''Due''', 'חַיָּב (chayyav, H225) - obligated, indebted. This Hebrew term indicates a sense of duty or obligation to fulfill the needs of others, particularly those who have a rightful claim to our assistance. The use of this term underscores the principle that our good deeds should be directed towards those who genuinely require our help.', 'textformat.abc', '[{"type": "strongs", "reference": "H225", "description": "Strong''s number for the Hebrew term indicating obligation."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 27, 27, 'kjv', 'when it is in the power of thine hand to do it.', 47, 94, 'question', 'Reflection on Capability', 'What does it mean to have the power to do good? Are there times when we have the means but hesitate to act? Reflecting on this can help us assess our own willingness to engage in acts of kindness and generosity, aligning our actions with our faith commitments.', 'lightbulb', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 28, 28, 'kjv', 'Say not unto thy neighbour, Go, and come again, and to morrow I will give; when thou hast it by thee.', 0, 101, 'connection', 'Neighborly Responsibility', 'This verse resonates with James 4:17, which states, ''Therefore to him that knoweth to do good, and doeth it not, to him it is sin.'' Both passages emphasize the moral obligation to act promptly and benevolently towards others when one has the means. This connection highlights the urgency of fulfilling our duties to our neighbors without delay.', 'link', '[{"type": "crossReference", "reference": "James 4:17", "description": "Emphasizes the importance of acting on knowledge of good."}]'::jsonb, 'standard', false, 'v1.0', 'gpt-4o-mini'),
(20, 3, 28, 28, 'kjv', 'when thou hast it by thee.', 75, 101, 'theology', 'Acting with Intent', 'This passage suggests that believers are called to act with intentionality when they possess the ability to help others. Many scholars believe this reflects a broader theological principle of stewardship, where one''s resources and abilities are seen as gifts from God meant for the benefit of others. This reinforces the concept of community and mutual support within Christian teachings.', 'sparkles', NULL, 'standard', true, 'v1.0', 'gpt-4o-mini'),
(20, 3, 28, 28, 'kjv', 'Say not unto thy neighbour, Go, and come again,', 0, 47, 'question', 'Urgency in Generosity', 'What does it reveal about our hearts when we delay helping a neighbor in need, especially when we have the means at hand? In what ways might we be unintentionally withholding good from those around us?', 'questionmark.circle.fill', NULL, 'standard', false, 'v1.0', 'gpt-4o-mini');